INSERT INTO `article_content` (`id`, `code`, `cp_id`, `cp_genre_id`, `cp_sub_genre_id`, `genre_id`, `sub_genre_id`, `uid`, `hash_timestamp`, `title`, `short_title`, `body`, `article_type`, `post_start_date`, `post_end_date`, `first_composition_date_main`, `first_composition_date_other`, `category_composition_date`, `category_integration_time`, `relation_title_1`, `relation_url_1`, `relation_title_2`, `relation_url_2`, `relation_title_3`, `relation_url_3`, `relation_title_4`, `relation_url_4`, `relation_title_5`, `relation_url_5`, `deleted_flag`, `import_deleted_flag`, `created_user_id`, `updated_user_id`, `created_date`, `updated_date`)
VALUES
    (1, 'code_1', 1, 2, 3, 2, 3, 'http://article.auone.jp/code_1', NULL, '長文タイトルです', '省略タイトル', '本文本文本文本文本文本文本文本文本文本文\n本文本文本文本文本文本文本文本文本文本文\n本文本文本文本文本文本文本文本文本文本文\n本文本文本文本文本文本文本文本文本文本文\n本文本文本文本文本文本文本文本文本文本文\n', 1, '2016-01-01 10:00:00', '2020-01-01 10:00:00', NULL, NULL, NULL, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 1, 1, '2016-11-14 18:55:21', '2016-11-14 18:55:21'),
    (2, '4_1_r_20161207_1481096043171951053', 4, 1, 1, 1, 1, 'http://www.oricon.co.jp/news/2082669/', 'db53893bec9878c793840a1abfa7c027_1481096043', '新垣結衣主演『逃げ恥』またも自己最高更新　第9話は16.9％', '', '<img src=\"http://news.oricon.co.jp/feed/images/size640wh/20161207_125712_size640wh_7041.jpg\" title=\"新垣結衣主演TBS系連続ドラマ『逃げるは恥だが役に立つ』（毎週火曜 後10：00）の視聴率が大幅アップ （C）TBS\" alt=\"新垣結衣主演TBS系連続ドラマ『逃げるは恥だが役に立つ』（毎週火曜 後10：00）の視聴率が大幅アップ （C）TBS\" ><br />\n　女優・新垣結衣（28）が主演するTBS系連続ドラマ『逃げるは恥だが役に立つ』(毎週火曜　後10：00)の第9話が6日放送され、平均視聴率が16.9％だったことが7日、わかった。（ビデオリサーチ調べ、関東地区）。前週の16.1％からまたも上昇となり、TBS火曜ドラマ枠の歴代最高視聴率をさらに更新した。<br />\n<br />\n　『逃げ恥』の視聴率は、初回から10.2％→ 12.1％→ 12.5％→ 13.0％→ 13.3％→ 13.6％→ 13.6％→ 16.1%→ 16.9％と好調に推移。これまで一度も視聴率を落としたことはなく大きなムーブメントとなっている。<br />\n<br />\n　また、各地方局での視聴率も好調な記録を残しており、第9話では、中国放送（広島地区）で23.5％、北海道放送（北海道地区）で22.8％、静岡放送（静岡地区）で22.6％、CBCテレビ（名古屋地区）で22.5％と、過去最多4つの局で20％の大台を超える結果となった。<br />\n<br />\n　第9話は、主人公・森山みくり（新垣）と津崎平匡（星野源）が「離婚の危機」から脱し、みくりは2人で過ごす日々の幸せをかみ締めていた。そんな中、ある日みくりが、津崎と取引先の女性・五十嵐杏奈（内田理央）が2人で歩いているところを目撃してしまう。杏奈にやきもちをやくみくりだったが、それをきっかけにお互いの気持ちを再確認し…といった展開だった。', 1, '2016-12-07 13:11:01', '2017-02-05 13:11:01', NULL, NULL, NULL, 0, 'http://www.oricon.co.jp/news/2081490/photo/4/?anc=096', '【写真】ドラマ内で清楚なセーラー服姿を披露したガッキー', 'http://www.oricon.co.jp/news/70401/photo/1/?anc=096', '【写真】こちらは7年前…制服姿を披露した新垣結衣', 'http://www.oricon.co.jp/news/2067714/photo/1/?anc=096', '【写真】美しすぎる…白のひざ上ワンピのガッキー', '', '', '', '', 0, 0, 1, 1, '2016-12-07 16:34:03', '2016-12-07 16:34:03'),
    (3, '4_1_r_20161207_1481096043189902019', 4, 1, 1, 1, 1, 'http://www.oricon.co.jp/news/2082659/', '923651ab8a48a1c5c059a37b9fb7b278_1481096043', '水川あさみ、サンタクロース登場に乙女の笑顔', '', '<img src=\"http://news.oricon.co.jp/feed/images/size640wh/20161207_125927_size640wh_2581.jpg\" title=\"淡いオリーブ色のミニワンピースで登場した水川あさみ／DHC新CMお披露目記者発表会にて （C）oricon ME inc.\" alt=\"淡いオリーブ色のミニワンピースで登場した水川あさみ／DHC新CMお披露目記者発表会にて （C）oricon ME inc.\" ><br />\n　女優の水川あさみが７日、都内で行われたDHC新CMお披露目記者発表会に登場。淡いオリーブ色のミニワンピース姿で大人の品を漂わせた水川だったが、同じくオリーブ色の衣装に身を包んだサンタクロースが登場すると思わず少女のような笑顔に。サンタクロースに両手で手を振ったり、サンタクロースの帽子の先に付いたオリーブの実を発見して会場に見えるよう帽子の先をつまんでみせるなど、乙女な一面を垣間見せた。<br />\n<br />\n　ロングセラーアイテム『DHC オリーブバージンオイル』のCMに抜擢された水川。スペイン・アンダルシア地方の、朝日を浴びるオリーブ畑で行われたCM撮影について「見たことないくらい幻想的でした」と振り返り、「マジックタイムと言われる朝の10分弱が勝負で。スタッフのみなさんも私も気合いを入れて挑みました」と裏話も明かした。<br />\n<br />\n　水川が出演する同CMは10日より放送される。', 1, '2016-12-07 12:54:04', '2017-02-05 12:54:04', NULL, NULL, NULL, 0, 'http://www.oricon.co.jp/news/2081991/?cat_id=TKD&anc=017', '水川あさみ主演、人気コラム「東京女子図鑑」実写ドラマ化 （16年11月25日）', 'http://www.oricon.co.jp/news/2059308/?cat_id=TKD&anc=017', '水川あさみ、大東駿介との熱愛語らず　“声”絶賛に赤面 （15年09月17日）', 'http://beauty.oricon.co.jp/trend-culture/trend/news/2082633/?cat_id=YMK&anc=017', 'サンタクロースが指南　Xmasギフトは「詰め込みスタイル」がベスト （16年12月06日）', '', '', '', '', 0, 0, 1, 1, '2016-12-07 16:34:03', '2016-12-07 16:34:03'),
    (4, '4_1_r_20161207_1481096043205045227', 4, 1, 1, 1, 1, 'http://www.oricon.co.jp/news/2082653/', '78eaa9eda48f734b01525b8086c048a3_1481096043', 'スター・ウォーズ最新作に、ダース・ベイダー以外のサプライズある　監督「保証します」', '', '<img src=\"http://news.oricon.co.jp/feed/images/size640wh/20161207_115516_size640wh_1109.jpg\" title=\"映画『ローグ・ワン／スター・ウォーズ・ストーリー』記者会見に出席した（左から）ディエゴ・ルナ、フェリシティ・ジョーンズ、ギャレス・エドワーズ監督 （C）ORICON NewS inc.\" alt=\"映画『ローグ・ワン／スター・ウォーズ・ストーリー』記者会見に出席した（左から）ディエゴ・ルナ、フェリシティ・ジョーンズ、ギャレス・エドワーズ監督 （C）ORICON NewS inc.\" ><br />\n　「スター・ウォーズ」最新作『ローグ・ワン／スター・ウォーズ・ストーリー』（12月16日公開）の主人公ジン役フェリシティ・ジョーンズ（33）、情報将校のキャシアン役ディエゴ・ルナ（36）、そしてギャレス・エドワーズ監督（41）が7日、都内で記者会見を行った。<br />\n<br />\n　同作は、ジョージ・ルーカス氏が生み出した銀河宇宙を舞台にした壮大な叙事詩、「スター・ウォーズ」シリーズ初のアナザーストーリー。『スター・ウォーズ　エピソード4／新たなる希望』（1977年）につながる物語ということで、すでにダース・ベイダーが登場することは発表されているが、エドワーズ監督は「ダース・ベイダー以外にもキャラクターは出てくることは保証します」と言及。「が、詳しくは言えないんです」と笑いに紛らせた。<br />\n<br />\n　エドワーズ監督は「ダース・ベイダーを演出すること自体、夢のようなこと。撮影現場はうるさいものだが、あれ？　静かになってきたなと思って、そちらを向くと、ダース・ベイダーがいるんだ。40代のスタッフが多かったんだけど、皆、5歳の子どもみたいになって、何もしゃべれなくなるんだ」と、撮影時の興奮を思い出したように笑顔で語った。<br />\n<br />\n　「ダース・ベイダー役の俳優には、リハーサルの時はヘルメットを取るようにお願いしたんだ。だってダメ出ししたらフォースでやられてしまいそうだろ」と話していた。<br />\n<br />\n　フェリシティは、約10年前にシェイクスピア劇の上演で来日して以来、プロモーションでは初。ディエゴは9年ぶり。エドワーズ監督は映画『GODZILLA ゴジラ』のプロモーション以来、2年ぶり4回目の来日となる。<br />\n<br /><br />\n<iframe width=\"640\" height=\"360\" src=\"https://www.youtube.com/embed/806Up7wBHfA\" frameborder=\"0\" allowfullscreen></iframe>', 1, '2016-12-07 11:57:08', '2017-02-05 11:57:08', NULL, NULL, NULL, 0, 'http://www.oricon.co.jp/news/2082653/photo/2/?anc=014', '【写真】SWの世界観と「希望」をテーマにした襖絵', 'http://www.oricon.co.jp/news/2082664/?anc=014', '「スター・ウォーズ」最新作、エドワーズ監督「黒澤映画みたいにしたかった」 （16年12月07日）', 'http://www.oricon.co.jp/news/2082585/?anc=014', '『ローグ・ワン』、“つながっている”『エピソード4』との比較映像公開 （16年12月06日）', '', '', '', '', 0, 0, 1, 1, '2016-12-07 16:34:03', '2016-12-07 16:34:03'),
    (5, '4_1_r_20161207_1481096043219187712', 4, 1, 1, 1, 1, 'http://www.oricon.co.jp/news/2082679/', 'd0e05cb102ce08f9c92415b5c6aa96cd_1481096043', '坂本龍一が『グラミー賞』候補に　ビヨンセが最多9部門', '', '<img src=\"http://news.oricon.co.jp/feed/images/size640wh/20161207_154337_size640wh_1949.jpg\" title=\"『第59回グラミー賞』にノミネートされた坂本龍一\" alt=\"『第59回グラミー賞』にノミネートされた坂本龍一\" ><br />\n　来年2月12日（日本時間13日）に開催される米音楽界最高峰の祭典『第59回グラミー賞』全84部門のノミネート作品／アーティストが6日（日本時間7日）、発表され、音楽家・坂本龍一がドイツの音楽家アルヴァ・ノトとともに手がけた米映画『レヴェナント：蘇えりし者』のサウンドトラックが「最優秀スコア・サウンドトラック・アルバム」部門にノミネートされた。<br />\n<br />\n　坂本がグラミー賞にノミネートされるのは、1995年に映画『リトル・ブッダ』のテーマ音楽に続いて3回目。受賞すると、89年に『ラストエンペラー』のテーマ音楽で「最優秀オリジナル映画音楽アルバム」を獲得して以来、2度目となる。<br />\n<br />\n　また、ピアニスト・内田光子がドイツのソプラノ歌手ドロテア・レシュマンと発表した歌曲アルバム『シューマン＆ベルク』が「最優秀クラシック・ヴォーカル・アルバム（ソロ）」部門にノミネート。受賞すれば、『モーツァルト：ピアノ協奏曲第23番・第24番』で2011年の「最優秀インストゥルメンタル・ソリスト演奏賞」以来、2度目となる。<br />\n<br />\n　今回の最多ノミネートは、ビヨンセ。主要3部門を含む9部門にノミネートされた。ビヨンセのグラミー・ノミネート数は通算62回となり、自身の持つ「女性アーティストのグラミー最多ノミネート数」最多記録を更新した。続いて、ドレイク、リアーナ、カニエ・ウェストがそれぞれ8部門、チャンス・ザ・ラッパーが7部門にノミネートされた。<br />\n<br />\n　『第59回グラミー賞授賞式』は来年2月12日に米ロサンゼルスのステイプルズセンターで開催。WOWOWでは日本時間13日に生中継する。主要4部門のノミネートは以下のとおり。<br />\n<br />\n■年間最優秀レコード候補<br />\n「ハロー」アデル<br />\n「フォーメーション」ビヨンセ<br />\n「7イヤーズ」ルーカス・グラハム<br />\n「ワーク」リアーナ ft. ドレイク<br />\n「ストレスド・アウト」トゥエンティ・ワン・パイロッツ<br />\n<br />\n■年間最優秀アルバム候補<br />\n「25」アデル<br />\n「レモネード」ビヨンセ<br />\n「パーパス」ジャスティン・ビーバー<br />\n「ビューズ」ドレイク<br />\n「ア・セイラーズ・ガイド・トゥ・アース」スターギル・シンプソン<br />\n<br />\n■年間最優秀楽曲候補<br />\n「フォーメーション」ビヨンセ<br />\n「ハロー」アデル<br />\n「アイ・トゥック・ア・ピル・イン・イビサ」マイク・ポズナー<br />\n「ラブ・ユアセルフ」ジャスティン・ビーバー<br />\n「7イヤーズ」ルーカス・グラハム<br />\n<br />\n■最優秀新人賞候補<br />\nケルシー・バレリーニ<br />\nザ・チェインスモーカーズ<br />\nチャンス・ザ・ラッパー<br />\nマレン・モリス<br />\nアンダーソン・パーク', 1, '2016-12-07 15:51:15', '2017-02-05 15:51:15', NULL, NULL, NULL, 0, 'http://www.oricon.co.jp/news/2066910/?anc=015', '＜昨年＞【グラミー賞】小澤征爾、8度目のノミネートで念願の受賞', 'http://www.oricon.co.jp/news/2066940/?anc=015', '＜昨年＞【グラミー賞】テイラー、女性初2度目の年間最優秀アルバム', 'http://www.oricon.co.jp/news/2066903/?anc=015', '＜昨年＞【グラミー賞】ガガ、“漢字衣装”でボウイさん追悼「出火吐暴威」', '', '', '', '', 0, 0, 1, 1, '2016-12-07 16:34:03', '2016-12-07 16:34:03'),
    (6, '4_1_r_20161207_1481096043335448899', 4, 1, 1, 1, 1, 'http://www.oricon.co.jp/news/2082664/', '991b1b81925c9e76146b70cebaca0258_1481096043', '「スター・ウォーズ」最新作、エドワーズ監督「黒澤映画みたいにしたかった」', '', '<img src=\"http://news.oricon.co.jp/feed/images/size640wh/20161207_122115_size640wh_1474.jpg\" title=\"（左から）ディエゴ・ルナ、フェリシティ・ジョーンズ、ギャレス・エドワーズ監督 （C）ORICON NewS inc.\" alt=\"（左から）ディエゴ・ルナ、フェリシティ・ジョーンズ、ギャレス・エドワーズ監督 （C）ORICON NewS inc.\" ><br />\n　「スター・ウォーズ」最新作『ローグ・ワン／スター・ウォーズ・ストーリー』（12月16日公開）の主人公ジン役フェリシティ・ジョーンズ（33）、情報将校のキャシアン役ディエゴ・ルナ（36）、そしてギャレス・エドワーズ監督（41）が7日、都内で記者会見を行った。<br />\n<br />\n　同作は、ジョージ・ルーカス氏が生み出した銀河宇宙を舞台にした壮大な叙事詩、「スター・ウォーズ」シリーズ初のアナザーストーリー。『スター・ウォーズ　エピソード4／新たなる希望』（1977年）につながる物語で、主人公ジンを中心に集まった反乱軍の寄せ集めチーム＜ローグ・ワン＞のメンバーが、究極兵器デス・スターの設計図を奪うミッションに挑む。<br />\n<br />\n　フェリシティは、約10年前にシェイクスピア劇の上演で来日して以来、プロモーションでは初。ディエゴは9年ぶり。エドワーズ監督は映画『GODZILLA ゴジラ』のプロモーション以来、2年ぶり4回目の来日となる。<br />\n<br />\n　彼らへのおもてなしとして、同映画の世界観と「希望」をテーマにした襖絵を用意。ギャレス監督は、「撮影中にほしかったな。このショットを撮ったのに、残念」と絶賛した。フェリシティも「次は日本で撮影したら？　（この襖絵のように）桜も登場させられるわ」と気に入った様子。<br />\n<br />\n　襖絵の中で高くジャンプしているように描かれていたキャシアンを見て、ディエゴは「私もこの絵のように高く飛べるから、いまからでも映画を撮り直しよう。2日あれば、できるよ」とギャレス監督に映画のリテイクを要求。すると、エドワーズ監督が「（実際の撮影で）やってみたけどうまくいかなかったじゃないか」とちょっとしたボケツッコミを見せて、会場の笑いを誘っていた。<br />\n<br />\n　さらに、襖絵製作者の柏原晋平氏（37）に促され、監督たちが一人ずつ襖絵の中に描かれた「☆」を金色に塗り、サインを入れて完成させた。フェリシティは「映画づくりと一緒ね、皆で力を合わせる。感動したわ」と笑顔を見せ、「私たちが作った映画が世界中の人に観てもらえることをうれしく思います」と話していた。<br />\n<br />\n　もともと「スター・ウォーズ」シリーズは、ルーカス氏が日本の映画、特に黒澤明監督の作品に影響を受けていることでも知られる。エドワーズ監督は「とても光栄なことだと思っているよ。日本の映画、特に黒澤明の『隠し砦の三悪人』は、ジョージ・ルーカスも影響を受けた『スター・ウォーズ』の原点のようなものだからね。シリーズの故郷ともいえる日本に本作を持って来られて、とてもうれしい」とコメント。<br />\n<br />\n　『ローグ・ワン』においても「黒澤映画のスチールや映像をデザイナーや衣装の担当者に渡しましたし、かなり影響を受けていると思います。オープニングから黒澤映画を彷彿とさせるものにしたかったし、盗むならベストから盗め、ってね。そういう意味でも黒澤映画から盗むのがいいと思いました」と明かした。「スター・ウォーズを1度も観たことがない方にも導入にぴったりの作品。『エピソード4』という続編がすでにあるということで、これまでのシリーズとは違う、大胆なこともできたし、ユニークなものを作ることができた」と出来栄えの良さをアピールしていた。<br />\n<br /><br />\n<iframe width=\"640\" height=\"360\" src=\"https://www.youtube.com/embed/806Up7wBHfA\" frameborder=\"0\" allowfullscreen></iframe>', 1, '2016-12-07 12:51:03', '2017-02-05 12:51:03', NULL, NULL, NULL, 0, 'http://www.oricon.co.jp/news/2082664/photo/2/?anc=014', '【写真】SWの世界観と「希望」をテーマにした襖絵', 'http://www.oricon.co.jp/news/2082653/?anc=014', 'スター・ウォーズ最新作に、ダース・ベーダー以外のサプライズある　監督「保証します」 （16年12月07日）', 'http://www.oricon.co.jp/news/2082585/?anc=014', '『ローグ・ワン』、“つながっている”『エピソード4』との比較映像公開 （16年12月06日）', '', '', '', '', 0, 0, 1, 1, '2016-12-07 16:34:03', '2016-12-07 16:34:03'),
    (7, '4_1_r_20161207_1481096043435682827', 4, 1, 1, 1, 1, 'http://www.oricon.co.jp/news/2082656/', 'c8a1933dff3ba143f5b7dcdaced19d10_1481096043', '【べっぴんさん】次世代キャスト発表　朝ドラ初出演・林遣都ら8人', '', '<img src=\"http://news.oricon.co.jp/feed/images/size640wh/20161207_174839_size640wh_2532.jpg\" title=\"NHK連続テレビ小説『べっぴんさん』に出演する（前列左から）井頭愛海、芳根京子、江波杏子、（後列左から）森永悠希、古川雄輝、林遣都、久保田紗友 （C）NHK\" alt=\"NHK連続テレビ小説『べっぴんさん』に出演する（前列左から）井頭愛海、芳根京子、江波杏子、（後列左から）森永悠希、古川雄輝、林遣都、久保田紗友 （C）NHK\" ><br />\n　NHKは7日、朝の連続テレビ小説『べっぴんさん』に年明けから出演する新世代キャスト8人を発表。主人公・坂東すみれ（芳根京子）の娘・さくら役（15歳）に井頭愛海（15）、すみれとともに子供服の店「キアリス」を経営する村田君枝（土村芳）の息子・健太郎役（15歳）に古川雄輝（28）、小澤良子（百田夏菜子）の息子・龍一役（16歳）に森永悠希（20）、ジャズ喫茶「ヨーソロー」で働きながらプロのドラマーを目指し、さくらたちと出会う19歳の青年・河合二郎役に林遣都（26）が決定。そのほか久保田紗友、江波杏子、永瀬匡、森優作の出演も発表された。<br />\n<br />\n　年明けからは、ジャズ喫茶「ヨーソロー」を舞台に新たな出会いが生まれる。久保田演じる山本五月は16歳とさくらたちより一つ年上だが、大人びた雰囲気を持つ少女。高校には行かず、「ヨーソロー」でバイトをし“お姉さん役”として、大人へと成長していくさくらの面倒を見る。<br />\n<br />\n　江波演じる大村すずは「ヨーソロー」の女主人。戦争で夫と息子を亡くしてから、一人で店を経営しながら自活してきた芯の強い女性だ。喫茶店では、仕事で忙しい両親と共に過ごす時間があまり持てず、寂しい気持ちを抱くさくらが河合に心引かれ、健太郎は幼なじみのさくらに恋心を抱くなど、登場人物のさまざまな思いが交錯する。<br />\n<br />\n　また、永瀬演じる西城一朗は「キアリス」の新入社員。聡明な性格である一方、酒癖が悪く周囲を困らせることも。森は、同じく「キアリス」の新入社員・中西直政を演じる。<br />\n<br />\n　井頭、古川、林、久保田、永瀬、森は連続テレビ小説初出演。井頭と古川は1月11日、森永、林、久保田、江波は同12日、永瀬と森は同13日からそれぞれ登場予定となっている。', 1, '2016-12-07 14:36:16', '2017-02-05 14:36:16', NULL, NULL, NULL, 0, 'http://www.oricon.co.jp/news/2081541/?anc=164', '『べっぴんさん』姉妹、ミスチルのコンサートに大感激 （16年11月16日）', 'http://www.oricon.co.jp/news/2081230/?anc=164', '【べっぴんさん】新キャスト発表、前田美波里は朝ドラ初出演 （16年11月09日）', 'http://www.oricon.co.jp/news/2081760/?anc=164', '【べっぴんさん】松下優也、片思いキャラ好演「反響うれしい」 （16年11月21日）', '', '', '', '', 0, 0, 1, 1, '2016-12-07 16:34:03', '2016-12-08 17:25:14'),
    (8, '4_1_r_20161207_1481096043537134397', 4, 1, 1, 1, 1, 'https://mdpr.jp/news/1643797', 'a9a5a194d6ccc8485c456376868a6137_1481096043', '藤原竜也、“新しい世界”での挑戦に「興奮した」', '', '【藤原竜也／モデルプレス＝12月7日】俳優の藤原竜也が7日、都内で行われたPlayStation4のゲームソフト「龍が如く6　命の詞。」の完成披露会に出席した。<br/><br/><img src=\"https://mdpr.jp/photo/images/2016/12/07/200_bb3115a30db690c8cb6480e020b33ec4a189389a7e6bd487.jpg\" alt=\"「龍が如く6　命の詞。」の完成披露会に出席した藤原竜也 （C）モデルプレス\" /><br/><div class=\"caption\">（写真 : 「龍が如く6　命の詞。」の完成披露会に出席した藤原竜也 （C）モデルプレス）</div><br/>ゲーム中に、広島の極道・宇佐美勇太役で登場している藤原は、声の出演だけでなく、藤原本人の顔を元に作成した3DCGのキャラクターとして登場。\n<br/>\n<br/>過去に同シリーズに声の出演をしている藤原は「今回は僕自身が『龍が如く』に参加できるんだという喜びがあって、非常に興奮したことを覚えています」とコメント。そして、圧倒的な映像美に驚き「ゲーム業界がここまで進化しているんだと思いました。新しい世界で仕事をさせてもらえて嬉しかったです」と喜びを伝えた。\n<br/>\n<br/><strong>◆「龍が如く」は実写化可能？</strong>\n<br/>\n<br/>新作には、ビートたけしも広島の極道・広瀬一家の広瀬徹役で登場。映画監督の顔も持つたけしは「ゲームから派生して実写化するなら監督を引き受ける？」と聞かれると「こんな凝った映像には敵わない。実写版では難しいね」と回答。\n<br/>\n<br/>また「北野作品に出たい？」と藤原に質問が及び、たけしが「『みんな～やってるか！』といった映画があって、あれは最低な映画と言われているんだけど…」と確認すると、藤原は「楽しくやらせていただきたいです」と前向きに答えた。\n<br/>\n<br/><strong>◆小栗旬・真木よう子らも登場</strong>\n<br/>\n<br/>同作はシリーズ累計800万本を超える人気シリーズの最新作。主人公は伝説の極道・桐生一馬となり、舞台となる東京・神室町や広島・尾道仁涯町で、熱き男たちの生き様をともに体験する。また、藤原ら豪華タレントが声だけでなく本人の顔を元に作成した3DCGのキャラクターとして出演している。\n<br/>\n<br/>藤原、たけしのほか、イベントには主人公の声優の黒田崇矢をはじめ、小栗旬、真木よう子、宮迫博之、大森南朋、シリーズ総監督の名越稔洋氏が出席した。（modelpress編集部）', 1, '2016-12-07 16:33:52', '2017-02-05 16:33:52', NULL, NULL, NULL, 0, 'https://mdpr.jp/news/1643797', '他の写真・記事詳細をもっと見る', 'https://mdpr.jp/cinema/1627558', '「デスノート」“月”藤原竜也＆“L”松山ケンイチ、2ショットでサプライズ登場 10年越しの嘘告白', 'https://mdpr.jp/cinema/1624009', '藤原竜也「デスノートLNW」に登場　“夜神月”が衝撃の復活', 'https://mdpr.jp/cinema/1598066', '藤原竜也×伊藤英明、初共演でW主演　激突する衝撃サスペンス始動＜コメント到着＞', 'https://mdpr.jp/news/1592139', '藤原竜也がパパに 第1子誕生を報告＜コメント全文＞', 0, 0, 1, 1, '2016-12-07 16:34:04', '2016-12-07 16:34:04'),
    (9, '4_1_r_20161207_1481096043656193019', 4, 1, 1, 1, 1, 'http://www.oricon.co.jp/news/2082622/', '3458755d779549e0057ae3fd2291c739_1481096043', 'ホンジャマカ、今年2回目の対面で不仲説一蹴  キス披露にスタジオ騒然', '', '<img src=\"http://news.oricon.co.jp/feed/images/size640wh/20161206_155541_size640wh_7806.jpg\" title=\"日本テレビ系バラエティー『1周回って知らない話　芸能界気になる夫婦仲・コンビ仲…根掘り葉掘り3時間SP』でホンジャマカがまさかのキス披露 （C）日本テレビ\" alt=\"日本テレビ系バラエティー『1周回って知らない話　芸能界気になる夫婦仲・コンビ仲…根掘り葉掘り3時間SP』でホンジャマカがまさかのキス披露 （C）日本テレビ\" ><br />\n　お笑いコンビ・ホンジャマカの石塚英彦と恵俊彰が7日に放送される日本テレビ系バラエティー『1周回って知らない話 芸能界気になる夫婦仲・コンビ仲…根掘り葉掘り3時間SP』(後7：00)に出演。常日頃から、“不仲説”がささやかれる2人だが、今年で会うこと自体2回目。石塚はグルメリポート、恵はTBS系昼の情報番組『ひるおび！』でキャスターを務めるなど個々に活躍をみせるが、コンビでの仕事が少ないことに2人は「オファーがない」と説明。不仲説を一蹴するように熱いキスを披露してスタジオを騒然とさせた。<br />\n<br />\n　「石ちゃんと、あと誰だっけ？」「片方は食べてばっかりで、片方はニュースばっかり」「なんで、コンビなのに二人でテレビでないんですか？」など若者たちから失礼な質問を投げかけられた石塚は「なんだよ片方は食べてばっかりって」とややご立腹（？）。今ではコンビ仕事がほぼないが、石塚は「恵さんでお願いしますって言っているのに、俺がついて行ったらバカみたいでしょ？」といい、恵は「仕事が来ればやる訳ですよ。仕事が来ないから、ホンジャマカとしてテレビに出ないという」と改めて強調した。<br />\n<br />\n　それでも不仲説が疑われる2人だが、恵は「いつも一緒にいることが仲いいことじゃない…と、いつも一緒にいなくても仲いいってこともあるじゃないですか」と関係性を明かし、石塚から「はっきり言って今もうキスできないことが残念です、今このテレビの前で…」との振りを受けると、そっと膝の上に手を置いた恵が石塚に優しくキス。スタジオには絶叫が巻き起こった。<br />\n<br />\n　このほか、元柔道家で政治家の谷亮子には「なんで政治家になったの？」「どんなママでどんな妻なの？」、タレント・ヒロミには「トップアイドルだった奥さんとどうやって結婚したの？」など普段はなかなか聞けないストレートな質問が及び、その意外な回答にも注目だ。', 1, '2016-12-07 15:00:00', '2017-02-05 15:00:00', NULL, NULL, NULL, 0, 'http://www.oricon.co.jp/news/2081588/?anc=096', '太川陽介＆蛭子能収、不仲説の実情は？　隠された“番組愛”明かす （16年11月17日）', 'http://www.oricon.co.jp/news/2075876/?anc=096', '出川哲朗、ダウンタウン＆とんねるず不仲説にツッコむ「ヤフートップよ！」 （16年07月28日）', 'http://www.oricon.co.jp/news/2064997/?anc=096', '橋本環奈、“メンバー不仲説”を一蹴「結束強くなった」 （16年01月08日）', '', '', '', '', 0, 0, 1, 1, '2016-12-07 16:34:04', '2016-12-07 16:34:04'),
    (10, '4_1_r_20161207_1481096043662011927', 4, 1, 1, 1, 1, 'https://mdpr.jp/cinema/1643313', '6128c550d4288521b702b5f724de33ec_1481096043', '本田翼＆菜々緒＆仲里依紗“土竜美女”が美しい！セクシーvsナチュラル…三者三様の個性が激突＜ファッション＆ヘアメイク解説＞', '', '【本田翼・菜々緒・仲里依紗／モデルプレス＝12月6日】5日、都内にて生田斗真さんが主演を務める映画「土竜の唄 香港狂騒曲」（12月23日公開）の完成披露試写会が開催されました。モデルプレスでは、同作を彩る華やかな女性キャスト・本田翼さん、菜々緒さん、仲里依紗さんの3人を直撃。皆さんは劇中での役柄はもちろん、普段のファッションやヘアメイクもとても個性豊か。今回は皆さんのイベント登壇時のファッションに注目。それぞれにポイントを伺ってきました！<br/><br/><img src=\"https://mdpr.jp/photo/images/2016/12/07/200_b605f0d156ab6e218ec0d5f2900ccbaed7e6dd024cabbf0d.jpeg\" alt=\"（左から）映画「土竜の唄 香港協奏曲」に出演する菜々緒、本田翼、仲里依紗 （C）モデルプレス\" /><br/><div class=\"caption\">（写真 : （左から）映画「土竜の唄 香港協奏曲」に出演する菜々緒、本田翼、仲里依紗 （C）モデルプレス）</div><br/>\n<br/>\n<br/><strong>♥本田翼さん（奇跡の処女・轟迦蓮役）</strong>\n<br/>\n<br/><strong>＜今日のファッションのポイント＞</strong>\n<br/>ドレス：LOKITHO（ロキト）\n<br/>イヤリング：SAINT LAURENT（サンローラン）\n<br/>\n<br/>“セミフォーマル”というイベントのテーマに合わせて、私はあんまりかっちりしすぎたものが好きじゃないので、タイトなドレスのラインで“セミフォーマル感”を出しつつ、羽の柄で自分らしさを出してみました。\n<br/>\n<br/>ドレスは「LOKITHO（ロキト）」というブランド。ここのブランド大好きなんです。\n<br/>\n<br/><strong>＜ヘア＞</strong>\n<br/>\n<br/>ヘアは片側に多めに流して、少しいつもより大人っぽくしてみました！私はキャストの皆さんの中で最年少になるので、できるだけ皆さんに合わせたいなと思って。\n<br/>\n<br/><strong>＜メイク＞</strong>\n<br/>\n<br/>菜々緒さんも、仲さんもしっかりしたメイクが似合う方なので、私はあえてナチュラルに。私は濃くするとケバくなりすぎてしまうので…。濃くすればするほど浮いちゃうんですよね。自分らしさがなくなってしまうので、できるだけ薄くしています。\n<br/>\n<br/><strong>♥菜々緒さん（美脚ヒットガール・胡蜂役）</strong>\n<br/>\n<br/>＜今日のファッションのポイント＞\n<br/>ドレス：DSQUARED2（ディースクエアード）\n<br/>\n<br/>他のお2人が天使みたいな感じの衣装で来るだろうと思ったので、私はもう真逆で！悪魔みたいな感じにしようと思って黒を選びました（笑）。\n<br/>\n<br/>劇中でもかなり派手というかわりと白と黒しか着ていなくて、黒のドレスは全身総レースで“お尻透け透け”みたいな衣装も着せていただいたんですけど、露出がすごい多かったんですよね。「そういえば最近、露出もしてなかったな」と思って、この衣装を着させてもらいました。\n<br/>\n<br/><strong>＜ヘア＞</strong>\n<br/>\n<br/>ヘアは胸元が隠れちゃうのはもったいないなと思ったので、後ろに流す感じにしたくて。それでオールバックのダウンスタイルにしました。\n<br/>\n<br/><strong>＜メイク＞</strong>\n<br/>\n<br/>最近、映画の撮影やこういったイベントの時は自分でメイクさせてもらうことが多いんですけど、今日もセルフメイクです。私が演じた胡蜂（フーフォン）という役はメイクが濃かったので、そのメイクのイメージを思い返してやってみました。ちょっとわからないぐらいだと思うんですけど、パープルっぽいシャドウを入れています。\n<br/>\n<br/><strong>♥仲里依紗さん（惚れた女・若木純奈役）</strong>\n<br/>\n<br/><strong>＜今日のファッションのポイント＞</strong>\n<br/>ドレス：ROSSELLA JARDINI（ロッセラヤルディーニ）\n<br/>\n<br/>シースルーなところですかね。下にレイヤードしている青のワンピースがうっすら透けていて、黄色と青っていうすごく好きな2色のコントラストがお気に入りです。私服でも黄色とかレイヤードのファッションが好きで、チョーカーも大好きなんです。\n<br/>\n<br/><strong>＜ヘア＞</strong>\n<br/>\n<br/>髪は最近オレンジにしてみました。金髪とかピンクとか赤とか青とかはあったんですけど、オレンジは今までなかったなぁと思って初挑戦。でも、結構私、暖色系が多いですね。明るめのカラフルな色が好きです！\n<br/>\n<br/><strong>＜メイク＞</strong>\n<br/>\n<br/>ヘアのオレンジに合わせているところですかね。眉も合わせて赤っぽく塗っているんです。結構、来シーズンの展示会とかを見ていてもワントーンのコーディネートが多かったので、服も髪もメイクもワントーンにするのが流行るのかなと思ってます。\n<br/>\n<br/><strong>＜ネイルも見せてください！＞</strong>\n<br/>\n<br/>ミラーネイルです！今流行ってますよね、ミラーネイル。白ベースのミラーが一番派手でいいなと思って、それにちょっとクリスマスっぽくキラキラも足してみました。\n<br/>\n<br/><strong>◆生田斗真主演、ハイテンションムービーの続編</strong>\n<br/>\n<br/>同作は生田斗真さん主演で、ヒットコミック「土竜の唄」（高橋のぼる／週刊ビッグコミックスピリッツ刊）を実写化した「土竜の唄 潜入捜査官REIJI」の続編。今回は「チャイニーズマフィア編」にスポットを当て、香港を舞台に潜入捜査官・菊川玲二（生田）が前作以上にミッション絶対インポッシブルな任務に挑みます。スリルあり、コーフンあり、アクションあり、お色気あり、バイオレンスあり、愛あり、男気あり、正義あり、爆笑あり、ルールなしのハイテンションムービー。（modelpress編集部）\n<br/>\n<br/><strong>■「土竜の唄 香港協奏曲」（2016年12月23日公開）</strong>\n<br/>\n<br/>監督：三池崇史\n<br/>原作：高橋のぼる\n<br/>脚本：宮藤官九郎\n<br/>音楽：遠藤浩二\n<br/>主題歌：関ジャニ∞「NOROSHI」\n<br/>出演：生田斗真　瑛太　本田翼　古田新太　菜々緒　上地雄輔　仲里依紗　堤真一　吹越満　遠藤憲一　皆川猿時　岩城滉一\n<br/>＜STORY＞\n<br/>交番勤務のダメ巡査、菊川玲二（生田）はある日突然クビになり超キケンな犯罪組織への先週捜査官＝“モグラ”になることを任命され、広域指定暴力団・数寄矢大会に潜り込む。何度も死にかけながらも合間にちゃっかり童貞を卒業、ケーサツである身分もなんとかバレず生き抜いた玲二は、最終ターゲットの数寄矢会会長・轟周宝（岩城滉一）に気に入られ、兄弟の契りを交わしてしまい、極道の世界にさらに深く潜っていくハメに。轟から玲二に与えられたビッグミッションは極悪非道のチャイニーズ・マフィア『仙骨竜』を叩きのめすこと。そして、轟の愛娘にして奇跡の処女・轟迦蓮（本田）のボディーガードになることだった。まさかの任務を与えられた玲二にかつてない危機が次々と襲いかかる！そしてその裏では、日本中をも巻き込むような巨大な陰謀が動き始めていた―。', 1, '2016-12-07 16:10:57', '2017-02-05 16:10:57', NULL, NULL, NULL, 0, 'https://mdpr.jp/cinema/1643313', '他の写真・記事詳細をもっと見る', 'https://mdpr.jp/review/1643526', '本田翼“奇跡の処女”裏側を明かす「身を任せつつ…」', 'https://mdpr.jp/news/1643106', '生田斗真、菜々緒＆本田翼の“セクシーエピソード”明かす「悪くない…」', 'https://mdpr.jp/news/1641689', '「JR SKISKI」の歴代ヒロインがすごい “若手女優の登竜門”として脚光浴びる', 'https://mdpr.jp/news/1640494', '仲里依紗、お正月休みに必ず行うことは？「定番になっています」', 0, 0, 1, 1, '2016-12-07 16:34:04', '2016-12-07 16:34:04'),
    (11, '4_1_r_20161207_1481096043884004288', 4, 1, 1, 1, 1, 'http://www.oricon.co.jp/news/2082654/', '7c8bc2618d00c08539c7adafbec35bbd_1481096043', '吉木りさ、恋人・和田正人とのラブラブ電話は1分間「ちょうどいい」', '', '<img src=\"http://news.oricon.co.jp/feed/images/size640wh/20161207_142335_size640wh_3238.jpg\" title=\"和田正人とのラブラブ！順調ぶりをアピールした吉木りさ （C）ORICON NewS inc.\" alt=\"和田正人とのラブラブ！順調ぶりをアピールした吉木りさ （C）ORICON NewS inc.\" ><br />\n　タレントの吉木りさ（29）が7日、都内で行われた格安SIMブランド『スマモバ』新アンバサダーお披露目発表会に登壇した。トークセッションでは吉木が『寝る前の1分間でする電話テクニック』を公開。現在、交際中の俳優・和田正人（37）とも「お仕事で遠くに行かれてたりしたら、お電話します」と照れ笑いしながら順調をアピールした。<br />\n<br />\n　その際は「1分間では足りないのでは？」との報道陣からの投げかけに「それはないです」といい「1分間が逆にちょうどいい」とニッコリ。「つい声が聞きたくなっちゃって」「元気そうで安心した」「また今度遊ぼうね～!!!」など“魔法の言葉”として女性司会者相手に披露したが、「1分間は意外と短いと思うけど、照れずに思いの丈を素早く伝えるのがポイント。『会いたい』というだけでもすごく伝わると思う」と力説した。<br />\n<br />\n　和田にも実践しているかには「恋人だけじゃなく家族や友達、いろいろな方に…はい」とはぐらかしていた吉木。この日は、結婚式場でイベントが開催されたことから「女性として憧れの場所なので幸せです」と乙女心をのぞかせると「もしかしたらいいご報告ができることもあるかもしれないので、その時は改めて報告しますので、あたたかく見守って」と呼びかけていた。', 1, '2016-12-07 14:25:01', '2017-02-05 14:25:01', NULL, NULL, NULL, 0, 'http://www.oricon.co.jp/prof/artist/403981/?anc=096', '■彼は…朝ドラにも出演　俳優・和田正人のプロフィール', 'http://www.oricon.co.jp/news/2064151/?anc=096', '吉木りさ、“元カレ”の“いま”に仰天　逃した魚は大きすぎた？ （15年12月20日）', 'http://www.oricon.co.jp/news/2082044/photo/5/?anc=096', '【写真集カット】吉木りさがお気に入り…ベッドで脚を上げた大胆な1枚', '', '', '', '', 0, 0, 1, 1, '2016-12-07 16:34:04', '2016-12-07 16:34:04'),
    (12, '4_1_r_20161207_1481096043886045001', 4, 1, 1, 1, 1, 'https://mdpr.jp/news/1643791', '8cd4ec7167e2df63579d17cde469c2bb_1481096043', '小栗旬「自分のことをぶっ飛ばしたい」', '', '【小栗旬／モデルプレス＝12月7日】俳優の小栗旬が7日、都内で行われたPlayStation4のゲームソフト「龍が如く6　命の詞。」の完成披露会に出席した。<br/><br/><img src=\"https://mdpr.jp/photo/images/2016/12/07/200_afb3dcba4a46b831c20398a2a70f77b18148a68668252016.jpg\" alt=\"「龍が如く6　命の詞。」の完成披露会に出席した小栗旬 （C）モデルプレス\" /><br/><div class=\"caption\">（写真 : 「龍が如く6　命の詞。」の完成披露会に出席した小栗旬 （C）モデルプレス）</div><br/>ゲーム中に、若き極道・染谷巧役で登場している小栗。声の出演だけでなく、小栗本人の顔を元に作成した3DCGのキャラクターとして登場しており「念願の『龍が如く』に出演できて非常に嬉しく思います」と感激し、「（主人公の）桐生一馬になって、自分のことをぶっ飛ばしたいと思います」と笑いを誘った。\n<br/>\n<br/><strong>◆小栗旬、達成率100パーセントに意気込み</strong>\n<br/>\n<br/>同シリーズは初代からプレイしていたという小栗。その魅力を「大筋のストーリーもおもしろいのですが、サブゲームもいっぱいあり、本編を忘れてそっちばっかりやっています」と紹介。これまでの作品をやりこんでいるようだが「達成率が100パーセントになりません。あと何をどうしたらいいんだろう？」と不思議がった。\n<br/>\n<br/>新作で「100パーセントを目指す？」と聞かれると「そうですね」と意気込み十分。続けて「声をやらせてもらったので、難しい隠れたものをこっそり聞き出そうと思います」とプレイを前に胸を膨らませた。\n<br/>\n<br/><strong>◆藤原竜也・真木よう子・ビートたけしらも登場</strong>\n<br/>\n<br/>同作はシリーズ累計800万本を超える人気シリーズの最新作。主人公は伝説の極道・桐生一馬となり、舞台となる東京・神室町や広島・尾道仁涯町で、熱き男たちの生き様をともに体験する。また、小栗ら豪華タレントが声だけでなく本人の顔を元に作成した3DCGのキャラクターとして出演している。\n<br/>\n<br/>小栗のほか、イベントには主人公の声優の黒田崇矢をはじめ、藤原竜也、真木よう子、宮迫博之、大森南朋、ビートたけし、シリーズ総監督の名越稔洋氏が出席した。（modelpress編集部）', 1, '2016-12-07 15:47:58', '2017-02-05 15:47:58', NULL, NULL, NULL, 0, 'https://mdpr.jp/news/1643791', '他の写真・記事詳細をもっと見る', 'https://mdpr.jp/cinema/1643378', '「銀魂」完全再現に反響　小栗旬・菅田将暉・橋本環奈のビジュアルに「完成度高い」「最高」と期待の声', 'https://mdpr.jp/cinema/1643182', '「銀魂」完全再現！小栗旬・菅田将暉・橋本環奈のビジュアル解禁', 'https://mdpr.jp/news/1640648', '小栗旬、“大の苦手”に初挑戦　福田雄一とタッグ', 'https://mdpr.jp/news/1636531', '小栗旬のものまねで話題　土佐兄弟・有輝の「次の電車だ！」がブームの兆し', 0, 0, 1, 1, '2016-12-07 16:34:04', '2016-12-07 16:34:04'),
    (13, '4_1_r_20161207_1481096044084092404', 4, 1, 1, 1, 1, 'http://www.oricon.co.jp/news/2082675/', 'd84db86c4c9eeb9768d6e02bcb442997_1481096044', 'サンリオ新感覚ぬいぐるみにキキララ、クロミらが仲間入り', '', '<img src=\"http://news.oricon.co.jp/feed/images/size640wh/20161207_142153_size640wh_4076.jpg\" title=\"サンリオの新感覚ぬいぐるみ『pyoconoru（ぴょこのる）』に新しいキャラクターが仲間入り！\" alt=\"サンリオの新感覚ぬいぐるみ『pyoconoru（ぴょこのる）』に新しいキャラクターが仲間入り！\" ><br />\n　さまざまな所に乗せることができる新感覚のぬいぐるみマスコットシリーズとして、10月にサンリオから登場した『pyoconoru（ぴょこのる）』。これまで発売されていたハローキティ、マイメロディなどの人気キャラクターに加え、きょう7日から新たに5キャラクターが追加。どこでも持ち歩けるマスコットサイズで、いろんな楽しみ方ができる。<br />\n<br />\n　追加されたのは、リトルツインスターズ（キキ＆ララ）、クロミ、ハンギョドン、タキシードサム、おさるのもんきちの5キャラクター、6アイテム。第1弾の8種も人気となっている。<br />\n<br />\n　マスコットサイズでどこでも持ち歩けて、どんな場所でも乗せられるから、自分ならではの写真撮影を楽しめるのがポイント。正面からはもちろん、キャラクター同士を向かい合わせてみたり、後ろ姿から撮ってみたりと、かわいい角度を見つけてみて。<br />\n<br />\n【商品詳細】<br />\n『pyoconoru（ぴょこのる）』<br />\n追加キャラクター：キキ（リトルツインスターズ）、ララ（リトルツインスターズ）、クロミ、ハンギョドン、タキシードサム、おさるのもんきち<br />\n税込価格：各756円<br />\n販売開始：2016年12月7日<br />\n販売店舗：サンリオ直営店、百貨店のサンリオコーナー、チェーンストア、一部コンビニエンスストアほか', 1, '2016-12-07 14:38:21', '2017-02-05 14:38:21', NULL, NULL, NULL, 0, 'http://joshiplus.jp/trend/news/2080346/?anc=044', '【第1弾】一緒にお出かけ！ サンリオ人気キャラが新感覚ぬいぐるみに', 'http://joshiplus.jp/trend/news/2081021/?anc=044', 'キティやマイメロも！ サンリオ人気キャラのフォトプロップス （16年11月06日）', 'http://joshiplus.jp/special/79/?anc=044', '【特集】ポムポムプリンに突撃！独占インタビュー☆', '', '', '', '', 0, 0, 1, 1, '2016-12-07 16:34:04', '2016-12-07 16:34:04'),
    (14, '4_1_r_20161207_1481096044084086833', 4, 1, 1, 1, 1, 'https://mdpr.jp/news/1643789', '182cd4ad37127bcb02dd0d6239320e8d_1481096044', 'インスタグラムに新機能　コメントに「いいね！」追加＆投稿ごとのコメントオフが可能に', '', '【Instagram／モデルプレス＝12月7日】インスタグラムが米国時間6日、コメントツールをアップデートしたことを発表。各コメントに「いいね！」ができる機能と、特定の投稿についてコメントをオフに設定できる機能が加わった。<br/><br/><img src=\"https://mdpr.jp/photo/images/2016/12/07/200_e5bac396f1c2c2c79db11106d6d3bb3d17ae0c44dd32a2ff.jpg\" alt=\"インスタグラムに新機能　コメントに「いいね！」追加＆投稿ごとのコメントオフが可能に\" /><br/><div class=\"caption\">（写真 : インスタグラムに新機能　コメントに「いいね！」追加＆投稿ごとのコメントオフが可能に）</div><br/><strong>◆コメントに対して「いいね！」が可能に</strong>\n<br/>\n<br/>投稿された写真や動画に付けられた各コメントの右側にハートアイコンが表示されるように。ハートアイコンをタップすると、コメントに「いいね！」を付けることが可能。「いいね！」で利用者同士がお互いをサポートする気持ちを表すことで、ポジティブなコミュニティ形成を促すと期待される。\n<br/> \n<br/><strong>◆投稿ごとにコメントをオフに</strong>\n<br/>\n<br/>特定の写真や動画のコメントをオフにする設定が、全てのアカウントで利用可能に。コメントをオフにするには、投稿時に「詳細設定」をタップし、「コメントをオフにする」を選択。また、投稿後に「…」をタップすると、投稿済みの写真や動画でもコメントをオンに設定し直すこともできる。\n<br/> \n<br/>また、アカウントを非公開設定にしている場合、一度フォローを承認した相手でも、後からフォローを外すことが可能となった。これまでは承認済みのフォロワーをブロックする方法しかなかったが、今後はフォロワーのプロフィール右上に表示される「…」からフォローを外すことができる。なお、フォローを外された相手に通知が届くことはない。\n<br/>\n<br/>これらの機能は全世界で順次展開予定。（modelpress編集部）', 1, '2016-12-07 15:29:20', '2017-02-05 15:29:20', NULL, NULL, NULL, 0, 'https://mdpr.jp/news/1643789', '他の写真・記事詳細をもっと見る', 'https://mdpr.jp/travel/1642298', '2016年日本で最も人気のスポットTOP10、Instagramが発表', 'https://mdpr.jp/news/1642155', 'Instagram、今年最も「いいね！」が多かった写真発表　日本で1番人気のハッシュタグは？＜2016年ハイライト＞', 'https://mdpr.jp/news/1602682', 'インスタグラム、新機能「Instagram Stories（インスタグラム ストーリーズ）」を発表', 'https://mdpr.jp/news/1599589', 'Instagram、最も使われている絵文字を発表＜総合＆カテゴリ別ランキング＞', 0, 0, 1, 1, '2016-12-07 16:34:04', '2016-12-07 16:34:04'),
    (15, '4_1_r_20161207_1481096044184538884', 4, 1, 1, 1, 1, 'http://www.oricon.co.jp/news/2082655/', '34cf95726714e1730bae04704a31e5ea_1481096044', '真木よう子がイベントで陳謝、風邪で声出ず…　たけしが“手話”でフォロー', '', '<img src=\"http://news.oricon.co.jp/feed/images/size640wh/20161207_140848_size640wh_4166.jpg\" title=\"風邪で声が出ない真木よう子（右）をフォローしたビートたけし （C）ORICON NewS inc.\" alt=\"風邪で声が出ない真木よう子（右）をフォローしたビートたけし （C）ORICON NewS inc.\" ><br />\n　女優の真木よう子が7日、人気アクションアドベンチャーゲームのシリーズ最新作『龍が如く6 命の詩。』（8日発売）の完成披露イベントに出席。ビートたけしら豪華キャストと共に多くのフラッシュを浴びたが、まさかの風邪で思うように声が出ずに陳謝する一幕があった。<br />\n<br />\n　2005年12月に第1作目が発売され、シリーズ累計出荷本数800万本を突破する同シリーズ。最新作でスナックのママ役を演じた真木は、大勢のファン、報道陣を前にマイクを持ったものの「本当にすいません…」と“かすれ声”で切り出すと「風邪で声だけ何故か出せなくなってしまって…」と明かし、申し訳なさそうに頭を下げた。<br />\n<br />\n　司会のガダルカナルタカが「これから『DESIRE -情熱-』を歌うんですか？」と小ボケを挟むと、共演の雨上がり決死隊・宮迫博之が「中森明菜の物まねちゃいますよ」とすかさずツッコミ。タカから「大きめのジェスチャーでお願いしますね」と優しく声をかけられた真木は、その後のトークセッションを“○×”のジェスチャーなどで乗り越えていた。<br />\n<br />\n　隣に座っていたたけしが、真木のコメントを“アテレコ”するフォローも。締めのあいさつとして、真木が「是非ゲームで私の声を聞いて頂けたらうれしいです」と声を絞り出し、呼びかけた際にも“テキトー手話”を披露して会場を沸かせていた。<br />\n<br />\n　イベントにはそのほか、主人公・桐生一馬役の黒田崇矢、藤原竜也、小栗旬、大森南朋、シリーズの総合監督・名越稔洋氏が出席。レギュラーキャストを代表して黒田は「こんな豪華なキャストがそろってくださったのをうれしく想っています」と感慨深げに語っていた。<br />\n<br /><br />\n<iframe width=\"640\" height=\"360\" src=\"https://www.youtube.com/embed/wijy_4lQw-4\" frameborder=\"0\" allowfullscreen></iframe>', 1, '2016-12-07 14:09:06', '2017-02-05 14:09:06', NULL, NULL, NULL, 0, 'http://www.oricon.co.jp/news/2082655/photo/4/?anc=013', '【写真】風邪でも笑顔をみせた真木よう子', 'http://beauty.oricon.co.jp/trend-culture/trend/news/photo/2031957/2/?anc=013', '【写真】真木よう子、写真集でオトナの色気を披露', 'http://www.oricon.co.jp/news/2049053/photo/7/?anc=013', '【写真】真木よう子、白ワンピでスレンダーBODY披露', '', '', '', '', 0, 0, 1, 1, '2016-12-07 16:34:04', '2016-12-08 17:25:15'),
    (16, '4_1_r_20161207_1481096044188120892', 4, 1, 1, 1, 1, 'https://mdpr.jp/news/1643780', 'dcec2173985442be502ab344c69336b2_1481096044', '真木よう子、体調不良でアクシデント　プロ根性見せる', '', '【真木よう子／モデルプレス＝12月7日】女優の真木よう子が7日、都内で行われたPlayStation4のゲームソフト「龍が如く6　命の詞。」の完成披露会に出席。体調不良の中、登場し、プロ根性を見せつけた。<br/><br/><img src=\"https://mdpr.jp/photo/images/2016/12/07/200_464fcbcb7c5f67c0e8dc15fee25bc163009a1b967bb387c5.jpg\" alt=\"声が出ず謝罪した真木よう子 （C）モデルプレス\" /><br/><div class=\"caption\">（写真 : 声が出ず謝罪した真木よう子 （C）モデルプレス）</div><br/>同作にスナックのママ・笠原清美役で登場している真木は、MCより「これまでにスナックのママ役をやったことはある？」と聞かれると、かすれ声で「ないです」と回答。声を絞り出し「本当に申し訳ないです。声が出なくなってしまって…」と謝罪すると、MCを務めたガダルカナル・タカが「これから『DESIRE』を歌う（中森明菜の）モノマネをするわけじゃないよね？」と問いかけ、会場を和ませた。\n<br/>\n<br/>声が思うように出ないというアクシデントもあったが、最後に「ゲームで私の声を聞いてください」と笑顔でアピールした真木。体調不良の中で、プロ魂を見せつけた。\n<br/>\n<br/><strong>◆藤原竜也・小栗旬・ビートたけしらも登場</strong>\n<br/>\n<br/>同作はシリーズ累計800万本を超える人気シリーズの最新作。主人公は伝説の極道・桐生一馬となり、舞台となる東京・神室町や広島・尾道仁涯町で、熱き男たちの生き様をともに体験する。また、真木ら豪華タレントが声だけでなく本人の顔を元に作成した3DCGのキャラクターとして出演している。\n<br/>\n<br/>真木のほか、イベントには主人公の声優の黒田崇矢をはじめ、藤原竜也、小栗旬、宮迫博之、大森南朋、ビートたけし、シリーズ総監督の名越稔洋氏が出席した。（modelpress編集部）', 1, '2016-12-07 15:21:52', '2017-02-05 15:21:52', NULL, NULL, NULL, 0, 'https://mdpr.jp/news/1643780', '他の写真・記事詳細をもっと見る', 'https://mdpr.jp/cinema/1582347', '真木よう子「経験したことのない」絶世の美女に　松田龍平が一目惚れ', 'https://mdpr.jp/news/1580584', '真木よう子「面倒くさがり」が原因で頭痛', 'https://mdpr.jp/news/1576886', '真木よう子、ディーン・フジオカに謝罪「ごめんなさい。存じ上げません」', 'https://mdpr.jp/news/1537750', '真木よう子、壇上で靴を脱いだ理由明かす「倒れるよかマシでしょ」', 0, 0, 1, 1, '2016-12-07 16:34:04', '2016-12-07 16:34:04'),
    (17, '4_1_r_20161207_1481096044385514206', 4, 1, 1, 1, 1, 'https://mdpr.jp/review/1643777', '5446f83d607dfd68bcf9a8e3b8f92096_1481096044', 'キスマイ千賀健永のズバ抜けた“美容愛”がすごい！今、注目の新美容は？', '', '【千賀健永／モデルプレス＝12月7日】Kis-My-Ft2の千賀健永が7日発売の雑誌「GLITTER」1月号に初登場。美容家顔負けの美容トークを繰り広げた。<br/><br/><img src=\"https://mdpr.jp/photo/images/2016/12/07/200_9d467d5105773f8ad5852b1b09283e455f0732f4ec54521d.jpg\" alt=\"「GLITTER」1月号（トランスメディア、2016年12月7日発売）表紙：ローラ\" /><br/><div class=\"caption\">（写真 : 「GLITTER」1月号（トランスメディア、2016年12月7日発売）表紙：ローラ）</div><br/>注目メンズのセクシーな素顔に迫った連載「Focus on HOT GUYS」に登場した千賀。クラッシュパンツや、スーツを着こなし、色っぽい表情で魅せた。\n<br/>\n<br/><strong>◆千賀健永、「ヒト幹細胞」に注目</strong>\n<br/>\n<br/>誌面では、詳しすぎる美容トークを展開。\n<br/>\n<br/>「美容、好きですね～！」と語る千賀の今、注目の新美容は“「ヒト幹細胞」成分の含まれた化粧水”だそう。「僕は頬の筋肉がキュッと上がるような専用の導入機器で肌に浸透させてます」と普段の使い方を紹介し「ビタミンCの原液とヒト幹細胞があれば自分的には完璧ですね！」と信頼をおく様子を見せた。\n<br/>\n<br/>このほか休日の過ごし方や理想の恋愛、ファッションへのこだわり、キスマイメンバーとの裏話などを明かした。（modelpress編集部）\n<br/>\n<br/>情報提供：「GLITTER」（トランスメディア）', 1, '2016-12-07 15:20:26', '2017-02-05 15:20:26', NULL, NULL, NULL, 0, 'https://mdpr.jp/review/1643777', '他の写真・記事詳細をもっと見る', 'https://mdpr.jp/news/1600852', 'SMAP中居正広の“後輩への対応”に絶賛　キスマイ千賀健永は“中居愛”炸裂「抱けます」', 'https://mdpr.jp/news/1596647', 'キスマイ千賀健永、電車で痴女被害の過去 NEWS手越祐也に助けられる', 'https://mdpr.jp/news/1477583', 'キスマイ千賀健永「全体的にキスがダメ」苦手分野を告白', 'https://mdpr.jp/news/1319650', 'キスマイ千賀健永、苦手なものを明かす', 0, 0, 1, 1, '2016-12-07 16:34:04', '2016-12-07 16:34:04'),
    (18, '4_1_r_20161207_1481096044392104856', 4, 1, 1, 1, 1, 'http://www.oricon.co.jp/news/2082676/', 'b8a8c482dc725e9d30495b21f903491a_1481096044', 'くりぃむ有田哲平、結婚の反響明かす　裏番組の爆笑問題も手荒い祝福「ローラだよ！」', '', '<img src=\"http://news.oricon.co.jp/feed/images/size640wh/20161205_222232_size640wh_8730.jpg\" title=\"結婚発表の反響について語ったくりぃむしちゅーの有田哲平 （C）ORICON NewS inc.\" alt=\"結婚発表の反響について語ったくりぃむしちゅーの有田哲平 （C）ORICON NewS inc.\" ><br />\n　先月29日に一般女性との結婚したお笑いコンビ・くりぃむしちゅーの有田哲平（45）が、6日深夜放送のラジオ『くりぃむしちゅーのオールナイトニッポン』（ニッポン放送）に生出演。5日放送の日本テレビ系『しゃべくり007　冬の2時間SP』で、結婚を発表してからの反響について「もう、きょうもLINEが500件くらい来て、鳴り止まないんですよ」と驚きながら明かした。<br />\n<br />\n　相方の上田晋也（46）から「本当に、アスリートが金メダル獲った時っていうような感じ？」と振られると「そんなの屁でもないですよ！」と豪語。上田から「何が『屁でもない』だ！　金メダリスト、アスリート全員に謝れ！」とツッコミを入れられると「すみません」とすぐさま笑いながら謝罪。上田が「近々（有田の妻と）お会いすることになるだろうけど…」と話すと、有田が「一般の方なので、ちょっと部外者には…」と冗談交じりに切り返すなど、和やかなムードで番組が進行した。<br />\n<br />\n　放送前に、アイドルグループの嵐と仕事をしてきたという上田は「まず、二宮くんが『上田さん、おめでとうございます有田さん』なんて言ってくれて、そしたら松潤も櫻井くんも相葉くんも『おめでとうございます』って言ってくれた」とメンバーたちから続々と祝福されたことを報告。リーダーの大野智だけは違った反応を示したようで「大野くん、ちょっとボーっとしてて『有田さん、合コンで知り合った方と結婚したんですよね？　…じゃあ！』って帰って行ったの」とうれしそうに語った。<br />\n<br />\n　同時間に放送されていた裏番組『JUNK爆笑問題カーボーイ』（TBSラジオ）でも、有田の結婚が話題になり、爆笑問題の太田光が「有田、この野郎！　水臭いじゃないか。何も知らなかったよ。オレたち仲間じゃねーか」と旧友の結婚を祝福。「ローラだよ！」と過去のうわさをネタにイジると、田中裕二が「太田さん、違います。相手、間違ってますよ！」と笑いながら訂正していた。', 1, '2016-12-07 14:30:46', '2017-02-05 14:30:46', NULL, NULL, NULL, 0, 'http://www.oricon.co.jp/news/2082582/?anc=148', '結婚発表のくりぃむ有田、“高橋ひとみ似”女性と「合コン婚です！」 （16年12月05日）', 'http://www.oricon.co.jp/news/2069264/?anc=148', 'ローラ、くりぃむ・有田との結婚報道を再々否定「みんなのイタズラ」 （16年03月29日）', 'http://www.oricon.co.jp/news/2035267/photo/1/?anc=148', '【写真】ロンブー田村淳、元モデルの妻・香那さんとハワイ挙式ショット', '', '', '', '', 0, 0, 1, 1, '2016-12-07 16:34:04', '2016-12-07 16:34:04'),
    (19, '4_1_r_20161207_1481096044485618322', 4, 1, 1, 1, 1, 'https://mdpr.jp/news/1643778', '4f764547a82aa4e27b2402c93f2b10da_1481096044', '林遣都・久保田紗友・古川雄輝ら朝ドラ初出演 「べっぴんさん」追加キャスト発表', '', '【べっぴんさん／モデルプレス＝12月7日】NHKは7日、女優の芳根京子がヒロインを務める連続テレビ小説「べっぴんさん」（毎週月～土曜あさ8：00）に来年1月より出演する追加キャストを発表した。<br/><br/><img src=\"https://mdpr.jp/photo/images/2016/12/07/200_32df75faea3480506ae28af566c32cc2682eab2faa97e097.jpg\" alt=\"NHK朝ドラ「べっぴんさん」追加キャストに発表された（左から）久保田紗友、林遣都、古川雄輝（C）モデルプレス\" /><br/><div class=\"caption\">（写真 : NHK朝ドラ「べっぴんさん」追加キャストに発表された（左から）久保田紗友、林遣都、古川雄輝（C）モデルプレス）</div><br/>新たに発表されたのは8名。主人公・すみれ（芳根京子）の娘・さくら役（15歳）に井頭愛海、すみれとともに子供服の店「キアリス」を経営する村田君枝（土村芳）の息子・健太郎役（15歳）に古川雄輝、小澤良子（百田夏菜子）の息子・龍一役（15歳）に森永悠希、喫茶店で働きながらプロのドラマーを目指している青年・河合二郎役（19歳）に林遣都、さくらたちより一つ年上だが非常に大人びた雰囲気を持つ少女・山本五月役（16歳）に久保田紗友が決定した。\n<br/>\n<br/>そのほか江波杏子、永瀬匡、森優作の出演も発表。森永、江波以外は連続テレビ小説初出演となる。\n<br/>\n<br/><strong>◆芳根京子主演朝ドラ「べっぴんさん」</strong>\n<br/>\n<br/>同作は、戦後の焼け跡の中、娘のため、女性のために、子供服作りにまい進し、日本中を元気にかけぬけていくヒロイン・坂東すみれ（芳根）とその家族、彼女の仲間たちが夢へと向かう物語で、すみれは日本初の総合子供用品店をオープンさせる。\n<br/>\n<br/>実在の人物である坂野惇子氏をモデルとするが、激動の時代を生きた女性たちの人生の物語として大胆に再構成し、登場人物や団体は改称し、フィクションとして描く。（modelpress編集部）', 1, '2016-12-07 15:36:26', '2017-02-05 15:36:26', NULL, NULL, NULL, 0, 'https://mdpr.jp/news/1643778', '他の写真・記事詳細をもっと見る', 'https://mdpr.jp/cinema/1639604', '古川雄輝、広瀬アリスとの関係に進展？', 'https://mdpr.jp/cinema/1632232', '古川雄輝、広瀬アリスとの距離感は「雰囲気で察して」エピソードに観客驚き', 'https://mdpr.jp/news/1622461', '桐山漣＆古川雄輝の“顔交換”が綾野剛似？「結果イケメン」と反響', 'https://mdpr.jp/cinema/1611173', '川口春奈の“恋人に求める条件”にツッコミ飛ぶ', 0, 0, 1, 1, '2016-12-07 16:34:04', '2016-12-07 16:34:04'),
    (20, '4_1_r_20161207_1481096044491401022', 4, 1, 1, 1, 1, 'http://www.oricon.co.jp/news/2082673/', '48e55816276f5acf29ab2f0e7bfe7193_1481096044', '【紅白】タモリ＆マツコ・デラックスがゲスト出演「いったい何が起きるのか？」', '', '<img src=\"http://news.oricon.co.jp/feed/images/size640wh/20161207_140700_size640wh_2304.jpg\" title=\"『第67回NHK紅白歌合戦』にタモリとマツコ・デラックスが出演決定 （C）ORICON NewS inc.\" alt=\"『第67回NHK紅白歌合戦』にタモリとマツコ・デラックスが出演決定 （C）ORICON NewS inc.\" ><br />\n　NHKは7日、大みそかの『第67回NHK紅白歌合戦』（後7：15～11：45 総合）にスペシャルゲストとしてタレントのタモリとマツコ・デラックスが出演することを発表した。<br />\n<br />\n　2人がどのようなコーナーに出演するかなど詳細は明かされていないが、「テレビで幅広く活躍し、さまざまな世代の心をとらえているお二人。大みそかの当日、いったい何が起きるのか？　お楽しみに！」と期待を煽っている。<br />\n<br />\n　番組のテーマは「夢を歌おう」。司会は有村架純（紅組）と嵐の相葉雅紀（白組）、総合司会は武田真一アナウンサーが務める。', 1, '2016-12-07 14:08:01', '2017-02-05 14:08:01', NULL, NULL, NULL, 0, 'http://www.oricon.co.jp/news/2081944/?anc=142', '【紅白】総合司会はNHKのエース投入　『ニュース7』武田真一アナ （16年11月24日）', 'http://www.oricon.co.jp/news/2081945/?anc=142', '【紅白】金爆、5年連続出場ならず　「女々しくて」新記録樹立も夢に… （16年11月24日）', 'http://www.oricon.co.jp/news/2081946/?anc=142', '【紅白】AKBが“紅白版”総選挙　視聴者投票で48人を選抜　横山「震えが止まらない」 （16年11月24日）', '', '', '', '', 0, 0, 1, 1, '2016-12-07 16:34:04', '2016-12-07 16:34:04'),
    (21, '4_1_r_20161207_1481096044623654189', 4, 1, 1, 1, 1, 'https://mdpr.jp/news/1643771', '03c01dd03620462346974b6e0cc8ca2b_1481096044', 'タモリ＆マツコ・デラックス「紅白歌合戦」出演決定', '', '【タモリ マツコ・デラックス／モデルプレス＝12月7日】NHKは7日、大晦日に放送される「第67回NHK紅白歌合戦」に、タレントのタモリとマツコ・デラックスが出演することを発表した。<br/><br/><img src=\"https://mdpr.jp/photo/images/2016/12/07/200_59053f078e509c47c7ed49b632daccaaf5938652fa1e1d5d.jpg\" alt=\"「第67回NHK紅白歌合戦」に出演することが決定したタモリ（左）とマツコ・デラックス（C）モデルプレス\" /><br/><div class=\"caption\">（写真 : 「第67回NHK紅白歌合戦」に出演することが決定したタモリ（左）とマツコ・デラックス（C）モデルプレス）</div><br/>2人はスペシャルゲストとして出演。制作統括を務める矢島良チーフ・プロデューサーは「テレビの世界にいつも新しい空気を送り込んでいるお2人が、大晦日にどんな共演を繰り広げてくださるのか、今からワクワクしています！」と期待のコメント。「皆さんも、紅白とお2人の共演がどんな化学反応を起こすのか？楽しみにお待ちください」と呼びかけている。\n<br/>\n<br/><strong>◆東京五輪に向けて“夢”を応援</strong>\n<br/>\n<br/>今年のテーマは「夢を歌おう」。東京オリンピック・パラリンピックが開催される2020年に向かって、多くの人々が夢や希望を持ち歩み始める中、「紅白歌合戦」では「歌の力」で夢を応援したいと考え、オリンピックイヤーを目前にした2019年の「第70回 NHK紅白歌合戦」までの4年間、この「夢を歌おう」をテーマに掲げ、様々な世代の人々の夢を「歌の力」で応援していく。<br/><br/>紅組司会者には女優の有村架純、白組司会者には嵐の相葉雅紀が決定。同番組司会を務めるのは有村は初、相葉は嵐として5年連続で司会を務めているが、単独では初となる。放送は2016年12月31日午後7時15分から午後11時45分まで（5分間の中断ニュースあり）。（modelpress編集部）', 1, '2016-12-07 14:40:31', '2017-02-05 14:40:31', NULL, NULL, NULL, 0, 'https://mdpr.jp/news/1643771', '他の写真・記事詳細をもっと見る', 'https://mdpr.jp/news/1589078', '斎藤工、“顔面性器”がさらに進化？マツコに“キャラ迷走”を相談', 'https://mdpr.jp/news/1577361', '深田恭子の“お姉ちゃん”にマツコ・デラックス', 'https://mdpr.jp/news/1533033', 'マツコ・デラックス、藤原紀香＆片岡愛之助カップルに興奮「濃度が高い」', 'https://mdpr.jp/news/1532871', 'マツコ・デラックス、藤原紀香と片岡愛之助の結婚報道にコメント　福山雅治の入籍にも意見', 0, 0, 1, 1, '2016-12-07 16:34:05', '2016-12-07 16:34:05'),
    (22, '4_1_r_20161207_1481096044625832203', 4, 1, 1, 1, 1, 'http://www.oricon.co.jp/news/2082672/', 'b796c85e9182d4c073ff9e03496a6400_1481096044', '元テニス部B’z稲葉浩志、錦織圭を質問攻めに　', '', '<img src=\"http://news.oricon.co.jp/feed/images/size640wh/20161207_134919_size640wh_5882.jpg\" title=\"錦織圭×B’z稲葉浩志の異色対談が実現\" alt=\"錦織圭×B’z稲葉浩志の異色対談が実現\" ><br />\n　B’zの稲葉浩志とプロテニスプレーヤー錦織圭が、大みそか放送のWOWOW特番『錦織圭×稲葉浩志「テニス対談」』（後4：00）の収録を行い、異色対談が実現した。学生時代、軟式テニス部に所属し、今でも試合会場に足を運ぶという稲葉が錦織を質問攻めにし、大盛り上がりとなった。<br />\n<br />\n　稲葉は最初こそ「あらゆる質問はし尽くされているんじゃないかな」と遠慮気味だったが、錦織との意外な接点が判明すると、一気にテンションアップ。グランドスラムや日本勢96年ぶりに銅メダルを獲得したリオ五輪、普段のリラックス方法についてなど、次々と質問を投げかけた。<br />\n<br />\n　「テニスマニアではなく、普通に見ているだけ」と言いながらも、質問は試合の細かいシーンについてまで及び、錦織は「僕の試合も見てくださって、これだけ選手に詳しいなんて」とびっくり。収録の合間には錦織が「稲葉さんのスウィングが見てみたい」「テニスウェアでくればよかった。それだったら勝負できますよね（笑）」と提案するなど、すっかり打ち解けた様子だった。<br />\n<br />\n　2人は2017年の公約も。錦織は「今の5位よりランキングを上げたい。できれば4位か3位で終われるように頑張ります！」と力強く宣言。稲葉は「どこか知らない街に行ってライブをやりたい。まだまだ世界は広いので」と、視線の先は錦織同様、世界へと向いていた。<br />\n<br />\n　対談を終えた稲葉は「激しい試合をやっている印象が強いですけど、非常にナチュラルで、いろんなところからインスピレーションを得られる人、非常にニュートラルなアスリートだなと思いました」とコメント。錦織は「音楽の話など聞ける機会が少ないですし、稲葉さんがこれだけ選手に詳しかったり、僕の試合を見てくださっていてうれしかったです」と感激していた。<br />\n<br />\n　なお、稲葉と米ギタリスト、スティーヴィー・サラスのコラボユニット「INABA／SALAS」の新曲「TROPHY」が、『WOWOWテニス2017シーズン』イメージソングに起用されることが決定。稲葉は「トロフィーをもらえる瞬間はアスリートじゃないとない。ただ、普段の生活の中でも、みんなトロフィーが欲しい、さび付かないトロフィーがもらえる瞬間が欲しい、という想いを込めて作った」と説明した。<br />\n<br />\n　稲葉の公式サイト『en-zine』では、この対談のディレクターズカット版が来年1月1日正午から公開される。', 1, '2016-12-07 13:58:01', '2017-02-05 13:58:01', NULL, NULL, NULL, 0, 'http://www.oricon.co.jp/news/2078121/?anc=015', '松岡修造、錦織選手の激勝に泣き崩れた「ジュニアの頃と重なって…」 （16年09月08日）', 'http://www.oricon.co.jp/news/2082458/?anc=015', 'ものまね芸人・しまぞうZ　錦織選手に「公認より結婚式に来て」 （16年12月02日）', 'http://www.oricon.co.jp/news/2082162/?anc=015', 'B’z稲葉、来年1・18コラボ作品　“INABA／SALAS”名義でアルバム （16年11月29日）', '', '', '', '', 0, 0, 1, 1, '2016-12-07 16:34:05', '2016-12-08 17:25:14'),
    (23, '4_1_r_20161207_1481096044801616348', 4, 1, 1, 1, 1, 'https://mdpr.jp/review/1643757', 'd7eba18583c5144c29d4bb3e92d99174_1481096044', '“憧れミューズ”井川遥、胸元ざっくり“大人の色気”放出　美ボディの作り方を明かす', '', '【井川遥／モデルプレス＝12月7日】女優の井川遥が7日発売の雑誌「InRed」1月号の表紙に登場。インタビューでは、その体作りに関して明かした。<br/><br/><img src=\"https://mdpr.jp/photo/images/2016/12/07/200_b65f57481d5e0a4b1f119a0de5269e40b739503c0edd10bb.jpg\" alt=\"「InRed」1月号（宝島社、2016年12月7日発売）表紙：井川遥\" /><br/><div class=\"caption\">（写真 : 「InRed」1月号（宝島社、2016年12月7日発売）表紙：井川遥）</div><br/>ネイビーのロングカーディガンを着こなした井川。V字にざっくりと大きく開いた胸元から美しい谷間をちらりとのぞかせ、大人の色気で魅了した。また誌面ではキャミソールコーデやボリューム感のあるニットコーデを披露。透明感あるデコルテをヘルシーに輝かせた。\n<br/>\n<br/><strong>◆30代女性憧れのミューズ…井川遥に迫る</strong>\n<br/>\n<br/>今月号では「井川遥をつくる美人スキル」と題し、30代女性憧れのミューズである井川を特集。“女っぽさ”に対する考えや、ボディケア、日常生活の中での幸せ、息抜きの方法なども明かした。\n<br/>\n<br/>しなやかで美しいボディで知られる井川だが若い時には闇雲に筋トレを重ね「望むようなボディラインにはならなかった」そう。今は鍛え方も変化し「負荷をかけすぎず、自重だったり、プールの水圧を利用したり、使う筋肉、見せる筋肉、からだの曲線を意識しながらのエクササイズを心がけています」と美ボディを作る秘訣を語った。（modelpress編集部）\n<br/>\n<br/>情報提供：「InRed」（宝島社）', 1, '2016-12-07 14:39:55', '2017-02-05 14:39:55', NULL, NULL, NULL, 0, 'https://mdpr.jp/review/1643757', '他の写真・記事詳細をもっと見る', 'https://mdpr.jp/news/1620493', '井川遥は「日本のアンジー」「昔から共演したい女優」絶賛受ける', 'https://mdpr.jp/news/1620278', '広末涼子が竹内結子と激突？井川遥は藤木直人と恋人関係？“かもしれない”妄想展開', 'https://mdpr.jp/review/1603663', '井川遥「VERY」カバーモデルを卒業 “10年目”の旅立ち', 'https://mdpr.jp/review/1602905', '井川遥が表紙に抜てき　アラフォー女性の新美容誌が宝島社から発行', 0, 0, 1, 1, '2016-12-07 16:34:05', '2016-12-07 16:34:05'),
    (24, '4_1_r_20161207_1481096044806663066', 4, 1, 1, 1, 1, 'http://www.oricon.co.jp/news/2082601/', '423742ebf2eefb4fd1a3e28ed5871506_1481096044', '高橋克典、西内まりやとの29歳差デュエットに大照れ「親子くらいの年齢差」', '', '<img src=\"http://news.oricon.co.jp/feed/images/size640wh/20161206_173308_size640wh_7259.jpg\" title=\"29歳差のデュエットを行った（左から）西内まりや、高橋克典 （C）ORICON NewS inc.\" alt=\"29歳差のデュエットを行った（左から）西内まりや、高橋克典 （C）ORICON NewS inc.\" ><br />\n　俳優の高橋克典（51）が13年ぶりとなるアルバム『SINGS』のリリースを記念して6日、東京・COTTON CLUBでスペシャルライブ『SPECIAL SHOW　“SINGS”』を開催。ライブ前に行われた囲み取材では、自身の代表作を引き合いに「最近ではすっかり只野仁（のイメージ）ですけど、デビューは歌手なんです。やっぱり、歌うことは好きですね」と声を弾ませた。<br />\n<br />\n　今回のアルバムでは、高橋がこれまで出演してきたドラマ主題歌の中から厳選した楽曲をジャズやボサノバ調にカバー。2001年放送のドラマ『FACE～見知らぬ恋人～』（日本テレビ）の挿入歌「愛してる」のカバーでは、歌手で女優の西内まりや（22）とのデュエットに挑戦している。<br />\n<br />\n　29歳下の西内とのデュエットに、高橋は「親子くらいの年齢差なので、恥ずかしいです。このあとのライブでも、この美女が一緒に歌ってくれるので緊張しますよ」と大照れ。西内から「歌手と役者のどちらも、とても魅力的ですよ」とラブコールを送られると、「ありがとうございます。最近の人は、できていますからね…」とすっかりタジタジになっていた。<br />\n<br />\n　さまざまな試みが詰まった同アルバムの聞きどころについて高橋は「あんまり聴かないような、いい作品に出来上がったと思っています。歌ってみてもきっと面白いと思うので、ぜひいろいろと感じてみてほしいなと思います」と笑顔でアピールしていた。', 1, '2016-12-06 22:30:01', '2017-02-04 22:30:01', NULL, NULL, NULL, 0, 'http://www.oricon.co.jp/news/2080894/?anc=148', '高橋克典、公園で生吹き替えに初挑戦「こんなに恐ろしいことない」 （16年11月02日）', 'http://www.oricon.co.jp/news/2080230/?anc=148', '高橋克典、赤ちゃん子役の共演で四苦八苦「手に負えない」 （16年10月20日）', 'http://www.oricon.co.jp/news/2071966/?anc=148', '高橋克典、チーズの公開試食で緊張「どうやって食べるんだったかな？」 （16年05月20日）', '', '', '', '', 0, 0, 1, 1, '2016-12-07 16:34:05', '2016-12-07 16:34:05'),
    (25, '4_1_r_20161207_1481096045007152176', 4, 1, 1, 1, 1, 'https://mdpr.jp/cinema/1643753', 'b45f66b2257f5dd1b8d0b64b3866ea16_1481096045', 'Sexy Zone佐藤勝利×橋本環奈、“窓ドン”で密着が話題「顔面の美しさにひれ伏す」「もはや2次元の世界」', '', '【佐藤勝利・橋本環奈／モデルプレス＝12月7日】Sexy Zoneの佐藤勝利と、橋本環奈がW主演を務める映画『ハルチカ』（2017年3月4日公開）の予告編とポスタービジュアルが7日に公開され、2人のその美しさに反響が殺到している。<br/><br/><img src=\"https://mdpr.jp/photo/images/2016/12/06/200_8e5b9111bb29168d17428f344506f1b575a1e117626fbf14.jpg\" alt=\"Sexy Zone佐藤勝利×橋本環奈、“窓ドン”で密着が話題「顔面の美しさにひれ伏す」「もはや2次元の世界」／写真は特別ビジュアル（C）2017「ハルチカ」製作委員会\" /><br/><div class=\"caption\">（写真 : Sexy Zone佐藤勝利×橋本環奈、“窓ドン”で密着が話題「顔面の美しさにひれ伏す」「もはや2次元の世界」／写真は特別ビジュアル（C）2017「ハルチカ」製作委員会）</div><br/><strong>◆佐藤勝利＆橋本環奈が彩る“吹キュン”純愛ストーリー</strong>\n<br/>\n<br/>今作は、初野晴氏による青春小説「ハルチカ」シリーズを実写映画化。少々頼りないが気の優しい美男子・ハルタ（佐藤）と、気が強くて明るく元気な女子高生のチカ（橋本）が、高校の入学式で運命的な再会を果たし、吹奏楽部で音楽を通して気持ちをつなげていく純愛ストーリー。\n<br/>\n<br/>監督を務めるのは「箱入り息子の恋」（2013）で繊細かつ温かい人間ドラマを描き「僕らのごはんは明日で待ってる」（2017年1月7日公開）の公開も控える市井昌秀氏。映画オリジナルストーリーとして、多感な高校生たちが経験する挫折や痛み、そしてハルタとチカに訪れる淡い初恋を、吹奏楽の音色にのせて爽やかに描く。<br/><br/>佐藤と橋本は、今作でそれぞれの担当楽器であるホルンとフルートに初挑戦。コンサートや仕事の合間を縫って週に複数回レッスンに通い、クランクイン前から約4か月間に及ぶ猛練習を経て、劇中で見事な演奏を披露するまでに上達したという。\n<br/>\n<br/><strong>◆圧倒的な顔面偏差値！幼なじみの2人が話題</strong>\n<br/>\n<br/>今回公開された予告編では、高校で偶然再会した幼なじみの2人が、廃部寸前の吹奏楽部を立て直そうと奔走する姿を映し出している。個性豊かな“ワケあり”のメンバーたちに声をかけ、徐々に部員が揃っていく中、かつて将来を嘱望される指揮者だった教師・草壁信二郎（小出恵介）が顧問に加わり、コンクール出場に向けて突き進む。\n<br/>\n<br/>しかし、そこから部にとっても2人にとっても大きな試練が待ち受ける。みんなになかなか追いつけず涙するフルート初心者のチカと、練習中に突如倒れてしまうハルタ。コンクール本番の日が迫る中、彼らは試練を乗り越えることができるのか、そして2人の関係性や、予告編後半で流れる「吹奏楽のための狂詩曲 第1番『春の光、夏の風』」にも注目が集まる。<br/><br/>また、ポスタービジュアルでは予告編でも最も印象的なワンシーンとなる、バスの車中でキス寸前まで顔が近づく「窓ドン」のハルタとチカの姿が。「響き出す、2人の青春」というキャッチコピーと、見つめ合う2人の眼差しから、幼なじみ同士の間に芽生えた「近くて遠い、トクベツな感情」を感じることができるビジュアルとなっている。\n<br/>\n<br/>そんな予告編やポスタービジュアルが「顔面人間国宝の佐藤勝利と、1000年に1度の橋本環奈…顔面の美しさにひれ伏す」「ハルチカやばい！2人が美しすぎてスクリーンが破壊しそう」「こんな美男美女の幼なじみとかもはや2次元の世界」「至近距離で見つめ合う2人の顔面が綺麗すぎてまるでファンタジー」など早くも話題沸騰。Twitterでは朝から「ハルチカ」がトレンド入りする反響ぶりを見せていた。（modelpress編集部）', 1, '2016-12-07 14:18:14', '2017-02-05 14:18:14', NULL, NULL, NULL, 0, 'https://mdpr.jp/cinema/1643753', '他の写真・記事詳細をもっと見る', 'https://mdpr.jp/cinema/1643449', 'Sexy Zone佐藤勝利、橋本環奈にキス寸前の“窓ドン”披露 心ときめく青春ビジュアル解禁', 'https://mdpr.jp/news/1643408', '橋本環奈が“捨て猫”に？反則級の可愛さに“飼い主”続出「ぜひうちに！」', 'https://mdpr.jp/cinema/1643378', '「銀魂」完全再現に反響　小栗旬・菅田将暉・橋本環奈のビジュアルに「完成度高い」「最高」と期待の声', 'https://mdpr.jp/cinema/1643182', '「銀魂」完全再現！小栗旬・菅田将暉・橋本環奈のビジュアル解禁', 0, 0, 1, 1, '2016-12-07 16:34:05', '2016-12-07 16:34:05'),
    (26, '4_1_r_20161207_1481096045011813309', 4, 1, 1, 1, 1, 'http://www.oricon.co.jp/news/2082666/', '9f782771261136f2590116bcc1ebc3b3_1481096045', '「L⇔R」黒沢健一さんが死去　今年10月に脳腫瘍を公表', '', '<img src=\"http://news.oricon.co.jp/feed/images/size640wh/20161004_133900_size640wh_5125.jpg\" title=\"黒沢健一さんが死去\" alt=\"黒沢健一さんが死去\" ><br />\n　脳腫瘍で療養中であることを公表していたシンガー・ソングライターの黒沢健一さんが、5日午前3時52分に亡くなった。享年48。黒沢さんの公式サイトで7日、発表された。通夜及び葬儀は近親者のみで執り行われ、後日ファンに向けた「お別れ会」の開催が予定される。<br />\n<br />\n　黒沢さんが結成したバンド「L⇔R」のメンバーもコメントを発表。ギターで黒沢さんの弟でもある黒沢秀樹は「僕の実兄である黒沢健一は12月5日の深夜、夜明けを待たずに旅立ちました。家族の見守る中、安らかに眠るような最期を迎えることが出来ました」と報告し、「兄の遺してくれた作品が、一人でも多くの人の悲しみや絶望を癒し、希望の糧となってくれることを心から願っています」と呼びかけた。<br />\n<br />\n　ベースの木下裕晴は「健一は自分の近くに長く一緒にいてくれた友人でもあります。よく会っていましたし、ソロの活動にも参加させてもらっていました。昨日亡くなった本人に会いましたが、寝ているくらいにしか思えず、未だに全く実感が湧きません」とコメント。<br />\n<br />\n　元メンバーの嶺川貴子も「3年前久しぶりの再会をした時、健一君はぜんぜん変わっていなくて、笑顔で優しかった。短い時間だったけど、あの時再会できてよかった。発見された、健一君が描いた \'\'いけんを言うもも\'\' の絵は、本当に私に似ていますね。大事にします。ありがとう」と追悼した。<br />\n<br />\n　黒沢さんは19歳で南野陽子、島田奈美などへ楽曲提供やCM曲提供など、作家としてデビュー。1991年に弟の秀樹、木下裕晴と共にバンド「L⇔R」を結成し「Knockin’ on your door」などがヒット。1997年に「L⇔R」を活動休止し、ソロアーティストとして活動を開始していた。', 1, '2016-12-07 12:28:14', '2017-02-05 12:28:14', NULL, NULL, NULL, 0, 'http://www.oricon.co.jp/news/2057093/full/?anc=142', '俳優・泉政行さんが死去　35歳　『仮面ライダー555』『科捜研の女』など出演', 'http://www.oricon.co.jp/news/2059474/?anc=142', '胃がん公表から1年・・・黒木奈々アナ死去 32歳 （15年09月19日）', 'http://www.oricon.co.jp/news/56408/photo/1/?anc=142', '【写真】L⇔R・黒沢健一＆秀樹兄弟が関係修復で10年ぶりに共作（08年07月17日）', '', '', '', '', 0, 0, 1, 1, '2016-12-07 16:34:05', '2016-12-07 16:34:05'),
    (27, '4_1_r_20161207_1481096045234050446', 4, 1, 1, 1, 1, 'https://mdpr.jp/news/1643755', 'e3276567093f88d8ab801415592426d7_1481096045', 'ディーン・フジオカが“今年の顔”に決定＜本人コメント＞', '', '【ディーン・フジオカ／モデルプレス＝12月7日】俳優のディーン・フジオカが、2016年に最も検索数が急上昇した“今年の顔”を選ぶ賞の大賞に選ばれ、「2016年最も検索された人」となった。<br/><br/><img src=\"https://mdpr.jp/photo/images/2016/12/07/200_45e00e2d4a518e1165576aef1b70013a44ca3e7713a1729a.jpg\" alt=\"受賞式にビデオメッセージを寄せたディーン・フジオカ （C）モデルプレス\" /><br/><div class=\"caption\">（写真 : 受賞式にビデオメッセージを寄せたディーン・フジオカ （C）モデルプレス）</div><br/><strong>◆ディーン・フジオカ“今年の顔”選出に歓喜</strong>\n<br/>\n<br/>2015年に放送されたNHKの朝ドラ「あさが来た」をきっかけに国内でブレークし、その後も俳優業を中心に活躍したディーンは、7日に都内にて行われた受賞式にビデオメッセージを寄せ、「自分では思いもよらぬ受賞で嬉しく思います」とコメント。\n<br/>\n<br/>同作出演以前は日本以外のアジアを拠点に活動していたため、「僕は日本ではないところで長いこと仕事だったり生活をしてきたので生まれた国で興味を持って頂いて検索して頂けたっていう、その一票一票というか“一検索一検索”して下さった方々にこの場で一人でも多くの方に感謝を伝えられたらいいなと思います」としみじみと心境を語った。\n<br/>\n<br/>さらに、今年1年を振り返り、「駆け抜けたって感じですね」と一言。「日本でこういう形でお仕事をさせて頂けるようになって自分の新しいスタートをきれた年だったと思ってます。起承転結の“起”」と表現した。\n<br/>\n<br/>また、関連検索ワードには「Cycle」「History Maker」「歌」などミュージシャン活動に関する話題が並び、「今年は俳優としての活動の比重が多かったんですけど、僕が好きでやってる音楽の部分にも興味を持ってもらえたら嬉しい」と笑顔を見せた。\n<br/>\n<br/><strong>◆「やりたいことは全部やる」</strong>\n<br/>\n<br/>最後に、「引き続き日本でお仕事させてもらえるっていうのが僕にとって有り難いことなので、1つ1つ丁寧に楽しく、プロフェッショナルな仕事をしていけたら良いなと思ってます」と今後の国内活動に意気込み。\n<br/>\n<br/>「いままで他の国でやってきたこと、応援して下さった方々、お世話になった方々に届くような形でエンターテイメントやアートの活動を続けていきたいと思っていますので、自分でリミットを作らずにやりたいことは全部やるって感じで頑張っていきたいので応援よろしくお願いします」と呼びかけた。\n<br/>\n<br/><strong>◆欅坂46・りゅうちぇるらが登壇</strong>\n<br/>\n<br/>受賞式はお笑いタレントの土田晃之が特別MCを務め、欅坂46の平手友梨奈、守屋茜、土生瑞穂、渡辺梨加、長濱ねる、りゅうちぇる、歌手で女優の藤原さくらが出席。\n<br/>\n<br/>ディーンのほか、お笑いタレントのピコ太郎、カズレーザー（メイプル超合金）、卓球選手の福原愛、女優の高畑充希、作家の村田紗耶香氏、映画監督の新海誠氏がビデオメッセージを寄せた。（modelpress編集部）', 1, '2016-12-07 16:17:54', '2017-02-05 16:17:54', NULL, NULL, NULL, 0, 'https://mdpr.jp/news/1643755', '他の写真・記事詳細をもっと見る', 'https://mdpr.jp/cinema/1641913', '実写版「鋼の錬金術師」ディーン・フジオカ着用衣装が世界初披露へ　映画公開月も発表＜本人コメント＞', 'https://mdpr.jp/music/1641892', 'ディーン・フジオカ、豪華タッグ結成「ユーリ!!! on ICE」OP曲に新展開', 'https://mdpr.jp/music/1639608', 'ディーン・フジオカ「ユーリ!!! on ICE」OP曲弾き語りでトレンド入りの話題に　「とにかくかっこいい」「動悸がやばい」興奮止まぬ視聴者続出', 'https://mdpr.jp/interview/1632872', 'ディーン・フジオカ＆ホラン千秋、意外な留学理由を告白　モデルプレスインタビュー', 0, 0, 1, 1, '2016-12-07 16:34:05', '2016-12-07 16:34:05'),
    (28, '4_1_r_20161207_1481096045242170785', 4, 1, 1, 1, 1, 'http://www.oricon.co.jp/news/2082670/', '0a036c962a6cdf00736a0b80c0ae103d_1481096045', '中居正広が驚きの飲酒習慣を告白、キスマイ北山との“師弟関係”も明かす？', '', '<img src=\"http://news.oricon.co.jp/feed/images/size640wh/20161207_132046_size640wh_2946.jpg\" title=\"自身のツイッターで“泥酔”を謝罪したことのある劇団ひとり （C）ORICON NewS inc.\" alt=\"自身のツイッターで“泥酔”を謝罪したことのある劇団ひとり （C）ORICON NewS inc.\" ><br />\n　6日放送の『中居正広のミになる図書館』（テレビ朝日系）で、SMAPの中居正広が、後輩である北山宏光（Kis-My-Ft2）を“安い店”に飲みに連れて行ったことが語られた。<br />\n<br />\n　人気コーナー“居酒屋40題”で、飲んだ翌日まで酒が残るようになったという出演者たち。下手したら年間100万円以上、二十歳から通算何千万を酒に費やしているかわからないと盛り上がった。<br />\n<br />\n　そんな中、20代から1週間に2升を飲むペースが変わらず、二日酔いにもならないという中居。性格上、外に飲みに行くことはほとんどなく、前日も午後9時から翌朝4時まで一人酒をしていたと明かした。<br />\n<br />\n　しかし、劇団ひとりに「（行くとしても）高い店では飲まないでしょう」と聞かれると「馬鹿にしてんだろう（笑）」と反論しながら、以前、北山を連れて飲みに行ったことを語った。が、続いて北山が中居に連れて行かれたと暴露したのは、「豚の串1本180円」というリーズナブルな店。「あれ美味かっただろ？ 食わせたかったんだよ北山に！」という中居に、北山は「確かに美味かったっすけど……」と苦笑していた。<br />\n<br />\n　実は、今年2月放送の『ナカイの窓』（日本テレビ系）で、「（中居は）6回誘っても1回も行ってくれない」と訴えていた北山。そんな北山について中居は、10月放送の『ミになる～』でも、「（俺の話に）一生懸命笑ってくれるのがつらい」「“いい角度で質問したでしょ”というアピールが強い」と、本番中にまさに“ミになる”ダメ出し。だが今回の放送で、プライベートでも北山の良き相談相手になっていることが発覚した。<br />\n<br />\n　ある種の師弟関係を覗かせた中居だが、後半には「飲むとエッチな気分になるのは何でですか？」「でも体がついていかないんです！」と酔った風情で専門家の女医に質問。女医から、「アルコールが脳に与える影響です」「それはまた別の薬が……」と普通に返され、スタジオは爆笑に包まれていた。<br />\n（文：坂戸希和美）', 1, '2016-12-07 13:24:42', '2017-02-05 13:24:42', NULL, NULL, NULL, 0, 'http://www.oricon.co.jp/news/2082497/?anc=166', '誰も狙えない“ポストSMAP”、その理由は？', 'http://www.oricon.co.jp/news/2082579/?anc=166', '『SMAP×SMAP』12月26日最終回は5時間特番「すべてを存分にお届けします」 （16年12月05日）', 'http://www.oricon.co.jp/news/2082046/?anc=166', '中居正広は“M”？ 稲垣吾郎が考えるSMAP内ポジション （16年11月25日）', '', '', '', '', 0, 0, 1, 1, '2016-12-07 16:34:05', '2016-12-07 16:34:05'),
    (29, '4_1_r_20161207_1481096045328659402', 4, 1, 1, 1, 1, 'https://mdpr.jp/review/1643743', '252bc03987655bdde2be2eeae7b0654d_1481096045', 'ローラのドキッとする視線に釘付け！“大人の魅力”でロエベNEWコレクションと競演', '', '【ローラ／モデルプレス＝12月7日】モデルでタレントのローラが7日発売の雑誌「GLITTER」1月号のカバーガールに登場。誌面では『LOEWE（ロエベ）』の新作を着こなした。<br/><br/><img src=\"https://mdpr.jp/photo/images/2016/12/07/200_9d467d5105773f8ad5852b1b09283e455f0732f4ec54521d.jpg\" alt=\"「GLITTER」1月号（トランスメディア、2016年12月7日発売）表紙：ローラ\" /><br/><div class=\"caption\">（写真 : 「GLITTER」1月号（トランスメディア、2016年12月7日発売）表紙：ローラ）</div><br/>繊細なホワイトレースのセットアップで表紙を飾ったローラ。ヌーディーなメイクで真っ直ぐ視線を投げかけ、テレビから伝わるキュートで元気なイメージとは、また違った表情を見せた。\n<br/>\n<br/><strong>◆“大人の魅力”で華麗に披露</strong>\n<br/>\n<br/>誌面では世界中のセレブから愛されるラグジュアリーブランド『LOEWE』の新作を着こなし、最新バッグコレクションを次々と紹介。\n<br/>\n<br/>最近ますます“大人の魅力”が増したローラが、オーバーサイズのワンピースや柔らかなレザーパンツ×麻トップス、オールレザーのブルゾンなどに身を包み、バッグを華麗に魅せた。（modelpress編集部）\n<br/>\n<br/>情報提供：「GLITTER」（トランスメディア）', 1, '2016-12-07 13:37:45', '2017-02-05 13:37:45', NULL, NULL, NULL, 0, 'https://mdpr.jp/review/1643743', '他の写真・記事詳細をもっと見る', 'https://mdpr.jp/news/1643397', 'ローラ、バスローブで美脚披露「たまらない」「神々しい」と反響', 'https://mdpr.jp/news/1642866', 'ローラ＆舞川あいくが再会 元「Popteen」モデルの美女2ショットにファン歓喜', 'https://mdpr.jp/news/1642783', 'ローラ、谷間＆美ヒップあらわなビキニショットで悩殺「美しすぎて言葉が出ない」', 'https://mdpr.jp/news/1642475', 'ローラ、スラリ美脚＆肩見せSEXYショットに「服着てる？」「ドキッとした」ファン興奮', 0, 0, 1, 1, '2016-12-07 16:34:05', '2016-12-07 16:34:05'),
    (30, '4_1_r_20161207_1481096045338299031', 4, 1, 1, 1, 1, 'http://www.oricon.co.jp/news/2082667/', '0ef36d04b90450ef1ec3f6d06c1cee0a_1481096045', '仕事復帰の梅宮辰夫、撮了で笑顔　高橋克典「完璧でした」', '', '<img src=\"http://news.oricon.co.jp/feed/images/size640wh/20161207_123312_size640wh_4459.jpg\" title=\"仕事復帰した梅宮辰夫（右）がオールアップを迎え、高橋克典が花束を贈呈\" alt=\"仕事復帰した梅宮辰夫（右）がオールアップを迎え、高橋克典が花束を贈呈\" ><br />\n　十二指腸乳頭部がんを患い今年7月に手術を受けた俳優の梅宮辰夫（78）が7日、療養後の復帰作となった動画配信サービス「AbemaTV」初のオリジナル連続ドラマ『特命係長　只野仁　AbemaTVオリジナル』のオールアップを迎えた。同日、主人公の只野仁を演じた高橋克典（51）がブログで報告した。<br />\n<br />\n　梅宮は、只野（高橋）に特命を下す電王堂会長・黒川重蔵役。本シリーズに欠かせないキーパーソンで、高橋と梅宮の息の合ったやり取りは、お約束の1つでもある。撮影を終え高橋は「本当にありがとうございました。今日は長い説明台詞を9シーンも」と振り返りつつも「完璧でした」と感謝。<br />\n<br />\n　続けて「それにやっぱり辰夫叔父貴はとてもチャーミングでカッコいい。改めて、いろいろ教えていただいた気がしました。ありがとうございました」とつづり、梅宮へ花束を渡す写真などを公開。そのほかにも撮影を無事終え「大変楽しゅうございました。ありがとうございます」と共演者やスタッフに感謝しスタジオをあとにする梅宮の動画も紹介している。<br />\n<br />\n　ドラマは2017年、年明け放送開始予定。', 1, '2016-12-07 13:06:02', '2017-02-05 13:06:02', NULL, NULL, NULL, 0, 'http://www.oricon.co.jp/news/2011395/photo/1/?anc=112', '【写真】アンナの娘・百々果も…梅宮家3世代が初共演', 'http://www.oricon.co.jp/news/77960/photo/1/?anc=112', '【写真】梅宮ファミリーの家族3ショット　クラウディア夫人も登壇', 'http://www.oricon.co.jp/news/2079354/?anc=112', '高橋克典の代表作『特命係長　只野仁』5年ぶり復活　AbemaTVで配信 （16年10月03日）', '', '', '', '', 0, 0, 1, 1, '2016-12-07 16:34:05', '2016-12-07 16:34:05'),
    (31, '4_1_r_20161207_1481096045441685667', 4, 1, 1, 1, 1, 'https://mdpr.jp/news/1643740', 'f3cfef0f0e4071372a26c72cded1d5fa_1481096045', '中条あやみ・河北麻友子・松井愛莉・池田エライザら集結「東京ガールズコレクション2017S／S」開催決定', '', '【中条あやみ・河北麻友子・松井愛莉・池田エライザ／モデルプレス＝12月7日】2017年3月25日に、国立代々木競技場第一体育館で「第24回 東京ガールズコレクション 2017SPRING／SUMMER」（以下：TGC）が開催されることが分かった。<br/><br/><img src=\"https://mdpr.jp/photo/images/2016/12/07/200_2a63dba1b2f0939cab274cff8484b9bae8af2a4873a8a5b8.jpg\" alt=\"「第24回 東京ガールズコレクション 2017SPRING／SUMMER」開催決定（左上から時計回りに）中条あやみ、河北麻友子、松井愛莉、池田エライザ、中村里砂、藤田ニコル、飯豊まりえ、朝比奈彩\" /><br/><div class=\"caption\">（写真 : 「第24回 東京ガールズコレクション 2017SPRING／SUMMER」開催決定（左上から時計回りに）中条あやみ、河北麻友子、松井愛莉、池田エライザ、中村里砂、藤田ニコル、飯豊まりえ、朝比奈彩）</div><br/>\n<br/><strong>◆出演モデル＆アーティストも発表</strong>\n<br/>\n<br/>最新のトレンド発信に欠かせないメインモデルには、朝比奈彩、飯豊まりえ、池田エライザ、河北麻友子、中条あやみ、中村里砂、藤田ニコル、松井愛莉らをはじめ、豪華モデルが決定。\n<br/>\n<br/>さらに、アーティストとしてlol－エルオーエル－、オープニングアクトとしてTHE BEAT GARDENが出演。今後も、続々と出演モデルやアーティストが発表されていく。\n<br/>\n<br/><strong>◆24回目のテーマは？</strong>\n<br/>\n<br/>第24回のテーマは「Female Hero」。「輝く女性の象徴である『Female Hero』は、全ての女性の中に存在し、力強く美しく夢や希望に溢れ、ときに世界を変えるエネルギーを秘めています。ガールズパワーで満ち溢れ、まさにFemaleHero たちの祭典であるTGCをきっかけに、それぞれの人生のrunwayが輝かしいものになりますように」という想いが込められている。\n<br/>\n<br/>そして、テーマを象徴するキービジュアルは、「ファイナルファンタジー」シリーズなどを手がける、世界的アーティスト・天野喜孝氏が担当。同ビジュアルについて天野氏は、「テーマを象徴するキービジュアルは、『Female Hero』というテーマのもと、ファッションによって意志ある女の子達が弾けるようなパワーをCANDY GIRLの世界と重ねあわせてPOPでパーティー感のある表現にしました」と語っている。\n<br/>\n<br/><strong>◆かつてない「ファッション」×「アート」の試みも</strong>\n<br/>\n<br/>さらに、今回のTGCは日本最大級のアート見本市「アートフェア東京2017」とコラボレーション。発信力のある日本の女性達へアートの魅力を伝えるべく、天野氏が手掛けるキービジュアルをはじめ、韓国人アーティスト、クワッ・チョル氏の作り出す「Female Hero」をテーマにした2メートル級の立体作品など、TGCだからこそできる、今までにない新たな「ファッション」×「アート」のムーブメントを創出する。\n<br/>\n<br/><strong>◆TGCで「TOKYO」を発信</strong>\n<br/>\n<br/>また、東京都が世界一の観光都市を目指し、国内外に向けて旅行地としての東京を強く印象づける「東京ブランド」の確立に向け取り組んでいる「東京ブランド推進プロジェクト」に、「＆TOKYO meets TOKYO GIRLS COLLECTION～熱狂的ガールズ・ファッションイベントを通した東京ブランディング～」が採択。\n<br/>\n<br/>世界から注目されるファッションイベントとして、東京ブランドを発信していく。（modelpress編集部）\n<br/>\n<br/><strong>■第24回 東京ガールズコレクション 2017SPRING／SUMMER」開催概要</strong>\n<br/>開催日時： 2017年3月25日（土） 開場 13：00 開演15：00 終演 21：00 (予定)\n<br/>会場：国立代々木競技場第一体育館 （東京都渋谷区神南2-1-1）\n<br/>予定動員数：約30,000人（のべ人数）\n<br/>集客対象： F1層（20～34才の女性）\n<br/>\n<br/>メインモデル ：朝比奈彩、アシュリー、飯豊まりえ、イーラン、池田エライザ、池田美優、江野沢愛美、瑛茉ジャスミン、岡崎紗絵、香川沙耶、河北麻友子、鹿沼憂妃、立花恵理、谷川りさこ、トラウデン直美、中条あやみ、中村里砂、長谷川ニイナ、hibiki（lol）、藤田ニコル、堀田茜、松井愛莉、松元絵里花、miu、八木アリサ、吉木千沙都ほか（※五十音順）\n<br/>アーティスト：lol－エルオーエル－\n<br/>オープニングアクト：THE BEAT GARDEN\n<br/>ステージ内容 ：ファッションショー、アーティストライブ、パートナーステージ 他', 1, '2016-12-07 13:51:40', '2017-02-05 13:51:40', NULL, NULL, NULL, 0, 'https://mdpr.jp/news/1643740', '他の写真・記事詳細をもっと見る', 'https://mdpr.jp/news/1615072', '乃木坂46、ライブ＆ランウェイで「TGC北九州2016」を盛り上げる！香里奈、竜星涼ら豪華追加ゲスト発表', 'https://mdpr.jp/beauty/1601491', '“赤リップ”が進化して再ブーム！「TGC2016A／W」4大メイクトレンドキーワード発表', 'https://mdpr.jp/news/1601202', '桐谷美玲・與真司郎（AAA）・みちょぱ・Da-iCEら、豪華追加出演者を発表「TGC北九州2016」', 'https://mdpr.jp/news/1593949', '大政絢・ダレノガレ明美・池田エライザら豪華モデル集結　TGCが北九州に再来', 0, 0, 1, 1, '2016-12-07 16:34:05', '2016-12-07 16:34:05'),
    (32, '4_1_r_20161207_1481096045604642241', 4, 1, 1, 1, 1, 'http://www.oricon.co.jp/news/2082548/', 'd1426e79fdce4ee9e6ab8b507a24de5e_1481096045', '2016年ブレイク女優　1位は高畑充希', '', '<img src=\"http://news.oricon.co.jp/feed/images/size640wh/20161206_182109_size640wh_8986.jpg\" title=\"2016年ブレイク女優ランキング、1位は高畑充希\" alt=\"2016年ブレイク女優ランキング、1位は高畑充希\" ><br />\n　ORICON STYLEでは、年末恒例『2016年 ブレイク女優ランキング』を今年も発表。堂々の1位に輝いたのは、朝ドラ『とと姉ちゃん』(NHK総合)で幅広い層のファンを獲得した【高畑充希】。今年は、映画初主演を果たしたほか、かんぽ生命のCM「それは人生、わたしの人生」篇で素晴らしい歌声を披露し話題になるなど圧倒的な活躍ぶりを見せ、文句なしの受賞となった。<br />\n<br />\n■舞台で培われた実力は本物　文字通り若手実力No.1の座に<br />\n<br />\n　2007年から6年間に渡りミュージカル『ピーターパン』の主演を務めただけあって、演技力や表現力、更には歌唱力も抜群の高畑。2013年に出演した朝ドラ『ごちそうさん』(NHK総合)でヒロインの義理の妹役を演じ、劇中で披露した「焼き氷の歌」が大きな話題となり歌の上手い演技派女優の称号を手に入れた。そして今年に入ると胸キュン映画『植物図鑑 運命の恋、ひろいました』で初主演を務め、朝ドラ『とと姉ちゃん』では少女が成長して出版社の社長を務めるまでを堂々たる演技でこなし多くの視聴者の心を掴んだ。<br />\n<br />\n　支持コメントには「以前は脇役のイメージが強かったが、最近はとと姉ちゃんなど主役級の女優というイメージが強くなった」（岐阜県／20代／女性）、「朝ドラだけではなく、CMや映画、舞台など彼女を見ない日はない」（神奈川県／30代／男性）、「歌が上手くて可愛い。上品だし演技も上手」（島根県／20代／女性）、「バラエティ番組で観る機会が圧倒的に増えた」（長野県／20代／男性）など、老若男女問わず様々な評価の声が挙がっていた。<br />\n<br />\n　高畑は雑誌『スクリーンプラス』のインタビューで「私はミュージカル出身なので、ミュージカルと映像の架け橋になりたいという気持ちはいつも持っています」と語っている。自分を育ててくれたミュージカルというフィールドを常に大切にしているのが伝わる言葉であり、忙しくなった今でも地に足をしっかりつけて一つ一つの仕事を丁寧にこなしているのがわかる。演じる役柄に関しても『いつかこの恋を思い出してきっと泣いてしまう』（フジテレビ系）で演じたOLのようにごく一般的な女性を演じることが多い高畑だが、だからこそ幅広い層からの支持を得やすく、高い演技力で見るものに安心感を与えくれるのではないだろうか。かと思えば映画『アズミ・ハルコは行方不明』で珍しく茶髪ギャルを演じ、新たな一面も見せている。まだ特定のイメージがついていない彼女はドラマや映画などで更に需要が高まり、来年以降も大女優への階段を一歩ずつ登っていくだろう。<br />\n<br />\n■地道な活動が開花 24時間TVのパーソナリティも好印象<br />\n<br />\n　2位にランクインしたのは、朝ドラ『あさが来た』（NHK総合）で明治を代表する女性実業家のヒロイン・白岡あさを好演し、一躍その名を全国に知らしめた【波瑠】。『あさが来た』の後、間髪入れずに出演したドラマ『世界一難しい恋』（日本テレビ系）では、あさとは真逆のしっとりした大人の女性を演じたことで朝ドラのイメージからの脱却に成功した。その後もドラマ『ON 異常犯罪捜査官・藤堂比奈子』（フジテレビ系）で初の刑事役にも挑戦している。<br />\n<br />\n　主な支持理由は「朝ドラが終わった後に2クール連続でドラマ出演し、24時間テレビのパーソナリティも務めかなり多忙なイメージ」（佐賀県／10代／女性）、「ひとつ格が上にあがった感じがする」（神奈川県／30代／男性）、「いろいろな役を演じていたがそれぞれ違う顔に見えた」（滋賀県／50代／女性）など、朝ドラ以降も幅広く活躍したことで注目が集まったと言える。<br />\n<br />\n　特に普段ドラマを観ない層からも支持を得たのは24時間テレビのパーソナリティを務めたことが大きかったのではないだろうか。24時間テレビ終了後には自身のブログで「幸せになること、夢を見ること、愛し愛されること、勇気を持つこと、その権利を持たない命なんてない」と綴り、400を超えるコメントが寄せられていた。女優、そしてバラエティなどで好感度をあげつつも、意思や考え方をハッキリと世の中に発信していく姿にいち人間としての波瑠の魅力が垣間見える。今後ますます活躍の場を広げていくだろう。<br />\n<br />\n■“女優豊作”となった2016年　気鋭の若手が次々と躍進 <br />\n<br />\n　このほか上位ランキングを振り返ると、3位には、映画『ちはやふる』の前編・後編のヒロイン役でなりふりかまわず必死でかるたをする本気の芝居を見せた【広瀬すず】が登場。支持コメントには「お人形さんみたいに可愛い」（愛知県／30代／女性）、「幅広い役柄に挑戦していて、その一つ一つが印象的。実力派の女優さんだと思う」（静岡県／20代／女性）、「バラエティにもたくさん出ていて結構しゃべりもいける」（愛知県／40代・男性）と容姿から演技力、バラエティ番組まで様々な面において評価を得ているのがわかる。『ちはやふる』のような青春ものから映画『怒り』でのシリアスな演技まで幅広くこなす彼女に今後の活躍が期待される。<br />\n<br />\n　そのほか、4位にはドラマや映画で活躍しつつも豪出身の歌手・シーアの新曲「アライヴ」の日本版MVで圧巻のダンスを見せ、『オールスター感謝祭』での“魂の走り”が多くの視聴者の心を掴んだ【土屋太鳳】。続く5位には女優業だけでなくバラエティでも活躍中の【二階堂ふみ】、7位に現在放送中のドラマ『家政婦のミタゾノ』（テレビ朝日系）やバラエティ『にじいろジーン』（フジテレビ系）で活躍中の【清水富美加】、そして8位には朝ドラ『あさが来た』の“のぶちゃん”役をきっかけに民放の人気ドラマへの出演も果たした【吉岡里帆】、そして9位には興行収入100億円の大ヒットとなった『君の名は。』で知名度がアップし、今後の活躍に期待がかかる【上白石萌音】がランクイン。<br />\n　<br />\n　昨年のブレイク女優ランキングでは10位だった高畑充希や6位だった波瑠など新たな才能が次々と花開きブレイクに繋がった2016年。映画やドラマなど俳優としての演技がブレイクのきっかけとなるのはもちろんのこと、最近ではバラエティ番組などで“面白い一面”や“意外な素顔”を見せることでより彼女達が身近に感じられ、多くの一般層から人気を集めるケースも目立ってきている。演技力だけでなく、どれだけ多くの引き出しを持っているのかが更なる飛躍を生む鍵となりそうだ。<br />\n<br />\n（文：奥村百恵）<br />\n<br />\n【2016年 ブレイク女優ランキングTOP10】<br />\n1位　高畑充希<br />\n2位　波瑠<br />\n3位　広瀬すず<br />\n4位　土屋太鳳<br />\n5位　二階堂ふみ<br />\n6位　松岡茉優<br />\n7位　清水富美加<br />\n8位　吉岡里帆<br />\n9位　上白石萌音<br />\n10位 川栄李奈<br />\n<br />\n【調査概要】<br />\n調査時期：2016年10月12日（水）～10月17日（火）<br />\n調査対象：計1000名（自社アンケート・パネル【オリコン・モニターリサーチ】会員10代、20代、30代、40代、50代の男女）<br />\n調査地域：全国<br />\n調査方法：インターネット調査<br />\n調査機関：オリコン・モニターリサーチ', 1, '2016-12-07 13:00:01', '2017-02-05 13:00:01', NULL, NULL, NULL, 0, 'http://www.oricon.co.jp/photo/1761/120975/?anc=019', '【写真】舞台で“ピーターパン”を演じた、初々しい頃の高畑充希', 'http://www.oricon.co.jp/photo/2170/?anc=019', '【写真】いろいろな表情をで魅了する女優たち…ブレイク女優フォトギャラリー', 'http://www.oricon.co.jp/photo/1417/107242/?anc=019', '【写真】広瀬すずのキュートなウェデイングドレス姿', '', '', '', '', 0, 0, 1, 1, '2016-12-07 16:34:06', '2016-12-08 17:25:16'),
    (33, '4_1_r_20161207_1481096045809901371', 4, 1, 1, 1, 1, 'https://mdpr.jp/news/1643725', '2f733b706a2893df281c31b0b5383096_1481096045', 'りゅうちぇる、ぺことの記念日毎月祝福「めんどくさくない？」の質問に回答', '', '【りゅうちぇる／モデルプレス＝12月7日】タレントのりゅうちぇるが7日、都内にて行われた受賞式に出席した。<br/><br/><img src=\"https://mdpr.jp/photo/images/2016/12/07/200_6af63477cc710f3ed2e77caf2ff2480911dfe3d972957114.jpg\" alt=\"受賞式に出席したりゅうちぇる （C）モデルプレス\" /><br/><div class=\"caption\">（写真 : 受賞式に出席したりゅうちぇる （C）モデルプレス）</div><br/><strong>◆りゅうちぇる「目が合っただけで記念日」</strong>\n<br/>\n<br/>先日、恋人で読者モデルのぺことは毎月交際記念日を祝い合い、お互いのSNSでも愛のメッセージを送り合うことが話題となったりゅうちぇる。受賞式後の囲み取材で記者から「1ヵ月ごとってめんどくさくないですか？」と質問が飛ぶと、りゅうちぇるは「全然～！1ヵ月起きたことって違う」と即座に否定。\n<br/>\n<br/>「目が合っただけで記念日にしちゃいたいくらいまだまだ足りないです」と持論を展開した。\n<br/>\n<br/><strong>◆結婚は「タイミングをちゃんと見図らたい」</strong>\n<br/>\n<br/>また、ぺことの結婚予定について問われたりゅうちぇるは、「やっぱり段々働いていくうちにぺこりんが『今はお仕事頑張るときだね』って言ってくれたり、2人のタイミングをちゃんと見図らたいなと思うようになりました」と告白。\n<br/>\n<br/>これまでも2017年あたりには結婚したいことをテレビ番組やイベントで公言しているが、改めて聞かれると「来年とかには結婚したいですね」と笑顔で答えた。\n<br/>\n<br/><strong>◆りゅうちぇるのエゴサーチの方法は？</strong>\n<br/>\n<br/>この日、2016年に最も検索数が急上昇した“今年の顔”として表彰されたりゅうちぇる。昨年の冬からバラエティ番組に出演し始め、今年テレビ番組で見ない日はないというほどブレイク。「こんなに働いたのは人生で初めて」だったことを回顧し、「ぺこりんとのデートはめっちゃ覚えてるけどお仕事の記憶があまりない！」とあっという間の1年だったことを明かした。\n<br/>\n<br/>また、りゅうちぇるの後に続く関連検索ワードの1位が「ぺこ」だったことを知ると歓喜し、「（ぺこも）喜んでくれるかな」とコメント。そのほか関連検索ワードには「イケメン」「すっぴん」「姉」などが続いた。\n<br/>\n<br/>りゅうちぇる自身は普段どんなことをネットで検索するかという話題では、「僕自身のことを検索したりもします」とエゴサーチすることを告白。「画像検索が大好きで、一番最初に出てきたやつが可愛いか、盛れてるかどうかを検索する」とこだわりを語った。\n<br/>\n<br/><strong>◆衣装のテーマにツッコミ</strong><br/><br/>この日の衣装のテーマは「アフリカの貴族のエアロビボーイ」だそうで、具体的な国を聞かれると「ジャマイカの方」と回答。MCの土田晃之から「ジャマイカはアフリカじゃないですけど」とツッコミを入れられ、会場の笑いを誘っていた。\n<br/>\n<br/>受賞式には、りゅうちぇるのほか、欅坂46の平手友梨奈、守屋茜、土生瑞穂、渡辺梨加、長濱ねる、歌手で女優の藤原さくらが出席。お笑いタレントのピコ太郎、カズレーザー（メイプル超合金）、卓球選手の福原愛、女優の高畑充希、俳優のディーン・フジオカ、作家の村田紗耶香氏、映画監督の新海誠氏がビデオメッセージを寄せた。（modelpress編集部）', 1, '2016-12-07 16:15:40', '2017-02-05 16:15:40', NULL, NULL, NULL, 0, 'https://mdpr.jp/news/1643725', '他の写真・記事詳細をもっと見る', 'https://mdpr.jp/news/1642860', 'りゅうちぇる、噂の心霊写真にまさかの鑑定結果「3体の霊が…」', 'https://mdpr.jp/gal/1641962', '藤田ニコル、りゅうちぇるが苦手だった過去「一緒は無理」共演NGの危機も？', 'https://mdpr.jp/news/1641921', 'りゅうちぇる、ブレイクで“ぺこへの思い”変化　岡田結実は思わず涙', 'https://mdpr.jp/news/1641782', 'ぺこ＆りゅうちぇる、“2カ月ぶり1日オフ”でラブラブデート「めっちゃ幸せ」報告に反響続々「泣けてきた」「世界一のカップル」', 0, 0, 1, 1, '2016-12-07 16:34:06', '2016-12-07 16:34:06'),
    (34, '4_1_r_20161207_1481096045976418988', 4, 1, 1, 1, 1, 'http://www.oricon.co.jp/news/2082665/', '9e672dd72b280201e356ebe8ba119e6b_1481096045', '欅坂46、ライバル質問に謙そん　初出場の紅白に不安も「21人で見せる！」', '', '<img src=\"http://news.oricon.co.jp/feed/images/size640wh/20161207_122614_size640wh_1849.jpg\" title=\"『Yahoo!検索大賞2016』でパーソンカテゴリーのアイドル部門を受賞した欅坂46（左から）長濱ねる、守屋茜、平手友梨奈、渡辺梨加、土生瑞穂 （C）ORICON NewS inc.\" alt=\"『Yahoo!検索大賞2016』でパーソンカテゴリーのアイドル部門を受賞した欅坂46（左から）長濱ねる、守屋茜、平手友梨奈、渡辺梨加、土生瑞穂 （C）ORICON NewS inc.\" ><br />\n　アイドルグループ・欅坂46が、2016年に「Yahoo!」での検索数が前年に比べ最も上昇した人物・作品・製品を表彰するアワード『Yahoo!検索大賞2016』の「アイドル部門」に選出され7日、都内で行われた授賞式に出席。同グループ最年少15歳でセンターを務める平手友梨奈は「まだ新人なんですけど、たくさんの方々に育てていただけて本当にうれしいです」と喜びをかみしめた。<br />\n<br />\n　授賞式には平手のほかに、土生瑞穂（19）、守屋茜（19）、渡辺梨加（21）、長濱ねる（18）が出席。特別ゲストMCを務めた土田晃之は、レギュラー番組『欅って、書けない？』（テレビ東京）でメンバーたちと共演していることから「デビューが、今年の4月6日ですもんね。それでここまで…いやぁー本当に良かったよ」と短期間での成長ぶりに目を細めた。<br />\n<br />\n　グループ名とともに検索されているワードとして「歌詞」が上位となった点について、長濱は「私たちは歌詞をすごく大切にしたいと思っているので、すごくうれしいです」としみじみ。土生が「欅坂ってこんなにも調べていただけるなんて、本当に信じられないです」と声を弾ませると、守屋も「けっこう個性豊かなメンバーが多いので、もっと知っていただけたら」と期待を寄せた。<br />\n<br />\n　大みそかの『第67回NHK紅白歌合戦』に初出場を果たすが、平手は「思うことはたくさんあって、まだ早いんじゃないかなと思ったり、本当に出させていただけるのはありがたいので、精一杯最高のパフォーマンスができるように頑張ります」と意気込み。「実感が湧いてきてなくて、不安な気持ちはあります。でも、21人で見せるっていうのが欅坂（の特徴）ではあるので、メンバーでも練習したいです」と目を輝かせた。<br />\n<br />\n　ライバルとなる存在については「まだ私たちは新人なので、ライバルって言っていただけるくらいまで、まずは登っていきたいと思います」と謙そん。今年を振り返る場面では、渡辺が「…来年も頑張ります」と緊張した表情を浮かべると、同席したりゅうちぇる（21）が「もう、ほかの人にいろいろ言われちゃったよねー」と優しくフォローしていた。<br />\n<br />\n　各部門の受賞は以下の通り。<br />\n<br />\n＜カルチャーカテゴリー＞<br />\n■アニメ部門：おそ松さん<br />\n■映画部門：君の名は。<br />\n■ゲーム部門：ポケモンGO<br />\n■小説部門：君の膵臓をたべたい<br />\n■ドラマ部門：真田丸<br />\n■流行語部門：PPAP<br />\n■スペシャル部門：卓球<br />\n<br />\n＜パーソンカテゴリー＞<br />\n■大賞・俳優部門（ダブル受賞）：ディーン・フジオカ<br />\n■アスリート部門：福原愛<br />\n■アイドル部門：欅坂46<br />\n■お笑い芸人部門：カズレーザー<br />\n■作家部門：村田沙耶香<br />\n■声優部門：中村悠一<br />\n■モデル部門：りゅうちぇる<br />\n■ミュージシャン部門：藤原さくら<br />\n■女優部門：高畑充希<br />\n<br />\n＜プロダクトカテゴリー＞<br />\n■飲料部門：キリン　メッツ<br />\n■お取り寄せ部門：格之進　メンチカツ<br />\n■家電部門：PlayStationVR<br />\n■クルマ部門：エスティマ<br />\n■コスメ部門：エスティローダー　婚活リップ<br />\n■食品部門：スーパー大麦グラノーラ<br />\n■スイーツ部門：幸せのパンケーキ<br />\n<br /><br />\n<iframe width=\"640\" height=\"360\" src=\"https://www.youtube.com/embed/bT_k3MIMCt8\" frameborder=\"0\" allowfullscreen></iframe>', 1, '2016-12-07 12:55:03', '2017-02-05 12:55:03', NULL, NULL, NULL, 0, 'http://www.oricon.co.jp/news/2079718/photo/2/?anc=148', '【写真】指原メロメロ　欅坂46平手友梨奈が男装', 'http://www.oricon.co.jp/news/2077219/photo/1/?anc=148', '【写真】欅坂46・平手友梨奈、最新グラビアは「15歳の夏休み」', 'http://www.oricon.co.jp/news/2081595/?anc=148', '欅坂46、新曲「二人セゾン」MV公開　光の中で美しく舞い踊る （16年11月17日）', '', '', '', '', 0, 0, 1, 1, '2016-12-07 16:34:06', '2016-12-08 17:25:15'),
    (35, '4_1_r_20161207_1481096046185182283', 4, 1, 1, 1, 1, 'https://mdpr.jp/news/1643727', '4ac5a0745d15158aa913dee9482d76e9_1481096046', '瑛茉ジャスミン、驚きの食生活明かす　グルメ論でアンジャッシュ渡部に対抗も', '', '【瑛茉ジャスミン／モデルプレス＝12月7日】モデルの瑛茉ジャスミンが、6日放送の日本テレビ系バラエティ番組「今夜くらべてみました」（毎週火曜23：59～）にゲスト出演。芸能界屈指の“グルメ通”として知られるお笑いコンビ・アンジャッシュの渡部建に対抗する一幕があった。<br/><br/><img src=\"https://mdpr.jp/photo/images/2016/03/29/200_b967384205b244e25782549ecd46d33b971fb562264d82fc.jpg\" alt=\"アンジャッシュ渡部に対抗した瑛茉ジャスミン（C）モデルプレス\" /><br/><div class=\"caption\">（写真 : アンジャッシュ渡部に対抗した瑛茉ジャスミン（C）モデルプレス）</div><br/><strong>◆“肉食”瑛茉ジャスミンの驚きの食生活</strong>\n<br/>\n<br/>「買い物のクセが強過ぎる女たち」をテーマに、ホラン千秋、釈由美子とともに登場した瑛茉は、トータル2kgの食肉を2日続けて購入してしまう“カートパンパン女”として紹介された。スレンダーな体型だが美味しいものには目がないようで、その食肉は一人暮らしにもかかわらず「1～2日でなくなります」と驚きの発言。\n<br/>\n<br/>肉食な代わりにお米はあまり食べないことが体型を保っている理由と明かしながら、「自炊しているのに食費は1日4000円」という事実も。また、現地に行って食材を調達するほど食にこだわりを持っているようで「仙鳳趾産の牡蠣が好きだから北海道に行く。シラスが食べたいから徳島に行く」と食べたいものに合わせて、都度現地へ向かっていることも語られた。\n<br/>\n<br/><strong>◆グルメ通・渡部建に対抗「真似しないほうがいい」</strong>\n<br/>\n<br/>そんな瑛茉のこだわりっぷりに、MCのSHELLYは「言ってることが渡部さん」と渡部を連想。フットボールアワーの後藤輝基が「渡部さんともちょっとアプローチが違うんちゃう？」と問うと、瑛茉は「渡部さんは、食べログでいったら4.0以上のところにいって美味しい、って言うタイプの人間。みんなが絶対美味しいよねっていうところに行く人」と持論を展開。\n<br/>\n<br/>さらに「瑛茉は、サラリーマンが食べに行ってる焼き鳥屋さんとか、食べログが2.8であろうと行ってみて『このお店のレバーだけは美味しい』、『このお店のささみが美味しい』って、お店にハシゴする」と食べ物で分けてお店に行くという渡部との違いを語ると、後藤から「ってことは、渡部さんのやり方は間違ってる？」と質問。食べログ公認のグルメリポーターとしても活動している瑛茉は「まぁ、真似はしないほうがいいかな」とグルメ論で激闘し、スタジオを沸かせた。\n<br/>\n<br/>これに対し、オンエア見ていた渡部は自身のTwitterにて「お言葉ですが、今回の本は食べログ4.0以下の店しか載せてませんよ。それどころか食べログ非掲載店も多数です」と著書「渡部流 いい店の見つけ方教えます｡」を上げながら、反論。瑛茉も「え！そうなんだー！読んでみるね」と返していた。（modelpress編集部）', 1, '2016-12-07 13:23:02', '2017-02-05 13:23:02', NULL, NULL, NULL, 0, 'https://mdpr.jp/news/1643727', '他の写真・記事詳細をもっと見る', 'https://mdpr.jp/news/1639901', '瑛茉ジャスミン、ビキニで圧巻美バスト＆ボディ披露', 'https://mdpr.jp/news/1611671', '瑛茉ジャスミン、色っぽキュートな魅力放出 肌見せコーデに歓声＜TGC2016 A／W＞', 'https://mdpr.jp/news/1611009', '“元てれび戦士”瑛茉ジャスミン「メンタルが小4でやられた」子役時代の苦悩明かす', 'https://mdpr.jp/news/1601712', '鈴木奈々・なつぅみらに続く個性発掘オーディション、即戦力な一部ファイナリスト発表', 0, 0, 1, 1, '2016-12-07 16:34:06', '2016-12-07 16:34:06'),
    (36, '4_1_r_20161207_1481096046239785799', 4, 1, 1, 1, 1, 'http://www.oricon.co.jp/news/2082668/', 'b68da86515bd491491e13091002e3bc1_1481096046', 'りゅうちぇる、心霊写真の正体は“もののけ”「勘違いして寄ってきた」', '', '<img src=\"http://news.oricon.co.jp/feed/images/size640wh/20161207_124201_size640wh_7608.jpg\" title=\"『Yahoo!検索大賞2016』でパーソンカテゴリーのモデル部門を受賞したりゅうちぇる （C）ORICON NewS inc.\" alt=\"『Yahoo!検索大賞2016』でパーソンカテゴリーのモデル部門を受賞したりゅうちぇる （C）ORICON NewS inc.\" ><br />\n　タレントのりゅうちぇる（21）が、2016年に「Yahoo!」での検索数が前年に比べ最も上昇した人物・作品・製品を表彰するアワード『Yahoo!検索大賞2016』の「モデル部門」に選出され7日、都内で行われた授賞式に出席。ネットを中心に話題となっている“心霊写真”の正体について「幽霊は木のコブだったんだけど、それ以外に『もののけ』っていう森の精霊が3体映っているみたい」と明かした。<br />\n<br />\n　今月2日放送のAbemaTVのバラエティー番組『りゅうちぇる×ちゃんねる』（毎週金曜　後10：00）で、霊能力者「花染（かぜん）」の弥生氏から「（3体は）霊的に言うと物の怪（もののけ）、つまり森の精霊に分類されます。りゅうちぇるさんの魂がきれいで輝いているので、精霊の仲間だと勘違いして、集まってきたのだろう」との診断結果を受けていたりゅうちぇる。<br />\n<br />\n　診断結果を聞いた当時を振り返り「何か、勘違いして寄ってきちゃったみたい。でも3体もいたなんて、めっちゃビックリ！　ネタになるからいいけど」と笑い飛ばしながら「僕が憧れている志茂田景樹さんも、ちょっとしたもののけですから、そういう部分はマネていきたいなと思います。僕もなろうと思えば、なれるんだなと思いました」と前向きに語った。<br />\n<br />\n　今年は、バラエティー番組を中心に大活躍だったが「カメラさんのズームとか、スタッフさんの求めている感じとかが少しずつわかってきちゃって。どうしようーやっぱり、初心がいいよね」としみじみ。それでも、この日のファッションについて「アフリカの貴族のエアロビボーイです」と独特な表現をして、同席した欅坂46の失笑を買うと「何でー笑いくらいできるでしょ！」と注意して、バラエティーの洗礼をしっかり浴びせていた。<br />\n<br />\n　各部門の受賞は以下の通り。<br />\n<br />\n＜カルチャーカテゴリー＞<br />\n■アニメ部門：おそ松さん<br />\n■映画部門：君の名は。<br />\n■ゲーム部門：ポケモンGO<br />\n■小説部門：君の膵臓をたべたい<br />\n■ドラマ部門：真田丸<br />\n■流行語部門：PPAP<br />\n■スペシャル部門：卓球<br />\n<br />\n＜パーソンカテゴリー＞<br />\n■大賞・俳優部門（ダブル受賞）：ディーン・フジオカ<br />\n■アスリート部門：福原愛<br />\n■アイドル部門：欅坂46<br />\n■お笑い芸人部門：カズレーザー<br />\n■作家部門：村田沙耶香<br />\n■声優部門：中村悠一<br />\n■モデル部門：りゅうちぇる<br />\n■ミュージシャン部門：藤原さくら<br />\n■女優部門：高畑充希<br />\n<br />\n＜プロダクトカテゴリー＞<br />\n■飲料部門：キリン　メッツ<br />\n■お取り寄せ部門：格之進　メンチカツ<br />\n■家電部門：PlayStationVR<br />\n■クルマ部門：エスティマ<br />\n■コスメ部門：エスティローダー　婚活リップ<br />\n■食品部門：スーパー大麦グラノーラ<br />\n■スイーツ部門：幸せのパンケーキ<br />\n<br /><br />\n<iframe width=\"640\" height=\"360\" src=\"https://www.youtube.com/embed/bT_k3MIMCt8\" frameborder=\"0\" allowfullscreen></iframe>', 1, '2016-12-07 12:45:01', '2017-02-05 12:45:01', NULL, NULL, NULL, 0, 'http://www.oricon.co.jp/news/2082652/photo/2/?anc=148', '【写真】藤原さくら、欅坂46も登場', 'http://www.oricon.co.jp/news/2079336/photo/1/?anc=148', '【写真】ぺこ、半年で10キロ減量　りゅうちぇるもメロメロ', 'http://www.oricon.co.jp/news/2081512/photo/2/?anc=148', '【写真】キュートな猫耳をつけて登場したぺこ&りゅうちぇる', '', '', '', '', 0, 0, 1, 1, '2016-12-07 16:34:06', '2016-12-08 17:25:16'),
    (37, '4_1_r_20161207_1481096046296249027', 4, 1, 1, 1, 1, 'https://mdpr.jp/news/1643744', 'ba90f9c3969679f522ceedbc6c00af2e_1481096046', '忽那汐里、「ますます美人になっていく」と話題', '', '【忽那汐里／モデルプレス＝12月7日】女優の忽那汐里（23）の“美人化が止まらない”と話題だ。<br/><br/><img src=\"https://mdpr.jp/photo/images/2016/12/07/200_5ede83ed4fe9f794f5c50f0b5432fd8896a53dae2e725620.jpg\" alt=\"忽那汐里（左2016年7月撮影・右2016年12月撮影） （C）モデルプレス\" /><br/><div class=\"caption\">（写真 : 忽那汐里（左2016年7月撮影・右2016年12月撮影） （C）モデルプレス）</div><br/><strong>◆今年の映画イベントでは…</strong>\n<br/>\n<br/>今年は、2月に公開された映画「女が眠る時」でビートたけし演じる主人公を翻弄するという新境地の役柄を演じ、来年1月には菅田将暉の恋人役を演じる映画「キセキ - あの日のソビト -」が公開される忽那。\n<br/>\n<br/>7月に公開され声優を務めた映画「KINGSGLAIVE FINAL FANTASY XV」のイベントでは前髪有りの大人っぽいショートボブスタイルを披露し、観客を魅了した。\n<br/>\n<br/>また、6日に都内で行われた「キセキ」の完成披露試写会では、小顔が強調されたラフなお団子スタイルで登場したが、美人に磨きがかかった姿を披露していた。\n<br/>\n<br/><strong>◆忽那汐里の美人化が止まらない…</strong>\n<br/>\n<br/>2007年に放送されたTBS系のドラマ「3年B組金八先生」の生徒役などで注目集め、2008年からポッキーのイメージキャラクターとしてCMでキュートなダンスを披露した忽那。\n<br/>\n<br/>14歳で女優デビューし、ワンレンのロングヘアが特徴でありながらもあどけなくかわいいイメージだったが、今年は露出するごとにネット上では「かわいかったしおりちゃんが美人になった！」「ますます美人になった」と話題となっていた。\n<br/>\n<br/>今月22日に24歳になるが、女優として大人の魅力を感じさせる演技にも期待がかかる。（modelpress編集部）', 1, '2016-12-07 13:42:05', '2017-02-05 13:42:05', NULL, NULL, NULL, 0, 'https://mdpr.jp/news/1643744', '他の写真・記事詳細をもっと見る', 'https://mdpr.jp/news/1639316', '河北麻友子＆忽那汐里、笑顔全開2ショットに「オーラ溢れてる」「美人すぎ」の声', 'https://mdpr.jp/cinema/1596189', '綾野剛は「すごい汗だくになっていた」忽那汐里が熱演ぶりを明かす', 'https://mdpr.jp/cinema/1594801', '忽那汐里、菅田将暉の運命の恋人に　GReeeeN映画「キセキ」平祐奈らキャスト続々決定', 'https://mdpr.jp/news/1591246', '忽那汐里、ネット上の噂に嘆き節', 0, 0, 1, 1, '2016-12-07 16:34:06', '2016-12-07 16:34:06'),
    (38, '4_1_r_20161207_1481096046436952218', 4, 1, 1, 1, 1, 'http://www.oricon.co.jp/news/2082652/', '919eea87c5e32fa496629691bf39c4a0_1481096046', 'ディーン・フジオカ“今年の顔”選出に喜び　2016年は「新しいスタート切れた」', '', '<img src=\"http://news.oricon.co.jp/feed/images/size640wh/20161207_115251_size640wh_1643.jpg\" title=\"“今年の顔”に選出されたディーン・フジオカ （C）ORICON NewS inc.\" alt=\"“今年の顔”に選出されたディーン・フジオカ （C）ORICON NewS inc.\" ><br />\n　2016年に「Yahoo!」での検索数が前年に比べ最も上昇した人物・作品・製品を表彰するアワード『Yahoo!検索大賞2016』の発表会が7日、都内で開催され、各部門の受賞者が出席。“今年の顔”となる「大賞」には、俳優で歌手のディーン・フジオカが選出された。<br />\n<br />\n　同アワードでは、「大賞」をはじめとして「パーソンカテゴリー（9部門）」「カルチャーカテゴリー（7部門）」「プロダクトカテゴリー（7部門）」の受賞者を発表。「大賞」と「俳優部門」でのダブル受賞を果たしたディーンは、ビデオメッセージで「自分でも思いもよらぬ、こんなすばらしい賞をいただけて本当にうれしく思います」と喜びをかみしめた。<br />\n<br />\n　『あさが来た』や『IQ246〜華麗なる事件簿〜』など、ドラマを中心に大活躍だった今年を振り返り「僕は日本ではないところで、長いこと生活をしたり仕事をしてきたので、自分の生まれた国で興味を持っていただいたことに感謝です」としみじみ。「あっという間に今年も12月になって、今年は本当に駆け抜けたという感じですね。新しいスタートを切れたんじゃないかなと思うので、これからもリミットを作らず幅広く頑張っていきたい」とさらなる飛躍を誓っていた。<br />\n<br />\n　発表会には「ミュージシャン部門」の藤原さくら、「モデル部門」のりゅうちぇる、「アイドル部門」の欅坂46、特別ゲストMCの土田晃之が登場。また「お笑い芸人部門」のメイプル超合金のカズレーザー、「女優部門」の高畑充希、「アスリート部門」の福原愛らはVTRや手紙などで喜びのメッセージを寄せた。<br />\n<br />\n　各部門の受賞は以下の通り。<br />\n<br />\n＜カルチャーカテゴリー＞<br />\n■アニメ部門：おそ松さん<br />\n■映画部門：君の名は。<br />\n■ゲーム部門：ポケモンGO<br />\n■小説部門：君の膵臓をたべたい<br />\n■ドラマ部門：真田丸<br />\n■流行語部門：PPAP<br />\n■スペシャル部門：卓球<br />\n<br />\n＜パーソンカテゴリー＞<br />\n■大賞・俳優部門（ダブル受賞）：ディーン・フジオカ<br />\n■アスリート部門：福原愛<br />\n■アイドル部門：欅坂46<br />\n■お笑い芸人部門：カズレーザー<br />\n■作家部門：村田沙耶香<br />\n■声優部門：中村悠一<br />\n■モデル部門：りゅうちぇる<br />\n■ミュージシャン部門：藤原さくら<br />\n■女優部門：高畑充希<br />\n<br />\n＜プロダクトカテゴリー＞<br />\n■飲料部門：キリン　メッツ<br />\n■お取り寄せ部門：格之進　メンチカツ<br />\n■家電部門：PlayStationVR<br />\n■クルマ部門：エスティマ<br />\n■コスメ部門：エスティローダー　婚活リップ<br />\n■食品部門：スーパー大麦グラノーラ<br />\n■スイーツ部門：幸せのパンケーキ<br />\n<br /><br />\n<iframe width=\"640\" height=\"360\" src=\"https://www.youtube.com/embed/bT_k3MIMCt8\" frameborder=\"0\" allowfullscreen></iframe>', 1, '2016-12-07 11:55:03', '2017-02-05 11:55:03', NULL, NULL, NULL, 0, 'http://www.oricon.co.jp/news/2082652/photo/2/?anc=148', '【写真】藤原さくら、欅坂46、りゅうちぇるも登場', 'http://www.oricon.co.jp/news/2067241/photo/1/?anc=148', '【写真】五代様が話題に！波瑠＆ディーン　“涙の”共演シーン', 'http://www.oricon.co.jp/news/2081107/photo/2/?anc=148', '【写真】ディーン・フジオカ 、共演した波瑠と久しぶりの2ショット', '', '', '', '', 0, 0, 1, 1, '2016-12-07 16:34:06', '2016-12-08 17:25:15'),
    (39, '4_1_r_20161207_1481096046561160746', 4, 1, 1, 1, 1, 'https://mdpr.jp/news/1643674', 'f0d0d40d6fda09077a69b65874e760a0_1481096046', '欅坂46の“急上昇”に土田晃之驚き 紅白初出場に平手友梨奈が本音', '', '【欅坂46／モデルプレス＝12月7日】欅坂46の平手友梨奈、守屋茜、土生瑞穂、渡辺梨加、長濱ねるが7日、都内にて行われた受賞式に出席した。<br/><br/><img src=\"https://mdpr.jp/photo/images/2016/12/07/200_18815f0b160f7c3d463611c8545391975036e71baabfe9fa.jpg\" alt=\"欅坂46の平手友梨奈 （C）モデルプレス\" /><br/><div class=\"caption\">（写真 : 欅坂46の平手友梨奈 （C）モデルプレス）</div><br/><strong>◆“今年の顔”として表彰</strong>\n<br/>\n<br/>今年4月6日に『サイレントマジョリティー』で鮮烈デビューをした欅坂46は、デビュー年で「第67回 NHK紅白歌合戦」初出場が決定。2016年に最も検索数が急上昇した“今年の顔”として表彰された。\n<br/>\n<br/>この日、特別ゲストMCを冠番組『欅って、書けない？』（テレビ東京／毎週日曜 深夜0：35）で共演中のお笑いタレントの土田晃之が務め、デビュー前からメンバーを見守ってきた土田は「今年の4月6日ですよね、それでね、こんなに（成長して）、ほんとに良かったよ…」とすっかり欅坂側の立場でしみじみとコメント。\n<br/>\n<br/>また、「今年1年を一言で表すと？」という質問を振られた渡辺は、「一瞬でした」と回答。普段はおっとりしたキャラクターの渡辺の素早い反応に土田は「意外に良いこと言うね」と感心した。<br/><br/><strong>◆平手友梨奈のコメントにりゅうちぇるも刺激</strong>\n<br/>\n<br/>また、紅白初出場決定について心境を問われた平手は「やっぱり思うことはたくさんあってまだ早いんじゃないかと思ったり…」と本音を吐露。「出させて頂けることが有り難いので、良いパフォーマンスをできるように頑張りたい」と意気込み、土田も「あの1大イベントにこんなに早く出れるとはね、こっちも思わなかったんでね、是非頑張ってもらいたいです」と驚きを口にした。\n<br/>\n<br/>受賞式後の囲み取材でも、異例のスピードでの紅白出場に関して質問が飛び、「かなり奇跡的というか珍しいことだと思いますがどう受け止めていますか？」と問われた平手は、「めっちゃ嬉しいというよりかはまだ早いんじゃないかっていう気持ちとか不安が大きい」と再びコメント。これにともに取材を受けたタレントのりゅうちぇるは「まじめー！！」と驚き、「謙虚な感じが素敵だなって思って！僕も見習います！」と刺激を受けていた。\n<br/>\n<br/>受賞式には、欅坂46のほか、りゅうちぇる、歌手で女優の藤原さくらが出席。お笑いタレントのピコ太郎、カズレーザー（メイプル超合金）、卓球選手の福原愛、女優の高畑充希、俳優のディーン・フジオカ、作家の村田紗耶香氏、映画監督の新海誠氏がビデオメッセージを寄せた。（modelpress編集部）', 1, '2016-12-07 16:20:01', '2017-02-05 16:20:01', NULL, NULL, NULL, 0, 'https://mdpr.jp/news/1643674', '他の写真・記事詳細をもっと見る', 'https://mdpr.jp/music/1638984', '欅坂46、紅白初出場決定 4月に「サイマジョ」で鮮烈デビュー…今年の活躍を振り返る', 'https://mdpr.jp/music/1638997', '欅坂46、デビュー1年で「紅白歌合戦」出場に驚き', 'https://mdpr.jp/news/1637880', '欅坂46の“流行語”気になるメンバー間の会話とは…「結果を出せる女性に必要な条件」にも回答', 'https://mdpr.jp/music/1637830', '欅坂46平手友梨奈、紅白歌合戦に言及　鮮烈デビューの一年も振り返る', 0, 0, 1, 1, '2016-12-07 16:34:07', '2016-12-07 16:34:07'),
    (40, '4_1_r_20161207_1481096046656559334', 4, 1, 1, 1, 1, 'http://www.oricon.co.jp/news/2082621/', 'ad4b4c275cc35d19be5e94b052b65171_1481096046', '小雪、フジ連ドラ初主演でシングルマザー役に挑戦', '', '<img src=\"http://news.oricon.co.jp/feed/images/size640wh/20161207_113429_size640wh_1515.jpg\" title=\"フジテレビ系ドラマ『大貧民』に出演がきまった凸凹コンビ（左から）小雪、伊藤淳史 （C）フジテレビ\" alt=\"フジテレビ系ドラマ『大貧民』に出演がきまった凸凹コンビ（左から）小雪、伊藤淳史 （C）フジテレビ\" ><br />\n　女優の小雪が、来年1月8日スタートのフジテレビ系連続ドラマ『大貧乏』（毎週日曜　後9：00）で同局連ドラに初主演することがわかった。権力者の欲により“大貧乏”に追い込まれたシングルマザー・七草ゆず子（小雪）が、理不尽な社会に立ち向かう“お金”と“愛”をテーマにした完全オリジナルのヒューマン・コメディー。彼女の相棒となるエリート弁護士・柿原新一には俳優・伊藤淳史が起用された。<br />\n<br />\n　主人公・ゆず子は、夫に裏切られ女手一つで暴れん坊な息子とおてんば盛りの娘を抱え、慌ただしい日々を送っていた。ある日、勤めていた会社が突然倒産し、給与も貯金も全てを失い途方に暮れていた彼女の前に、年商107億円を稼ぐ敏腕弁護士の柿原から連絡がくる。実は柿原はゆず子の高校の同級生で、当時からゆず子に憧れていたのだ。「会社の倒産には裏がある」と助言する柿原に当初は戸惑うゆず子だったが、愛しい子供たちとの生活を守るため、巨悪を暴いていくことに…。<br />\n<br />\n　小雪は実際にシングルマザーの知り合いにインタビューするなど、役作りのため入念にリサーチしているという。「シングルマザーが抱える問題を社会派ドラマとして描いていくのではなく、そういう設定をベースとしながら、ひとりの女性の生き方や在り方を前向きに描く作品なので、ひとりの女性の人生に必ずある本音やリアリティーをちゃんと盛り込みつつ、ファミリードラマやコメディーとしてどう表現していけるか、という部分を探っています」と試行錯誤している様子。<br />\n<br />\n　プライベートでも母親であるが「私も母として子どもたちと接する中で、子供が変化してく姿や、大人が想像する以上のことを言ったり、思いもかけない崇高なことをしたりするのを目の当たりにして、日々新たな発見のもと勉強させられています」と実感を込め、「そんな子供たちのきらめきみたいなものもこの作品の中で描けたら」と展望している。<br />\n<br />\n　また、見た目も中身もチグハグな2人が凸凹コンビとして悪をくじく爽快感が期待されるが、伊藤とのタッグには「全くかみ合っていないコンビになると思いますが、そのかみ合っていないところが面白くなると思います」と自信をみせ、伊藤も「物語の中にもドキドキするシーンがあるのですが、“小雪さんと一緒にお芝居できる”というドキドキする思いが、リアルにいい感じに僕の中にあるので、うまくミックスして出していけたら」と共演を心待ちにしている。', 1, '2016-12-07 12:08:01', '2017-02-05 12:08:01', NULL, NULL, NULL, 0, 'http://beauty.oricon.co.jp/trend-culture/trend/news/photo/88623/1/?anc=096', '【写真】小雪、背中が大きく開いたウエディングドレス姿を披露', 'http://www.oricon.co.jp/news/2082181/photo/1/?anc=096', '【写真】小雪、姉・弥生とイベント共演　姉妹でカーペットウォーキング', 'http://beauty.oricon.co.jp/trend-culture/trend/news/86870/?anc=096', '【結婚会見】松山ケンイチが小雪との生活を暴露　「尻に敷かれてます」', '', '', '', '', 0, 0, 1, 1, '2016-12-07 16:34:07', '2016-12-08 17:25:10'),
    (41, '4_1_r_20161207_1481096046844681174', 4, 1, 1, 1, 1, 'https://mdpr.jp/cinema/1643720', '7e0d92fefb1cf2c2d872c245fb9523db_1481096046', '三浦翔平か白濱亜嵐“究極の選択”に悶絶する女子続出「朝から発狂」「最高に羨ましい三角関係」', '', '【永野芽郁・三浦翔平・白濱亜嵐・ひるなかの流星／モデルプレス＝12月7日】主演に永野芽郁を迎え、共有に三浦翔平、白濱亜嵐（EXILE／GENERATIONS from EXILE TRIBE）が揃う映画「ひるなかの流星」（2017年3月24日）の特報映像が7日、解禁された。胸キュンシーンが詰め込まれた特報映像が、女子を中心に話題を集めている。<br/><br/><img src=\"https://mdpr.jp/photo/images/2016/12/06/200_760b74751b9cfc8356d7a8f5ae5fdef0864f475ffe9dcd25.jpeg\" alt=\"映画「ひるなかの流星」のポスター（C）2017 フジテレビジョン 東宝 集英社　（C）やまもり三香／集英社\" /><br/><div class=\"caption\">（写真 : 映画「ひるなかの流星」のポスター（C）2017 フジテレビジョン 東宝 集英社　（C）やまもり三香／集英社）</div><br/><strong>◆“初恋のバイブル”を映画化</strong>\n<br/>\n<br/>2011年～2014年まで集英社「マーガレット」で連載され、コミックス累計発行部数は212万部を突破、女子中高生の“初恋のバイブル”として絶大な支持を集めている「ひるなかの流星」（やまもり三香／集英社マーガレットコミックス刊）を実写化。\n<br/>\n<br/>主人公の女子高生・与謝野すずめ役を主演の永野、すずめが初めて恋に落ちる担任教師・獅子尾五月役を三浦、獅子尾に対するすずめの想いを知りながらも、徐々に彼女に惹かれていく“女子が大の苦手”なクラスメイト・馬村大輝役を白濱がそれぞれ演じる。\n<br/>\n<br/><strong>◆胸キュンシーンに女子悶絶！“究極の選択”に</strong><br/><br/>映像には、獅子尾（三浦）が、頭を撫でる頭ポン（あたぽん）、そして馬村（白濱）の告白や、手つなぎ登校など、恋心をくすぐるファン悶絶の胸キュンシーン・台詞が満載。\n<br/>\n<br/>中でも、三浦の切ない表情が印象的な突然のバックハグと、白濱の限界まで顔が接近した床ドンに、ファンから「もうヤバすぎます！朝から発狂した」「一気に目が覚めた…カッコ良すぎないか？」「予告でこの破壊力とか、本編観たら倒れるよね」と興奮の声が止まらず。\n<br/>\n<br/>ポスターには「わたしの初恋、究極の選択」というキャッチフレーズもあり、優しい獅子尾、クールな馬村に対し「どっちも選べない！どっちもかっこいいもん！！」「最高に羨ましい三角関係なんですけど…永野芽郁ちゃんになりたい」「こんな美しい2人のどっちか選ぶとか…ずっと考えてたけど無理だわwww」と自身をすずめに置き換えながら“究極の選択”に戸惑う女性が続出するなど、反響も大きいようだ。\n<br/>\n<br/><strong>◆豪華キャスト＆スタッフ揃う</strong><br/><br/>このほか映画には、すずめのクラスメイトで“女子力No.1美女”猫田ゆゆか役で山本舞香、すずめのおじであり東京での父親的存在として心のよりどころになる熊本諭吉役で佐藤隆太、すずめの母親・与謝野聡子役で西田尚美らが出演。\n<br/>\n<br/>監督は、「四月は君の嘘」「僕の初恋をキミに捧ぐ」といった青春恋愛映画を手掛けてきた新城毅彦氏。脚本は、フジテレビ月9ドラマ「リッチマン・プアウーマン」「失恋ショコラティエ」の安達奈緒子氏が手掛ける。（modelpress編集部）', 1, '2016-12-07 12:38:37', '2017-02-05 12:38:37', NULL, NULL, NULL, 0, 'https://mdpr.jp/cinema/1643720', '他の写真・記事詳細をもっと見る', 'https://mdpr.jp/cinema/1643470', '永野芽郁に三浦翔平“突然のバックハグ”＆白濱亜嵐“限界接近の床ドン”…胸キュン連発の初恋解禁', 'https://mdpr.jp/cinema/1640865', '永野芽郁、初主演映画クランクアップ　三浦翔平、白濱亜嵐、山本舞香らと完走「皆様のおかげ」＜キャストコメント＞', 'https://mdpr.jp/news/1636628', '倉科カナ＆三浦翔平の“ドロキュン”恋愛劇 「放送できないレベル」に期待の声', 'https://mdpr.jp/news/1636037', '倉科カナ＆三浦翔平、初共演で“ドロキュン”恋愛劇「ラブシーンもあります」', 0, 0, 1, 1, '2016-12-07 16:34:07', '2016-12-07 16:34:07'),
    (42, '4_1_r_20161207_1481096046951317624', 4, 1, 1, 1, 1, 'http://www.oricon.co.jp/news/2082663/', '86e957f0f034bf11eaa351c4f25e554e_1481096046', 'JYが来年5月に初ソロツアー「本当にうれしい」', '', '<img src=\"http://news.oricon.co.jp/feed/images/size640wh/20161207_115704_size640wh_3971.jpg\" title=\"来年5月に初のソロツアーを行うJY（知英）\" alt=\"来年5月に初のソロツアーを行うJY（知英）\" ><br />\n　女優の知英（ジヨン）が、アーティスト名義「JY」として来年5月に初のライブツアーを行うことが明らかになった。きょう7日に同時発売した3rdシングル「フェイク」と4thシングル「恋をしていたこと」に封入されているフライヤーで発表された。<br />\n<br />\n　JYは「こんなに早くソロツアーができるとは思っていませんでしたが、シングル2枚同時発売の記念日に初ツアーのお知らせができて本当にうれしいです！　まだ先ですが、これからツアーに向けて準備していくので、すごくワクワクしています。とても楽しみです」とコメントしている。<br />\n<br />\n　チケット発売日など詳細は、公式サイトやSNSで随時発表される。また、きょう7日午後8時からLINE LIVEで生配信特番を、あす8日には東京・shibuya duo MUSIC EXCHANGEでライブイベントを行う。<br />\n<br />\n■1stライブツアー<br />\n2017年5月13日（土）：愛知・Zepp Nagoya<br />\n2017年5月14日（日）：大阪・Zepp Namba<br />\n2017年5月21日（日）：東京・Zepp DiverCity', 1, '2016-12-07 12:08:05', '2017-02-05 12:08:05', NULL, NULL, NULL, 0, 'http://music.oricon.co.jp/php/artist/ArtistTop.php?artist=A933246&ref_cd=MS0098&anc=015', '【試聴】JYの主な配信楽曲', 'http://www.oricon.co.jp/news/2080416/?anc=015', 'JY、12・7シングル2作同時発売　本当に同一人物？ （16年10月24日）', 'http://www.oricon.co.jp/news/2080657/?anc=015', '知英、ドラマ初主演で関西弁に挑戦「すごくワクワク」 （16年10月29日）', '', '', '', '', 0, 0, 1, 1, '2016-12-07 16:34:07', '2016-12-07 16:34:07'),
    (43, '4_1_r_20161207_1481096047168150264', 4, 1, 1, 1, 1, 'https://mdpr.jp/news/1643703', 'e5590baae6fbb4233aa60cd9b6823266_1481096047', '石原さとみ、15年前は“石原サトイモ”？', '', '【石原さとみ／モデルプレス＝12月7日】女優の石原さとみが7日放送の日本テレビ系「PON！」（毎週月～木曜、あさ10時25分）にゲスト出演。きょう最終回を迎える主演ドラマ「地味にスゴイ！校閲ガール・河野悦子」（毎週水曜よる10時～）での撮影秘話を明かした。<br/><br/><img src=\"https://mdpr.jp/photo/images/2016/09/19/200_25c02863d72eb67a8bae9df33d1d4a965345766d17a35f39.jpg\" alt=\"石原さとみ、15年前は“石原サトイモ”？（C）モデルプレス\" /><br/><div class=\"caption\">（写真 : 石原さとみ、15年前は“石原サトイモ”？（C）モデルプレス）</div><br/><strong>◆岸谷五朗、石原さとみと15年ぶり共演</strong>\n<br/>\n<br/>番組では、岸谷五朗、菅田将暉、和田正人、青木崇高ら「地味スゴ」男性キャストからのコメントを紹介。15年前にNHK朝の連続テレビ小説「てるてる家族」で親子役を演じて以来の共演となった岸谷は、当時まだ10代だった石原を「石原サトイモって感じだった」とユーモアたっぷりに回顧。「女優としてのテクニック、表現の幅、美しさ、全て輝いていた」と石原の成長ぶりを絶賛した。\n<br/>\n<br/>VTRを笑顔で見ていた石原は、今回、岸谷さんとのクランクインシーンが長台詞だったことを明かし、「15年ぶりの岸谷さんに完璧な姿を見せようと思って、それだけのために完璧にセリフ入れていきました」と並々ならぬ覚悟で挑んでいたことを告白。\n<br/>\n<br/>クランクアップ時には、岸谷から「頑張ったね」と声をかけられ涙を流してしまったといい「本当に泣くつもりなかったんですけど、すごい涙がこみあげてきちゃって」と思い入れを明かした。<br/><br/><strong>◆「地味スゴ」ファッションを全部見せ</strong>\n<br/>\n<br/>石原演じる河野悦子の華やかなファッションとヘアメイクでも注目を集めている同作。番組では、石原が着用した全80体のコーディネートが紹介された。\n<br/>\n<br/>スタイリング、そして髪型にも意見が反映されているといい、メイクは自分自身で行っていたことを明かし、出演者を驚かせた。（modelpress編集部）', 1, '2016-12-07 12:14:42', '2017-02-05 12:14:42', NULL, NULL, NULL, 0, 'https://mdpr.jp/news/1643703', '他の写真・記事詳細をもっと見る', 'https://mdpr.jp/news/1643087', '石原さとみ「地味スゴ」涙のクランクアップ「20代最後の作品がこれで良かった」', 'https://mdpr.jp/cinema/1636431', '「君の名は。」「シン・ゴジラ」どれくらい流行った？＜流行語大賞候補＞', 'https://mdpr.jp/news/1635953', '石原さとみは「顔小さくて可愛かった」　「地味スゴ」第7話ゲスト発表', 'https://mdpr.jp/news/1633574', '菅田将暉「大好きだよ」“直球告白”にファン興奮「破壊力やばい」「フリーズしそう」', 0, 0, 1, 1, '2016-12-07 16:34:07', '2016-12-07 16:34:07'),
    (44, '4_1_r_20161207_1481096047168154712', 4, 1, 1, 1, 1, 'http://www.oricon.co.jp/news/2082662/', '2a8fc081f50a62a68e68d24b53fee0c4_1481096047', 'ライブハウスeggman 35周年感謝祭を武道館で　NOKKO・マンウィズ・miwaら集結', '', '<img src=\"http://news.oricon.co.jp/feed/images/size640wh/20161207_114035_size640wh_3332.jpg\" title=\"日本武道館で行われる「eggman 35周年大感謝祭」（イラスト＝浦沢直樹氏）\" alt=\"日本武道館で行われる「eggman 35周年大感謝祭」（イラスト＝浦沢直樹氏）\" ><br />\n　東京・渋谷の老舗ライブハウス「shibuya eggman」が、35周年記念イヤーの締めくくりとして、来年2月19日に東京・日本武道館で『LIVE EGG BUDOKAN ～shibuya eggman 35周年大感謝祭～』を開催することがわかった。ライブハウス主催としては最大規模のライブにNOKKO、MAN WITH A MISSION、miwaらが出演することも発表された。<br />\n<br />\n　eggmanは1981年3月11日にオープン。初年度に桑田佳祐率いる桑田バンド、井上陽水が出演したのをはじめ、久保田利伸、レベッカ、プリンセス プリンセス、X JAPAN、福山雅治ら名だたるアーティストがステージに立ち、“新人の登竜門”とも言われてきた。35周年を迎えた今年は、さまざまなアニバーサリー企画が展開されてきたが、そのフィナーレ公演として日本武道館ライブを行うことが決定した。<br />\n<br />\n　出演するのは、オープン当初から出演を重ねているレベッカのボーカル・NOKKO、2010年に同所でデビューライブを開催し、その後も節目のタイミングで“凱旋”しているMAN WITH A MISSIONとmiwaをはじめ、Czecho No Republic、SUPER BEAVER、sumikaらが名を連ねる。<br />\n<br />\n　また、本公演のメインビジュアルは、『20世紀少年』『YAWARA!』などの作者として知られ、ミュージシャンとしてeggmanでライブ経験がある浦沢直樹氏が描き下ろした35周年キャラクターのイラストを採用。卵の殻をかぶったeggmanオリジナルキャラクターが記念ライブを彩る。<br />\n<br />\n　店長は「渋谷から文化を発信し続ける小さなライブハウスの大きな挑戦。歴代を彩ったアーティストの原点を感じさせる音や物語を、日本武道館公演でぜひとも胸に焼き付けてほしい！」としている。<br />\n<br />\n　チケットは各アーティストの公式サイトで今月9日から先行受付開始。一般発売は来年1月28日にスタートする。<br />\n<br />\n■『LIVE EGG BUDOKAN ～shibuya eggman 35周年 大感謝祭～』<br />\n2017年2月19日（日）：東京・日本武道館<br />\n【出演者】NOKKO／MAN WITH A MISSION／miwa／SUPER BEAVER／Czecho No Republic／sumika／shibuya eggman 35th special session band　ほか', 1, '2016-12-07 12:00:00', '2017-02-05 12:00:00', NULL, NULL, NULL, 0, 'http://www.oricon.co.jp/news/2047944/?anc=015', 'MAN WITH A MISSION、“原点”eggmanでファイナル（15年01月30日）', 'http://www.oricon.co.jp/news/87673/?anc=015', '観月ありさ、20年ぶり“デビューの地”eggmanで凱旋ライブ（11年05月15日）', 'http://www.oricon.co.jp/news/2082303/?anc=015', 'ガンズ・アンド・ローゼズ前座にベビメタ＆マンウィズ（16年12月01日）', '', '', '', '', 0, 0, 1, 1, '2016-12-07 16:34:07', '2016-12-07 16:34:07'),
    (45, '4_1_r_20161207_1481096047309676276', 4, 1, 1, 1, 1, 'https://mdpr.jp/news/1643721', '0db27967996aa970d30c77d466ba1605_1481096047', '俳優・横浜流星、“イケメンすぎる空手の世界チャンピオン“　ギャップに菅田将暉もびっくり', '', '【横浜流星／モデルプレス＝12月7日】極真空手の世界チャンピオンで、スーパー戦隊シリーズ「烈車戦隊トッキュウジャー」で知られる俳優・横浜流星について7日、俳優の菅田将暉がエピソードを披露した。<br/><br/><img src=\"https://mdpr.jp/photo/images/2016/12/07/200_041e10c8a7d7ce3f5fc92298c1c7b8634025acf94b053e49.jpg\" alt=\"横浜流星 （C）モデルプレス\" /><br/><div class=\"caption\">（写真 : 横浜流星 （C）モデルプレス）</div><br/><strong>◆菅田将暉もびっくり！強すぎる横浜流星</strong>\n<br/>\n<br/>7日、フジテレビ系の「めざましテレビ」（毎週月～金曜あさ5時25分）に横浜と菅田らが出演し、菅田が“見た目の印象と違う人”に横浜をあげた。\n<br/>\n<br/>甘いマスクの横浜だが、極真空手の初段で、中学3年生（2011年）の時に空手の世界大会「国際青少年空手道選手権大会13・14歳男子55kgの部」にて優勝。\n<br/>\n<br/>菅田は「流星はこんなにかわいい顔して空手世界一と聞いて、見てみたいから実際にミットとグローブを（横浜の元へ）持っていった」とエピソードを明かし、「流星にグローブ付けてもらって受けたら、もう怖くて…」と、横浜の空手のレベルに驚いたようだった。\n<br/>\n<br/><strong>◆放送中のドラマでは空手を活かし…</strong>\n<br/>\n<br/>現在放送中の連続ドラマ「潜入捜査アイドル・刑事ダンス」（テレビ東京系／毎週土曜深夜0：20～）ではアイドルを演じているが、“コミュ障の元引きこもりで実は空手が強い”という設定で、特技の空手を活かしている。\n<br/>\n<br/>また、TBS系の人気番組「究極の男は誰だ！？最強スポーツ男子頂上決戦」でも話題となった空手で鍛えた腹筋を同ドラマでも披露しており、「腹筋！！」「流星くんの腹筋チラ見せやばい…」と反響を得ていた。（modelpress編集部）\n<br/>\n<br/><strong>■横浜流星 プロフィール</strong>\n<br/>\n<br/>1996年9月16日生まれ。神奈川県出身。174cm、O型。2011年9月号から2015年5月号まで「nicola（ニコラ）」メンズモデルとして活躍。代表作はTVドラマ「リアル鬼ごっこ THE ORIGIN」「放課後グルーヴ」「烈車戦隊トッキュウジャー」トッキュウ4号／ヒカリ役など。舞台「武士白虎～もののふ白き虎～」、「スーパーダンガンロンパ2 THE STAGE～さよなら絶望学園～」で主演を務めた。現在連続ドラマ「潜入捜査アイドル・刑事ダンス」が放送中のほか、映画「キセキ-あの時のソビト-」（1月23日）の公開が控えている。', 1, '2016-12-07 13:35:43', '2017-02-05 13:35:43', NULL, NULL, NULL, 0, 'https://mdpr.jp/news/1643721', '他の写真・記事詳細をもっと見る', 'https://mdpr.jp/news/1641440', '“座長”横浜流星のリーダーシップは？仲間思いの優しき姿が垣間見える', 'https://mdpr.jp/news/1627073', '横浜流星、理想のデートシチュエーションを告白', 'https://mdpr.jp/cinema/1623588', '菅田将暉ら、劇中グループでのCDデビューに反響「イケメンだらけ」', 'https://mdpr.jp/cinema/1623061', '菅田将暉・横浜流星・成田凌・杉野遥亮がCDデビュー　GReeeeN名曲カバーも', 0, 0, 1, 1, '2016-12-07 16:34:07', '2016-12-07 16:34:07'),
    (46, '4_1_r_20161207_1481096047310436198', 4, 1, 1, 1, 1, 'http://www.oricon.co.jp/news/2082623/', 'bcbf181d7a68950be7521e741972f88f_1481096047', '川栄がドリカム、倉科が尾崎豊歌う特別動画が公開', '', '<img src=\"http://news.oricon.co.jp/feed/images/size640wh/20161206_162505_size640wh_9554.jpg\" title=\"女優の川栄李奈、倉科カナ、貫地谷しほり、真飛聖、小野ゆり子、堀田茜が、来年1月15日スタートのCSチャンネル「フジテレビTWOドラマ・アニメ／フジテレビTWOsmart」で放送される連続ドラマ『感情8号線』（毎週日曜 後11：30）に出演 （C）フジテレビ\" alt=\"女優の川栄李奈、倉科カナ、貫地谷しほり、真飛聖、小野ゆり子、堀田茜が、来年1月15日スタートのCSチャンネル「フジテレビTWOドラマ・アニメ／フジテレビTWOsmart」で放送される連続ドラマ『感情8号線』（毎週日曜 後11：30）に出演 （C）フジテレビ\" ><br />\n　女優の川栄李奈、倉科カナ、貫地谷しほり、真飛聖、小野ゆり子、堀田茜が主演する来年1月15日スタートのCSチャンネル「フジテレビTWOドラマ・アニメ／フジテレビTWOsmart」連続ドラマ『感情8号線』（毎週日曜　後11：30）の特別動画が解禁された。<br />\n<br />\n　同ドラマは畑野智美氏の連作小説をもとに、直線距離だと近いのに電車だと遠回りになる環状八号線沿いの街・荻窪、八幡山、千歳船橋、二子玉川、上野毛、田園調布に住む女性たちが片想いやDV、不倫などままならない恋に揺れる姿を1話ごとに主人公を変えて描く。一人ひとり置かれた境遇も違えば悩みも違うが、そんな6人が絡み合いながら展開していく。<br />\n　<br />\n　劇中では、各話ごとに主人公がそれぞれの想いを重ねながら歌を口ずさむシーンがあり、ドラマの劇中とエンディングで6人の女優陣それぞれが歌声を披露。川栄李奈は「何度でも」（DREAMS COME TRUE）、倉科カナは「僕が僕であるために」（尾崎豊）、貫地谷しほりは「幸せになるために」（松任谷由実）、真飛聖は「ひょっこりひょうたん島」、小野ゆり子「あなたに会えてよかった」（小泉今日子）、堀田茜は「ぼくらが旅に出る理由」（小沢健二）で、その歌声の一部をまとめたものが今回、放送前に先行公開されている。<br />\n<br />\n公式サイト<br />\nhttp://otn.fujitv.co.jp/kanjo8gosen/', 1, '2016-12-07 10:00:00', '2017-02-05 10:00:00', NULL, NULL, NULL, 0, 'http://www.oricon.co.jp/news/2059005/photo/4/?anc=096', '【写真】かっこいい！殺陣シーンをお披露目した川栄李奈', 'http://www.oricon.co.jp/news/2077838/photo/2/?anc=096', '【写真】堀田茜、大人ドレスで美背中披露', 'http://www.oricon.co.jp/news/2066290/?anc=096', '松山ケンイチ＆倉科カナ、マナーCMで放送禁止用語連発　実写『珍遊記』 （16年02月05日）', '', '', '', '', 0, 0, 1, 1, '2016-12-07 16:34:07', '2016-12-07 16:34:07'),
    (47, '4_1_r_20161207_1481096047473430608', 4, 1, 1, 1, 1, 'http://www.oricon.co.jp/news/2082558/', 'e05ca0688480e39446d51b3f5d83aab8_1481096047', '万が一に備えて…　おさえておきたい「預貯金の遺産分割」', '', '<img src=\"http://news.oricon.co.jp/feed/images/size640wh/20161206_190250_size640wh_8489.jpg\" title=\"トラブルを避けるためにも「遺産分割」のポイントや注意点をおさえておこう\" alt=\"トラブルを避けるためにも「遺産分割」のポイントや注意点をおさえておこう\" ><br />\n　トラブルに発展しがちな遺産相続。特に相続人が複数いる場合、預貯金の遺産分割について揉めることは珍しくない。そこで、この遺産分割における基本的なポイントおよび注意点をおさらいしておこう。<br />\n<br />\n　相続分について、被相続人（故人）が意思を表明していないときは、民法が定めた「法定相続分」に従って遺産の分割が行われる。法定相続分とは、わかりやすくいえば「こう分けるのが好ましい」と定められたルールのこと。例えば以下のような形だ。<br />\n<br />\n【配偶者と子どもが相続人の場合】<br />\nそれぞれ2分の1。子どもが複数なら、2分の1を子どもの人数で等分する<br />\n<br />\n【配偶者と直系尊属（父母、祖父母など）が相続人の場合】<br />\n配偶者の相続分は3分の2、直系尊属は3分の1。直系尊属が複数いれば、親など近い人が相続人となり、複数なら等分する<br />\n<br />\n【配偶者と兄弟姉妹が相続人の場合】<br />\n配偶者の相続分は4分の3、兄弟姉妹は4分の1。兄弟姉妹が複数なら等分する。ただし、父母の一方のみを同じくする兄弟姉妹の相続分は、父母の双方を同じくする兄弟姉妹の2分の1となる<br />\n<br />\n　相続人の間で、上記のような内容を踏まえてすんなり話し合いがつけば問題ないが、厄介なのは誰かが納得しなかったとき。預金の払い戻しには、基本的に相続人全員の同意が必要とされるため、解決するまで金融機関が応じないケースは多くみられる。ひとりがすべての遺産を相続する場合でも、全員が署名と実印を押印した「遺産分割協議書」が必要となる。<br />\n<br />\n　いずれにせよ、大切なのは相続人による話し合い。大切な人が亡くなった直後に遺産の話をするのは、はばかられるかもしれないが、トラブルを避けるためにも抜かりなく行おう。<br />\n<br />\n参照／日本司法支援センターHP', 1, '2016-12-07 09:30:00', '2017-02-05 09:30:00', NULL, NULL, NULL, 0, 'http://life.oricon.co.jp/rank_netbank/bank/?anc=099', '「ネット銀行」を選ぶなら！　利用者が選ぶ“満足度ランキング”をチェック', 'http://life.oricon.co.jp/rank_netbank/news/2081309/?anc=099', '実は大きなメリットが!?　「入出金明細」は面倒でも確認を！', 'http://life.oricon.co.jp/rank_netbank/bank/evaluation-item/rate.html?anc=099', '＜最新ランキング＞利用前に確認したい　“預金金利”で選ばれた「ネット銀行」', '', '', '', '', 0, 0, 1, 1, '2016-12-07 16:34:07', '2016-12-07 16:34:07'),
    (48, '4_1_r_20161207_1481096047599586657', 4, 1, 1, 1, 1, 'http://www.oricon.co.jp/news/2082592/', '6655f6e5cded1d3c0f6a12a709abb4d5_1481096047', '及川奈央、41歳一般男性との結婚を発表「今年の春頃から自然と惹かれ合い」', '', '<img src=\"http://news.oricon.co.jp/feed/images/size640wh/20161206_101940_size640wh_3141.jpg\" title=\"結婚を発表した及川奈央\" alt=\"結婚を発表した及川奈央\" ><br />\n　女優の及川奈央（35）が5日、自身のブログを更新し、41歳の会社員の男性と結婚したことを発表した。<br />\n<br />\n　及川は「私ごとで大変恐縮でございますが、本日12月5日に入籍致しました事をご報告させていただきます。お相手は41歳の会社員の方です。一般の方なので、名前や写真は控えさせていただきます」と報告した。<br />\n<br />\n　夫との出会いについて「数年前になりますが、今年の春頃から自然と惹かれ合い、お付き合いを始めさせていただくようになりました」と明かし、「一緒にいると私自身が自然体でいられて安心し穏やかな気持ちでいられる方です」と紹介。<br />\n<br />\n　これからの新しい生活に向けて「どんなときも互いを慈しみ、感謝し、笑顔の溢れる家庭を築いてゆきたいと思っております。まだまだ未熟者の私ですが、今後とも、どうか温かく見守っていただけましたら幸せです」と結んだ。<br />\n<br />\n　及川はNHK大河ドラマ『龍馬伝』やテレビ朝日系『炎神戦隊ゴーオンジャー』などに出演している。', 1, '2016-12-06 10:29:03', '2017-02-04 10:29:03', NULL, NULL, NULL, 0, 'http://www.oricon.co.jp/news/2035267/photo/1/?anc=142', '【写真】ロンブー田村淳、元モデルの妻・香那さんとハワイ挙式ショット', 'http://www.oricon.co.jp/news/2052856/?anc=142', '及川奈央、“女性としての幸せ”に開眼「母性持て余してる」 （15年05月17日）', 'http://www.oricon.co.jp/news/2072177/photo/1/?anc=142', '【写真】ニッチェ江上、出雲大社で挙式　「ドレス姿かわいい」と反響', '', '', '', '', 0, 0, 1, 1, '2016-12-07 16:34:08', '2016-12-07 16:34:08'),
    (49, '4_1_r_20161207_1481096047684798166', 4, 1, 1, 1, 1, 'http://www.oricon.co.jp/news/2082568/', '8b980eaf4cbcb0d3cce1cf9ae809bc08_1481096047', 'レースクイーン女王・三城千咲、究極ボディを余すところなく披露', '', '<img src=\"http://news.oricon.co.jp/feed/images/size640wh/20161205_184202_size640wh_2612.jpg\" title=\"“神ってる”ボディラインを大胆に見せつけた三城千咲（C）栗山秀作／週刊プレイボーイ\" alt=\"“神ってる”ボディラインを大胆に見せつけた三城千咲（C）栗山秀作／週刊プレイボーイ\" ><br />\n　レースクイーンの三城千咲（27）が、5日発売の『週刊プレイボーイ』51号（集英社）に登場。身長166・バスト85・ウエスト・58・ヒップ87センチの“神ってる”ボディラインを大胆に見せつけた。<br />\n<br />\n　三城は、年間を通してもっとも注目を集めたレースクイーンを表彰する「レースクイーン・オブ・ザ・イヤー15-16」を受賞。今回のグラビアでは、黒いビキニやランジェリー風の衣装をまとい、女王と呼ぶにふさわしい究極ボディを余すところなく披露している。<br />\n<br />\n　なお、同誌公式サイト『週プレnetEXTRA』では、雑誌未公開を含む60カット以上を閲覧できるデジタル写真集「女神のボディライン」も発売された。<br />\n<br />\n　同号にはそのほか、女優の新垣結衣、石橋杏奈、グラビアアイドルの鈴木ふみ奈、モデルの松本愛らが登場。表紙は写真集『色っぽょ』が話題を呼んでいるモデル・女優の馬場ふみかで、写真集のメイキングDVDも付属している。', 1, '2016-12-05 19:00:02', '2017-02-03 19:00:02', NULL, NULL, NULL, 0, 'http://www.oricon.co.jp/news/2081456/photo/3/?anc=142', '【写真】レースクイーン頂点に三城千咲　水着でスレンダーBODYを披露', 'http://www.oricon.co.jp/news/2082527/photo/1/?anc=142', '【写真】セクシー全開！大人グラビアに挑戦した松本愛', 'http://www.oricon.co.jp/news/2082080/photo/1/?anc=142', '【写真】『地味スゴ』出演・水原ゆき、人生初水着グラビアで全開！', '', '', '', '', 0, 0, 1, 1, '2016-12-07 16:34:08', '2016-12-07 16:34:08'),
    (50, '4_1_r_20161207_1481096047762072114', 4, 1, 1, 1, 1, 'http://www.oricon.co.jp/news/2082542/', 'db2aa66dca7d4aefd7594912105b49f5_1481096047', '大渕愛子弁護士が第2子男児出産「心の底からほっと安心」', '', '<img src=\"http://news.oricon.co.jp/feed/images/size640wh/20161205_144236_size640wh_5062.jpg\" title=\"金山一彦＆大渕愛子夫妻 （C）ORICON NewS inc.\" alt=\"金山一彦＆大渕愛子夫妻 （C）ORICON NewS inc.\" ><br />\n　俳優・金山一彦（49）の妻で弁護士の大渕愛子氏（39）が6日午前9時37分、第2子となる2924グラムの男児を出産した。同日、自身のブログで報告した。<br />\n<br />\n　大渕氏は「いつもブログを読んで下さっているみなさま、妊娠中は温かい励ましのお言葉やアドバイスを下さり、本当にありがとうございました」と感謝し「おかげさまで、本日9時37分、2924gの男の子を無事出産いたしました。母子ともに健康です」と報告。<br />\n<br />\n　「元気に生まれてきてくれて、心の底からほっと安心するとともに、生まれてきてくれた赤ちゃんにも、支えてくれた家族にも、応援してくださったみなさまにも、感謝の気持ちでいっぱいです。ありがとうございました」と重ねて感謝し「名前は、いくつか候補を考えていましたが、顔を見てから決めようと話していたので、これから家族と相談して決めようと思います」とつづった。<br />\n<br />\n　最後は「病院に駆けつけた私の母に抱っこされて眠っている弟くんです」と誕生した愛息子の写真を添え「私も早く起き上がって抱っこしたいです」と記しブログを結んでいる。<br />\n<br />\n　また、金山もブログを通じて「分娩室の前では、出て来るのが遅かったのでハラハラしましたが、母子共に健康で安心しました」と心境を明かし、次男について「手の指が長く、直ぐに僕の指を力強く掴んでくれました」と紹介している。<br />\n<br />\n　2人は、2014年8月に結婚し、昨年3月に第1子男児が誕生。今年7月に妊娠5ヶ月であることを発表していた。', 1, '2016-12-06 15:35:08', '2017-02-04 15:35:08', NULL, NULL, NULL, 0, 'http://www.oricon.co.jp/news/2060080/photo/4/?anc=112', '【貴重ショット】愛息子とゴロ寝・・・幸せそうな大渕弁護士', 'http://beauty.oricon.co.jp/trend-culture/love/news/photo/2046284/1/?anc=112', '【写真】大渕愛子＆金山一彦が公開キス！　ラブラブ結婚式', 'http://beauty.oricon.co.jp/trend-culture/love/news/2074493/?anc=112', '北村晴男弁護士、大渕の毒舌を暴露　産休中の代理弁護士に「デブ」（16年07月03日）', '', '', '', '', 0, 0, 1, 1, '2016-12-07 16:34:08', '2016-12-07 16:34:08'),
    (51, '4_1_r_20161207_1481096047864818948', 4, 1, 1, 1, 1, 'http://www.oricon.co.jp/news/2082634/', '3c7f154370bfa790b866013dc33833c5_1481096047', 'ニューヨークの冬の風物詩！　イルミネーションとスケートを楽しめる2大スポット', '', '<img src=\"http://news.oricon.co.jp/feed/images/size640wh/20161206_181451_size640wh_2479.jpg\" title=\"ロックフェラー・センターの巨大なクリスマスツリー\" alt=\"ロックフェラー・センターの巨大なクリスマスツリー\" ><br />\n　もうすぐ年に一度のクリスマス。街のいたるところで、彩り鮮やかなイルミネーションが見られるようになってきた。東京の赤坂サカスや横浜の赤レンガ倉庫などは、屋外スケートリンクを合わせて設置する場所として有名になりつつあるが、実は米ニューヨークでは、屋外スケートは冬の風物詩として定着している。<br />\n<br />\n　そこで今回は、ニューヨークでイルミネーションと屋外スケートを同時に楽しめるスポットを紹介したい。<br />\n<br />\n■ロックフェラー・センター<br />\n<br />\n　ニューヨークのクリスマスといえば、ロックフェラー・センター（Rockefeller Center）の巨大なクリスマスツリーを思い浮かべる人も少なくない。今年度のライトアップは来年の1月7日まで行われ、巨大なツリーを眺めながらスケートを楽しめる。来年4月までオープンしており、入場料は大人が25～32ドル、子ども（11歳まで）が15ドルだ。<br />\n<br />\n　この場所は、1992年に公開された映画『ホーム・アローン2』で、主人公のケビンが母親と再会を果たしたシーンでも有名。同映画にもスケートリンクが登場しているが、これはセントラル・パーク（Central Park）のものだそう。<br />\n<br />\n■ブライアント・パーク<br />\n<br />\n　タイムズスクエア（Times Square）からほど近いブライアント・パーク（Bryant Park）では、大規模なクリスマスマーケット「バンク・オブ・アメリカ・ウインター・ビレッジ（Bank of America Winter Village）がオープン中。今月3日（現地時間2日）にはツリーの点灯式が行われ、スケートリンクではハビエル・フェルナンデスやジョニー・ウィアーなど有名なスケーターを迎えてアイスショーが開催された。<br />\n<br />\n　スケートリンクは入場無料。子ども向けにスケート補助具の貸し出しも行っており、カップルからファミリーまで楽しめるスポットだ。来年の3月5日までオープンしている。<br />\n<br />\n　クリスマスや年末年始をニューヨークで過ごす人は、ぜひ訪れてみてはいかがだろうか。<br />\n<br />\n【文／寺本亜紀】', 1, '2016-12-07 08:20:00', '2017-02-05 08:20:00', NULL, NULL, NULL, 0, 'http://juken.oricon.co.jp/rank_english/master.html?anc=158', 'この冬「英会話」を極めるなら！　“習得度”で選ばれたスクールランキング', 'http://juken.oricon.co.jp/rank_english/news/2064069/?anc=158', 'じっくり聞けば英語も上達!!　オススメ「クリスマスソング」3選', 'http://juken.oricon.co.jp/rank_english/level/ordinary-conversation.html?anc=158', '初心者におすすめ！　“日常会話レベル”で選ばれた「英会話スクール」1位は…', '', '', '', '', 0, 0, 1, 1, '2016-12-07 16:34:08', '2016-12-07 16:34:08'),
    (52, '4_1_r_20161207_1481096048009133977', 4, 1, 1, 1, 1, 'http://www.oricon.co.jp/news/2082640/', 'a5009e1f74594cef08100b57f0665179_1481096048', '永野芽郁、三浦翔平からバックハグ＆白濱亜嵐から床ドン『ひるなかの流星』映像解禁', '', '<img src=\"http://news.oricon.co.jp/feed/images/size640wh/20161206_195707_size640wh_3027.jpg\" title=\"ポスタービジュアルも公開された（C）2017 フジテレビジョン 東宝 集英社（C）やまもり三香／集英社\" alt=\"ポスタービジュアルも公開された（C）2017 フジテレビジョン 東宝 集英社（C）やまもり三香／集英社\" ><br />\n　『UQ mobile』CMの3女役でもおなじみ、人気急上昇中の若手女優・永野芽郁が主演し、人気少女コミックを実写映画化する『ひるなかの流星』（2017年3月24日公開）の本編映像を収録した特報が7日、解禁された。<br />\n<br />\n　同映像には、三浦翔平に後ろから抱きしめられるバックハグ、頭を撫でられる“あたポン”、白濱亜嵐（EXILE/GENERATIONS from EXILE TRIBE）からの告白や、手つなぎ登校など、観るものの恋心をくすぐる永野の胸キュンシーンとセリフが満載。<br />\n<br />\n　ラストは、主人公・与謝野ずずめ（永野）と馬村大輝（白濱）がお互いの顔を限界まで接近させた床ドン・シーン。原作の名場面が目白押しになった特報に仕上がっている。<br />\n<br /><br />\n<iframe width=\"640\" height=\"360\" src=\"https://www.youtube.com/embed/pZRPuZkRgIQ\" frameborder=\"0\" allowfullscreen></iframe>', 1, '2016-12-07 08:00:00', '2017-02-05 08:00:00', NULL, NULL, NULL, 0, 'http://www.oricon.co.jp/photo/2151/135822/?anc=023', '【写真】妖艶なオーラを放つ個性派すぎる？美しきCM三姉妹', 'http://www.oricon.co.jp/special/49546/?anc=023#link3', '【新CM動画】深田＆多部＆永野、妖艶3姉妹の「UFO」ダンス', 'http://www.oricon.co.jp/photo/2151/135825/?anc=023', '【写真】UQ前CMでは右横顔ショットだった永野芽郁', '', '', '', '', 0, 0, 1, 1, '2016-12-07 16:34:08', '2016-12-07 16:34:08'),
    (53, '4_1_r_20161207_1481096048098302289', 4, 1, 1, 1, 1, 'http://www.oricon.co.jp/news/2082650/', 'e94adaa32ecc0e6e085280fa5db330a1_1481096048', 'シリーズ新3部作が始動　『トランスフォーマー／最後の騎士王』来年夏公開', '', '<img src=\"http://news.oricon.co.jp/feed/images/size640wh/20161207_031951_size640wh_4022.jpg\" title=\"『トランスフォーマー／最後の騎士王』は2017年夏公開\" alt=\"『トランスフォーマー／最後の騎士王』は2017年夏公開\" ><br />\n　世界中でヒットした『トランスフォーマー』シリーズ最新作の邦題が『トランスフォーマー／最後の騎士王』に決定し、2017年夏に日本公開することが6日、わかった。ハリウッドきっての“破壊王”として知られるマイケル・ベイ監督のあまりに壮大な構想を実現させるため、毎年1本ずつ3年連続で公開するという新たな3部作構想に挑む。<br />\n<br />\n　あわせて解禁された予告編では、相変わらずの爆破シーンやカーアクション、驚異のVFXなどに加えて中世の時代での戦いも描かれるだけでなく、最後には仲間であるはずのオプティマスとバンブルビーが戦いを繰り広げるという衝撃の展開が待ち受けている。<br />\n<br />\n　ロケ地にはバッキンガム宮殿などイギリスの名所の数々が登場。脚本は『アイアンマン』でコンビを組んだマット・ハロウェイ氏＆アート・マーカム氏、『ブラックホーク・ダウン』のケン・ノーラン氏。主演は、前作で主人公を務めたマーク・ウォールバーグ、新キャストとして名優アンソニー・ホプキンス、新ヒロイン役で『ガーディアンズ・オブ・ギャラクシー』のローラ・ハドックが出演。シリーズの顔ともいうべきジョシュ・デュアメル、タイリース・ギブソン、ジョン・タトゥーロ、前作で印象的だったスタンリー・トゥッチが続投する。<br />\n<br />\n■『トランスフォーマー／最後の騎士王』予告編<br />\nhttps://youtu.be/Qg2hqzlWgmQ<br />\n<br />\n （C）2016 Industrial Light & Magic, a division of Lucasfilm Entertainment Company Ltd., All Rights Reserved', 1, '2016-12-07 08:00:00', '2017-02-05 08:00:00', NULL, NULL, NULL, 0, 'http://www.oricon.co.jp/news/2082645/full/?anc=115', '【動画】「スター・ウォーズ」新メンバーが来日　空港にファン300人が出迎え （16年12月06日）', 'http://www.oricon.co.jp/news/2082563/?anc=115', 'ハリポタ新章『ファンタビ』クイニー＆ジェイコブ、イメージ定着は「誇りに思う」 （16年12月05日）', 'http://www.oricon.co.jp/news/2082382/full/?anc=115', '【動画】『ローグ・ワン』“盲目の戦士”はドニー・イェンのアイデアだった （16年12月02日）', '', '', '', '', 0, 0, 1, 1, '2016-12-07 16:34:08', '2016-12-07 16:34:08'),
    (54, '4_1_r_20161207_1481096048186004186', 4, 1, 1, 1, 1, 'http://www.oricon.co.jp/news/2082624/', 'db392e574217b4faf7d615742d4ce663_1481096048', '飲むチョコのよう！？　マックカフェの“チョコづくし”ドリンク', '', '<img src=\"http://news.oricon.co.jp/feed/images/size640wh/20161206_160533_size640wh_3618.jpg\" title=\"マックカフェからチョコレートづくしのドリンクがホットとフラッペで登場\" alt=\"マックカフェからチョコレートづくしのドリンクがホットとフラッペで登場\" ><br />\n　マクドナルドに併設するカフェ「McCafe by Barista（マックカフェ バイ バリスタ）」から、チョコレートを使った新ドリンクがきょう7日より登場する。今年を締めくくる期間限定のデザート系ドリンクは、チョコレートをふんだんに使った贅沢な1杯。チョコづくしの味わいがフラッペとホットで楽しめる。<br />\n<br />\n　『プレミアムチョコレートフラッペ』は、チョコレートソース、チョコレートパウダーとミルクを混ぜたフラッペがベース。そこにチョコホイップ、シェイブチョコレートと、チョコレートをたっぷりトッピングしたドリンクとなっている。<br />\n<br />\n　もうひとつの『プレミアムホットチョコレート』は、チョコレートソースとチョコレートパウダーに、ふわふわのフォームミルクを注いだミルキーな味わい。とろとろのチョコレートを飲んでいるかのようなホットドリンクに体が温まる。どちらも仕上げにガーナ産カカオを使用したチョコレートステック2本が添えられ、チョコ好きにはマストで飲んでほしいドリンクとなっている。<br />\n<br />\n【商品詳細】※すべて税込<br />\n『プレミアムチョコレートフラッペ』（500円）<br />\n『プレミアムホットチョコレート』（450円）<br />\n販売期間：2016年12月7日～2017年1月中旬（予定）<br />\n販売エリア：全国の「McCafe by Barista」併設マクドナルド店舗（89店舗 ※2016年11月29日時点）', 1, '2016-12-07 08:00:00', '2017-02-05 08:00:00', NULL, NULL, NULL, 0, 'http://joshiplus.jp/gourmet/news/2081527/?anc=044', 'マックフルーリーに“大人ティラミス”味 （16年11月22日）', 'http://joshiplus.jp/gourmet/news/2081475/?anc=044', '今年はホワイトが新登場！　マック人気の「三角チョコパイ」 （16年11月15日）', 'http://joshiplus.jp/special/113/?cat_id=ISM&anc=044', '【特集】注目の冬アイスを紹介！アレンジレシピ付き☆', '', '', '', '', 0, 0, 1, 1, '2016-12-07 16:34:08', '2016-12-07 16:34:08'),
    (55, '4_1_r_20161207_1481096048264460112', 4, 1, 1, 1, 1, 'http://www.oricon.co.jp/news/2082651/', '2d434ecd0f637a2faede0ac8ec3236ee_1481096048', '佐藤勝利＆橋本環奈W主演　映画『ハルチカ』予告編が公開', '', '<img src=\"http://news.oricon.co.jp/feed/images/size640wh/20161207_033517_size640wh_3817.jpg\" title=\"映画『ハルチカ』の予告編が公開 （C）2017「ハルチカ」製作委員会\" alt=\"映画『ハルチカ』の予告編が公開 （C）2017「ハルチカ」製作委員会\" ><br />\n　人気グループ・Sexy Zoneの佐藤勝利と女優の橋本環奈がW主演する映画『ハルチカ』（2017年3月4日公開）の予告編が7日、解禁された。高校で偶然再会した幼なじみのハルタ（佐藤）とチカ（橋本）が、廃部寸前の吹奏楽部を立て直そうと奔走する姿を収めている。<br />\n<br />\n　同作は、初野晴氏による青春小説『ハルチカ』シリーズを、『僕らのごはんは明日で待ってる』（2017年1月7日公開）の市井昌秀監督が映画化。ちょっぴり頼りないけど気の優しい美男子ハルタと、気が強くて明るく元気な女子高生のチカという幼なじみの2人が、高校入学式で再会を果たし、ともに吹奏楽部に入り音楽を通して気持ちをつなげていく。<br />\n<br />\n　予告編では、個性豊かな“ワケあり”のメンバーたちに声をかけ、吹奏楽部員を集める2人。徐々にメンバーがそろっていくなか、かつて将来を嘱望される指揮者だった教師・草壁信二郎（小出恵介）が顧問に加わり、コンクール出場に向けて突き進むが、部にとっても2人にとっても大きな試練が待っていた。<br />\n<br />\n　みんなになかなか追いつけず涙するフルート初心者のチカ、そして、練習中に突如倒れてしまうハルタ。多感な高校生たちが経験する不安や挫折、幼なじみ同士の淡い初恋と、ある秘密…。コンクール本番の日が迫るなか、彼らは試練を乗り越えられるのか。<br />\n<br /><br />\n<iframe width=\"640\" height=\"360\" src=\"https://www.youtube.com/embed/qyfGxhwyQFo\" frameborder=\"0\" allowfullscreen></iframe>', 1, '2016-12-07 07:00:01', '2017-02-05 07:00:01', NULL, NULL, NULL, 0, 'http://www.oricon.co.jp/news/2082572/photo/6/?anc=115', '【写真】実写『銀魂』では地毛をオレンジ色に染め上げた橋本環奈', 'http://www.oricon.co.jp/news/2077527/?anc=115', 'Sexy Zone・佐藤勝利、“顔面人間国宝”でギネス熱望 （16年08月29日）', 'http://www.oricon.co.jp/news/2081692/photo/1/?anc=115', '【写真】橋本環奈、かわいいすぎる猫耳姿　可憐で美しい17歳の魅力全開', '', '', '', '', 0, 0, 1, 1, '2016-12-07 16:34:08', '2016-12-07 16:34:08'),
    (56, '4_1_r_20161207_1481096048351414199', 4, 1, 1, 1, 1, 'http://www.oricon.co.jp/news/2082626/', 'a15f089e751167c8fe8189d4982869b9_1481096048', '北斗晶、闘病生活でも体重変化なし　上沼恵美子が復帰歓迎「おかえり！」', '', '<img src=\"http://news.oricon.co.jp/feed/images/size640wh/20161206_164140_size640wh_3570.jpg\" title=\"９日放送の関西テレビ『快傑えみちゃんねる』（毎週金曜 後7：00）に北斗晶が休養後初登場 （C）関西テレビ\" alt=\"９日放送の関西テレビ『快傑えみちゃんねる』（毎週金曜 後7：00）に北斗晶が休養後初登場 （C）関西テレビ\" ><br />\n　乳がん闘病のため1年2カ月間芸能活動を休業していたタレントの北斗晶が、9日放送の関西テレビ『快傑えみちゃんねる』（毎週金曜 後7：00）に出演。抗がん剤治療を終え、少し伸びてきた短髪で現れた北斗は「やっとここまで生えてきました！痩せた？とよく言われるんですが1キロも痩せてないんです！」と休業前と変わらぬはつらつとした笑顔で復帰を報告した。<br />\n<br />\n　番組冒頭からメインパーソナリティーの上沼恵美子は「北斗さんおかえり！」と歓迎。復帰発表前に北斗から手紙をもらい復帰を心待ちにしていた上沼は、元気そうな北斗の姿に一安心した様子。「お子さんは大丈夫やった？」「ひょっとしたらダメかなと思う時はなかった？」と北斗へ直球の質問を投げかける上沼に、北斗は「外に出られない時期があった。誰かに見られているんじゃないかと思って…」と闘病中の苦労も明かした。<br />\n<br />\n　また、つらい抗がん剤の副作用で、弱気になったという北斗は、夫の佐々木健介や子供たちの献身的なサポート、そして愛犬が大きな支えになったことに感謝しつつも、健介が子どもたちに作るお弁当の中身に強烈なダメ出し。「家族は一生懸命やってくれるんですけど、できないこともある。逆に（私がやらなきゃと思えて）それがよかったのかもしれない」と明るく振り返ると、上沼は「パワーアップした感じやね！」と目を細めていた。<br />\n<br />\n　以前にも増してパワフルトークを繰り広げる北斗に、ある人物からお祝いメッセージが届けられ、「PPAP」が大ヒット中のピコ太郎からも歌のプレゼントが。このほか番組では、俳優・山崎樹範と結婚した吉井怜が、彼に対する熱い思いを激白する。', 1, '2016-12-07 06:00:00', '2017-02-05 06:00:00', NULL, NULL, NULL, 0, 'http://www.oricon.co.jp/news/2053295/photo/1/?anc=096', '【写真】ラブラブぶりは健在…北斗晶、お姫様抱っこに照れ', 'http://www.oricon.co.jp/news/2015432/photo/1/?anc=096', '【写真】長男＆次男も！佐々木健介&北斗晶ファミリーの仲睦まじいショット', 'http://www.oricon.co.jp/news/video/2063117/full/?anc=096', '【動画】健介、妻・北斗晶との“手つなぎデート”希望「外を歩きたい」 （15年12月01日）', '', '', '', '', 0, 0, 1, 1, '2016-12-07 16:34:08', '2016-12-07 16:34:08'),
    (57, '4_1_r_20161207_1481096048449754272', 4, 1, 1, 1, 1, 'http://www.oricon.co.jp/news/2082649/', '10bfddc70f6bec5d1de69de8a189f3d1_1481096048', '佐藤健＆土屋太鳳がW主演　感動の実話を映画化『8年越しの花嫁』', '', '<img src=\"http://news.oricon.co.jp/feed/images/size640wh/20161207_030829_size640wh_6565.jpg\" title=\"映画『8年越しの花嫁』にW主演する（左から）佐藤健、土屋太鳳\" alt=\"映画『8年越しの花嫁』にW主演する（左から）佐藤健、土屋太鳳\" ><br />\n　俳優の佐藤健（27）、土屋太鳳（21）が、映画『8年越しの花嫁』（2017年冬公開）でW主演することが6日、わかった。YouTubeに投稿された動画をきっかけに広まった、あるカップルに起きた奇跡の実話を映画化。2人は『るろうに剣心 京都大火編』『るろうに剣心 伝説の最期編』（共に14年）以来3年ぶりに共演し、初の恋人役。「敬意を込めて、全身全霊を捧げ撮影に臨みます」（佐藤）、「全てを注ぎ全てを捨てて、演じます」（土屋）と、それぞれ熱意を込めたコメントを寄せた。<br />\n<br />\n　本作のモデルとなったのは、岡山在住の中原尚志さん・麻衣さん夫婦。交際から2年目の記念日にプロポーズ、2007年3月の挙式に向けて準備をしていた06年末、麻衣さんを突然原因不明の病が襲う。その後、「抗NMDA受容体脳炎」という発症率が300万人に1人とされる難病と診断。長い昏睡状態とその後のリハビリを経て、8年後の2014年12月に晴れて挙式した。<br />\n<br />\n　式場の運営会社が撮影したそのときの映像が、2015年2月に「8年越しの結婚式」としてYouTubeに投稿され、『8年越しの花嫁 キミの目が覚めたなら』（主婦の友社刊）として書籍化もされた。<br />\n<br />\n　メガホンをとるのは、『64 ロクヨン前・後編』（16年）などの瀬々敬久監督。「真実の物語に敬虔（けいけん）な心を忘れることなく、そして臆することなく、果敢に挑戦していきたいと思っています」と意気込み、脚本は来年のNHKの連続テレビ小説『ひよっこ』の脚本も手がける岡田惠和氏が務める。<br />\n<br />\n　佐藤は「僕たちの作る映画は、尚志さん達が実際に送られた人生の感動には遠く及ばないかもしれません。しかしこの、あるカップルに舞い降りた奇跡の実話を、少しでも多くの方に伝えたいという想いで、この役を引き受けさせていただきます」と使命感をにじませ、土屋は「中原尚志さん・麻衣さん御夫妻、そして支えてこられた方々へ。お2人の純粋な愛を伝えて下さって、本当にありがとうございます。心からの感謝と共に、大切な記憶に真摯に向き合い、その日々を生きたいと思います」と決意。2人とも同作へ込める熱い思いを伝えている。<br />\n<br />\n■佐藤健<br />\nこのドキュメンタリーを観た時の感動を今でも鮮明に覚えています。もちろん尚志さんと麻衣さんの8年間は、あの映像には収まりきらないほど壮絶で、しかしその中に、普通の人の一生分全部を足しても届かない程の大きな大きな愛があったのだと感じました。現実にこんな人生が有り得るのかと、心が震えました。そして奇跡は起きるものじゃなく起こすものなのだと、お2人に教えていただきました。<br />\n<br />\n僕達の作る映画は、尚志さん達が実際に送られた人生の感動には遠く及ばないかもしれません。しかしこの、あるカップルに舞い降りた奇跡の実話を、少しでも多くの方に伝えたいという想いで、この役を引き受けさせて頂きます。お2人のことをちゃんと見守り続けてくれていた神様と、この作品との出会い、そしてこんなにも眩い生き方を僕達に見せてくれた尚志さん、麻衣さん、お2人のご家族の皆様に心から感謝して、そして敬意を込めて、全身全霊を捧げ撮影に臨みます。<br />\n<br />\n■土屋太鳳<br />\n私が出会った一冊の本。愛らしい表紙には、ひまわりのような新郎新婦。でも、その物語に刻まれていたのは、壮絶な奇跡でした。中原尚志さん・麻衣さん御夫妻、そして支えてこられた方々へ。お2人の純粋な愛を伝えて下さって、本当にありがとうございます。心からの感謝と共に、大切な記憶に真摯に向き合い、その日々を生きたいと思います。<br />\n<br />\n佐藤健さんへ。魂を削り役を生きる背中は、常に私の目標です。全力で、ぶつかります。<br />\n<br />\n応援して下さってる方々、この物語を愛していらっしゃる方々へ。貴重な奇跡を私で表現出来るのか御心配かと思います。未熟者ですが、全てを注ぎ全てを捨てて、演じます。どうか見守って下さい。よろしくお願い致します。', 1, '2016-12-07 06:00:00', '2017-02-05 06:00:00', NULL, NULL, NULL, 0, 'http://www.oricon.co.jp/news/2082448/photo/1/?anc=115', '【写真】土屋太鳳、久々パティシエ姿を披露', 'http://www.oricon.co.jp/news/2078995/photo/1/?anc=115', '【写真】佐藤健、IKKOと一緒に“なにもの～♪”「巷で流行らせて」', 'http://www.oricon.co.jp/news/2080979/?anc=115', '佐藤健、実写映画『亜人』に主演　人気漫画の映画化「避けては通れない」 （16年11月04日）', '', '', '', '', 0, 0, 1, 1, '2016-12-07 16:34:08', '2016-12-07 16:34:08'),
    (58, '4_1_r_20161207_1481096048632717645', 4, 1, 1, 1, 1, 'http://www.oricon.co.jp/news/2082648/', '8650db14e8725afad416bef8202cb961_1481096048', '【オリコン】ケツメイシ、15周年ライブDVDで通算9作目TOP3', '', '<img src=\"http://news.oricon.co.jp/feed/images/size640wh/20161207_003537_size640wh_4835.jpg\" title=\"ケツメイシの15周年ライブDVD『15th Anniversary「一五の夜」～今夜だけ練乳ぶっかけますか？～』\" alt=\"ケツメイシの15周年ライブDVD『15th Anniversary「一五の夜」～今夜だけ練乳ぶっかけますか？～』\" ><br />\n　メジャーデビュー15周年を迎えたケツメイシのライブDVD『15th Anniversary「一五の夜」～今夜だけ練乳ぶっかけますか？～』（11月30日発売）が初週2.3万枚を売り上げ、12/12付オリコン週間DVDランキング総合2位に初登場。『ケツの穴．．．らへん』（2013年10月発売、最高1位）から4作連続通算9作目のDVD総合TOP3入りを果たした。<br />\n <br />\n　同日発売のBlu-ray Disc（以下BD）も初週売上0.8万枚で週間BDランキング総合8位にランクイン。音楽DVDとBDの売上枚数を合算した総合ミュージック映像ランキングではジャニーズWESTに続き、2位に初登場した。<br />\n <br />\n　今作は、8月6日に横浜・日産スタジアムで行われたデビュー15周年記念ライブの模様を収録。7万人を動員した一夜限りのリクエストベストライブで披露された3時間に及ぶパフォーマンスが収められている。', 1, '2016-12-07 04:00:00', '2017-02-05 04:00:00', NULL, NULL, NULL, 0, 'http://music.oricon.co.jp/php/artist/ArtistTop.php?artist=A00287&ref_cd=MS0098&anc=015', '【試聴】ケツメイシの主な配信楽曲', 'http://www.oricon.co.jp/news/2076416/?anc=015', 'ケツメイシ、日産スタジアムで15周年ライブ　7万人熱狂 （16年08月07日）', 'http://www.oricon.co.jp/news/2076846/?anc=015', '剛力彩芽、ケツメイシ主題歌披露にノリノリ「誰よりも盛り上がった」 （16年08月16日）', '', '', '', '', 0, 0, 1, 1, '2016-12-07 16:34:09', '2016-12-07 16:34:09'),
    (59, '4_1_r_20161207_1481096048748855302', 4, 1, 1, 1, 1, 'http://www.oricon.co.jp/news/2082617/', '07cc4d771b64d72a2c2f39576a5b5fa9_1481096048', '【オリコン】ジャニーズWEST、初アリーナツアー映像作品が3部門制覇', '', '<img src=\"http://news.oricon.co.jp/feed/images/size640wh/20161019_162958_size640wh_4809.jpg\" title=\"ジャニーズWESTが映像部門3冠\" alt=\"ジャニーズWESTが映像部門3冠\" ><br />\n　人気グループ・ジャニーズWESTのライブDVD／Blu-ray Disc（以下BD）『ジャニーズWEST CONCERT TOUR 2016 ラッキィィィィィィィ7』（11月30日発売）がDVDは3.9万枚、BDは3.0万枚を初週に売り上げ、12/12付オリコン週間DVD／BD両ランキングで総合1位を獲得した。音楽DVDとBDの売上を合算した総合ミュージック映像ランキングでも1位となり、主要3部門を制覇。また、同日発売のアルバム『なうぇすと』（初週売上8.6万枚）も週間1位を獲得しており、ジャニーズWESTが今週付のランキングを席巻した。<br />\n<br />\n　音楽映像作品でDVD／BD同時総合1位を獲得したのは、前作『ジャニーズWEST 1st Tour パリピポ』（5月発売）から2作連続通算2作目。DVD総合1位獲得作品数は『なにわともあれ、ほんまにありがとう！』（2015年6月発売）から4作連続通算4作目となった。DVDの初週売上枚数は前作で記録した3.8万枚を上回り、自己最高をマークしている。<br />\n<br />\n　今作は、1月から約3ヶ月にわたって開催された自身初のアリーナツアーのうち、3月24日の大阪城ホール公演を収録。2ndアルバム『ラッキィィィィィィィ7』を引っ提げて行われた同ツアーは、コスプレ、シャッフルユニット、ゲームなどユニークな演出が満載で、特典映像には各地で行われた罰ゲーム映像がダイジェストで盛り込まれている。', 1, '2016-12-07 04:00:00', '2017-02-05 04:00:00', NULL, NULL, NULL, 0, 'http://www.oricon.co.jp/news/2082557/?anc=015', '【同日発売】ジャニーズWEST、アルバム首位　自己最高スタート （16年12月06日）', 'http://www.oricon.co.jp/news/2081576/?anc=015', 'ジャニーズWEST中間、“原宿の神様”の手でファッションチェンジ （16年11月16日）', 'http://www.oricon.co.jp/news/2080076/?anc=015', 'ジャニーズWEST、MBSのレギュラー番組初収録「家に帰ってきた感じ」 （16年10月17日）', '', '', '', '', 0, 0, 1, 1, '2016-12-07 16:34:09', '2016-12-07 16:34:09'),
    (60, '4_1_r_20161207_1481096048910020143', 4, 1, 1, 1, 1, 'http://www.oricon.co.jp/news/2082562/', '99c8349633abd4cfd9f2b5f33e79031c_1481096048', 'AKBじゃんけん選抜曲は「逆さ坂」　“こぶし”で勝ち取った演歌歌謡曲MV公開', '', '<img src=\"http://news.oricon.co.jp/feed/images/size640wh/20161205_164021_size640wh_6765.jpg\" title=\"AKB48じゃんけん選抜ユニット“じゃんけん民”シングル曲名は「逆さ坂」\" alt=\"AKB48じゃんけん選抜ユニット“じゃんけん民”シングル曲名は「逆さ坂」\" ><br />\n　10月に行われた『AKB48グループじゃんけん大会』上位7人で結成された新ユニット「じゃんけん民（みん）」が12月21日にリリースするシングルのタイトルが「逆さ坂」に決まり、アートワークとミュージックビデオ（MV）が一斉に解禁となった。<br />\n<br />\n　じゃんけん大会7代目女王“たなみん”こと田名部生来（たなべ・みく、24）が苦節10年、初センターを務める「逆さ坂」（さかさざか）は、“こぶし”で勝ち取った演歌調歌謡曲。総合プロデューサーの秋元康氏が「上り坂も下りの坂も　向きを変えりゃ　みんな同じ　つらいことも楽しいことも　同じ数だけやって来る」と田名部にエールを贈るような歌詞を書き下ろした。ジャケットは“演歌のキングレコード”らしい、かつてのアナログ盤のようなデザインとなっている。<br />\n<br />\n　渡辺麻友、柏木由紀と同期の田名部がこれまでに選抜入りしたのは、2010年の「第1回じゃんけん大会」で12位となり、19thシングル「チャンスの順番」（同年12月発売）を歌った1回のみ。「センターに立たせていただいていることは自分自身も動揺しています」と戸惑いつつも「もともと日本の文化が大好きなので、すてきな楽曲をいただけて、とてもうれしく思っています。そして、歌詞に自分が応援されているような気分になり、励まされてます」と胸中を明かした。<br />\n<br />\n　MVでは、田名部、湯本亜美（19）、込山榛香（18）、渋谷凪咲（20）、福岡聖菜（16）、竹内彩姫（17）、野澤玲奈（18）のメンバーが扮する架空の大人気歌謡曲ユニット“じゃんけん民”が、コンサートで「逆さ坂」を披露。しかし、ステージ上でくしゃみをするたびにおばあさんになったり若返ったりと大騒ぎとなり、あげく、週刊誌では「ホントはお婆さん!?」と年齢詐称疑惑まで持ち上がる。<br />\n<br />\n　田名部は「みんながおばあちゃんになる姿がおもしろおかしくて、笑いが絶えなかったです。みんなのお芝居も上手で見どころも満載です。そして、メイクさんの技術がすばらしすぎて、将来的にはこうなるのかと思うと、実は今から震えています」と裏話を披露。「誰もが歌いやすい曲だと思いますので、これからの忘・新年会でぜひ歌っていただけたら」とアピールするとともに「もしかすると世界進出もありえますよね!?」と野望ものぞかせている。', 1, '2016-12-07 04:00:00', '2017-02-05 04:00:00', NULL, NULL, NULL, 0, 'http://music.oricon.co.jp/php/artist/ArtistTop.php?artist=A01674&ref_cd=MS0098&anc=015', '【試聴】AKB48の主な配信楽曲', 'http://www.oricon.co.jp/news/2079736/?anc=015', '【AKBじゃんけん大会】田名部生来が7代目女王　「どっきりですか？」と号泣', 'http://www.oricon.co.jp/news/2080962/?anc=015', 'AKBじゃんけん選抜、12・21デビュー　田名部「嘘じゃなかった」 （16年11月03日）', '', '', '', '', 0, 0, 1, 1, '2016-12-07 16:34:09', '2016-12-07 16:34:09'),
    (61, '4_1_r_20161207_1481096049006749874', 4, 1, 1, 1, 1, 'http://www.oricon.co.jp/news/2082610/', 'b1f48fb451502c5f75d2df03421381f9_1481096049', '徳永ゆうきの“駅名恋愛ソング”配信開始　難読33駅の名が歌詞に', '', '<img src=\"http://news.oricon.co.jp/feed/images/size640wh/20161206_132822_size640wh_1953.jpg\" title=\"「恋は難読駅名」の配信をスタートさせる徳永ゆうき\" alt=\"「恋は難読駅名」の配信をスタートさせる徳永ゆうき\" ><br />\n　鉄道オタク“撮り鉄”としても話題の演歌歌手・徳永ゆうき（21）の“駅名恋愛ソング”「恋は難読駅名」がきょう7日からiTuneほか主要配信サイトで配信される。<br />\n<br />\n　全国の“難読駅名”を歌詞に織り交ぜて恋愛の難しさを表現した同曲。歌詞に出てくる駅名は33駅あり、徳永は「自分自身、鉄道が好きですが、知らない駅名もありました。皆さんもびっくりするような駅名も出てきます。読めないです」とコメント。<br />\n<br />\n　続けて「駅名と同じく恋も読めないですよね…。クリスマスまでに一緒に過ごしてくれる人、探します…。恋も読めないよね…」と、さみしげな発言も飛び出したが、最後は、演歌のイメージを新しい形で、鉄道ファンのみならず、世界の人々に発信すると力を込めている。', 1, '2016-12-07 00:00:00', '2017-02-05 00:00:00', NULL, NULL, NULL, 0, 'http://www.oricon.co.jp/news/2081935/?anc=112', '映画『バケツと僕！』撮影スタート　紘毅、徳永ゆうき、岡本玲ら意気込み （16年11月24日）', 'http://www.oricon.co.jp/news/2057294/?anc=112', '“撮り鉄”徳永ゆうきが一日船長「人生初の浮気」 （15年08月07日）', 'http://www.oricon.co.jp/news/2068870/?anc=112', '徳永ゆうき、“イワシ”ネタで芸人・永野と禁断コラボ「自慢できる」 （16年03月22日）', '', '', '', '', 0, 0, 1, 1, '2016-12-07 16:34:09', '2016-12-07 16:34:09'),
    (62, '4_1_r_20161207_1481096049063278225', 4, 1, 1, 1, 1, 'http://www.oricon.co.jp/news/2082647/', '570c32a717de298cda45fc00fa1c5ae4_1481096049', 'よしもと新喜劇「恋ダンス」に反響　新垣結衣のパートに酒井藍を抜てき', '', '<img src=\"http://news.oricon.co.jp/feed/images/size640wh/20161206_231217_size640wh_6297.jpg\" title=\"新喜劇メンバーが話題の「恋ダンス」に挑戦\" alt=\"新喜劇メンバーが話題の「恋ダンス」に挑戦\" ><br />\n　女優・新垣結衣が主演を務めるTBS系ドラマ『逃げるは恥だが役に立つ』（毎週火曜　後10：00）のエンディングで、キャスト陣が踊る「恋ダンス」の吉本新喜劇メンバーによるバージョンが6日、YouTubeにて公開された。<br />\n<br />\n　今回は、MBSテレビの人気番組『よしもと新喜劇』（毎週土曜　後0：54～1：54、関西ローカルほか全国16局で放送）のメンバー9人が、座長の小籔千豊の企画・演出のもと「恋ダンス」に挑戦。川畑泰史＆すっちーの2座長が、今別府直之、吉田裕、松浦真也、諸見里大介、宇都宮まき、森田まりこの7人とともに、新喜劇のセット前で「恋ダンス」を踊り、島田一の介も特別出演した。<br />\n<br />\n　また、メインとなる新垣のパートには、若手の酒井藍を抜てき。ふくよかなスタイルながら、ガッキー顔負けのかわいい笑顔を振りまいており、その横では星野源ならぬ、すち子がキレのいいダンスを披露するなど、新喜劇ならではのノリも交えた楽しい仕上がりになっている。<br />\n<br />\n　小籔はスケジュールの関係で収録に参加できなかったものの、メンバーの立ち位置や、ダンスのパートを誰と誰が踊るなど、カット割りまで細かく指示。「自分がガッキーをやっても良かったけど、今ノリにノッっている若手の酒井藍ちゃんを恋ダンスに便乗しつつ、日本中に知ってもらいたかった。新喜劇が全国に広がるきっかけになれば…」と若手を中心に抜てきしたという。<br />\n<br />\n　シュールな仕上がりでありながらも、クオリティーの高い動画にネット上では「新喜劇好きにはたまらん！」「藍ちゃんかわいい」「完成度高い」「めっちゃ笑った」「最高すぎる」など反響が続々と寄せられている。<br />\n<br />\n■「恋ダンス」新喜劇バージョン<br />\nhttps://youtu.be/tGLaNKVWXqE', 1, '2016-12-06 23:21:55', '2017-02-04 23:21:55', NULL, NULL, NULL, 0, 'http://www.oricon.co.jp/news/2081490/photo/4/?anc=112', '【写真】ドラマ内で清楚なセーラー服姿を披露したガッキー', 'http://www.oricon.co.jp/news/70401/photo/1/?anc=112', '【写真】こちらは7年前…制服姿を披露した新垣結衣', 'http://www.oricon.co.jp/news/2067714/photo/1/?anc=112', '【写真】美しすぎる…白のひざ上ワンピのガッキー', '', '', '', '', 0, 0, 1, 1, '2016-12-07 16:34:09', '2016-12-07 16:34:09'),
    (63, '4_1_r_20161207_1481096049181861992', 4, 1, 1, 1, 1, 'http://www.oricon.co.jp/news/2082646/', 'c6c6e84daa65d2199d47840c66795cec_1481096049', 'テレ朝・紀＆桝田アナ、サンタの衣装でイルミネーションツリー点灯式', '', '<img src=\"http://news.oricon.co.jp/feed/images/size640wh/20161206_223940_size640wh_5496.jpg\" title=\"テレビ朝日の本社1階にイルミネーションツリーが登場。点灯式に出席した紀真耶（右）＆桝田沙也香（左）アナウンサーと「ゴーちゃん。」\" alt=\"テレビ朝日の本社1階にイルミネーションツリーが登場。点灯式に出席した紀真耶（右）＆桝田沙也香（左）アナウンサーと「ゴーちゃん。」\" ><br />\n　東京・六本木ヒルズにあるテレビ朝日の本社1階アトリウムに、1万球のLED電球を使用した高さ約8メートルの「テレ朝X’masツリー」が登場。6日夕方、サンタの衣装に身を包んだテレビ朝日の紀真耶と桝田沙也香の両アナウンサーと、マスコットキャラクター「ゴーちゃん。（ゴーエクスパンダ）」が出席して、点灯式が行われた。ツリーの展示は25日まで。入場無料。<br />\n<br />\n　ツリーの特徴は、ドラマやバラエティー番組の出演者直筆のグリーティングオーナメント。絶好調のドラマ『ドクターX～外科医・大門未知子～』に主演する女優の米倉涼子、大みそかの大型特番『くりぃむ VS 林修！年越しクイズサバイバー2016』に出演するくりぃむしちゅー、林修氏らのメッセージ入りオーナメントが飾られている。設置期間中、番組出演者によるグリーティングオーナメントは増えていく予定。<br />\n<br />\n　六本木ヒルズは、六本木けやき坂や毛利庭園のイルミネーションが有名で、今年も多くの人が訪れているが、テレ朝X’masツリーもお見逃しなく。', 1, '2016-12-06 22:45:01', '2017-02-04 22:45:01', NULL, NULL, NULL, 0, 'http://www.oricon.co.jp/news/2082391/?anc=014', '水卜麻美アナ『好きな女性アナ』4連覇に歓喜　有吉は毒舌で祝福 （16年12月02日）', 'http://www.oricon.co.jp/news/2081732/?anc=014', 'テレ朝の大みそかは3年連続クイズまつり　『ドクターX』にあやかりたい （16年11月20日）', 'http://beauty.oricon.co.jp/trend-culture/love/news/2080751/?anc=014', 'テレ朝・本間智恵アナが結婚　お相手は「慶應大学の同級生」 （16年10月31日）', '', '', '', '', 0, 0, 1, 1, '2016-12-07 16:34:09', '2016-12-07 16:34:09'),
    (64, '4_1_r_20161207_1481096049273604374', 4, 1, 1, 1, 1, 'http://www.oricon.co.jp/news/2082607/', 'ecf3e63f47119b3bc5539b2a4daa2094_1481096049', '乃木坂46橋本奈々未、誕生日に卒業＆引退　5周年ライブはさいたま3days', '', '<img src=\"http://news.oricon.co.jp/feed/images/size640wh/20161206_212002_size640wh_1541.jpg\" title=\"乃木坂46がクリスマスライブ初日に橋本奈々未（中央）の卒業コンサートを発表（C）乃木坂46LLC\" alt=\"乃木坂46がクリスマスライブ初日に橋本奈々未（中央）の卒業コンサートを発表（C）乃木坂46LLC\" ><br />\n　人気アイドルグループ・乃木坂46が6日、東京・日本武道館で年末恒例のクリスマスライブ『Merry Xmas Show 2016』5daysの初日公演を開催した。ライブ終盤には、来年2月20～22日にさいたまスーパーアリーナでデビュー5周年を記念したバースデーライブ3days公演を行い、初日は橋本奈々未の卒業コンサートを兼ねることが発表された。<br />\n<br />\n　2012年2月22日にシングル「ぐるぐるカーテン」でデビューした乃木坂46は、来年2月で5周年。当初からレギュラー番組のMCを担当しているバナナマンらが映像でメッセージを寄せるなかで、設楽統が「来年のバースデーライブ、さいたまスーパーアリーナでやるんだってね。すごいね」とスクリーン越しに発表。事前に知らされていなかったメンバーは、観客と一緒になって興奮し、設楽は「ごめんね、我々のほうが先に知ってて」とにんまりして笑わせた。<br />\n<br />\n　「5th YEAR BIRTHDAY LIVE」は、2月20・21・22日の3日間、さいたまスーパーアリーナで開催され、計10万人を動員予定。初日に24歳の誕生日を迎える橋本は、同日に行われる卒業コンサートをもって芸能界を引退する。橋本は「卒業コンサートを誕生日にやらせてもらえることになりました。ステージで皆さんにお会いするのもそれが最後になるんです。調整して来ていただけたらうれしいです」とあいさつした。<br />\n<br />\n　この日武道館で行われたのは、少し早いクリスマスライブ。キャプテンの桜井玲香が客席を埋め尽くした1万2000人の観客に「皆さん、メリークリスマース！」と叫び「まだ2週間以上先なんですけどね」と笑いながらも「初めて選抜単独でライブをやるんですよ。グループ初の企画とかもやったりしますので、楽しんでいってください」と呼びかけた。<br />\n<br />\n　人気曲「君の名は希望」「制服のマネキン」のほか、「クリスマスメドレー」などを披露。新企画パートでは「1人1曲プロデュース」と題し、各メンバーが好きな楽曲を選び、衣装や演出を自己プロデュース。西野七瀬、星野みなみ、生田絵梨花、秋元真夏らがそれぞれの個性を発揮した演出で沸かせた。さらには、メンバーにだまされた橋本が北海道弁で「なまらメリークリスマス！」と呼びかけるとともに一人で投げキッスするはめとなり、ふくれっ面をしながら赤面し、大歓声があがった。<br />\n<br />\n　恒例のクリスマスライブはグループの勢いを象徴するように、前年の日本武道館2daysから5日間連続公演に規模を拡大。初日と3日目は16thシングル「サヨナラの意味」の選抜メンバー19人、2日目と4日目はアンダーメンバー、最終日は9月4日に加入したばかりの3期生12人をお披露目する「お見立て会」が行われる。チケットは5日とも即完売し、計6万人を動員する。<br />\n<br />\n■乃木坂46「5th YEAR BIRTHDAY LIVE」<br />\n▽2017年<br />\n2月20日：5th YEAR BIRTHDAY LIVE Day1　～橋本奈々未卒業コンサート～<br />\n2月21日：5th YEAR BIRTHDAY LIVE Day2<br />\n2月22日：5th YEAR BIRTHDAY LIVE Day3<br />\n※橋本は、2・3日目は出演せず', 1, '2016-12-06 21:14:04', '2017-02-04 21:14:04', NULL, NULL, NULL, 0, 'http://www.oricon.co.jp/news/2000936/photo/7/?anc=015', '＜貴重写真＞4年前…オーディション時の橋本奈々未', 'http://www.oricon.co.jp/news/2063083/photo/1/?anc=015', '【写真】橋本奈々未、肩出しワンピでオトナの色気披露', 'http://joshiplus.jp/fashion/newsphoto/2078709/1/?cat_id=OBT_1610202116&anc=015', '【写真】乃木坂・白石麻衣、三毛ネコメイクで“まいにゃん”に変身', '', '', '', '', 0, 0, 1, 1, '2016-12-07 16:34:09', '2016-12-07 16:34:09'),
    (65, '4_1_r_20161207_1481096049352612198', 4, 1, 1, 1, 1, 'http://www.oricon.co.jp/news/2082645/', '15b7bab8ceb71cc1caddcf6eea70aa71_1481096049', '「スター・ウォーズ」新メンバーが来日　空港にファン300人が出迎え', '', '<img src=\"http://news.oricon.co.jp/feed/images/size640wh/20161206_221737_size640wh_2900.jpg\" title=\"映画『ローグ・ワン／スター・ウォーズ・ストーリー』（12月16日公開）のプロモーションのため来日したフェリシティ・ジョーンズ\" alt=\"映画『ローグ・ワン／スター・ウォーズ・ストーリー』（12月16日公開）のプロモーションのため来日したフェリシティ・ジョーンズ\" ><br />\n　「スター・ウォーズ」最新作『ローグ・ワン／スター・ウォーズ・ストーリー』（12月16日公開）の主人公ジン役フェリシティ・ジョーンズ、情報将校のキャシアン役ディエゴ・ルナ、そしてギャレス・エドワーズ監督が6日、プロモーションのため来日。千葉・成田国際空港に到着したフェリシティたちを約300人ものファンが出迎えた。<br />\n<br />\n　到着出口にはシリーズの人気キャラクター、ストームトルーパーのマスクや反乱軍のヘルメットをかぶった人なども見られ、朝から9時間以上も待っていたという熱心なファンも。<br />\n<br />\n　フェリシティは、プロモーションでは初来日。彼女が笑顔で手を振るだけで甲高い歓声が上がった。映画『GODZILLA ゴジラ』のプロモーション以来、2年ぶりのエドワーズ監督は「とても光栄なことだと思っているよ。日本の映画、特に黒澤明の『隠し砦の三悪人』は、ジョージ・ルーカスも影響を受けた『スター・ウォーズ』の原点のようなものだからね。シリーズの故郷ともいえる日本に本作を持って来られて、とてもうれしいよ」とコメント。続けて、「ぜひ映画を観に行ってください！　もし、観てくれなかったら帝国軍がデス・スターで破壊しにきますよ！」と茶目っ気を見せて、ファンを喜ばせた。<br />\n<br />\n　9年ぶりに来日を果たしたというディエゴは、長時間のフライトの疲れも見せず、ファンサービスに応じ、「ありがとう！　日本に来られてすごくうれしいよ。ファンの皆さんに会えたことも最高だ。この3日間がすごく楽しみだよ！」と、語った。<br />\n<br />\n　3人は滞在中、記者会見、レッドカーペットイベント、特別映像上映会などに参加する予定となっている。<br />\n<br />\n　同作は、ジョージ・ルーカス氏が生み出した銀河宇宙を舞台にした壮大な叙事詩、「スター・ウォーズ」シリーズ初のアナザーストーリー。『スター・ウォーズ　エピソード4／新たなる希望』（1977年）つながる物語で、主人公ジンを中心に集まった反乱軍の寄せ集めチーム＜ローグ・ワン＞のメンバーが、究極兵器デス・スターの設計図を奪うミッションに挑む。', 1, '2016-12-06 22:22:17', '2017-02-04 22:22:17', NULL, NULL, NULL, 0, 'http://www.oricon.co.jp/news/2082382/?anc=014', '『ローグ・ワン』“盲目の戦士”はドニー・イェンのアイデアだった （16年12月02日）', 'http://www.oricon.co.jp/news/2081581/?anc=014', 'スター・ウォーズ最新作　寄せ集め「ローグ・ワン」6人のメンバーたち （16年11月17日）', 'http://www.oricon.co.jp/news/2081291/?anc=014', 'SWシリーズ新作『ローグ・ワン』　日本向け予告編にはフォースがいっぱい （16年11月11日）', '', '', '', '', 0, 0, 1, 1, '2016-12-07 16:34:09', '2016-12-07 16:34:09'),
    (66, '4_1_r_20161207_1481096049553729224', 4, 1, 1, 1, 1, 'http://www.oricon.co.jp/news/2082608/', 'e916c3caab7c363b54d6c1e4dd5b8ab2_1481096049', '綾小路翔＆北方謙三、吉川晃司の武勇伝トークで興奮「前田日明と殴り合い」', '', '<img src=\"http://news.oricon.co.jp/feed/images/size640wh/20161206_214059_size640wh_8405.jpg\" title=\"熱いトークを展開した（左から）綾小路翔、北方謙三 （C）ORICON NewS inc.\" alt=\"熱いトークを展開した（左から）綾小路翔、北方謙三 （C）ORICON NewS inc.\" ><br />\n　ロックバンド・氣志團の綾小路翔が6日、都内で行われた集英社文庫『岳飛伝』刊行開始記念トークライブ「荒くれ者たちへの憧れ－ロックンロール梁山泊－」に登場。同書の作者・北方謙三氏と、小説や音楽をテーマに熱のこもったトークを繰り広げた。<br />\n<br />\n　同書は、累計発行部数950万部を突破した北方氏の「大水滸伝」シリーズの最新作。腐敗混濁の世を糺（ただ）す「替天行道（たいてんぎょうどう）」の志のもと、国に戦いを挑む漢たちの物語を描く。<br />\n<br />\n　「大水滸伝」シリーズの大ファンだという綾小路は、初対面の北方氏を前に「いつか、お会いしたいと思っていたんですけど、やっぱり緊張しますね」と恐縮。一方、テンガロンハットを被って登場した北方氏は「頭で負けたくなかったからね」と茶目っ気たっぷりに話しながら「けっこう聴いていますよ。『俺んとこ　こないか？』『かかってこいや　喧嘩上等！』だよね。あなた方の音楽は、ツッパッたりしながらも本当はやさしい。聴いた時に清々しい気持ちになりますね」と絶賛した。<br />\n<br />\n　その後は、共通の知人・吉川晃司にまつわるトークで大盛り上がり。北方氏が「『水滸伝』はロックンロール。弟分に吉川晃司っていうのがいるんだけど、アイツが『解説を書きたいです』って言うんだよ。その解説が全然文章になってなかったんだけど、何か伝わってくる。それが、表現なんだよ」とニヤリと笑うと、綾小路も「お酒の席で、格闘家の前田日明さん、高田延彦さんと（タイマンで）殴り合いをして、2回ともやられていますから」と吉川の武勇伝エピソードを披露した。<br />\n<br />\n　北方氏が続けて「吉川が、押さえつけても『すみません』って言わないから、もっとギュッとされて…」と事のてん末を説明した上で「でもね、そんな前田日明が泣かされた現場を見たことがあるんだよ」と新たな逸話を披露。「泣かしたのは山田詠美っていう女流作家で、言い合いをしていると、彼女が立ち上がって『アンタ、男よね？　それが女に手を出したわねー』って言って、30発くらい叩いて。前田はジッとしていたんだけど、しばらくすると『ウッ…コノヤロー』って泣いちゃったんだよ。この話はするなって言われたんだけどね」といたずらっぽく笑った。<br />\n<br />\n　最後は再び吉川の話に戻り、綾小路が「吉川さんは『前田は強いけど、高田はそうでもないな』って言っていましたよ。水球をやってらっしゃったので『水の中だったら、ヒクソン・グレイシーにも勝てる』とも言っていました」と後日談を紹介。北方氏も「アイツ、そんなこと言っていたの？」と驚きながら「アイツは首から下は強いけど、締められたら一発で終わりじゃない？」と愛のあるイジりを見せていた。', 1, '2016-12-06 21:46:35', '2017-02-04 21:46:35', NULL, NULL, NULL, 0, 'http://www.oricon.co.jp/news/2066848/photo/2/?anc=148', '【写真】綾小路翔、ノーリーゼント＆すっぴんに「自分が一番衝撃」', 'http://www.oricon.co.jp/news/2066486/?anc=148', '綾小路翔＆まちゃまちゃ、中学の同級生が知られざる過去を暴露 （16年02月07日）', 'http://www.oricon.co.jp/news/2071144/?anc=148', 'ヒャダイン＆綾小路翔、アイドル審査員に悲鳴「不眠不休の覚悟で」 （16年05月05日）', '', '', '', '', 0, 0, 1, 1, '2016-12-07 16:34:10', '2016-12-07 16:34:10'),
    (67, '4_1_r_20161207_1481096049649938148', 4, 1, 1, 1, 1, 'http://www.oricon.co.jp/news/2082604/', '0b2abfd6b9c2db828d3e6b79dc2f0a1c_1481096049', '松坂桃李は縁結びの神様？　相手役女優の結婚続くも「自分にいいことはまったくない」', '', '<img src=\"http://news.oricon.co.jp/feed/images/size640wh/20161206_192213_size640wh_8707.jpg\" title=\"“キセキ”にまつわるエピソードを語った（左から）松坂桃李、菅田将暉 （C）ORICON NewS inc.\" alt=\"“キセキ”にまつわるエピソードを語った（左から）松坂桃李、菅田将暉 （C）ORICON NewS inc.\" ><br />\n　俳優の松坂桃李（28）が6日、都内で行われた映画『キセキ －あの日のソビト－』（2017年1月28日公開）の完成披露舞台あいさつに出席。タイトルにちなみ、“キセキ”にまつわるエピソードを聞かれた松坂は「相手役をやった女優さんがことごとく電撃結婚している」と話し、「基本的に自分にいいことはまったくない。向こうがどんどん幸せになっていく」と苦笑した。<br />\n<br />\n　松坂がフリップで出した“結婚”の文字に会場がざわつき、菅田将暉（23）ら共演者たちが「きのうの有田さんパターンですか」と、くりぃむしちゅー・有田哲平と同じく、突然結婚発表するかと思いきや、「『梅ちゃん先生』の堀北真希さんから始まり、最近では（『サイレーン』で共演した）木村文乃さんですね」と、相手役を演じた女優たちが結婚したと説明。<br />\n<br />\n　菅田が「縁結びの神なんだね」と驚くと、「基本的に自分にいいことはまったくない」と嘆き、「木村文乃さんに関しては、（明石家）さんまさんからご飯のセッティングを頼まれていたのに、結婚してしまったので、お会いしたとき謝ろうって思っています」と裏話も告白した。<br />\n<br />\n　同作は、覆面ボーカルグループ・GReeeeNの代表曲「キセキ」誕生秘話を映画化。歯医者と歌手という2つの夢を追いかける兄弟とその家族、仲間たちの青春ドラマを描く。<br />\n<br />\n　歌うシーンはないと聞いていたものの、劇中で歌声を披露している松坂は「騙された。恥ずかしかったです」と照れ笑い。グリーンボーイズとしてCDデビューが決定している菅田、横浜流星（20）、成田凌（23）、杉野遥亮（21）の横で「僕はCD出しません。（歌うのは）今回が最後です」と強調していた。<br />\n<br />\n　そのほか、忽那汐里（23）、兼重淳監督（49）が出席した。<br />\n<br /><br />\n<iframe width=\"640\" height=\"360\" src=\"https://www.youtube.com/embed/_LyjGKpiDSk\" frameborder=\"0\" allowfullscreen></iframe>', 1, '2016-12-06 19:25:03', '2017-02-04 19:25:03', NULL, NULL, NULL, 0, 'http://www.oricon.co.jp/news/2082604/photo/4/?anc=115', '【イベント写真】仲良さそうにじゃれる松坂桃李＆菅田将暉', 'http://www.oricon.co.jp/news/2080797/full/?anc=115#vk', '【動画】菅田将暉たちが歌う「キセキ」が流れる予告編', 'http://www.oricon.co.jp/news/2079802/full/?anc=115#vk', '【動画】グリーンボーイズが「道」を歌う本編特別映像', '', '', '', '', 0, 0, 1, 1, '2016-12-07 16:34:10', '2016-12-08 17:25:14'),
    (68, '4_1_r_20161207_1481096049748412990', 4, 1, 1, 1, 1, 'http://www.oricon.co.jp/news/2082642/', '575e01c2507fd4ce03341b087b3b9d1f_1481096049', '元ほっしゃん。星田英利が引退発言を撤回「ひたむきに頑張ります」', '', '<img src=\"http://news.oricon.co.jp/feed/images/size640wh/20161206_174851_size640wh_4534.jpg\" title=\"星田英利 （C）ORICON NewS inc.\" alt=\"星田英利 （C）ORICON NewS inc.\" ><br />\n　お笑い芸人の星田英利（旧芸名：ほっしゃん。）が6日夜、自身のインスタグラムを更新し、同日朝に示唆した芸能界からの引退を撤回した。<br />\n<br />\n　星田は、今朝方インスタで「デビューからお世話になった大先輩方々を裏切る、っていうか、お世話になり過ぎて、その方々にご相談できへんかったけど、年内の引退を夏からずっと会社と相談してて、そうしようかなと思います。27年。…凄い」と投稿。さらに「あ、今やってる仕事は、3月までやから、それは絶対、手ぇ抜かんと死ぬ気でやるから」と芸能界からの引退を示唆し、その後、該当の投稿を削除していた。<br />\n<br />\n　同日夜に再びインスタを更新した星田は、削除した文面の写真を再度アップ。続けて「心配かけてすみません。いろいろ考えましたが、続けさせてもらおうと思います。ひたむきに頑張ります」と記し、騒動を謝罪した。', 1, '2016-12-06 20:39:05', '2017-02-04 20:39:05', NULL, NULL, NULL, 0, 'http://www.oricon.co.jp/news/2034718/?anc=112', 'ほっしゃん。浮気していた過去を告白 （14年03月05日）', 'http://www.oricon.co.jp/news/2040669/?anc=112', 'ほっしゃん。誕生日に本名「星田英利」に改名 （14年08月06日）', 'http://www.oricon.co.jp/news/2013580/?anc=112', '尾野真千子とデート報道のほっしゃん。「嫁にも怒られてない」 （12年06月28日）', '', '', '', '', 0, 0, 1, 1, '2016-12-07 16:34:10', '2016-12-08 17:25:09'),
    (69, '4_1_r_20161207_1481096049869828981', 4, 1, 1, 1, 1, 'http://www.oricon.co.jp/news/2082631/', '1362c8c66360ec52ca8570ddf9e45a80_1481096049', '鷲尾春果が妊娠8ヶ月を報告　日テレ・辻岡アナ2児のパパに', '', '<img src=\"http://news.oricon.co.jp/feed/images/size640wh/20161206_173948_size640wh_4081.jpg\" title=\"鷲尾春果 （C）ORICON NewS inc.\" alt=\"鷲尾春果 （C）ORICON NewS inc.\" ><br />\n　日本テレビ・辻岡義堂アナウンサー（30）の妻でキャスターの鷲尾春果（30）が6日、自身のブログを更新し、第2子を妊娠したことを発表した。<br />\n<br />\n　「30歳を迎えた今日。私事ではありますが、家族が増えることになりました」とつづった鷲尾は「育児をしながらの悪阻（つわり）や、体調が落ち着かない日々に、娘の時とは違う戸惑いを感じることもありましたが、今は4人家族になることを楽しみに、毎日過ごしています」と現状を報告。<br />\n<br />\n　「お医者様の話だと、赤ちゃんは随分スクスクと育ってくれていて、今の段階ではかなりのビッグベビーだそうです」と明かし「残り2カ月ほど。。。ビッグベビーが安心して産まれてこられるよう、娘との時間を大切にしながら、家族で準備をしていきたいと思います」とブログを結んでいる。<br />\n<br />\n　鷲尾は、大学在学中にBSフジ『BSフジNEWS』の学生キャスターや女性ファッション誌『Ray』の読者モデルを務め、2007年のミス女子大生日本一を決める『Miss of Miss Campus Queen Contest』ではグランプリを受賞。『Oha！4 NEWS LIVE』のほか、テレビ神奈川系『KICK OFF F・Marinos』のMCなどを務めた。<br />\n<br />\n　辻岡アナは同局のスポーツ中継のほか『Oha！4 NEWS LIVE』『ズームイン!!サタデー』『メレンゲの気持ち』などを担当。2人は2011年9月に結婚。14年8月に第1子女児が誕生している。', 1, '2016-12-06 17:45:05', '2017-02-04 17:45:05', NULL, NULL, NULL, 0, 'http://www.oricon.co.jp/prof/1011889/?anc=112', '■鷲尾春果のプロフィール　過去にはミスキャンパスGPも', 'http://beauty.oricon.co.jp/trend-culture/love/news/photo/2080274/2/?anc=112', '【写真】ゆず・北川と結婚　アヤパン、愛娘2人との3ショットを公開', 'http://www.oricon.co.jp/news/2080647/photo/1/?anc=112', '【写真】パパの顔に…ロンブー淳が愛娘を抱く微笑ましいショット', '', '', '', '', 0, 0, 1, 1, '2016-12-07 16:34:10', '2016-12-07 16:34:10'),
    (70, '4_1_r_20161207_1481096049963888022', 4, 1, 1, 1, 1, 'http://www.oricon.co.jp/news/2082639/', '5696a026caf7b3f1fe27d9825a725a94_1481096049', '内海桂子師匠、チャンス青木さん偲ぶ　若者への漫才指導と伝授「功績大である」', '', '<img src=\"http://news.oricon.co.jp/feed/images/size640wh/20161206_195736_size640wh_1493.jpg\" title=\"内海桂子師匠　（C）ORICON NewS inc.\" alt=\"内海桂子師匠　（C）ORICON NewS inc.\" ><br />\n　女性漫才師で女優の内海桂子師匠（94）が6日、自身のツイッターを更新。漫談家のチャンス青木さん（享年72）の訃報を受け「長年若者に漫才を指導して漫才師の立ち振舞いを伝授したのは誠に功績大である」と讃えた。<br />\n<br />\n　青木さんと同じく漫才協会に所属する内海師匠は「漫才協会の若手指南役チャンス青木の突然の訃報に戸惑っている」とつらい胸中を吐露。「つい先日東洋館で銘鳥銘木をやったばかりなのに」とつづり「数年前に倒れてからも必死のリハビリに耐えて  舞台に復帰した根性は見上げたもの、と期待していたのに」と思いを記した。<br />\n<br />\n　内海師匠のほか、同じく、同協会所属で元Wコロンの木曽さんちゅう（46）もツイッターで「あのいつもハイパーなチャンス師匠が…信じられません。ご冥福を心よりお祈りいたします」と追悼し「僕が漫才協会に入って以来、ずっと変わらず気に掛けてくれて、本当にありがとうございました」と感謝の言葉をつづった。<br />\n<br />\n　青木さんは1972年に宮城けんじに弟子入りし、77年に斎藤チャンスと「Wチャンス」を結成。83年のコンビを解消し、2008年にチャンス青木に改名。漫才協会の若手の指導役として知られ、同協会の元理事。<br />\n<br />\n　葬儀告別式は、親族の意向で近親者のみで12月5日に執り行われた。お別れの会は来年2月以降、親族、出身のW一門関係者を中心に行う予定。', 1, '2016-12-06 20:12:02', '2017-02-04 20:12:02', NULL, NULL, NULL, 0, 'http://www.oricon.co.jp/news/2057093/full/?anc=112', '俳優・泉政行さんが死去　35歳　『仮面ライダー555』『科捜研の女』など出演', 'http://www.oricon.co.jp/news/2079756/?anc=112', 'ブンブンサテライツ・川島道行さんが死去　妻・須藤理彩も追悼コメント （16年10月11日）', 'http://www.oricon.co.jp/news/2064055/?anc=112', '作詞家・岡本おさみさんが死去　「旅の宿」「襟裳岬」など　 （15年12月17日）', '', '', '', '', 0, 0, 1, 1, '2016-12-07 16:34:10', '2016-12-07 16:34:10'),
    (71, '4_1_r_20161207_1481096050015119991', 4, 1, 1, 1, 1, 'http://www.oricon.co.jp/news/2082603/', '6d81196a6a9a4658a315e6d3b7edaebb_1481096050', '有働由美子アナ「NHKに勤めていよかったわぁ」と感嘆した理由は？', '', '<img src=\"http://news.oricon.co.jp/feed/images/size640wh/20161206_193211_size640wh_6873.jpg\" title=\"（左から）有働由美子、鈴木おさむ、千原ジュニア （C）ORICON NewS inc.\" alt=\"（左から）有働由美子、鈴木おさむ、千原ジュニア （C）ORICON NewS inc.\" ><br />\n　NHKの有働由美子アナウンサーが6日、東京・渋谷のNHKで千原ジュニア、カンニング竹山らと取材に応じ、「NHKに勤めていよかったわぁ」と感嘆の声をあげる場面があった。<br />\n<br />\n　有働アナは、21日に総合テレビで放送される特番『ココがズレてる健常者　障害者100人がモノ申す』（後10：30～11：15）の司会として収録に参加。番組は、Eテレで放送中の障害者情報バラエティー『バリバラ』内で提案された企画を発展させたもので、「健常者が良かれと思ってやっている行動が、実は当事者からすると迷惑なこと」を障害者100人がぶちまける前代未聞のバリアフリー・エンターテインメント。<br />\n<br />\n　発案者で、有働アナとともに司会を務めた放送作家の鈴木おさむ氏は「このような番組を年末に、総合テレビで放送することがすばらしい」と絶賛し、有働アナも身内ながら感動して先の発言につながった。<br />\n<br />\n　通常の『バリバラ』の視聴率は0.3％程度で、レギュラー出演している玉木幸則氏（脳性まひ）は「Eテレで福祉番組として放送しているだけでは限界がある。皆で考えていかなければいけないことだから（より多くの人に番組を観てもらえるように）総合でも定期的にやってほしい」と今回の特番を歓迎。<br />\n<br />\n　収録には、肉体的に、知的に、精神的にさまざまな障害を持つ人たちが、この機会に自分の意見を主張しようと、やる気満々で集まり、活発なトークが展開。発言者を指名する役割を担った有働アナは「入局から25年間経って、いろいろ経験したんですけど、これほどあたふたしたことはないな、アナウンサーとして気をつけなければならない文言が、皆さんとのやりとりの中で飛んでしまいまして、いろんな汗をかきながらやらせていただきました」と、反省が先に立ったが、鈴木氏は「観れば勉強になるし、放送されたらSNSを騒がせることになりそう」と手応え十分に語っていた。<br />\n<br />\n　バイク事故で負傷し、車椅子で生活したこともあるジュニアも「腫れ物にさわれ、ということですね。そこからはじまることがいっぱいあるのかな。知らないことだからけだった」と感想を述べた。知らなかったことの一つに、緊急連絡先や障害特性にあった支援を受けられるような情報が記載されたヘルプカードがあり、ジュニアは「すぐにでも普及させるべき」と話していた。', 1, '2016-12-06 19:40:01', '2017-02-04 19:40:01', NULL, NULL, NULL, 0, 'http://www.oricon.co.jp/news/2076078/photo/1/?anc=014', '【写真】新婚…小島よしお＆美人妻・あいさんの微笑ましい2ショット', 'http://www.oricon.co.jp/photo/590/43745/?anc=014', '【写真】身体にリボン…過激的なしょこたん', 'http://www.oricon.co.jp/news/2060617/photo/4/?anc=014', '【写真】キュート！真っ赤なメイド服を披露したしょこたん', '', '', '', '', 0, 0, 1, 1, '2016-12-07 16:34:10', '2016-12-07 16:34:10'),
    (72, '4_1_r_20161207_1481096050180713100', 4, 1, 1, 1, 1, 'http://www.oricon.co.jp/news/2082606/', '036e396c3cb474443a3d15ab11b63ecd_1481096050', '長谷川博己、結婚質問に笑顔も無言', '', '<img src=\"http://news.oricon.co.jp/feed/images/size640wh/20161206_193125_size640wh_8240.jpg\" title=\"結婚質問に無言を貫いた長谷川博己 （C）ORICON NewS inc.\" alt=\"結婚質問に無言を貫いた長谷川博己 （C）ORICON NewS inc.\" ><br />\n　俳優・長谷川博己（39）が6日、都内で開催されたクリスマススペシャルイベント『リアル・リッツ・パーティー』に出席した。かねてから交際が報じられている女優・鈴木京香（48）との結婚間近とうわさされるなか、イベント中はカメラマンをステージに近づかせない厳戒態勢。去り際、駆け寄った報道陣から結婚について追及を受けると立ち止まって笑顔で会釈するも、無言のままその場を後にした。<br />\n<br />\n　イベントでは、1988年から28年に渡ってCMキャラクターを務めた女優・沢口靖子（51）に代わりモンテリーズ・ジャパンのビスケット『リッツ』の新しい顔となった長谷川が、招待された女性客50人とともに“リアル・リッツパーティー”を開催。クリスマスオーナメントの飾り付けも実施し「久しぶりに飾り付けするのでワクワクします」と楽しんだ。<br />\n<br />\n　また、クリスマスの思い出を聞かれた長谷川は「小学生の時、みんながパーティーとかやっているなかレンタルショップでビデオを3、4本借りてみていた」と特に満喫したことはないようで「最近は仕事ばかり。（今年も）残念ながら仕事。現場でリッツを持っていって（パーティーを）やれればいいな」と話していた。', 1, '2016-12-06 19:35:02', '2017-02-04 19:35:02', NULL, NULL, NULL, 0, 'http://www.oricon.co.jp/news/2077961/photo/3/?anc=096', '【新CMカット】“いまどきのリッツパーティー”を楽しむ長谷川博己', 'http://www.oricon.co.jp/news/2077835/photo/2/?anc=096', '【画像】新CMでパーティーはしないが…うっとり笑顔の沢口靖子', 'http://www.oricon.co.jp/news/2077835/?anc=096', '沢口靖子、『リッツ』後継商品のCMではパーティーせず　リビングで一人で… （CM動画あり）', '', '', '', '', 0, 0, 1, 1, '2016-12-07 16:34:10', '2016-12-07 16:34:10'),
    (73, '4_1_r_20161207_1481096050285105265', 4, 1, 1, 1, 1, 'http://www.oricon.co.jp/news/2082602/', '92255568adebca3288282bd333ad43a9_1481096050', '橋本マナミ「脱いじゃえば？」　“後輩”MYNAMEに助言', '', '<img src=\"http://news.oricon.co.jp/feed/images/size640wh/20161206_195540_size640wh_1573.jpg\" title=\"MYNAMEにアドバイスを送った橋本マナミ（右） （C）ORICON NewS inc.\" alt=\"MYNAMEにアドバイスを送った橋本マナミ（右） （C）ORICON NewS inc.\" ><br />\n　K-POPの5人組ボーイズグループ・MYNAMEが6日、都内で日本オリジナルアルバム『ALIVE～Always In Your Heart～』の発売記念イベントを行い、タレントの橋本マナミがゲスト出演した。<br />\n<br />\n　MYNAMEと事務所の先輩後輩関係にあたる橋本は“応援隊長”として駆けつけた。パフォーマンスを間近で見て「皆さんすごくかっこよくて、歌もうまくて、セクシー」と絶賛する一方で、芸能界の先輩として「一つだけ足りていたいのは洋服を着てるということ。体を鍛えてるし、見せてもいいと思う。脱いじゃえばいいんじゃないですか？」と助言を送った。<br />\n<br />\n　橋本のアイデアに駆けつけた女性ファンも「フゥ～！」「いいね！」と大盛り上がり。メンバーたちは「仕事の種類が違くないですか？」と困惑しつつ、セヨンは「先輩が言ってくれたから次からは脱げるように考えてみます」と笑顔で話していた。<br />\n<br />\n　あす7日に発売される新アルバムは、メンバーのコヌ、インス、ジュンQが作詞・作曲した楽曲も初めて収録され、これまでの活動の集大成となる新曲11曲がラインアップ。メンバー別ソロ・ジャケット盤（全5種）にも、アルバムとは別にメンバーが作詞・作曲したソロ楽曲や、セヨン＆ジュンQのデュオ楽曲も収録される。<br />\n<br />\n　この日のイベントでは、アルバム収録曲から「Alive or Fallen」、「RAIN」、「FIREWORK」を披露し、大勢のファンは終始熱狂。メンバーたちは「作詞作曲、ダンスのフリまでプロデュースした作品です。これからもいっぱい作るので応援よろしくお願いします！」と元気いっぱいに呼びかけていた。', 1, '2016-12-06 20:00:21', '2017-02-04 20:00:21', NULL, NULL, NULL, 0, 'http://www.oricon.co.jp/news/2082524/photo/9/?anc=013', '【写真】橋本マナミ、バーカウンター上で女豹ポーズ', 'http://www.oricon.co.jp/photo/1805/122333/?anc=013', '【写真】“寝そべる”だけで色気あふれ出す橋本マナミ', 'http://www.oricon.co.jp/news/2060555/photo/6/?anc=013', '【写真】橋本マナミの“接写”ショット…美しいボディ披露', '', '', '', '', 0, 0, 1, 1, '2016-12-07 16:34:10', '2016-12-07 16:34:10'),
    (74, '4_1_r_20161207_1481096050403857724', 4, 1, 1, 1, 1, 'http://www.oricon.co.jp/news/2082633/', '31b9d660894a15e390372f137cfedf86_1481096050', 'サンタクロースが指南　Xmasギフトは「詰め込みスタイル」がベスト', '', '<img src=\"http://news.oricon.co.jp/feed/images/size640wh/20161206_195458_size640wh_4363.jpg\" title=\"Afternoon Tea LIVINGのおすすめクリスマスギフト\" alt=\"Afternoon Tea LIVINGのおすすめクリスマスギフト\" ><br />\n　日常に使えてクリスマス気分も盛り上がる…。そんなクリスマスギフトのアイデアを、Afternoon Tea LIVINGが提案中。グリーンランド国際サンタクロース協会が公認しているサンタクロース・パラダイス山元さんとコラボレーションしている同企画。パラダイスさんによると、ワクワクするクリスマスギフトは“詰め込みスタイル”なんだそう。<br />\n<br />\n　「あれもこれも“ギュギュッと”詰め込むのが楽しい！」とアドバイスを贈るパラダイス山元さん。一点豪華主義も良いけれど、ひとつのボックスにクッキーやキャンディ、ハンドクリームやミラーなどが続々と出てくるサプライズ感は、楽しいクリスマスにピッタリ。<br />\n<br />\n　とはいえ、商品の選び方と組み合わせはセンスが問われるもの。そこで、同ショップではマグカップやブランケット、ハンドクリームセット、ボックスティー、クッキーなど、クリスマスギフトにピッタリな「サンタクロースおすすめギフト」を用意。クリスマス限定ラッピングや、店頭にフォトパネルを用意し、SNSに特定のハッシュタグをつけて写真を投稿したら非売品シールがもらえるキャンペーンも行っている。', 1, '2016-12-06 19:58:01', '2017-02-04 19:58:01', NULL, NULL, NULL, 0, 'http://beauty.oricon.co.jp/trend-culture/trend/photo/24/?cat_id=TKD&anc=017', '【写真まとめ】個性派続々　都内のイルミネーションをバーチャル体験', 'http://beauty.oricon.co.jp/trend-culture/trend/news/photo/2081792/1/?cat_id=TKD&anc=017', '＜写真＞あたり一面がブルーの世界　「青の洞窟」イルミの様子', 'http://beauty.oricon.co.jp/trend-culture/trend/news/2081520/?cat_id=TKD&anc=017', '恒例青色イルミからシャンパン風まで　東京ミッドタウンのイルミ （16年11月15日）', '', '', '', '', 0, 0, 1, 1, '2016-12-07 16:34:10', '2016-12-07 16:34:10'),
    (75, '4_1_r_20161207_1481096050522979261', 4, 1, 1, 1, 1, 'http://www.oricon.co.jp/news/2082638/', '2670e85526f66ab8d744aa20c41531cd_1481096050', 'ガイナックス、庵野監督「カラー」との訴訟騒動を謝罪', '', '<img src=\"http://news.oricon.co.jp/feed/images/size640wh/20161122_200911_size640wh_8304.jpg\" title=\"庵野秀明氏 （C）ORICON NewS inc.\" alt=\"庵野秀明氏 （C）ORICON NewS inc.\" ><br />\n　人気アニメ「エヴァンゲリオン」シリーズの庵野秀明監督（56）が経営するアニメ制作会社「カラー」が、かつて庵野監督が『新世紀エヴァンゲリオン』などを手がけた制作会社「ガイナックス」に対し、借入金など約1億円の支払いを求める訴訟を東京地裁立川支部におこした件に対して、ガイナックス側が6日、公式サイトでコメントを発表。「多大なるご心配をおかけしましたこと、誠に申し訳なく、深くお詫び申し上げます」と一連の騒動を謝罪した。<br />\n<br />\n　報道では、両社は庵野氏がガイナックス時代に手がけた作品を商品化し同社が収入を得た場合、一定のロイヤリティー（使用料）をカラー側が受け取る契約になっていた。しかしガイナックス側に多額の債務が発生し、貸付金など約1億円の支払いが滞る事態となり、カラー側が訴訟に踏み切ったと報じられていた。<br />\n<br />\n　ガイナックスは「このたびの裁判関連の報道につきましては、関係者の皆さまには、多大なるご心配をおかけしましたこと、誠に申し訳なく、深くお詫び申し上げます」と謝罪。続けて「米子をはじめ、福島、京都、新潟、それぞれで活動しております各社に対しても、資本関係がない中、少なからず不安を与えてしまったことも誠に遺憾に存じます」とつづっている。<br />\n<br />\n　今後について「現在弊社は、組織の再編、一部の業務整理、会社移転などを実行し、ガイナックス本来のあるべき姿に立ち戻り、企画プロデュースを中心とした企業として再生を行っております」と経営を建て直す方針を伝えるとともに「今後とも、皆さまに愛されるアニメーション作品を制作できるよう、一歩一歩前進してまいりますので、何卒、お力添えの程、よろしくお願い申し上げます」と呼びかけた。<br />\n<br />\n　ガイナックスは1984年に設立。庵野監督は同社で『新世紀エヴァンゲリオン』（1995年）のほか、『ふしぎの海のナディア』（1990年）、『トップをねらえ！』（1989年）などヒットアニメを手がけてきた。', 1, '2016-12-06 19:16:11', '2017-02-04 19:16:11', NULL, NULL, NULL, 0, 'http://www.oricon.co.jp/news/2081886/photo/2/?anc=164', '【カラー内覧会の様子】『エヴァンゲリオン』の貴重な資料も展示', 'http://www.oricon.co.jp/news/2078470/?anc=164', '庵野秀明氏、『シン・ゴジラ』続編に言及「僕はもういい」　長谷川博己は前向き （16年09月15日）', 'http://www.oricon.co.jp/news/2077463/?anc=164', '庵野秀明×鶴巻和哉、『龍の歯医者』BSプレミアムでテレビアニメ化 （16年08月26日）', '', '', '', '', 0, 0, 1, 1, '2016-12-07 16:34:11', '2016-12-07 16:34:11'),
    (76, '4_1_r_20161207_1481096050669014199', 4, 1, 1, 1, 1, 'http://www.oricon.co.jp/news/2082636/', 'fac3bd325b8e19fc040c0d914c9d1380_1481096050', '“和”のポップコーン！　ギャレット『抹茶黒蜜きなこ』が再登場', '', '<img src=\"http://news.oricon.co.jp/feed/images/size640wh/20161206_182205_size640wh_7578.jpg\" title=\"ギャレット『抹茶黒蜜きなこ』が再登場！\" alt=\"ギャレット『抹茶黒蜜きなこ』が再登場！\" ><br />\n　米・シカゴ生まれの老舗ポップコーンブランド「ギャレット ポップコーン ショップス」が、“和”の素材を使った日本限定フレーバー『抹茶黒蜜きなこ』を26日より販売。昨年人気を集めた和フレーバーが、パワーアップして再登場する。<br />\n<br />\n　同商品は、甘くてコクのある黒蜜と香り豊かなきなこを合わせた、和風テイストのポップコーン。相性抜群の組み合わせで、ポップコーンながら和スイーツのような味わいを楽しめる。2017年の干支・酉をあしらった紅白デザイン缶『2017 Eto缶』も同時発売。正月らしいデザインなので、年末年始の手土産にもぴったり。<br />\n<br />\n【商品詳細】<br />\n『抹茶黒蜜きなこ』（S：450円、M：710円、L：1060円、J：2100円、クォート：1400円、1ガロン：3300円、2ガロン：6000円）<br />\n『2017 Eto缶』（クォート：1130円～）<br />\n販売期間：2016年12月26日～2017年1月12日予定', 1, '2016-12-06 18:24:01', '2017-02-04 18:24:01', NULL, NULL, NULL, 0, 'http://joshiplus.jp/gourmet/news/2082597/?cat_id=OBT&anc=016', 'キャンドルみたいでキュート！　スタバのホリデースイーツ （16年12月06日）', 'http://joshiplus.jp/gourmet/news/2082537/?cat_id=OBT&anc=016', 'ポートランド発の大人ドーナツにホリデー限定フレーバー （16年12月05日）', 'http://joshiplus.jp/gourmet/news/2082425/?cat_id=OBT&anc=016', 'ロリポップキャンディーみたいなXmasケーキ！ （16年12月03日）', '', '', '', '', 0, 0, 1, 1, '2016-12-07 16:34:11', '2016-12-07 16:34:11'),
    (77, '4_1_r_20161207_1481096050778913944', 4, 1, 1, 1, 1, 'http://www.oricon.co.jp/news/2082632/', 'e9f510a2df782955b84036d92c242a62_1481096050', '引退示唆の星田英利、該当の投稿を削除', '', '<img src=\"http://news.oricon.co.jp/feed/images/size640wh/20161206_174851_size640wh_4534.jpg\" title=\"星田英利 （C）ORICON NewS inc.\" alt=\"星田英利 （C）ORICON NewS inc.\" ><br />\n　きょう6日にインスタグラムの投稿で来年3月をめどに芸能界引退を示唆したお笑い芸人の星田英利（旧芸名：ほっしゃん。）が同日、該当の投稿を削除した。<br />\n<br />\n　星田は6日、自身のインスタに「デビューからお世話になった大先輩方々を裏切る、っていうか、お世話になり過ぎて、その方々にご相談できへんかったけど、年内の引退を夏からずっと会社と相談してて、そうしようかなと思います。27年。・・・凄い」と投稿。さらに「あ、今やってる仕事は、3月までやから、それは絶対、手ぇ抜かんと死ぬ気でやるから」と続け、来年3月に芸能界からの引退を示唆した。<br />\n<br />\n　所属事務所は「事実確認中」としていたが、星田はこの日のうちに該当の投稿を削除。「3月まで～～」の投稿は残しており、「引退するのは寂しい」「ほっしゃんが辞めたら誰がうどん芸やるの？」「いなくなったら悲しいから引退じゃなく休業にしてほしい」などのコメントが寄せられている。<br />\n<br />\n　星田はこの日の午前中、曜日レギュラーを務める関西テレビ『ごきげんライフスタイル よ〜いドン！』に通常通り出演したが、引退には触れなかった。', 1, '2016-12-06 17:52:04', '2017-02-04 17:52:04', NULL, NULL, NULL, 0, 'http://www.oricon.co.jp/news/2034718/?anc=142', 'ほっしゃん。浮気していた過去を告白 （14年03月05日）', 'http://www.oricon.co.jp/news/2040669/?anc=142', 'ほっしゃん。誕生日に本名「星田英利」に改名 （14年08月06日）', 'http://www.oricon.co.jp/news/2013580/?anc=142', '尾野真千子とデート報道のほっしゃん。「嫁にも怒られてない」 （12年06月28日）', '', '', '', '', 0, 0, 1, 1, '2016-12-07 16:34:11', '2016-12-07 16:34:11'),
    (78, '4_1_r_20161207_1481096050792088409', 4, 1, 1, 1, 1, 'http://www.oricon.co.jp/news/2082599/', '2568d805c292626efac94abd30ad4a7a_1481096050', 'カトパン、自作ストーリー披露に照れ「家族を想って書きました」', '', '<img src=\"http://news.oricon.co.jp/feed/images/size640wh/20161206_154720_size640wh_2686.jpg\" title=\"自作のクリスマスストーリーを披露した加藤綾子アナ （C）ORICON NewS inc.\" alt=\"自作のクリスマスストーリーを披露した加藤綾子アナ （C）ORICON NewS inc.\" ><br />\n　フリーアナウンサーの加藤綾子（31）が6日、都内で行われた仏高級筆記具ブランド『ウォーターマン』のクリスマスコレボレーションイベントに出席した。<br />\n<br />\n　赤のエレガントなドレス姿で登場した加藤は、同ブランドの新作ペンをテーマにした自作のクリスマスストーリーを披露。ローズ色のペンで家族へのクリスマスメッセージをつづる内容で「家族を想って書きました。普段からちょっとしたことで気軽に『ありがとう』と言えるけど、改めて『いつもありがとう』と言えることも少ないので、いいきっかけになりました」と照れながら話した。<br />\n<br />\n　実際に同ブランドの新作ペンを手にすると「とてもいい。こんなにスラスラ書けるんだって。ほっそりとしたシルエットなので持ちやすいです」と絶賛。「入社して初めての誕生日に友人がペンをくれて、そのペンを日常から使うことで日常がキラキラ輝くような特別感はありました。一歩、大人の女性になれたなという気持ちでした」と局アナ時代の思い出を振り返っていた。<br />\n<br /><br />\n<iframe width=\"640\" height=\"360\" src=\"https://www.youtube.com/embed/km3n_kdICXg\" frameborder=\"0\" allowfullscreen></iframe>', 1, '2016-12-06 15:44:04', '2017-02-04 15:44:04', NULL, NULL, NULL, 0, 'http://www.oricon.co.jp/news/2082599/photo/2/?anc=013', '【全身ショット】赤のエレガントなドレス姿で登場した加藤綾子', 'http://www.oricon.co.jp/news/2081354/photo/1/?anc=013', '【写真】カトパン、はんなり着物姿で秋の京都を満喫　漫画誌グラビア初登場', 'http://www.oricon.co.jp/news/2069610/photo/1/?anc=013', '【写真】ウエディングドレス姿を披露したカトパン', '', '', '', '', 0, 0, 1, 1, '2016-12-07 16:34:11', '2016-12-07 16:34:11'),
    (79, '4_1_r_20161207_1481096050862507931', 4, 1, 1, 1, 1, 'http://www.oricon.co.jp/news/2082628/', 'bd16f4ea0fbe155a3dbd91aa84b88a6b_1481096050', '再生の道重さゆみ、ファンの支えに感謝　復帰への葛藤も告白「忘れられてたら」', '', '<img src=\"http://news.oricon.co.jp/feed/images/size640wh/20161125_154932_size640wh_8253.jpg\" title=\"道重さゆみ （C）ORICON NewS inc.\" alt=\"道重さゆみ （C）ORICON NewS inc.\" ><br />\n　来年春ごろから芸能活動を再開させることを発表した元・モーニング娘。の道重さゆみ（27）が6日、自身のブログを更新し「たくさんのいいね！、コメント、本当にありがとうございます」と感謝の言葉をつづった。<br />\n<br />\n　約2年間、芸能活動を無期限休養していた道重は「13歳から25歳までの約12年間、モーニング娘。に在籍し、それまでモーニング娘。が大好きという気持ちだけでやってきて、とにかくモーニング娘。が全てだったので、これから1人で何をしていくのか、したいのか、できるのか、、、休業中に自分自身と向き合いじっくりゆっくり考えていました」と“空白”の期間について告白。<br />\n<br />\n　その間に「たくさん考えた」といい「またみなさんの前で歌って踊りたい！！歌は苦手だけどやっぱり好き！そう思いました」と結論に至ったとし、復帰を決断。しかし一方で「正直な気持ちを話すと流れの早い芸能界、、忘れられてたら。。とか、ネガティブな気持ちからなかなか抜け出せませんでした」と葛藤があったことも明かした。<br />\n<br />\n　「そんな中、休業中にも関わらず、ブログに増え続けていくコメントを見たり、去年と今年の私のお誕生日に私の名前がツイッターのトレンド入りしてるのを見たり　そのほかにもたくさん！　休業している2年間もみなさんからの愛をたくさん感じていました」と支え続けてくれたファンに感謝した道重。<br />\n<br />\n　「勝手に休んでいつ復帰するかもわからないこんな私のことをずっと待ってると言ってくれたファンの方、ずっと大好きだと言ってくれたファンの方、何度も何度もみなさんの顔が頭に浮かんできました」と続け「休業中支えてくれたのも、道重再生を背中押してくれたのもファンのみなさんです！本当にありがとうございます」と重ねて感謝の言葉を記した。<br />\n<br />\n　先日の“再生”の発表から「想像以上に反響が大きくて、喜んでくれる方の声を聞いて、とってもとっても嬉しかったです」と素直な思いも明かし「早くみなさんに会いたいですっ◇◇◇◇」（◇＝ハートの絵文字）と気持ちをぶつけると「これからもよろしくお願いします」と呼びかけた。<br />\n<br />\n　道重は2002年12月に1万4000人が応募したモーニング娘。のオーディションに見事合格し、13歳でデビュー。03年には、田中れいなとともに“平成生まれ史上初”となる『NHK紅白歌合戦』出場を果たす。その後もモーニング娘。の一員として活躍を続け、2012年には8代目リーダーに就任。14年11月26日にモー娘。を卒業し、その後、芸能活動を無期限休養。先月26日にブログを通じて芸能活動を再開させることを発表した。', 1, '2016-12-06 17:01:07', '2017-02-04 17:01:07', NULL, NULL, NULL, 0, 'http://www.oricon.co.jp/photo/2012/129498/?anc=112', '【写真】まさに王道！ モー娘。時代のキュートな道重さゆみ', 'http://www.oricon.co.jp/photo/2012/129543/?anc=112', '【写真】道重さゆみ、写真集で美ボティを披露', 'http://www.oricon.co.jp/news/2014056/photo/2/?anc=112', '【写真】ゲームキャラになりきる道重さゆみのコスプレ姿', '', '', '', '', 0, 0, 1, 1, '2016-12-07 16:34:11', '2016-12-07 16:34:11'),
    (80, '4_1_r_20161207_1481096051031603607', 4, 1, 1, 1, 1, 'http://www.oricon.co.jp/news/2082627/', '5f5e7822a8f74396268f9ba8a816b2fd_1481096051', '安田大サーカス・HIRO、ハリウッドデビューを報告　ジャレッド・レト主演作に出演', '', '<img src=\"http://news.oricon.co.jp/feed/images/size640wh/20161206_165832_size640wh_4397.jpg\" title=\"安田大サーカスHIRO（左）がハリウッドデビュー　共演者の椎名桔平との2ショットを公開\" alt=\"安田大サーカスHIRO（左）がハリウッドデビュー　共演者の椎名桔平との2ショットを公開\" ><br />\n　お笑いトリオ・安田大サーカスのHIROが6日、自身のブログを更新し、来年に全米公開の映画『THE OUTSIDER』で俳優としてハリウッドデビューすることを報告した。<br />\n<br />\n　HIROは「私がハリウッドデビュー（笑）コレはマジで」と切り出しし、「チョイ役じゃ無くガッチリ出てます」とエキストラでの出演でないことを強調。共演者の椎名桔平との2ショット写真も公開した。<br />\n<br />\n　同作は、『スーサイド・スクワッド』でジョーカーを演じた米俳優のジャレッド・レトが主演し、マーチン・ピータ・サンフリト監督がメガホンを取るハリウッド作品。1954年頃の日本を舞台に、レト演じる元アメリカ兵のニックが、日本のヤクザたちと出会ったことをきっかけに、生きる意味や誇りを取り戻していくさまを描く。大阪の刑務所でニックと出会い兄弟の契りを交わすキヨシを浅野忠信が演じる。<br />\n<br />\n　HIROの突然のハリウッドデビュー報告に、ファンからは「衝撃っ!!すごくない!?」「マジで！スゴい！おめでとう！」「今から公開が待ち遠しいです！」など祝福のコメントが寄せられている。', 1, '2016-12-06 17:05:02', '2017-02-04 17:05:02', NULL, NULL, NULL, 0, 'http://www.oricon.co.jp/news/2070459/?anc=142', '安田大サーカスのHIRO、3ヶ月で20キロ減量「オシャレしたい」 （16年04月21日）', 'http://beauty.oricon.co.jp/trend-culture/trend/news/photo/72287/1/?anc=142', '【写真】安田大サーカス団長・安田裕己、美人妻さちさんとの2ショット', 'http://beauty.oricon.co.jp/trend-culture/trend/news/photo/76120/1/?anc=142', '【写真】安田大サーカス・団長安田が第1子愛娘を公開　名前は…', '', '', '', '', 0, 0, 1, 1, '2016-12-07 16:34:11', '2016-12-07 16:34:11'),
    (81, '4_1_r_20161207_1481096051130916492', 4, 1, 1, 1, 1, 'http://www.oricon.co.jp/news/2082600/', '54ab0c735d52e5b353bdf9c217b42bf9_1481096051', '新旧・大岡越前が白洲で対決　東山紀之、加藤剛との共演は「ご褒美」', '', '<img src=\"http://news.oricon.co.jp/feed/images/size640wh/20161206_162649_size640wh_2836.jpg\" title=\"約30年にわたり『大岡越前』に主演した加藤剛（左）と息子の加藤頼 （C）ORICON NewS inc.\" alt=\"約30年にわたり『大岡越前』に主演した加藤剛（左）と息子の加藤頼 （C）ORICON NewS inc.\" ><br />\n　俳優の東山紀之（50）が6日、東京・渋谷のNHKで行われたBS新春時代劇『大岡越前スペシャル　白洲に咲いた真実』（2017年1月3日　後7：30～8：43　BSプレミアム）の試写会に出席した。既報の通り本作では、新旧大岡越前の競演が実現。TBS系で約30年わたり放送された『大岡越前』に主演した加藤剛（78）が、物語の鍵を握る浪人役で出演し、東山演じる大岡忠相と白州で対決する。東山は「今回、ご褒美をいただけたのかな。楽しかった」と京都での撮影を振り返っていた。<br />\n<br />\n　加藤は、旧『大岡越前』シリーズの開始（1970年）から2006年3月に同作が完結するまで、江戸町奉行・大岡忠相を演じ続けた。その後、13年3月に東山を主演に迎え、新シリーズがNHKで復活。第3シーズンまで回を重ねている。<br />\n<br />\n　東山は「加藤剛さんの『大岡越前』を観て育った世代。中学校の修学旅行で京都の撮影所を訪れたときは、『大岡越前』のセットで大岡忠相を真似したこともありました。そんな自分が大岡忠相を演じることになるとは思ってもいなかったし、まさか加藤剛さんを白洲で裁く日がくるとは。今回、加藤さんと目を合わせてお芝居をさせてもらい、その精神を受け継ぐことができたらいいなと思いました」と話した。<br />\n<br />\n　10年ぶりに『大岡越前』の世界に戻ってきた加藤は「久しぶりの京都の撮影所で、なじみのスタッフにあたたかく迎えてもらってうれしかった」と笑みを浮かべ、「白州で裁かれるというのは初めての経験。白洲から見上げたとき、お奉行はこう見えるのか、本当のこと言わないといけない気になるものだな、と実感しました」。劇中では見事な刀さばきを披露しており、「剣の達人という設定でしたので、立ち回りが大変でした。まだできるんだと思いました」とニッコリ。「老人の役でしたので、メイクも工夫しました」と言って笑いを誘っていた。<br />\n<br />\n　新シリーズでは、パート1から加藤の息子で俳優の加藤頼（36）が、南町奉行の同心・片瀬堅太郎役でレギュラー出演しているが、今回、東山が父を裁く評定のシーンにも同席しており、「加藤家にとって、『大岡越前』は生活の一部のような作品でした。それを『少年隊のヒガシが演じるの？』と、正直、複雑な気持ちを抱いたこともありましたが、いまとなっては東山さんこそ、我らがお奉行。そういうタイミングで、父と共演できたことが、一役者としても、息子としても幸せな時間でした」と感無量な様子だった。<br />\n<br />\n　2017年は、実在した大岡忠相が江戸南町奉行に就任して300年の節目。新旧の大岡越前の競演、加藤剛と頼の親子共演、さらには、加藤主演の「大岡越前」シリーズから脚本を手がけ、今年1月に亡くなった脚本家・大西信行さんが最期に書き遺した作品と、さまざまな人たちの思いが詰まった作品となった。忠相の父・大岡忠高役の津川雅彦は「劇的な顔合わせで、劇的に誕生したこの作品が、劇的ではないけれど、正月にオンエアされるのはめでたいことだ」と話していた。<br />\n<br />\n　ほかに、忠相の妻・雪絵役のミムラ、忠相の母・妙役の松原智恵子も出席した。', 1, '2016-12-06 16:30:02', '2017-02-04 16:30:02', NULL, NULL, NULL, 0, 'http://www.oricon.co.jp/news/2079550/photo/1/?anc=014', '【場面カット】『大岡越前』にゲスト出演する加藤剛', 'http://www.oricon.co.jp/news/2079550/?anc=014', '東山紀之×加藤剛、新旧『大岡越前』が競演 （16年10月06日）', 'http://www.oricon.co.jp/news/2075714/?anc=014', '東山紀之、関ジャニの“ジャクソン・ステップ”に感嘆「ジャニーズなんだな」 （16年08月10日）', '', '', '', '', 0, 0, 1, 1, '2016-12-07 16:34:11', '2016-12-07 16:34:11'),
    (82, '4_1_r_20161207_1481096051233132848', 4, 1, 1, 1, 1, 'http://www.oricon.co.jp/news/2082625/', '3a2b7c9527600954de57e6d85eacb799_1481096051', '漫談家・チャンス青木さん心不全で死去　72歳', '', '<img src=\"http://news.oricon.co.jp/feed/images/size640wh/20161206_163822_size640wh_8678.jpg\" title=\"死去したチャンス青木さん\" alt=\"死去したチャンス青木さん\" ><br />\n　漫談家のチャンス青木さんが11月29日、心不全のため、都内自宅で死去した。72歳だった。6日、同協会の公式サイトで発表された。<br />\n<br />\n　青木さんは1972年に宮城けんじに弟子入りし、77年に斎藤チャンスと「Wチャンス」を結成。83年のコンビを解消し、2008年にチャンス青木に改名。漫才協会の若手の指導役として知られ、同協会の元理事。<br />\n<br />\n　葬儀告別式は、親族の意向で近親者のみで12月5日に執り行われた。お別れの会は来年2月以降、親族、出身のW一門関係者を中心に行う予定。', 1, '2016-12-06 16:41:36', '2017-02-04 16:41:36', NULL, NULL, NULL, 0, 'http://www.oricon.co.jp/news/2057093/full/?anc=112', '俳優・泉政行さんが死去　35歳　『仮面ライダー555』『科捜研の女』など出演', 'http://www.oricon.co.jp/news/2079756/?anc=112', 'ブンブンサテライツ・川島道行さんが死去　妻・須藤理彩も追悼コメント （16年10月11日）', 'http://www.oricon.co.jp/news/2064055/?anc=112', '作詞家・岡本おさみさんが死去　「旅の宿」「襟裳岬」など　 （15年12月17日）', '', '', '', '', 0, 0, 1, 1, '2016-12-07 16:34:11', '2016-12-07 16:34:11'),
    (83, '4_1_r_20161208_1481185504415024828', 4, 1, 1, 1, 1, 'https://mdpr.jp/news/1644054', '83459b4719d83ddb9f2a3f211e93a7ee_1481185504', '実はまだ「逃げ恥」見てない人、挙手！今からでも間に合う「逃げ恥5つの魅力」徹底解説', '', '【逃げるは恥だが役に立つ／モデルプレス＝12月8日】女優の新垣結衣が主演をつとめるTBS系ドラマ『逃げるは恥だが役に立つ』（毎週火曜よる10時　通称：逃げ恥）の勢いが止まらない。6日に放送された第9話で平均視聴率16.9％（ビデオリサーチ調べ、関東地区）と、またも自己最高を更新。そのブームはあらゆる方面に波及しているが、「実はまだ見てない！どうしてそんなに人気なの？」という人もいるはず。何故、ここまで視聴者の心を掴んで離さないのか、その魅力を解説しよう。<br/><br/><img src=\"https://mdpr.jp/photo/images/2016/10/18/200_9149cf7c487afd0f04f8d66d9a1e5db16e3dfaa543ce9246.jpg\" alt=\"新垣結衣、星野源「逃げるは恥だが役に立つ」第2話・場面カット（C）TBS\" /><br/><div class=\"caption\">（写真 : 新垣結衣、星野源「逃げるは恥だが役に立つ」第2話・場面カット（C）TBS）</div><br/>\n<br/><strong>◆「逃げるは恥だが役に立つ」概要</strong>\n<br/>\n<br/>原作は、月刊「Kiss」（講談社）で連載中の海野つなみ氏の同名漫画。\n<br/>\n<br/>主人公は彼氏なし・院卒なのに内定ゼロ、さらには派遣切りにあい誰からも必要とされない辛さ、居場所の無さを強く感じている25歳の森山みくり（新垣）。\n<br/>\n<br/>家事手伝いとして雇われたことをきっかけに、恋愛経験の無い独身サラリーマン・津崎平匡（星野源）と「仕事としての結婚」をすることになり、“夫＝雇用主、妻＝従業員”という契約結婚を描いた社会派ラブコメディとなっている。\n<br/>\n<br/><strong>◆魅力その1：目が離せないムズキュン恋模様</strong>\n<br/>\n<br/>メインとなるのは、妄想女子・みくりとウブ男・津崎のじれったい恋模様。\n<br/>\n<br/>いきなり同じ屋根の下で暮らす“契約夫婦”から始まった2人の関係は、親密感を出し周囲の「仮面夫婦なのでは？」という疑いを晴らすという理由で“契約恋人”に発展。さらに、そこからハグ、キスとステップを踏む内にお互い相手への恋心を自覚するようになる。\n<br/>\n<br/>普通の恋愛とはまさに逆の順番だが、結婚前から2人の相性が良いことは誰の目にも明らか。大学院で学んだ心理学の知識を駆使した“小賢しい”思考から生まれるみくりの突拍子もない提案を、優秀なプログラマーである津崎が持ち前の分析力で現実的なプランに落とし込むという関係性が出来上がっており、徐々に近づく2人の距離に、思わず観ながら「早く付き合っちゃえよ！」とムズキュン（＝“ムズムズ”しながら“胸キュン”）してしまう構成だ。\n<br/>\n<br/>そんな2人の会話から生まれる印象的なワードにも注目。“契約恋人”となった2人は毎週火曜の資源ごみの日を“ハグの日”に制定。混乱した自分の気持ちを「因数分解してみたんです」とみくりが言い出す場面では、津崎が「素因数分解ですね」と応じるなどシュールな応酬も見どころで、毎回放送中のTwitterのトレンドを関連ワードが埋め尽くす。<br/><br/><strong>◆魅力その2：新垣結衣と星野源がハマり役！</strong>\n<br/>\n<br/>勿論、新垣と星野の役へのハマりっぷりも「逃げ恥」を語る上で欠かせないポイント。\n<br/>\n<br/>フジテレビ系ドラマシリーズ「リーガル・ハイ」（フジテレビ系、2012年～）を始め、これまでも多くの作品でコメディエンヌぶりを発揮してきた新垣。脚本の野木亜紀子氏は、これまで同局系「空飛ぶ広報室」（2013年）、日本テレビ系「掟上今日子の備忘録」（2015年）で新垣とタッグを組んでおり、今回もその魅力を最大限に引き出している。\n<br/>\n<br/>一方、星野は、35年間彼女がいたことがない高齢童貞で、“プロの独身”を自負する津崎を原作に忠実な演技で好演。思わずクスッと笑ってしまうようなシーンも多く、峠田浩プロデューサーが「津崎の真面目でウブで不器用だけど、かわいらしさも同居している絶妙な空気を出していただけるのは星野さんしかいない」と起用理由を語っている通り、見事なハマり役となっており、“平匡さん”に萌える人が続出。\n<br/>\n<br/>キャラクターの愛らしさと2人の素の好感度の高さも相まって、毎回視聴者からは「両方とも可愛すぎる」という声が寄せられる。<br/><br/><strong>◆魅力その3：悪役不在の愛すべき登場人物たちに共感</strong>\n<br/>\n<br/>さらに、2人を取り囲むキャラクターも特徴的。\n<br/>\n<br/>みくりの伯母であり親友でもある“百合ちゃん”こと百合（石田ゆり子）は、化粧品会社に勤めるバリバリのキャリアウーマンだが、生涯独身で男性経験なしの“高齢処女”。今の自分に満足はしているが、時折隠しきれない寂しさを見せる。\n<br/>\n<br/>津崎の会社の後輩でスーパーハイスペックイケメン・風見（大谷亮平）は、みくりとの“シェア婚”を提案するなど2人の関係を脅かす存在となるが、彼もまた、イケメン故の悩みを内に秘める。\n<br/>\n<br/>津崎の同僚として脇を固めるのは、仮面夫婦に疑惑の目を向けるゲイの沼田（古田新太）と、家族サービスを欠かさない既婚者・日野（藤井隆）。みくりの親友で一児の母の安恵（真野恵里菜）は、夫に浮気され離婚したシングルマザーと、登場するのは誰もが共感できるような現実社会を映した等身大の人物ばかりだ。\n<br/>\n<br/>2人を故意に邪魔する人物などはおらず、しっかりとそれぞれの立場から苦悩を描写。「悪役不在」と言っても過言ではない世界も、どの世代も安心して視聴できる要因と言えそうだ。<br/><br/><strong>◆魅力その4：攻めたパロディにガッキーのコスプレ姿…みくりの妄想シーンが楽しい</strong>\n<br/>\n<br/>こういったストーリーのメイン軸とは別に、話題を集める数々の仕掛けもブームを後押ししてきた。\n<br/>\n<br/>原作でも登場するみくりの妄想パートでは、回想や妄想シーンでの名物番組のパロディが定番化。初回からいきなり同局系ドキュメンタリー「情熱大陸」のパロディでスタートするなど毎回凝った演出が見どころに。\n<br/>\n<br/>また、チアガールや女性議員、バスガイドなど新垣がコスプレで七変化。第6話では、9年前の同局系主演ドラマ「パパとムスメの7日間」と同じセーラー服姿を披露し「9年前と変わらない」「粋な演出」とファンを喜ばせた。<br/><br/><strong>◆魅力その5：“恋ダンス”が一大ブームに</strong>\n<br/>\n<br/>最後に、忘れてはいけないのが“恋ダンス”。エンディングで、星野が歌う主題歌『恋』に合わせてキャスト陣が踊るダンスのことで、Perfumeのダンスなどで知られる演出振付家のMIKIKO氏が振付を担当。\n<br/>\n<br/>キュートな振付がドラマスタート直後から話題を呼び、ネット上では、「恋ダンス踊ってみた」という完コピ動画が一大ブームに。一般人だけでなく、芸能界やスポーツ界にも広がりを見せている。<br/><br/><strong>◆これまでのあらすじ</strong>\n<br/>\n<br/>“契約恋人”としてのスキンシップや“新婚旅行”という名の“社員旅行”などを経て、お互いへの思いが溢れ出してきた2人。しかし、良い雰囲気になったところで恋愛への自信のなさから津崎はみくりを拒否。ショックを受けたみくりが家出をするも、離れたことで自分の気持ちを冷静に見つめ直し、再び津崎の部屋に戻る。\n<br/>\n<br/>6日放送の第9話では、ついに誤解から生まれた嫉妬をきっかけにみくりと津崎がお互いの気持ちを確認しあい、2人は晴れて本物の“恋人同士”となった。\n<br/>\n<br/><strong>◆物語はついに佳境へ</strong><br/><br/>次週、13日放送の第10話（15分拡大）の予告では、津崎がリストラ危機！？みくりにも何やら新たな仕事が舞い込む予感で、物語はついに佳境に突入。<br/><br/>なお、TBSの公式動画チャンネルでは、8話までの「『逃げるは恥だが役に立つ』ダイジェスト」（4日に一部で放送）を20日よる9時59分までの期間限定で公開している。（modelpress編集部）', 1, '2016-12-08 17:12:05', '2017-02-06 17:12:05', NULL, NULL, NULL, 0, 'https://mdpr.jp/news/1644054', '他の写真・記事詳細をもっと見る', 'https://mdpr.jp/news/1643586', '“恋ダンス”に菊地亜美＆大川藍が挑戦！「さすが元アイドル」「2人とも息ピッタリ」と反響', 'https://mdpr.jp/news/1643468', '新垣結衣＆星野源、アヒル口2ショットが可愛すぎ', 'https://mdpr.jp/news/1643432', '「逃げ恥」平匡さん（星野源）のファッションが“オシャレで可愛い”と話題「彼氏に着てほしい服No.1」の声', 'https://mdpr.jp/news/1643047', '“恋ダンス”、ガッキーNGシーン公開で「可愛すぎて卒倒」「もうOKでいい」の声', 0, 0, 1, 1, '2016-12-08 17:25:04', '2016-12-08 17:25:04'),
    (84, '4_1_r_20161208_1481185504442799286', 4, 1, 1, 1, 1, 'https://mdpr.jp/gal/1644087', '8f8fbec7929aace21e2c8187a49f8fc9_1481185504', '藤田ニコル、体を張ったロケへの思い「月に1回虫を食べている」', '', '【藤田ニコル／モデルプレス＝12月8日】雑誌「Popteen」モデルでタレントの“にこるん”こと藤田ニコルが8日、都内で行われた所属事務所であるオスカープロモーションの晴れ着撮影会に出席。今年1年を振り返った。<br/><br/><img src=\"https://mdpr.jp/photo/images/2016/12/08/200_20d774b99cce927f8b77a4514a24f0638c0e7f15618338cb.jpg\" alt=\"晴れ着撮影会に出席した藤田ニコル （C）モデルプレス\" /><br/><div class=\"caption\">（写真 : 晴れ着撮影会に出席した藤田ニコル （C）モデルプレス）</div><br/>2015年からバラエティ番組をはじめ、多数のテレビ番組に出演している藤田。2016年も引き続き、活躍ぶりを見せた藤田は「2年間すごく忙しい日々を送らせてもらったんですけど、来年は今年の自分に勝てるようにがんばりたいと思います」と気合い。来年やりたいことを聞かれると「体を張るロケが多くて月に1回虫を食べているので、それが続けばいいなと思います」と独特な意気込みを語った。\n<br/>\n<br/>おバカ女王と呼ばれていることについては「返上したいとは思いません」ときっぱり。存在感を発揮し、このイベントに参加できたことを「ここに立てていることが嬉しいです」と喜んだ。\n<br/>\n<br/><strong>◆武井咲・河北麻友子・藤田ニコルらも登場</strong>\n<br/>\n<br/>今年は藤田のほか武井咲、剛力彩芽、河北麻友子、本田望結、吉本実憂、小芝風花、高橋ひかる（※「高」は正式には「はしごだか」）、岡田結実、そして5月に行われた「第1回ミス美しい20代コンテスト」でグランプリに輝いた是永瞳の計10名が華やかに集結。2016年、様々な方面で活躍し注目を浴びた12歳～25歳の美女が晴れ着姿を披露した。（modelpress編集部）', 1, '2016-12-08 17:05:37', '2017-02-06 17:05:37', NULL, NULL, NULL, 0, 'https://mdpr.jp/gal/1644087', '他の写真・記事詳細をもっと見る', 'https://mdpr.jp/news/1644056', '武井咲、藤田ニコルと初対面「同じ事務所だったんだ」', 'https://mdpr.jp/news/1644023', '武井咲・剛力彩芽・河北麻友子・藤田ニコルら美女10人が華やか晴れ着姿で豪華集結', 'https://mdpr.jp/news/1642839', '藤田ニコル「月イチで虫を食べている」人気者ならではの悩みも', 'https://mdpr.jp/gal/1642755', '藤田ニコル“SNS疲れ”を告白 心配＆エール殺到', 0, 0, 1, 1, '2016-12-08 17:25:04', '2016-12-08 17:25:04'),
    (85, '4_1_r_20161208_1481185504456063494', 4, 1, 1, 1, 1, 'https://mdpr.jp/news/1644105', '2b442cc0d19ffc5ede4b0cce34ffd7f3_1481185504', '「地味スゴ」石原さとみの“えっちゃん”に憧れる！「なりきってみた」人気モデルが可愛い', '', '【地味にスゴイ！／モデルプレス＝12月8日】雑誌「HR」などでモデルとして活躍する相羽瑠奈が、ドラマ「地味にスゴイ！校閲ガール・河野悦子」（日本テレビ系）で石原さとみが演じた“えっちゃん”こと河野悦子のなりきりコーディネートを披露し、注目を集めている。<br/><br/><img src=\"https://mdpr.jp/photo/images/2016/12/08/200_7f8eed54d3f2fd7d18602a25b442b91c4ddd35fa1aadcd29.jpg\" alt=\"「地味スゴ」“えっちゃん”のなりきりコーデを披露した相羽瑠奈／Twitterより\" /><br/><div class=\"caption\">（写真 : 「地味スゴ」“えっちゃん”のなりきりコーデを披露した相羽瑠奈／Twitterより）</div><br/>7日に最終回を迎えた同ドラマは、ストーリーだけでなく悦子が着こなす個性的なファッションやヘアメイクもトレンドに。相羽は8日、Twitterで「えっちゃんになりきってみた #地味スゴ」とのコメントを添えて自撮りショットを公開。劇中で特に印象的だったスカーフを頭に巻き、めがねをかけ、レトロな紫色のコートで“えっちゃん”になりきった。\n<br/>\n<br/>まん丸のたれ目にぽってりとした唇も相まって、ファンからは「本当にえっちゃんかと思った！」「めちゃくちゃ似てる」「可愛すぎる！」と好評。ドラマの熱心な視聴者だった相羽は「やる気出したい時えっちゃんになることにした笑」とつづっている。\n<br/>\n<br/>相羽はInstagramフォロワー8万人以上を抱え、自身でもブランド「RRR BY SUGAR SPOT FACTORY」のディレクターをつとめている人気ファッションアイコン。8日に発売された「HR」1・2月号で表紙を飾っている。（modelpress編集部）', 1, '2016-12-08 17:09:09', '2017-02-06 17:09:09', NULL, NULL, NULL, 0, 'https://mdpr.jp/news/1644105', '他の写真・記事詳細をもっと見る', 'https://mdpr.jp/news/1644024', '石原さとみ＆菅田将暉の恋の行方は？「地味スゴロス」の声相次ぐ「えっちゃんの笑顔が見られない水曜なんて…」', 'https://mdpr.jp/news/1643703', '石原さとみ、15年前は“石原サトイモ”？', 'https://mdpr.jp/news/1643087', '石原さとみ「地味スゴ」涙のクランクアップ「20代最後の作品がこれで良かった」', 'https://mdpr.jp/cinema/1636431', '「君の名は。」「シン・ゴジラ」どれくらい流行った？＜流行語大賞候補＞', 0, 0, 1, 1, '2016-12-08 17:25:04', '2016-12-08 17:25:04'),
    (86, '4_1_r_20161208_1481185504604590857', 4, 1, 1, 1, 1, 'https://mdpr.jp/news/1644096', 'be4fd090e307acc5de46ce812306fb9c_1481185504', '水原希子が悩む“モヤモヤ” 解消方法も明かす', '', '【水原希子／モデルプレス＝12月8日】女優の水原希子が8日、都内で行われた美容サロンのオープニングイベントに出席した。<br/><br/><img src=\"https://mdpr.jp/photo/images/2016/12/08/200_ee155de4e025b5ea8a14944fe4d996d4eea9e071804db06d.jpg\" alt=\"水原希子（C）モデルプレス\" /><br/><div class=\"caption\">（写真 : 水原希子（C）モデルプレス）</div><br/><strong>◆水原希子が“モヤモヤ”すること</strong>\n<br/>\n<br/>今年26歳になった水原は、「子どものときは25、26歳になったら結婚しているって思っていたけど、大人になっていくに連れて、大人の女性の理想像が変わってきたので、ほど遠いと感じるとともに、考えも変わってきたと思います」と吐露。\n<br/>\n<br/>「その中でモヤモヤとすることは？」と聞かれると、「毎日です。私はすごく自信がなくて、恥ずかしがり屋なので、例えばお芝居とかに関してもまだまだ力不足だなと思って、自分がやりたいとこをできないとか、やりたいことがあるんだけどそこに向けて通っていかなきゃいけない道を考えると、楽じゃないしモヤモヤします」と仕事面について告白。加えて“美容のモヤモヤ”についても、「若いときは寝て起きてパッといろんなところに行けていたのが、今は責任感もあるので。今日こうやって皆さんの前にくるので、昨日もゆっくりお風呂に入りました。いろいろこまめにしていかないといけないので…」と語った。\n<br/>\n<br/><strong>◆解消方法は？</strong>\n<br/>\n<br/>さらに、モヤモヤの解消法についても尋ねられ、「仕事のことに関しては、お芝居の現場では、知っている人に頼んで読み合わせをしてもらったり、演技レッスンに行ったりしています。人が見るものなので仕事にモヤモヤを持ち込むのはイヤだなと思うので、時間をたくさん削ってでもそういうところに通ったり」と紹介。\n<br/>\n<br/>美容面に関しては、「惜しみなく時間をかけていって、やらなきゃということは全部時間を費やしてたっぷりやるようにしています」と明かした。\n<br/>\n<br/><strong>◆来年の抱負を語る</strong>\n<br/>\n<br/>また、フリップに『飛びこめ！』と来年の抱負を書き込んだ水原。\n<br/>\n<br/>「毎年、抱負を聞かれて考えると“チャレンジ”なんですけど、来年は仕事的にも飛び込んでいかなきゃいけない現場が多いというか、自分に自信がなかったり、怖がりなところがあるので、腹をくくって飛び込めって自分の背中を押す意味でもでも、この抱負を書きました」と説明し、「精一杯頑張ります」と意気込んだ。（modelpress編集部）', 1, '2016-12-08 16:20:28', '2017-02-06 16:20:28', NULL, NULL, NULL, 0, 'https://mdpr.jp/news/1644096', '他の写真・記事詳細をもっと見る', 'https://mdpr.jp/news/1639496', '草なぎ剛「もう帰りません」水原希子らと波乱の幕開け　新キャストも発表', 'https://mdpr.jp/news/1630772', '藤木直人・水原希子・山本美月・安田顕ら豪華キャストが集結　草なぎ剛主演ドラマ出演者発表', 'https://mdpr.jp/news/1628909', '水原希子「25ans」カバーガールを卒業「すごくしんどかった」時期を回顧', 'https://mdpr.jp/news/1626037', '水原希子、妹・佑果と合同バースデーでド派手コスプレ「クオリティ高すぎ」「思わず二度見」', 0, 0, 1, 1, '2016-12-08 17:25:05', '2016-12-08 17:25:05'),
    (87, '4_1_r_20161208_1481185504695858207', 4, 1, 1, 1, 1, 'https://mdpr.jp/disney/1644093', '58a614d6820ee3b994e676f7c9c53f4c_1481185504', 'スター・ウォーズ最新作「ローグ・ワン」監督、撮り直しを希望？キャストは“究極ミッション”完了に「感動」＜来日会見＞', '', '【ローグ・ワン／モデルプレス＝12月8日】7日、都内にて『スター・ウォーズ』シリーズ最新作『ローグ・ワン／スター・ウォーズ・ストーリー』（12月16日公開）の来日記者会見が行われた。<br/><br/><img src=\"https://mdpr.jp/photo/images/2016/12/08/200_79664c276f622fea06969666791695e99cbf29c1aec68ccd.jpg\" alt=\"スター・ウォーズ最新作「ローグ・ワン」監督、撮り直しを希望？キャストは“究極ミッション”完了に「感動」＜来日会見＞（C）2016 Lucasfilm Ltd. All Rights Reserved.\" /><br/><div class=\"caption\">（写真 : スター・ウォーズ最新作「ローグ・ワン」監督、撮り直しを希望？キャストは“究極ミッション”完了に「感動」＜来日会見＞（C）2016 Lucasfilm Ltd. All Rights Reserved.）</div><br/>本作は『エピソード 4／新たなる希望』の直前までが描かれるアナザー・ストーリーで、反乱軍の寄せ集め極秘チーム＜ローグ・ワン＞が、銀河全体を脅かす究極兵器デス・スターの設計図を奪うという97.6％生還不可能なミッションに挑む姿が描かれる。\n<br/>\n<br/><strong>◆ダース・ベイダーについて質問飛ぶ</strong>\n<br/>\n<br/>全米でのワールドプレミア直前という多忙な時期に、アジアの国々の中で最速で来日を果たしたのは、主人公ジンを演じるフェリシティ・ジョーンズ、情報将校キャシアン役のディエゴ・ルナ、そしてギャレス・エドワーズ監督。\n<br/>\n<br/>記者会見に登場したギャレス監督は来日の喜びについて「スター・ウォーズは日本にとても強く影響を受けているので、故郷に戻ってきたような気分で、この映画をシェアすることをとても光栄に思います」と語り、日本と『ローグ・ワン』との関連性について聞かれると「日本の文化に間違いなく影響を受けているよ。本作は『隠し砦の三悪人』を基にして作っているし、黒澤明監督のいろんな作品を参考にしています。黒澤監督作品のスチール写真、映像を見て、本作のディベロップメントを行いました。アイディアを盛り込むなら、ベストな映画から盛り込むべきだと思い、そしてそれは黒澤監督作品だと確信していました」と答えた。\n<br/>\n<br/>さらに監督は、11年ぶりにスクリーンに戻ってきたダース・ベイダーについての質問が記者から飛び出すと「いつも映画の撮影現場は騒がしかったけど、ダース・ベイダーの俳優が登場すると、映画の中でベイダーが登場するとそうであるように、現場が緊張感で満たされ皆が静かになるんだ」とスター・ウォーズらしい撮影秘話を披露。「ダース・ベイダー以外にもこれまでのスター・ウォーズ・シリーズのキャラクターが登場することは保障するが、それ以上は言えないよ。映画館で確かめて欲しいね」とスター・ウォーズ・ファンの心を鷲づかみにするような発言も飛び出した。\n<br/>\n<br/><strong>◆主人公ジン役フェリシティ・ジョーンズ</strong>\n<br/>\n<br/>主人公ジンを演じたフェリシティ・ジョーンズは「コンニチハ」と日本語での挨拶から始まり会場を沸かせ、「実は10年前に一度日本に来たことがあるの。戻ってこれて嬉しいわ」とにっこり。\n<br/>\n<br/>全世界で多くのファンがいる伝説のシリーズ「スター・ウォーズ」のアナザー・ストーリーの主人公に抜擢されたフェリシティは、自身が演じるジンという役どころについて「明確な目的を持ったキャラクターです。窃盗や暴行などを行うが、そんなアウトサイダーな彼女が仲間と出会い、同じ決意、同じ勇気、同じ善を持ったキャラクターたちとともに戦う物語になっていると思います」と明かした。\n<br/>\n<br/><strong>◆監督、撮り直しを希望？「もう1回撮影をするべき」</strong>\n<br/>\n<br/>この日は、会見のために作られた世界でただ1枚の豪華な襖絵（ふすまえ）がゲストへのサプライズとして登場。日本が大好きという監督は「映画の撮影中にこの襖絵があればよかったのに！そうすれば、映画にこの素晴らしいシーンを入れることが出来たからね」と感動をあらわに。\n<br/>\n<br/>自身が演じたジン、キャシアンが描かれた作品に対して、フェリシティは「もし続編が作られるとするならば、日本で撮影したいわ。この絵のように、美しい桜も登場させることができるから」、ディエゴは「素晴らしい。この絵の中では帝国軍も少しフレンドリーに描かれているね。僕はこの絵のように高くジャンプするシーンを撮るために、もう1回撮影をするべきだと思うんだ。2、3日で撮れるから、是非やろう！」と大絶賛のコメントとともに、冗談を飛ばし会場を笑いに包んだ。\n<br/>\n<br/><strong>◆3名に“究極ミッション”</strong>\n<br/>\n<br/>その後、ゲスト3名の拍手で襖絵の製作者である柏原晋平氏がステージに迎えられると、柏原氏は「映画のテーマである“希望は死なない”にインスパイアされて描きました」と作品に込めた想いを語った。\n<br/>\n<br/>続けて「この絵の中にある“希望の光”は皆さんの心にあるもの。僕ひとりで描けるものではないので、皆さんに描いて欲しい」と明かし、来日ゲスト3名に「襖絵の完成」という“究極ミッション”を発表。\n<br/>\n<br/>3名のゲストが襖絵に描かれて色が付けられていない「☆」を金色に塗り最後の一筆ずつを入れるという作業を行い、最後に3名のサインを入れて作品が完成すると、会場中に大きな拍手が鳴り響いた。\n<br/>\n<br/>襖絵の完成を受けてフェリシティは「星に色を塗るという作業を皆で一緒にできたことに感動しました。映画作りもまた、みんなで力を合わせて作りました。それを世界の皆さんと分かちあうことができると思うと嬉しいです」と喜んだ。（modelpress編集部）', 1, '2016-12-08 15:40:56', '2017-02-06 15:40:56', NULL, NULL, NULL, 0, 'https://mdpr.jp/disney/1644093', '他の写真・記事詳細をもっと見る', 'https://mdpr.jp/cinema/1589057', 'スター・ウォーズのスピンオフ映画第一弾、撮り直しは&quot;危機的状況&quot;？', 'https://mdpr.jp/disney/1580025', '「スター・ウォーズ」最新作がディズニーツムツムに', 'https://mdpr.jp/disney/1574534', 'スター・ウォーズ“BB-8”の本編映像Web初解禁＜モデルプレス独占先行＞', 'https://mdpr.jp/disney/1551574', '「アナ雪」から受け継ぐ「スター・ウォーズ」「シンデレラ」進化系ヒロインたちの魅力', 0, 0, 1, 1, '2016-12-08 17:25:05', '2016-12-08 17:25:05'),
    (88, '4_1_r_20161208_1481185504897682946', 4, 1, 1, 1, 1, 'http://www.oricon.co.jp/news/2082738/', '117be77bc4d463df51bd97cd8b423c18_1481185504', '岩井俊二監督の名作『打ち上げ花火』劇場アニメ化　声優に広瀬すず・菅田将暉・宮野真守', '', '<img src=\"http://news.oricon.co.jp/feed/images/size640wh/20161208_145542_size640wh_6221.jpg\" title=\"映画『打ち上げ花火、下から見るか？横から見るか？』で声優を務める（左から）広瀬すず、菅田将暉、宮野真守 （C）2017「打ち上げ花火、下から見るか？横から見るか？」製作委員会\" alt=\"映画『打ち上げ花火、下から見るか？横から見るか？』で声優を務める（左から）広瀬すず、菅田将暉、宮野真守 （C）2017「打ち上げ花火、下から見るか？横から見るか？」製作委員会\" ><br />\n　映画監督・岩井俊二氏の名を世に知らしめた名作ドラマ『打ち上げ花火、下から見るか？横から見るか？』（フジテレビ　1993年）が劇場アニメ化され、来年8月18日に公開されることが明らかになった。声優に広瀬すず（18）、菅田将暉（23）、宮野真守（33）を起用し、傑作が20年以上の時を経て生まれ変わる。<br />\n<br />\n　岩井氏が監督を務めた原作ドラマは“繰り返される夏の一日”の恋の奇跡を描いた物語。夏休みのある日、花火大会をまえに「花火は横から見たら丸いのか？　平たいのか？」の答えを求め、町の灯台から花火を見ようと計画する少年達。一方、クラスのアイドル的存在・及川なずなに想い寄せる島田典道は時間が巻き戻る体験のなか、なずなから「かけおち」に誘われる。何度も繰り返す一日のなか、なずなと典道を不思議な運命が待ち受ける。<br />\n<br />\n　声の出演は、なずな役を広瀬、典道役を菅田が担当。また、典道の恋敵となるクラスメイト・安曇祐介役を宮野が務める。アニメ声優を務めるのは、広瀬が『バケモノの子』（15年）以来2度目、菅田は今作が初挑戦となる。<br />\n<br />\n　また、脚本には『モテキ』（11年）、『バクマン。』（15年）を手がけた大根仁氏を起用。総監督はアニメ『魔法少女まどか☆マギカ』シリーズ、『＜物語＞シリーズ』などで知られる新房昭之氏、アニメーション制作はシャフトと、スタッフにも充実の布陣が顔をそろえる。<br />\n<br />\n　声優初挑戦となる菅田は「ずっと声のお仕事をしたいと思っていたので、今回念願の初チャレンジ、興奮しました」と熱くコメント。「そしてやはり声優さんの凄さを実感しました。色んなテクニックを他のキャストの方々に教わりながらの時間は、公園デビューを果たした赤ん坊のような新しい刺激と快感が止まりませんでした」と新境地への充実感をにじませた。<br />\n<br />\n　一方、ヒロインを演じる広瀬は「なずなは、言葉にはなっていないけれど、秘められた想いを抱えていて、凄く繊細な印象を受けました。あんまり掴み所がないんだけど大人っぼさもあり、子供っぽさもあり、目の前にいるのに触れられないような…」となずなの人物像を語った。<br />\n<br />\n　そんな新進の“若手”2人と共演した宮野は「収録では、広瀬さんと菅田さんがとっても素晴らしくて、お2人のフレッシュなオーラに、終始キュンキュンしながらも、同級生として演じる自分も、気合いを入れて若づくりを頑張りました（笑）」と刺激にあふれた現場であったことを明かしている。', 1, '2016-12-08 16:12:04', '2017-02-06 16:12:04', NULL, NULL, NULL, 0, 'http://www.oricon.co.jp/photo/1939/127133/?anc=164', '【写真】筋トレの成果？チア姿で引き締まったBODYを披露した広瀬すず', 'http://joshiplus.jp/fashion/newsphoto/2058134/3/?anc=164', '【写真】広瀬すず、赤リップでオトナの表情チラリ', 'http://www.oricon.co.jp/news/2081492/?anc=164', '菅田将暉、全身剃毛でフンドシ姿に？『帝一の國』実写化 「みそぎのような気持ちで挑む」 （16年11月16日）', '', '', '', '', 0, 0, 1, 1, '2016-12-08 17:25:05', '2016-12-08 17:25:05'),
    (89, '4_1_r_20161208_1481185505140486962', 4, 1, 1, 1, 1, 'https://mdpr.jp/news/1644095', '190912c0bbb64cca9c4bc37891a29bb5_1481185505', '平愛梨の“美しい寝顔”が話題「女神が寝てる」の声', '', '【平愛梨／モデルプレス＝12月8日】お笑いコンビ・はんにゃの川島章良が7日、自身のInstagramにて女優の平愛梨の寝顔を公開し、話題を呼んでいる。<br/><br/><img src=\"https://mdpr.jp/photo/images/2016/12/08/200_42ac2fd3096473d1e20a5e897f5d50f083256ef032d8f4ff.jpg\" alt=\"寝顔を公開された平愛梨／川島章良Instagramより\" /><br/><div class=\"caption\">（写真 : 寝顔を公開された平愛梨／川島章良Instagramより）</div><br/>川島は、コートをかけ、座席に横たわるようにして眠る平の横顔を投稿。長いまつ毛とスッと通った鼻筋が際立っており、「#爆睡 #してるのに #寝顔 #きれい」と絶賛した。\n<br/>\n<br/><strong>◆平愛梨の寝顔が美しすぎる</strong>\n<br/>\n<br/>今回の投稿に対し、ファンからは「女神が寝てる…！」「可愛すぎる」「爆睡しててこの美しさってどういうこと」「美人は何してても美人！」などの多くのコメントが寄せられている。（modelpress編集部）', 1, '2016-12-08 15:36:48', '2017-02-06 15:36:48', NULL, NULL, NULL, 0, 'https://mdpr.jp/news/1644095', '他の写真・記事詳細をもっと見る', 'https://mdpr.jp/news/1641955', '長友佑都選手、平愛梨に向けた“アモーレ”の意味語る＜2016年新語・流行語大賞＞', 'https://mdpr.jp/news/1641906', '平愛梨、「アモーレ」受賞に涙　長友選手との結婚発表に言及＜2016年新語・流行語大賞＞', 'https://mdpr.jp/news/1634837', '平祐奈“19歳差”兄に「似てきた」姉・愛梨との3ショットに「美形家族」の声', 'https://mdpr.jp/news/1618297', '平祐奈、姉・平愛梨にサプライズ　姉妹愛に反響', 0, 0, 1, 1, '2016-12-08 17:25:05', '2016-12-08 17:25:05'),
    (90, '4_1_r_20161208_1481185505178664163', 4, 1, 1, 1, 1, 'http://www.oricon.co.jp/news/2082744/', 'c34c2c8d552327a2799629c76cd888e8_1481185505', '『キミコエ・オーディション』発、新人声優ユニット「NOW ON AIR」　3・8デビューシングル発売', '', '<img src=\"http://news.oricon.co.jp/feed/images/size640wh/20161208_161848_size640wh_4742.jpg\" title=\"『キミコエ・オーディション』発の新人声優ユニット「NOW ON AIR」がデビュー\" alt=\"『キミコエ・オーディション』発の新人声優ユニット「NOW ON AIR」がデビュー\" ><br />\n　新世代声優の発掘・育成プロジェクト『キミコエ・オーディション ～supported by ファミリー劇場』の合格者6名で結成された新人声優ユニット「NOW ON AIR（ナウオンエアー）」のデビューシングル「この声が届きますように」が3月8日、ランティスから発売されることが決定した。<br />\n<br />\n　14歳～25歳まで、さまざまなジャンルで活躍できる新世代の声優を発掘する『キミコエ・オーディション』には約3000人の応募が集まった。8月7日、都内で開催された最終審査で見事「NOW ON AIR」メンバーに選ばれたのは、飯野美紗子（20）、岩淵桃音（16）、片平美那（17）、神戸光歩（20）、鈴木陽斗実（20）、田中有紀（18）の6名。今後は「君に『声』を届ける超応援型コトダマ発信ユニット」というコンセプトのもと活動していく。<br />\n<br />\n　デビュー曲「この声が届きますように」はオーディションの課題曲として歌われてきた1曲。6人がメインキャストを務める劇場アニメ『きみの声をとどけたい』（2017年夏公開）のイメージソングにも決定しており、作品を盛り上げていく。', 1, '2016-12-08 16:56:01', '2017-02-06 16:56:01', NULL, NULL, NULL, 0, 'http://www.oricon.co.jp/news/2082629/?anc=164', '目薬成分の“擬人化”アニメ、新エピソード公開　丹下桜、阿澄佳奈ら演じる新キャラ登場 （16年12月07日）', 'http://www.oricon.co.jp/news/2082339/?anc=164', '『マクロスF』中島愛、約3年ぶり本格復帰　アニメ『風夏』エンディング曲担当 （16年12月01日）', 'http://www.oricon.co.jp/news/2082309/?anc=164', '声優・入野自由、来年から休業　海外留学へ「知らないことをもっと知りたい」 （16年11月30日）', '', '', '', '', 0, 0, 1, 1, '2016-12-08 17:25:05', '2016-12-08 17:25:05'),
    (91, '4_1_r_20161208_1481185505274202681', 4, 1, 1, 1, 1, 'https://mdpr.jp/news/1644088', '9ce3dc1bea59bfe8e3d61a04303a021a_1481185505', 'きゃりー、有村架純からメッセージで興奮「鼻血でました」　やり取りが「可愛い」「ほっこり」と話題', '', '【きゃりーぱみゅぱみゅ・有村架純／モデルプレス＝12月8日】アーティストのきゃりーぱみゅぱみゅが8日、Instagramでの女優の有村架純とのやり取りを紹介。有村からの返信に歓喜した。<br/><br/><img src=\"https://mdpr.jp/photo/images/2016/12/08/200_d18213941b4070b24d2fb7d24b898eb242c661068a524cff.jpg\" alt=\"有村架純（右）からのコメントに感激した様子のきゃりーぱみゅぱみゅ（左） （C）モデルプレス\" /><br/><div class=\"caption\">（写真 : 有村架純（右）からのコメントに感激した様子のきゃりーぱみゅぱみゅ（左） （C）モデルプレス）</div><br/>7日、撮影を終えたという有村はInstagramにて「お疲れ様でした。みんなは何してましたか？」とファンへ問いかけ。これにきゃりーが「今日はラーメンを食べました」とコメントを寄せると、有村は「ラーメン良いですね」と返答した。\n<br/>きゃりーはこのやり取りを、キャプチャー画像とともにTwitterにて紹介。「昨日有村架純ちゃんがインスタで『みなさんは今日なにしてましたか？』って書いてあったからコメントしたら返信きて鼻血でました」と興奮した様子で明かした。\n<br/>\n<br/><strong>◆ファンから「ほっこり」「可愛い」の声</strong>\n<br/>\n<br/>ファンからは「架純ちゃんときゃりーちゃんのやり取りびっくり」「きゃりーちゃんに親近感わいた（笑）」「仲良いのかな？？」「同い年コンビだね！ほっこり」「きゃりーちゃん良かったね（笑）2人の会話可愛い」といった声が上がった。（modelpress編集部）', 1, '2016-12-08 15:22:06', '2017-02-06 15:22:06', NULL, NULL, NULL, 0, 'https://mdpr.jp/news/1644088', '他の写真・記事詳細をもっと見る', 'https://mdpr.jp/cinema/1643028', 'きゃりー、初の海外レッドカーペットに降臨　絶賛＆歓迎受ける', 'https://mdpr.jp/cinema/1639227', '有村架純主演「ビリギャル」ノーカット地上波放送決定にファン歓喜「受験頑張れる」', 'https://mdpr.jp/review/1639107', '有村架純、朝ドラ主演で新たな想い　私生活＆仕事を語る', 'https://mdpr.jp/cinema/1638579', '有村架純主演「ビリギャル」“ノーカット”で地上波初放送', 0, 0, 1, 1, '2016-12-08 17:25:05', '2016-12-08 17:25:05'),
    (92, '4_1_r_20161208_1481185505343904614', 4, 1, 1, 1, 1, 'http://www.oricon.co.jp/news/2082743/', 'c98fc618279e40954c4c06419bd69f82_1481185505', '至福のおいしさ！　スタバから冬の甘酸っぱいベリー系スイーツ', '', '<img src=\"http://news.oricon.co.jp/feed/images/size640wh/20161208_161731_size640wh_6339.jpg\" title=\"スターバックス コーヒーの冬にぴったりベリー系スイーツ\" alt=\"スターバックス コーヒーの冬にぴったりベリー系スイーツ\" ><br />\n　スターバックス コーヒーでは、濃厚フレーバーとの相性も良いベリーを使ったスイーツがラインナップ。「Bliss（ブリス：至福）」と名付けられた『クランベリーブリスバー』は、クリスマスシーズンの定番スイーツのひとつで、昨年よりジンジャーを効かせた味わいが、冬ならでは。海外で親しまれるスパイシーな味わいを再現している。<br />\n<br />\n　アメリカの店舗で毎年人気の『オレンジクランベリースコーン』が、昨年に続き日本でも登場。オレンジとクランベリーの香りや風味を楽しめる、爽やかな味わいとなっている。イートインの場合は、オーブンでヒーティングしてもらうと、表面にかかったシュガーグレーズが溶け出して、違った味わいが楽しめるそう。<br />\n<br />\n　『ラズベリーチョコレートパイ』は、甘酸っぱいラズベリーにビターなチョコレートの相性が抜群の、少し大人向けのチョコレートパイ。濃厚なチョコレートとサクサクのパイ食感が楽しめる、ご褒美スイーツにもぴったりな一品となっている。<br />\n<br />\n【商品詳細】※すべて税抜<br />\n『クランベリーブリスバー』（270円）<br />\n『オレンジクランベリースコーン』（260円）<br />\n『ラズベリーチョコレートパイ』（440円）', 1, '2016-12-08 16:43:29', '2017-02-06 16:43:29', NULL, NULL, NULL, 0, 'http://joshiplus.jp/gourmet/news/2082597/?anc=044', 'キャンドルみたいでキュート！ スタバのホリデースイーツ （16年12月06日）', 'http://joshiplus.jp/gourmet/news/2082002/?anc=044', '【前飲み！】2つの表情！スタバのホリデー限定“焼きりんご”ドリンク', 'http://joshiplus.jp/gourmet/news/2081480/?anc=044', '朝が狙い目！ 話題の「スタバプリン」 （16年11月16日）', '', '', '', '', 0, 0, 1, 1, '2016-12-08 17:25:05', '2016-12-08 17:25:05'),
    (93, '4_1_r_20161208_1481185505345631158', 4, 1, 1, 1, 1, 'https://mdpr.jp/news/1644089', '3c64306d86f5f3d537aade986b91b942_1481185505', 'マギー「私のために？」猛アピールにキョトン', '', '【マギー／モデルプレス＝12月8日】モデルのマギーが、都内にて開催された体感型ラウンジのオープニングイベントに、お笑いコンビ・オリエンタルラジオの藤森慎吾と出席した。<br/><br/><img src=\"https://mdpr.jp/photo/images/2016/12/08/200_a9da87fae662f82879fb090cc67d3f0b2b06a86cf548b9d3.jpg\" alt=\"マギー （C）モデルプレス\" /><br/><div class=\"caption\">（写真 : マギー （C）モデルプレス）</div><br/><strong>◆藤森慎吾が猛アピール</strong>\n<br/>\n<br/>クリスマスの過ごし方が話題にあがると、「クリスマスはカナダで家族と過ごすことが多かったのです」とマギー。「お父さんがターキーを焼いて、立派なテーブルセッティングをしてみんなで食べることが小さなときからの習慣なので、よっぽど仕事が夜遅くなどにならない限りは家族で集まります。私もアップルパイを焼いたりして、一緒に過ごしています」と打ち明けた。\n<br/>\n<br/>一方、今年のクリスマスの予定について質問された藤森は、「もちろんお仕事はあるのですが、お仕事の後は時間がありますので。マギーちゃん、心配しないでください。スケジュールを空けています」と告白。しかし、当のマギーは何を言われたのかが理解できなかった様子でキョトン。\n<br/>\n<br/>藤森が「僕は空いていますよ！」と畳み掛けると、マギーは「私のために空けてくださっているのですか？」と確認。「そうです。もちろん！」と返答されると、「私はちょっとお仕事がありまして。その後は、…藤森さんとは過ごさないかな」と苦笑いを浮かべて笑いを誘った。\n<br/>\n<br/><strong>◆エスコートに感謝</strong>\n<br/>\n<br/>この日マギーは、ゴールドクリスマスをイメージしたロングドレス姿で、藤森にエスコートされながらステージに登壇。イタリア紳士のイメージでドレスアップしたという藤森は、「今、そこでマギーちゃんにお会いしたのですが、マギーちゃんのスタイルの良さに恐れおののいています。こんなにも身長差があるのかとびっくりしています」と心境を明かした。\n<br/>\n<br/>そんな藤本に、マギーから「階段だし、ロングドレスなので、やはり男性にはエスコートしてもらいたいなという気持ちがあります。しっかりやっていただいてありがとうございます」と感謝の言葉。「身長のことを気にしなくていいくらいにエスコートしていただきました」と笑顔をみせた。\n<br/>\n<br/><strong>◆今後目標は？</strong>\n<br/>\n<br/>また、今年10月に『ViVi』専属モデルから卒業することを発表したマギーは、「今年は、6年間務めさせていただいた専属モデルを卒業しましたので、来年へと繋がる変化の年だったかなと思います」と述懐。\n<br/>\n<br/>今後の抱負については、「色々と雑誌（の仕事）ができるようになりましたので、これからもどんどん色々な雑誌と、テレビと、色々なことにチャレンジしていけたらいいなと思います。もっと色々な雑誌の表紙を飾りたいなと思います」と声を弾ませた。（modelpress編集部）', 1, '2016-12-08 16:03:27', '2017-02-06 16:03:27', NULL, NULL, NULL, 0, 'https://mdpr.jp/news/1644089', '他の写真・記事詳細をもっと見る', 'https://mdpr.jp/news/1642924', 'マギー、ナイスな対応が話題｢マギー違いです｣　野球ファンも反応', 'https://mdpr.jp/review/1641251', 'マギー「ピリピリしてた」2年前からの変化＆「ストイックと言われる」本音告白…SEXYショットも公開', 'https://mdpr.jp/news/1641057', 'マギー、胸元ざっくりSEXYドレスで大人の色気', 'https://mdpr.jp/news/1640962', 'マギー、ベッドで“上半身裸”ショットに反響「背中が美しい」「セクシーすぎる」', 0, 0, 1, 1, '2016-12-08 17:25:05', '2016-12-08 17:25:05'),
    (94, '4_1_r_20161208_1481185505546654976', 4, 1, 1, 1, 1, 'http://www.oricon.co.jp/news/2082741/', '879274e05b61aa2280f58c2cf1f22bd8_1481185505', '『シブヤノオト』紅白SPに西野カナ・欅坂46・Perfumeら　出場歌手会見の舞台裏公開', '', '<img src=\"http://news.oricon.co.jp/feed/images/size640wh/20161208_160203_size640wh_2625.jpg\" title=\"11日放送の『シブヤノオト』で生パフォーマンスを披露する西野カナと欅坂46\" alt=\"11日放送の『シブヤノオト』で生パフォーマンスを披露する西野カナと欅坂46\" ><br />\n　NHK総合で11日に放送される音楽番組『シブヤノオト』（後5：00～5：54　生放送）で、紅白歌合戦スペシャルが組まれることが明らかになった。紅白出場が決まった西野カナ、欅坂46、Perfume、RADIO FISHの4組が出演する。<br />\n<br />\n　番組では、11月24日に行われた出場歌手記者会見の舞台裏に独占密着した映像を公開。さらに、見どころや目玉企画をいち早く紹介する。<br />\n<br />\n■『シブヤノオト』紅白歌合戦スペシャル<br />\n西野カナ「君が好き」（トーク＆生パフォーマンス）<br />\n欅坂46「二人セゾン」（トーク＆生パフォーマンス）<br />\nPerfume（トークのみ）<br />\nRADIO FISH（トークのみ）', 1, '2016-12-08 16:04:04', '2017-02-06 16:04:04', NULL, NULL, NULL, 0, 'http://www.oricon.co.jp/news/2081948/?anc=015', '【紅白】欅坂46、スピード出場に驚き　デビュー8ヶ月で （16年11月24日）', 'http://www.oricon.co.jp/news/2081946/?anc=015', '【紅白】AKBが“紅白版”総選挙　視聴者投票で48人を選抜（16年11月24日）', 'http://www.oricon.co.jp/news/2082673/?anc=015', '【紅白】タモリ＆マツコ・デラックスがゲスト出演 （16年12月07日）', '', '', '', '', 0, 0, 1, 1, '2016-12-08 17:25:06', '2016-12-08 17:25:06'),
    (95, '4_1_r_20161208_1481185505549824333', 4, 1, 1, 1, 1, 'https://mdpr.jp/news/1644085', 'bffb619ad292aef3a47011a2639a0cdf_1481185505', '乃木坂46堀未央奈も台湾でランウェイ　齋藤飛鳥・西野七瀬・松村沙友理…グループから計4人', '', '【堀未央奈／モデルプレス＝12月8日】乃木坂46の堀未央奈（ほりみおな・20）が10日に台湾・W台北で開催される「ASIA FASHION AWARD 2016 in TAIPEI」にゲストモデルとして出演することが明らかになった。<br/><br/><img src=\"https://mdpr.jp/photo/images/2016/12/08/200_958a3e2576c9d59924832474d51b06f9a3d65c4316354260.jpg\" alt=\"堀未央奈「ASIA FASHION AWARD 2016 in TAIPEI」にゲストモデルとして出演することが決定\" /><br/><div class=\"caption\">（写真 : 堀未央奈「ASIA FASHION AWARD 2016 in TAIPEI」にゲストモデルとして出演することが決定）</div><br/>同イベントは東京ガールズコレクションがプロデュース。アジアの最旬「ファッション」「文化」「エンターテイメント」（以下TGC）を世界へ発信する。先日、乃木坂46から齋藤飛鳥、西野七瀬、松村沙友理の出演も発表されており、計4人がモデルとしてランウェイを歩く。\n<br/>\n<br/>齋藤、西野、松村の3人はモデルとしてもTGC常連だが、堀が関連イベントでランウェイを歩くのは初。今年10月開催の「GirlsAward 2016 AUTUMN／WINTER」で初ランウェイを飾り、今月12日に発売する女性ファッション誌『ar』2017年1月号から新連載もスタート。専属モデルが多数所属する乃木坂46だけに、着実にソロでも露出を増やしている彼女から目が離せない。（modelpress編集部）\n<br/>\n<br/><strong>■堀未央奈（ほり・みおな）プロフィール</strong>\n<br/>生年月日：1996年10月15日／出身地：岐阜県／血液型：O型／星座：てんびん座／身長：160cm\n<br/>乃木坂46の2期生。加入後8ヵ月という驚異的なスピードで7thシングル『バレッタ』（13年11月発売）で初選抜・初センターを務めた。\n<br/>\n<br/><strong>■「TOYOTA presents ASIA FASHION AWARD 2016 in TAIPEI」概要</strong>\n<br/>開催日時：2016年12月10日（土）開演18:00 終演22:00（予定）\n<br/>会場：W Taipei（住所：110, 台湾, 台北, 10 Zhongxiao East Road Sec. 5Xinyi District）\n<br/>＜出演者＞\n<br/>【ゲストモデル】池端レイナ、齋藤飛鳥（乃木坂46）、ダレノガレ明美、西野七瀬（乃木坂46）、藤井リナ、堀未央奈（乃木坂46）、松村沙友理（乃木坂46）、miu、八木アリサ、ローラ　※50音順\n<br/>【モデル】恵里花、太田沙羅、瀧澤しほ、豊島はるか、西田奈未、蜂谷晏海、吉江幸貴 ※50音順\n<br/>【台湾モデル】Ru Ru、Jill、Bernice、Jina、Angel、Pin、Nina、Crystal、Lenny、Hsin、Catherine、Lynn\n<br/>【DJ】DIEGO MIRANDA、中田ヤスタカ、AMIAYA、Adorable Girls\n<br/>【アーティスト】小室哲哉、FEMM　※50音順\n<br/>【パフォーマー】Adorable Girls、CYBERJAPAN DANCERS　※50音順', 1, '2016-12-08 14:50:09', '2017-02-06 14:50:09', NULL, NULL, NULL, 0, 'https://mdpr.jp/news/1644085', '他の写真・記事詳細をもっと見る', 'https://mdpr.jp/news/1641950', 'ローラ、乃木坂46齋藤飛鳥らが豪華集結 台湾で大型ファッションショー開催決定', 'https://mdpr.jp/news/1638933', '【恋ダンス】乃木坂46井上小百合×堀未央奈の「逃げ恥」恋ダンスが可愛い！「めっちゃ癒された」の声', 'https://mdpr.jp/news/1622302', '乃木坂46堀未央奈、笑顔弾ける初ランウェイ 堂々ウォーキングで魅了＜GirlsAward 2016 A／W＞', 'https://mdpr.jp/review/1505503', '乃木坂46堀未央奈、めがね×血色メイクで大人顔にイメチェン', 0, 0, 1, 1, '2016-12-08 17:25:06', '2016-12-08 17:25:06'),
    (96, '4_1_r_20161208_1481185505654141766', 4, 1, 1, 1, 1, 'http://www.oricon.co.jp/news/2082705/', '09f8730c1aff4a771a57648ea9637bcf_1481185505', '広末涼子、今年の漢字は“楽”　書道披露で自画自賛「いいですね」', '', '<img src=\"http://news.oricon.co.jp/feed/images/size640wh/20161208_155548_size640wh_7927.jpg\" title=\"書道披露で自画自賛した広末涼子 （C）ORICON NewS inc.\" alt=\"書道披露で自画自賛した広末涼子 （C）ORICON NewS inc.\" ><br />\n　女優の広末涼子（36）が8日、都内で行われた楽天『R fan！』新CM記者発表会に出席。「今年の漢字一文字は？」と聞かれると、広末は大きな半紙に筆で「楽」としたためた。「楽しかった1年だったという意味で」と説明しつつ、自身が記した漢字をまじまじと見つめ、「いいですねぇ！　自画自賛です」と満足そうにほほ笑んだ。<br />\n<br />\n　今年のクリスマスの過ごし方は「大きなパーティーをするんです。親族が集まって」と告白。「たくさん集まるのでツリーの下にはプレゼントだらけになるくらいで、幸せな光景なんです」といい、当日にあわせて姪っ子や甥っ子のプレゼントを購入する予定だという。<br />\n<br />\n　楽天ファンを全国により広めるための同CMに出席した広末。普段は「メールより手紙」というアナログだが、ネットショッピングを実際に体験し「私にもできましいた」とにっこり。私生活では記念日以外にもプレゼントを購入する習慣があうりょうで「ドラマのクランクインときや、御礼に買ったりします」と明かした。<br />\n<br />\n　広末は出演するCMはあす9日より全国で放送を開始する。', 1, '2016-12-08 16:15:01', '2017-02-06 16:15:01', NULL, NULL, NULL, 0, 'http://www.oricon.co.jp/news/2078890/photo/2/?anc=072', '【写真】白のシースルーワンピで登場した広末涼子', 'http://www.oricon.co.jp/news/photo/2026705/1/?anc=072', '【写真】広末涼子の秘蔵写真を“蔵出し”　貴重なグラビアショットも', 'http://www.oricon.co.jp/news/2081637/?anc=072', '桑田佳祐新曲MV主演は広末涼子　史上初の“歌謡”サスペンス!? （16年11月18日）', '', '', '', '', 0, 0, 1, 1, '2016-12-08 17:25:06', '2016-12-08 17:25:06'),
    (97, '4_1_r_20161208_1481185505656852149', 4, 1, 1, 1, 1, 'https://mdpr.jp/news/1644064', '2d9c9267a655171cf2f7633c8e134e92_1481185505', '岡田結実「芸能人である前に…」自身の反省を明かす', '', '【岡田結実／モデルプレス＝12月8日】お笑いコンビ・ますだおかだの岡田圭右の長女でタレントの岡田結実が8日、都内で行われた所属事務所であるオスカープロモーションの晴れ着撮影会に出席。自身の反省点を明かした。<br/><br/><img src=\"https://mdpr.jp/photo/images/2016/12/08/200_6f097358a2e7e25f3512d63d3995c4466907c40cf3586b80.jpg\" alt=\"岡田結実（C）モデルプレス\" /><br/><div class=\"caption\">（写真 : 岡田結実（C）モデルプレス）</div><br/>岡田は今年、バラエティ番組を中心に飛躍した1年となったが、「自分自身、マネージャーさんや周りの人に頼り切っていて自立できていない1年だったとすごく思うので、来年は芸能人である前に人としてちゃんとした人になりたい」と今年の自分を反省し、来年の抱負を宣言。\n<br/>\n<br/>具体的にどこがダメだったのかと問われると、「絶対持って来なきゃいけないものを忘れちゃったり、ブログの期限をちゃんと守らなかったりしてダメだった（笑）」と茶目っ気たっぷりにコメント。そんな岡田の姿に、周りからは「可愛いからいいよ！」とフォローが入っていた。\n<br/>\n<br/><strong>◆晴れ着撮影会に初参加で感慨</strong>\n<br/>\n<br/>この日、晴れ着撮影会に初めての参加となった岡田は、ダリヤを中心に色とりどりの小花が散りばめられた着物を着こなし登場。「今ここに立てていることに実感がないので、地に足が着いていることに感謝。これからも周りの人に感謝を忘れず、頑張りたい。もっともっと、先輩方のように綺麗に輝けたら」と目標を掲げた。\n<br/>\n<br/><strong>◆武井咲、剛力彩芽らが登場</strong>\n<br/>\n<br/>今年は岡田のほか、武井咲、剛力彩芽、河北麻友子、本田望結、吉本実憂、小芝風花、高橋ひかる（※「高」は正式には「はしごだか」）、藤田ニコル、そして5月に行われた「第1回ミス美しい20代コンテスト」でグランプリに輝いた是永瞳の計10名が華やかに集結。2016年、様々な方面で活躍し注目を浴びた12歳～25歳の美女が晴れ着姿を披露した。（modelpress編集部）', 1, '2016-12-08 17:02:42', '2017-02-06 17:02:42', NULL, NULL, NULL, 0, 'https://mdpr.jp/news/1644064', '他の写真・記事詳細をもっと見る', 'https://mdpr.jp/news/1639188', '岡田結実、ドレスアップで“色っぽオーラ”「ドキドキしすぎてびっくり」', 'https://mdpr.jp/news/1635657', '岡田結実、父・ますだおかだ岡田圭右に「怒られる」ことは？激動の1年を回顧　', 'https://mdpr.jp/news/1630985', '「2016年ヒット商品ベスト30」＆「2017年ヒット予測ベスト30」発表', 'https://mdpr.jp/news/1630749', '岡田結実、父・ますだおかだ岡田圭右の“偉大さ”痛感　家庭での素顔明かす', 0, 0, 1, 1, '2016-12-08 17:25:06', '2016-12-08 17:25:06'),
    (98, '4_1_r_20161208_1481185505839366969', 4, 1, 1, 1, 1, 'http://www.oricon.co.jp/news/2082742/', 'dfb22685760de0f8cd4480e7417f6cd2_1481185505', '“バンドリ”声優バンド「Poppin’Party」、アニメ放送前に早くも3rdシングル', '', '<img src=\"http://news.oricon.co.jp/feed/images/size640wh/20161208_161555_size640wh_8169.jpg\" title=\"声優ガールズバンド「Poppin’Party」（左から西本りみ、大橋彩香、愛美、伊藤彩沙、大塚紗英）\" alt=\"声優ガールズバンド「Poppin’Party」（左から西本りみ、大橋彩香、愛美、伊藤彩沙、大塚紗英）\" ><br />\n　キャラクターとリアルライブがリンクするガールズバンドプロジェクト『BanG Dream!（バンドリ！）』から派生して結成された5人組声優ユニット「Poppin’Party」（ポッピン パーティー）が、3rdシングル「走り始めたばかりのキミに／ティアドロップス」（7日発売）をリリースした。<br />\n<br />\n　来年1月21日からTOKYO MXほかでテレビアニメ化される『BanG Dream!』から誕生した「Poppin’Party」は、声優の愛美（G＆Vo）、伊藤彩沙（Key）、西本りみ（B）、大塚紗英（G）、大橋彩香（Dr）が作中のキャラクターにちなんだ楽器を演奏するガールズバンド。<br />\n<br />\n　アニメ放送開始前にして既に1stシングル「Yes! BanG_Dream!」（2月発売）、2ndシングル「STAR BEAT!～ホシノコドウ～」（8月発売）をリリースし、8月にはアニソンライブイベント「Animelo Summer Live 2016 刻 -TOKI-」に出演。11月に東京ドームシティホールで開催したワンマンライブのチケットが30秒で完売するなど、勢いを加速させている。<br />\n<br />\n　ニューシングルは、メンバー全員に初のソロパートがある「走り始めたばかりのキミに」と、ライブでは定番のロックチューン「ティアドロップス」の両A面。楽曲プロデュースはElements Gardenの上松範康が一貫して担当している。<br />\n<br />\n　Blu-ray付生産限定盤（CD＋Blu-ray）、通常盤（CD）の2形態共通で、来年2月5日に東京ドームシティホールで行われる3rdライブのチケット最速抽選申込券（応募は12月11日まで）を封入。付属Blu-rayには、初ライブ映像やキャストのオーディオコメンタリーなどが盛り込まれている。', 1, '2016-12-08 16:24:26', '2017-02-06 16:24:26', NULL, NULL, NULL, 0, 'http://www.oricon.co.jp/news/2080722/?anc=015', '秋元康氏プロデュース“デジタルアイドル”8人全員の絵柄出そろう （16年10月29日）', 'http://www.oricon.co.jp/news/2080066/?anc=015', '秋元康氏、デジタルアイドル手がける　著名クリエイター8人がデザイン担当 （16年10月17日）', 'http://www.oricon.co.jp/news/2081913/?anc=015', '乃木坂46松村・秋元らSPユニットがアニメ映画主題歌　声優初挑戦も （16年11月23日）', '', '', '', '', 0, 0, 1, 1, '2016-12-08 17:25:06', '2016-12-08 17:25:06'),
    (99, '4_1_r_20161208_1481185505846057136', 4, 1, 1, 1, 1, 'https://mdpr.jp/news/1644077', '4d3574e36152f195fd8e32c548ce4572_1481185505', '「逃げ恥」“恋ダンス”が新コラボ 星野源＆石田ゆり子に安住アナ参戦', '', '【安住紳一郎・石田ゆり子／モデルプレス＝12月8日】9日放送のTBS系「ぴったんこカン・カン」（よる7時56分）は、同局の火曜ドラマ「逃げるは恥だが役に立つ」（毎週火曜よる10時）とコラボレーションする。<br/><br/><img src=\"https://mdpr.jp/photo/images/2016/12/08/200_92f6442381e5e47d51042299eed63e556178554f8063f092.jpg\" alt=\"石田ゆり子（中央）、星野源（左）とともに“恋ダンス”を踊った安住紳一郎ＴＢＳアナウンサー（右）／（C）TBS\" /><br/><div class=\"caption\">（写真 : 石田ゆり子（中央）、星野源（左）とともに“恋ダンス”を踊った安住紳一郎ＴＢＳアナウンサー（右）／（C）TBS）</div><br/><strong>◆「逃げ恥」って？</strong>\n<br/>\n<br/>同ドラマは、月刊「Kiss」（講談社）で連載中の海野つなみ氏の同名漫画が原作。彼氏なし・院卒なのに内定ゼロ、さらには派遣切りにあい誰からも必要とされない辛さ、居場所の無さを強く感じている25歳の主人公・森山みくり（新垣）が、あることがきっかけで恋愛経験の無い独身サラリーマン・津崎平匡（星野源）と「仕事としての結婚」をすることになる、“夫＝雇用主、妻＝従業員”という契約結婚を描いた社会派ラブコメディとなっている。\n<br/>\n<br/><strong>◆石田ゆり子が登場</strong>\n<br/>\n<br/>今回の「ぴったんこカン・カン」には、みくりの伯母であり親友でもあるアラフィフの独身キャリアウーマン・土屋百合役の石田ゆり子が出演。どちらかといえばインドア派だという石田の希望を叶えるため、同局の安住紳一郎アナウンサーが街の最新スポットや人気のお店を案内する。\n<br/>\n<br/><strong>◆「逃げ恥」スタジオに潜入</strong>\n<br/>\n<br/>さらに、「逃げ恥」を撮影している緑山スタジオを訪れた石田と安住アナ。スタジオの様子やドラマのセットなどを石田が案内し、撮影中の「逃げ恥」キャストも続々登場する。\n<br/>\n<br/>そんな中、石田が「安住さんと恋ダンスが踊りたい」と一言。必死に抵抗する安住アナだったが、石田やスタッフに押され、急遽エンディングで流れる“恋ダンス”（主題歌『恋』に合わせてキャスト陣が踊るダンス）を、石田と平匡役で主題歌「恋」を歌う星野と踊ることに。<br/><br/><strong>◆石田ゆり子・星野源・安住アナで“恋ダンス”</strong>\n<br/>\n<br/>番組では、恋ダンスの練習風景から本番までの様子をたっぷり放送。今回の“恋ダンス”がドラマのエンディングで流れたら面白いという石田の提案もあったが、「石田ゆり子・星野源・安住紳一郎の“恋ダンス”ぴったんこバージョンの完成披露版」として「ぴったんこカン・カン」のエンディングで流れる。\n<br/>\n<br/>なお、同バージョンは、9日の「ぴったんこカン･カン」放送終了後から13日の「逃げるは恥だが役に立つ」第10話放送直前まで、ドラマ公式ホームページ内のみで公開される。（modelpress編集部）', 1, '2016-12-08 14:35:23', '2017-02-06 14:35:23', NULL, NULL, NULL, 0, 'https://mdpr.jp/news/1644077', '他の写真・記事詳細をもっと見る', 'https://mdpr.jp/news/1643692', '「逃げ恥」みくり×平匡さんが“いちゃいちゃ”！？バックハグに初デート…視聴者興奮「萌え死ぬ」「早く火曜こい」', 'https://mdpr.jp/news/1643634', '「逃げ恥」風見さん、百合ちゃんの涙を“ソフト壁ドンガード”「ただのキュンキュンじゃない」と反響', 'https://mdpr.jp/news/1643628', '「逃げ恥」みくりが嫉妬、平匡さんに思わず告白で…急展開に興奮収まらず「過去最高にヤバい」「神回」', 'https://mdpr.jp/news/1643487', '石田ゆり子＆ひかり姉妹、“約20年前”美人2ショット公開で「お宝」「良い写真」と反響続々', 0, 0, 1, 1, '2016-12-08 17:25:06', '2016-12-08 17:25:06'),
    (100, '4_1_r_20161208_1481185506124757695', 4, 1, 1, 1, 1, 'http://www.oricon.co.jp/news/2082725/', 'a062c36481b189ab88242116491e62c4_1481185506', '松山ケンイチ、家族の大切さ語る「自分を取り戻させてくれる存在」', '', '<img src=\"http://news.oricon.co.jp/feed/images/size640wh/20161208_110258_size640wh_1303.jpg\" title=\"最新主演映画『聖の青春』の撮影秘話を語った俳優・松山ケンイチ （C）oricon ME inc.　撮影：RYUGO SAITO\" alt=\"最新主演映画『聖の青春』の撮影秘話を語った俳優・松山ケンイチ （C）oricon ME inc.　撮影：RYUGO SAITO\" ><br />\n　弱冠29歳の若さで亡くなった実在のプロ棋士・村山聖の生きざまを描いた映画『聖の青春』が公開中。村山聖を演じる松山ケンイチは、インタビューで役者人生初の体験があったという撮影秘話を語った。<br />\n<br />\n■初めて芝居であることを忘れるほど“深く没頭した”<br />\n<br />\n　東出昌大演じる羽生善治と村山との最後の対局シーンは、約3時間の長回しで撮影し、その映像から数分間のみが使われた。松山と東出は実際に行われた村山と羽生の対局の手を憶えて臨んだという。<br />\n<br />\n　「3時間の長回しは初めての体験でした。僕は演じる人物のリサーチを徹底して行うのですが、そこに頼り過ぎてしまうとコピーにしかならないので、演じる前にリサーチした情報を捨てたり忘れるようにしています。今回も捨てていく作業をしてから演じたのですが、演じている途中で芝居をしている感覚自体を捨てていることに気づきました。それは今作が初めてで新鮮でした」（松山）。<br />\nネフローゼという難病と戦いながら将棋に全てを懸けた村山聖の壮絶な生きざまに、松山は徹底した役作りで臨んでいる。<br />\n<br />\n<br />\n■自分を取り戻させてくれるのは「家族」<br />\n<br />\n　松山は本作の役作りのために体形を変えるところからスタート。準備期間は1年半以上かけており、今までの役作りの中で最も長いものだった。そこまで役に没頭する松山の心の支えは家族だったという。<br />\n<br />\n　「20代前半は仕事が続いて芝居に没頭すると、自分が今どこにいるのかわからなくなる時期がありました。そういう時にはお酒を飲み過ぎたり、ケンカをしたりして自分を痛めつけることで普段の自分らしさを取り戻していたんですよね。今もあまりに役に没頭しすぎて怖くなることがあります。ですが、今は家に帰って家族とふれあうことで本来の自分を取り戻せるようになりました」と松山。<br />\n<br />\n　「仕事は大切ですけれど、あくまでも生活のためにやるもの。プライベートでは自分の人生も大事にしたい。家族を持ってからこんな風に考え方が変わりました」と、30代になり考え方やライフスタイルが変化していると明かした。<br />\n公開中の本作は角川映画40周年作品。', 1, '2016-12-08 11:30:03', '2017-02-06 11:30:03', NULL, NULL, NULL, 0, 'http://mens.oricon.co.jp/feature/photo/52/915/?anc=162', '【フォトギャラリー】松山ケンイチ『聖の青春』スペシャルインタビュー', 'http://beauty.oricon.co.jp/trend-culture/trend/news/86870/?anc=162', '【結婚会見】松山ケンイチが小雪との生活を暴露　「尻に敷かれてます」', 'http://beauty.oricon.co.jp/trend-culture/trend/news/photo/88623/1/?anc=162', '【写真】小雪、背中が大きく開いたウエディングドレス姿を披露', '', '', '', '', 0, 0, 1, 1, '2016-12-08 17:25:06', '2016-12-08 17:25:06'),
    (101, '4_1_r_20161208_1481185506128563273', 4, 1, 1, 1, 1, 'https://mdpr.jp/news/1644086', 'db3090189450bb19ef1cc635f955f59b_1481185506', '日本一のイケメン高校生ファイナリスト、圧倒的1位の自己アピールに「鳥肌立った」', '', '【男子高生ミスターコン／モデルプレス＝12月8日】“日本一のイケメン高校生”を決める「男子高生ミスターコン2016」の全国ファイナリストが出演するAbemaTVの番組「男子高生ミスターコンTV」第7回（毎週土曜よる6時）が放送。今回は自己アピール動画コンテストが行われた。<br/><br/><img src=\"https://mdpr.jp/photo/images/2016/09/11/200_a6eea712c2e0730d2bc95073e0c579093fe8b47a25a5459b.jpg\" alt=\"mireiが選ぶ「自己アピール動画コンテスト」1位に選ばれた本田響矢くん（C）モデルプレス\" /><br/><div class=\"caption\">（写真 : mireiが選ぶ「自己アピール動画コンテスト」1位に選ばれた本田響矢くん（C）モデルプレス）</div><br/>12月18日に行われる最終決戦「全国ファイナイル審査」を控える全国ファイナリスト13人。自己アピール動画の審査は、MCを務める変顔モデル・mireiが担当し、独断でベスト3を決定した。\n<br/>\n<br/>ファイナリストたちはそれぞれ趣向を凝らした動画を披露。特技のバク転を見せたり、ピアノやギターを弾いたり、ダンスも公開。中にはけん玉をするファイナリストも。\n<br/>\n<br/><strong>◆mirei審査員のハートを射止めた自己アピール動画は…</strong>\n<br/>\n<br/>1位：本田響矢(中部地方 グランプリ）\n<br/>2位：藤井晴大（中国・四国地方 準グランプリ）\n<br/>3位：林拓磨（高1ミスターコン 準グランプリ）\n<br/>\n<br/>1位の本田くんが見せたのはアカペラ歌唱。ビブラートを活かした美しい歌声で中島みゆきの「糸」を披露。「ヤバイ鳥肌立った！最高！」と興奮するmireiは、本田くんをぶっちぎりで1位に選んでいた。\n<br/>\n<br/>2位の藤井くんは、友達と普段している動画を公開。スタジオで見せる姿とはまた違った、テンション高い藤井くんの姿がそこに。趣味のスケボーやカメラを披露し、途中で転んでしまうシーンも。リアルな素の姿に好感を覚えたmireiは、藤井くんを2位と発表した。\n<br/>\n<br/>3位の林くんは、豪華な自宅で撮影。ヘアアレンジを紹介する際、ノーセットから一瞬でセット完了する動画編集テクも見せた。mireiも「編集が良かった」と、動画全体の良さでランクインした。（modelpress編集部）\n<br/>\n<br/><strong>■「男子高生ミスターコンTV」第8回…12月10日（土）18時～／AbemaTV</strong>\n<br/>\n<br/>日本一のイケメン男子高生を決める全国規模で開催される「男子高生ミスターコン2016」。 数あるエントリー者の中から、難関な審査を突破したファイナリスト13名が、グランプリを目指して奮闘する姿をAbemaTVが完全密着。彼らの魅力をあらゆる角度から迫っていく。 \n<br/>\n<br/>番組URL：<span class=\"moki_url_text\">https://abema.tv/channels/abema-special/slots/8fuBWx7A5FG1EP</span>\n<br/>\n<br/><strong>■男子高生ミスターコン2016</strong>\n<br/>\n<br/>みんなで選ぶ「にほんいち、かっこいい男子高生」をコンセプトに、全国規模で開催される男子高生限定のコンテスト。昨年より規模が日本全国に拡大。同系列のコンテストでは過去に雑誌『SamuraiELO』で活躍中の笹森裕貴や小林佑弥、『Popteen』で活躍中の池田翼らを輩出している。\n<br/>\n<br/>今回グランプリを獲得すると、大手事務所所属や賞金100万円だけでなく、「OCEANTOKYO」の専属モデル「OCEANS STAR」に選ばれ、ファッションブランド「SPINNS」や人気フレグランスブランド「RISINGWAVE」のイメージモデルにも。なお“日本一のイケメン高校生”が決まるファイナル審査は12月18日に開催される。', 1, '2016-12-08 14:31:32', '2017-02-06 14:31:32', NULL, NULL, NULL, 0, 'https://mdpr.jp/news/1644086', '他の写真・記事詳細をもっと見る', 'https://mdpr.jp/news/1640380', '“日本一のイケメン高校生”ミスターコン、運勢ランキングNo.1が決定', 'https://mdpr.jp/news/1638489', '“日本一のイケメン高校生”に密着　歩くだけで街が騒然？イケメンすぎる人気とは', 'https://mdpr.jp/news/1636743', '日本一のイケメン高校生、胸キュンと話題“カーテンの刑”でバトル！映画「きょうのキラ君」×「男子高生ミスターコン2016」がコラボ', 'https://mdpr.jp/news/1635201', '“日本一のイケメン高校生”ファイナリストへ“遠慮なし”の本気指導', 0, 0, 1, 1, '2016-12-08 17:25:06', '2016-12-08 17:25:06'),
    (102, '4_1_r_20161208_1481185506460285167', 4, 1, 1, 1, 1, 'http://www.oricon.co.jp/news/2082704/', 'd75a996f7eacfc09ed3152534a2eb1ad_1481185506', '瀬戸朝香、2年ぶり連ドラ主演　イノッチも「応援はしてくれています」', '', '<img src=\"http://news.oricon.co.jp/feed/images/size640wh/20161208_150833_size640wh_2779.jpg\" title=\"2年ぶりに連ドラの主演を務める瀬戸朝香 （C）ORICON NewS inc.\" alt=\"2年ぶりに連ドラの主演を務める瀬戸朝香 （C）ORICON NewS inc.\" ><br />\n　女優の瀬戸朝香（39）が8日、第2子出産後の女優復帰作となるNHK・BSプレミアムのドラマ『女の中にいる他人』（1月8日スタート、毎週日曜　後10：00、全7回）の試写会（同局で開催）に出席した。夫は同局の『あさイチ』にレギュラー出演している井ノ原快彦（40）。記者から家族の反応を聞かれると、「応援はしてくれています」と答えていた。<br />\n<br />\n　同ドラマは、ある日、夫に「不倫」と「殺人」を告白された平凡な主婦の百合子（瀬戸）が、家族を守るため、次第に別の顔を持った女へと変貌していくクライム・サスペンス。1966年に公開された成瀬巳喜男監督の傑作、映画『女の中にいる他人』のリメイクとなる。<br />\n<br />\n　瀬戸は「子どものためなら何でもできる、家族を守らなければと思いつめる百合子の気持ちはわかるのですが、夫の罪を隠し通そうとする強さはどうなんだろう。実際、考えたら恐ろしい」と役をつかむまでに苦労があったことを明かしたが、「私にとってもすてきな作品に仕上がるんじゃないか、と。視聴者の皆さんにもこの先どうなっていくのだろうとハラハラドキドキしながら観ていただける、と自信を持っておすすめできる作品になりました」とアピール。<br />\n<br />\n　今後の活動についても言及し、「子育て中心に過ごした2年間も楽しくて、仕事復帰したいという思いはそれほどなかったのですが、実際、復帰してみると、やはり演じるって楽しいな、女優として仕事をやっていきたいなと思いました」と自分自身にも自信が持てた様子だった。<br />\n<br />\n　試写会には、尾美としのり（51）、石黒賢（50）、板谷由夏（41）も同席。瀬戸を除けば、尾美も石黒も板谷も、その顔を観ないクールはないほど、数多くのドラマ、そして映画でも活躍しているが、瀬戸と尾美、尾美と石黒は本作が初共演となった。<br />\n<br />\n　石黒は「登場人物、全員悪人。ジェットコースタームービーのように目が話せないドラマになっていると思います」と手応え。不倫の末に殺人を犯してしまった百合子の夫・和男を演じた尾美は、回を増すごとにさまざまな人物から追い詰められていき、「撮影が進むにつれ、尾美さんがどんどん小さく見えていった」（瀬戸）。実際、尾美は7キロ減量して撮影に臨んでおり、「食欲がない、眠れない、というせりふがあったので普通でいたら怒られるかな、と思って（笑）。今回、現場で弁当も、皆さんが持ち寄った差し入れも食べなかった。すごく損した現場でした」と話して笑いを誘っていた。', 1, '2016-12-08 15:10:02', '2017-02-06 15:10:02', NULL, NULL, NULL, 0, 'http://www.oricon.co.jp/news/2012492/photo/2/?anc=014', '【写真】ママになっても…シックなドレスでスレンダースタイルの瀬戸朝香', 'http://www.oricon.co.jp/news/2045665/photo/1/?anc=014', '【写真】パンツスタイルで抜群のスタイルを披露した瀬戸朝香', 'http://beauty.oricon.co.jp/trend-culture/love/news/2031235/?anc=014', '瀬戸朝香が第2子長女出産　イノッチ「妻に似てくれることを祈りつつ…」 （13年11月24日）', '', '', '', '', 0, 0, 1, 1, '2016-12-08 17:25:06', '2016-12-08 17:25:06'),
    (103, '4_1_r_20161208_1481185506466134799', 4, 1, 1, 1, 1, 'https://mdpr.jp/news/1644070', '745f8867cccdfdd4809b2cfce07f6910_1481185506', '嵐・相葉雅紀、二宮和也の秘密を暴露「すげえ言いづらくて…」', '', '【相葉雅紀・二宮和也／モデルプレス＝12月8日】嵐の相葉雅紀が8日放送の日本テレビ系情報番組「PON！」（毎週月～木曜、あさ10時25分）にVTR出演。メンバーの二宮和也の秘密を暴露した。<br/><br/><img src=\"https://mdpr.jp/photo/images/2016/04/05/200_43efbc1443985a043b23b7835ab236dec5e974e7f7a5adaf.jpg\" alt=\"嵐・相葉雅紀、二宮和也の秘密を暴露「すげえ言いづい」 （C）モデルプレス\" /><br/><div class=\"caption\">（写真 : 嵐・相葉雅紀、二宮和也の秘密を暴露「すげえ言いづい」 （C）モデルプレス）</div><br/>VTRにて、MCでジャニーズの大ファンとしても知られる青木源太同局系アナウンサーは、相葉へ「（二宮と）ジュニア時代に同じ電車で帰ってましたよね、その時のエピソードとか」とファンの間で“総武線コンビ”として知られる2人のエピソードトークをお願い。青木アナのリクエストに対し、相葉は「昔の話じゃないけどいいですか？」とことわりを入れ、大野智が11月26日に誕生日を迎えた時の話を披露した。\n<br/>\n<br/>「俺とニノがはまってる海外ドラマがあって、面白いから（大野にも）観てもらおうと思って1から出ているところまで（DVDを）買って2人で割り勘して渡したんですよ」と2人で大野へ誕生日プレゼントを贈ったそう。しかし「あれから一週間くらい経つけど割り勘のお金、俺もらってないんですよ」と暴露した。\n<br/>\n<br/><strong>◆青木アナ通してお願い「待ってます」</strong>\n<br/>\n<br/>続けて、二宮からまだ支払われていないことに関し「言いづらくて、すげえ言いづらくて…」という相葉は「言っといて」と青木アナにお願い。青木アナは「このオンエアご覧になってて気づく可能性もあります」と「二宮さん、これは払っておいたほうがいいと思いますよ、本当に」と訴えると、相葉も「待ってます」と笑顔を見せた。（modelpress編集部）', 1, '2016-12-08 14:20:37', '2017-02-06 14:20:37', NULL, NULL, NULL, 0, 'https://mdpr.jp/news/1644070', '他の写真・記事詳細をもっと見る', 'https://mdpr.jp/news/1643066', '嵐・相葉雅紀「ドッキリだと思った」紅白単独司会への本音と決意「嵐も近くに居てくれる」', 'https://mdpr.jp/news/1632631', '嵐・二宮和也、“ニノ太郎”でPPAP披露予告にファン「多大な期待」', 'https://mdpr.jp/news/1634482', '嵐・相葉雅紀＆有村架純「紅白歌合戦」司会起用の理由は？', 'https://mdpr.jp/news/1634481', '嵐・相葉雅紀「紅白歌合戦」単独で白組司会に抜擢「こんな有難いお話は一生いただけない」＜コメント全文＞', 0, 0, 1, 1, '2016-12-08 17:25:06', '2016-12-08 17:25:06'),
    (104, '4_1_r_20161208_1481185506558410211', 4, 1, 1, 1, 1, 'http://www.oricon.co.jp/news/2082737/', '2bfca5f1feb59a4c9d520ef97c9237ed_1481185506', 'セカオワ、『The Dinner』ツアーの興奮再び　ダイジェスト映像＆ジャケ写公開', '', '<img src=\"http://news.oricon.co.jp/feed/images/size640wh/20161208_144922_size640wh_2378.jpg\" title=\"SEKAI NO OWARIがアリーナツアー映像作品のジャケット写真公開\" alt=\"SEKAI NO OWARIがアリーナツアー映像作品のジャケット写真公開\" ><br />\n　人気バンド・SEKAI NO OWARIが、来年1月11日にリリースするDVD／Blu-ray『The Dinner』のダイジェスト映像とジャケット写真を公開した。<br />\n<br />\n　本作は3月～6月まで全国11ヶ所25公演で25万人を動員した全国アリーナツアー『The Dinner』のファイナルとして行われたさいたまスーパーアリーナ公演を完全収録。きょう8日に公開されたダイジェストは、ライブの興奮と芸術作品のような美しい世界観を楽しめる“いいとこ取り”の映像となっている。<br />\n<br />\n　ジャケット写真は、ベルギーの画家・マグリットの絵から構想を得たというステージセットを採用。鮮やかな青空の下、古びた洋館に明かりが灯る、昼と夜が共存した1枚の絵画のような画像となっている。<br />\n<br /><br />\n<iframe width=\"560\" height=\"315\" src=\"https://www.youtube.com/embed/wkdcquMct2Q\" frameborder=\"0\" allowfullscreen></iframe>', 1, '2016-12-08 16:00:00', '2017-02-06 16:00:00', NULL, NULL, NULL, 0, 'http://music.oricon.co.jp/php/artist/ArtistTop.php?artist=A44682&ref_cd=MS0098&anc=015', '【試聴】SEKAI NO OWARIの主な配信楽曲', 'http://www.oricon.co.jp/special/49067/?anc=015', 'Fukaseが語った「生まれてきて良かった」の意味とは？', 'http://www.oricon.co.jp/news/2082071/?anc=015', 'セカオワ・DJ LOVEはなぜフェイスパックになったのか （16年11月26日）', '', '', '', '', 0, 0, 1, 1, '2016-12-08 17:25:07', '2016-12-08 17:25:07'),
    (105, '4_1_r_20161208_1481185506566381848', 4, 1, 1, 1, 1, 'https://mdpr.jp/news/1644069', '951b80037d656f8d62670bf5f7e318ab_1481185506', 'りゅうちぇる、コンプレックスが“持ちネタ”に「すっごいウケる」', '', '【りゅうちぇる／モデルプレス＝12月8日】タレントのりゅうちぇるが7日深夜放送の日本テレビ系「ナカイの窓」（毎週水曜23時59分～、※この日は24時19分）にゲスト出演。MCを務めるSMAPの中居正広からアドバイスを受けた。<br/><br/><img src=\"https://mdpr.jp/photo/images/2016/10/27/200_85ddf01c7977adffb22685103e8b8a1e3ebda823ddcdb35c.jpg\" alt=\"「ナカイの窓」に出演したりゅうちぇる （C）モデルプレス\" /><br/><div class=\"caption\">（写真 : 「ナカイの窓」に出演したりゅうちぇる （C）モデルプレス）</div><br/><strong>◆りゅうちぇる、濃いヒゲをレーザー脱毛中だけど…</strong>\n<br/>\n<br/>最近、りゅうちぇるはバラエティー番組に出演した際、コンプレックスである濃いヒゲをイジられ、カメラに向かって「やめて！映さないで！」と叫ぶという流れが定番に。この日も中居から「砂消し」「アスファルトみたい」とイジられ、「やめて！映さないで！アップにしないで」とりゅうちぇるが訴えるお決まりの流れになり、スタジオは大盛り上がり。\n<br/>\n<br/>ここで中居はりゅうちぇるがレーザー脱毛をしていることに言及し、「レーザー脱毛しない方がいいって。ここ（口元）が青い方がいいよ、愛嬌あって」と助言。「えー！？本当に！？」と半信半疑で返答したりゅうちぇるは、「最近さ、これでオチ持っていったらすっごいウケるんだよね」と真剣なトーンで相談し、スタジオの笑いを誘っていた。（modelpress編集部）', 1, '2016-12-08 15:38:22', '2017-02-06 15:38:22', NULL, NULL, NULL, 0, 'https://mdpr.jp/news/1644069', '他の写真・記事詳細をもっと見る', 'https://mdpr.jp/news/1643945', 'りゅうちぇる、スーツ着こなし“イケちぇる”姿でTV初出演　本名で登場', 'https://mdpr.jp/news/1643725', 'りゅうちぇる、ぺことの記念日毎月祝福「めんどくさくない？」の質問に回答', 'https://mdpr.jp/news/1642860', 'りゅうちぇる、噂の心霊写真にまさかの鑑定結果「3体の霊が…」', 'https://mdpr.jp/gal/1641962', '藤田ニコル、りゅうちぇるが苦手だった過去「一緒は無理」共演NGの危機も？', 0, 0, 1, 1, '2016-12-08 17:25:07', '2016-12-08 17:25:07'),
    (106, '4_1_r_20161208_1481185506806993193', 4, 1, 1, 1, 1, 'http://www.oricon.co.jp/news/2082706/', '99f30b7ab40b38c93a52192aa76b9595_1481185506', '山村紅葉、声優業に手応え　初挑戦づくしの1年に笑顔「東宝カレンダーに載った！」', '', '<img src=\"http://news.oricon.co.jp/feed/images/size640wh/20161208_155005_size640wh_9160.jpg\" title=\"声優業への手応えを語った山村紅葉 （C）ORICON NewS inc.\" alt=\"声優業への手応えを語った山村紅葉 （C）ORICON NewS inc.\" ><br />\n　女優の山村紅葉（56）が8日、都内で行われた海外ドラマ『ブラックリスト　シーズン4』公開アフレコに参加。海外ドラマの吹替え声優初挑戦となった山村は「サスペンスであればと思って、このお仕事を受けさせていただきました。最初の吹替えは、とってもドキドキしながらやらせてもらいました」と喜びをかみしめた。<br />\n<br />\n　同作は、世界中の凶悪事件の頂点に立つレイモンド・“レッド”・レディントンとFBIの新人捜査官のエリザベス・“リズ”・キーンが数々の犯罪捜査に挑むアクションサスペンス。山村は、最新シリーズの第1話「エステバン」に登場する誘拐犯・ロミーナの日本語吹替え声優を務める。<br />\n<br />\n　『サザエさん』（フジテレビ）での迷惑おばさん役やABC創立65周年記念連続ラジオドラマ『ナデシコですから』など、今年に入ってから声優としての仕事が増えてきたといい「夏頃のアニメの仕事をやったら、とてもほめていただきまして、ラジオドラマも挑戦してみて、ちょっと声に対して自信ができてきました」とニッコリ。「NHKの朝ドラにも出させてもらったし、東宝のカレンダーにも載ったので、あらかた初挑戦のものが終わりました」と初づくしとなった今年に充実感をにじませた。<br />\n<br />\n　2016年時点で、500本以上の2時間ドラマに出演していることから“サスペンスドラマの裏女王”の異名を取る山村。サスペンスという言葉に敏感になっているようで「サスペンスはもともと好きだったので、大学もミステリークラブに入っていて、母（山村美紗）の本とかも読んでいました。サスペンスは人より先に見られるといいなとか、謎解きとかは大好きです」と笑顔を見せていた。<br />\n<br />\n　山村が出演する同シリーズの第1話「エステバン」は、来年1月にBSスカパー！、J：COMテレビ、ひかりTVチャンネルにて放送予定。', 1, '2016-12-08 15:50:01', '2017-02-06 15:50:01', NULL, NULL, NULL, 0, 'http://www.oricon.co.jp/news/2073598/?anc=148', '三倉茉奈、3度目の朝ドラは「少しばかりヒロインをいじめる」役どころ （16年06月18日）', 'http://www.oricon.co.jp/news/2073472/?anc=148', '三倉茉奈、『べっぴんさん』で3度目の朝ドラ　初の“佳奈なし”単独出演 （16年06月16日）', 'http://www.oricon.co.jp/news/2047182/?anc=148', 'サスペンスドラマ“裏女王”山村紅葉、犯人探しのコツ伝授 （15年01月14日）', '', '', '', '', 0, 0, 1, 1, '2016-12-08 17:25:07', '2016-12-08 17:25:07'),
    (107, '4_1_r_20161208_1481185506820664164', 4, 1, 1, 1, 1, 'https://mdpr.jp/news/1644056', '641cdb046c6891375aad5f10c5a9ef70_1481185506', '武井咲、藤田ニコルと初対面「同じ事務所だったんだ」', '', '【武井咲・藤田ニコル／モデルプレス＝12月8日】女優の武井咲と、雑誌「Popteen」モデルでタレントの“にこるん”こと藤田ニコルが8日、都内で行われた所属事務所であるオスカープロモーションの晴れ着撮影会に出席。この日、藤田と初対面したという武井は、同じ事務所に所属していたということに驚いた。<br/><br/><img src=\"https://mdpr.jp/photo/images/2016/12/08/200_8cffd8f9e771bcef0a2ab1baaffa91c490e2037b837c3d8f.jpg\" alt=\"武井咲、藤田ニコルと初対面「同じ事務所だったんだ」 （C）モデルプレス\" /><br/><div class=\"caption\">（写真 : 武井咲、藤田ニコルと初対面「同じ事務所だったんだ」 （C）モデルプレス）</div><br/>最後のあいさつを求められた武井は「本日は寒い中ありがとうございました。この晴れ着撮影会は代々あるもので、今ここに立てていることに感謝しています」と思いを伝え、続けて「まだまだこうしてニコルさんだったり、私初めてお会いしたんですけど『同じ事務所だったんだ』って衝撃が（笑）」と告白。藤田も、武井と顔を見合わせて「そうですね。びっくりしちゃいましたよね」と同意していた。\n<br/>\n<br/><strong>◆武井咲の来年の抱負</strong>\n<br/>\n<br/>笑いに包まれる中で「切磋琢磨してがんばっていければと思うので来年もよろしくお願いいたします」と呼びかけ。また、2016年は作品に恵まれた1年だと振り返り「来年は今よりもっとがんばらないといけないと思います」と意欲的で「体調管理をしっかりしていきたい」と誓った。\n<br/>\n<br/><strong>◆剛力彩芽・河北麻友子らも登場</strong>\n<br/>\n<br/>今年は武井、藤田のほか剛力彩芽、河北麻友子、本田望結、吉本実憂、小芝風花、高橋ひかる（※「高」は正式には「はしごだか」）、岡田結実、そして5月に行われた「第1回ミス美しい20代コンテスト」でグランプリに輝いた是永瞳の計10名が華やかに集結。2016年、様々な方面で活躍し注目を浴びた12歳～25歳の美女が晴れ着姿を披露した。（modelpress編集部）', 1, '2016-12-08 16:20:09', '2017-02-06 16:20:09', NULL, NULL, NULL, 0, 'https://mdpr.jp/news/1644056', '他の写真・記事詳細をもっと見る', 'https://mdpr.jp/news/1644023', '武井咲・剛力彩芽・河北麻友子・藤田ニコルら美女10人が華やか晴れ着姿で豪華集結', 'https://mdpr.jp/news/1642839', '藤田ニコル「月イチで虫を食べている」人気者ならではの悩みも', 'https://mdpr.jp/cinema/1642805', '武井咲、妖怪の仕業報告？「なにかいる」', 'https://mdpr.jp/gal/1642755', '藤田ニコル“SNS疲れ”を告白 心配＆エール殺到', 0, 0, 1, 1, '2016-12-08 17:25:07', '2016-12-08 17:25:07'),
    (108, '4_1_r_20161208_1481185507075408524', 4, 1, 1, 1, 1, 'https://mdpr.jp/gal/1644060', '0bdacdc4af24360f46f6a3c5dd7e6127_1481185507', 'みちょぱ、通知表公開でスタジオ騒然 中居正広「学校行ってた？」', '', '【みちょぱ／モデルプレス＝12月8日】雑誌「Popteen」モデルの“みちょぱ”こと池田美優が7日深夜放送の日本テレビ系「ナカイの窓」（毎週水曜23時59分～、※この日は24時19分）にゲスト出演。中学3年生の時の通知表を公開した。<br/><br/><img src=\"https://mdpr.jp/photo/images/2016/05/23/200_77e001cf9f20688a8f730fbda08d94874c531af9960cb132.jpg\" alt=\"「ナカイの窓」に出演した“みちょぱ”こと池田美優 （C）モデルプレス\" /><br/><div class=\"caption\">（写真 : 「ナカイの窓」に出演した“みちょぱ”こと池田美優 （C）モデルプレス）</div><br/><strong>◆みちょぱ、中3の通知表を公開</strong>\n<br/>\n<br/>番組では「東大VSおバカ、どちらが幸せ？」というテーマでトークを展開。おバカ代表としてタレントのりゅうちぇるらとともに出演したみちょぱは、中学3年生の時の通知表を公開した。\n<br/>\n<br/>保健体育を除くほぼ全ての教科が5段階評価中の1という衝撃の内容で、司会の中居正広をはじめ共演者は騒然。\n<br/>\n<br/>MCの中居正広は「学校行ってたの？」と授業を受けていないために低い成績だったのかと確認したが、みちょぱは「まぁまぁ行ってた」とあっけらかんと返答し、スタジオを笑わせていた。（modelpress編集部）\n<br/>\n<br/><strong>■池田美優（いけだ・みゆう）プロフィール</strong>\n<br/>\n<br/>愛称：みちょぱ\n<br/>生年月日：1998年10月30日\n<br/>出身地：静岡県\n<br/>血液型：A型\n<br/>身長：164cm\n<br/>\n<br/>雑誌「Popteen」モデルで“JKモデルカリスマ四天王”。当時中学3年生の6人で結成された「スーパーJC」のメンバーとして、2013年末から「Popteen」誌面に登場。2014年7月号では「JC」加入から史上最速でカバーガールに起用。2015年1月号で初ピン表紙を飾ると、以降5ヶ月連続表紙の快挙を達成。ギャルなルックスと抜群のプロポーションでティーンから圧倒的な人気を誇る。また天真爛漫なキャラクターなどでバラエティ番組にも出演するなど、活躍の場を広げている。', 1, '2016-12-08 15:58:06', '2017-02-06 15:58:06', NULL, NULL, NULL, 0, 'https://mdpr.jp/gal/1644060', '他の写真・記事詳細をもっと見る', 'https://mdpr.jp/gal/1643034', 'みちょぱ、不登校の反抗期に母と殴り合い ギャルサー総代表時代も回顧', 'https://mdpr.jp/gal/1642285', 'みちょぱ、美人母と殴り合いのケンカ？知られざる過去明らかに', 'https://mdpr.jp/gal/1641717', 'にこるん×みちょぱ×ゆらゆら、3ショットで「Popteen」久々表紙', 'https://mdpr.jp/gal/1638164', 'みちょぱ、すっぴん？お家メガネ姿公開', 0, 0, 1, 1, '2016-12-08 17:25:07', '2016-12-08 17:25:07'),
    (109, '4_1_r_20161208_1481185507075379736', 4, 1, 1, 1, 1, 'http://www.oricon.co.jp/news/2082740/', '8f678a5d210470d2fec81e2ffe9d02f7_1481185507', 'VRで海外旅行へ　世界初 “空の旅”の疑似体験ができる施設が誕生', '', '<img src=\"http://news.oricon.co.jp/feed/images/size640wh/20161208_153004_size640wh_7264.jpg\" title=\"VRとリアルが融合した世界初 “空の旅”の疑似体験ができる施設が誕生\" alt=\"VRとリアルが融合した世界初 “空の旅”の疑似体験ができる施設が誕生\" ><br />\n　普段の旅行や出張ではなかなか利用する機会のない旅客機のファーストクラスを、日本にいながら疑似体験できるエンタテインメント施設「FIRST AIRLINES」が8日、東京・西池袋に誕生する。シェフによる機内食を提供し、世界の街並みや音楽などをVRで体験できる。<br />\n<br />\n　同施設ではVR機器を使い、地上にいながら航空機内を舞台にした空の旅を擬似体験できる。リアルでもCAの接客、就航先のニューヨーク、パリ、ローマの特色を生かした飲食を日替わりで提供。上空8000mの世界の映像体験に注目だ。また、フライト後のミニラウンジでの参加者同士の交流もできる。<br />\n<br />\n　1回12名限定の完全予約制。約100分間のフライト体験とラウンジ利用80分でファーストクラス料金は税抜価格5980円となっている。VRコンテンツを主軸にすえた新しいサービスに注目だ。', 1, '2016-12-08 15:53:07', '2017-02-06 15:53:07', NULL, NULL, NULL, 0, 'http://mens.oricon.co.jp/news/2082430/?cat_id=PRR&anc=149', 'メルセデス・ベンツでラーメン!?　「メルセデス・ベンツ　コネクション」のクリスマスコンテンツ （16年12月02日）', 'http://mens.oricon.co.jp/news/2079447/?cat_id=PRR&anc=149', '大人の遊び　超低空飛行で新感覚の「ドローン」が登場 （16年10月04日）', 'http://mens.oricon.co.jp/news/2079242/?cat_id=PRR&anc=149', '名作30タイトルを収録したミニ・ファミコン発売 （16年09月30日）', '', '', '', '', 0, 0, 1, 1, '2016-12-08 17:25:07', '2016-12-08 17:25:07'),
    (110, '4_1_r_20161208_1481185507307493248', 4, 1, 1, 1, 1, 'https://mdpr.jp/news/1644023', 'ab71d85888690009aa32f10405989dd9_1481185507', '武井咲・剛力彩芽・河北麻友子・藤田ニコルら美女10人が華やか晴れ着姿で豪華集結', '', '【武井咲・剛力彩芽・河北麻友子・藤田ニコル／モデルプレス＝12月8日】武井咲、剛力彩芽、河北麻友子、藤田ニコルらオスカープロモーション所属の女優・タレント陣が8日、都内にて毎年恒例の晴れ着撮影会を行った。<br/><br/><img src=\"https://mdpr.jp/photo/images/2016/12/08/200_fdd15ed6b13086c6c52ac072a613e309bc7fac1509f8076b.jpg\" alt=\"（左から）藤田ニコル、小芝風花、吉本実憂、本田望結、剛力彩芽、武井咲、河北麻友子、高橋ひかる、岡田結実、是永瞳（C）モデルプレス\" /><br/><div class=\"caption\">（写真 : （左から）藤田ニコル、小芝風花、吉本実憂、本田望結、剛力彩芽、武井咲、河北麻友子、高橋ひかる、岡田結実、是永瞳（C）モデルプレス）</div><br/>今年は武井、剛力、河北、藤田のほか本田望結、吉本実憂、小芝風花、高橋ひかる（※「高」は正式には「はしごだか」）、岡田結実、そして5月に行われた「第1回ミス美しい20代コンテスト」でグランプリに輝いた是永瞳の計10名が華やかに集結。2016年、様々な方面で活躍し注目を浴びた12歳～25歳の美女が晴れ着姿を披露した。\n<br/>\n<br/><strong>◆“10者10様”晴れ着のポイントを明かす</strong>\n<br/>\n<br/>深みのある上品な緑の晴れ着姿で登場した武井は、「いつもは赤とかが多かったんですけど、今回はクラシカルな色合いが着られて新鮮な気持ちです」とにっこり。毎年明るい色が多かったという剛力も、「今年は大人っぽく落ち着いたものが着られて嬉しいです。でもみんなに若さが負けないよう、可愛いらしさもあります」とポイントを語った。\n<br/>\n<br/>また、自身でデザインしたという晴れ着を披露した藤田は「ウサギちゃんとかユニコーンがいて、自分って感じがして気持ちが良いです！」と笑顔でアピールしていた。（modelpress編集部）', 1, '2016-12-08 16:02:08', '2017-02-06 16:02:08', NULL, NULL, NULL, 0, 'https://mdpr.jp/news/1644023', '他の写真・記事詳細をもっと見る', 'https://mdpr.jp/news/1642839', '藤田ニコル「月イチで虫を食べている」人気者ならではの悩みも', 'https://mdpr.jp/cinema/1642805', '武井咲、妖怪の仕業報告？「なにかいる」', 'https://mdpr.jp/gal/1642755', '藤田ニコル“SNS疲れ”を告白 心配＆エール殺到', 'https://mdpr.jp/news/1642324', '剛力彩芽、恋愛解禁にコメント', 0, 0, 1, 1, '2016-12-08 17:25:07', '2016-12-08 17:25:07'),
    (111, '4_1_r_20161208_1481185507309680800', 4, 1, 1, 1, 1, 'http://www.oricon.co.jp/news/2082730/', '8a4db4a1ada8992a0ba164751f90c20e_1481185507', 'スマホ視聴に特化したドラマ『スマドラ』　桜井日奈子、三吉彩花ら出演', '', '<img src=\"http://news.oricon.co.jp/feed/images/size640wh/20161208_153005_size640wh_1094.jpg\" title=\"『スマドラ』の「恋の503」に出演する桜井日奈子\" alt=\"『スマドラ』の「恋の503」に出演する桜井日奈子\" ><br />\n　フジテレビは、総合エンターテイメントサービス「FOD（フジテレビオンデマンド）」で、スマートフォンでの視聴に特化したオリジナルオムニバスショートドラマ『スマドラ』（全6話）の後編3作を9日の正午から配信を開始する。<br />\n<br />\n　同ドラマは、スマートフォンで視聴することを前提とし、縦型動画をはじめ、作中の出来事が自分のスマートフォンで起きているような仕掛け、実際の世界やWEBと連動する企画など、その特性を活かした新しい視聴体験を提案する挑戦的な作品。<br />\n<br />\n　時代の動画視聴スタイルとのマッチングするよう、全6作品をオムニバス形式の10分以内で構成しており、あす9日の正午から配信する後編3作では、「彼方からのエール」に女優の吉岡里帆、吉沢亮、「Insta・nt manager」には三吉彩花、「恋の503」は桜井日奈子が出演する。<br />\n<br />\n　「彼方からのエール」は、テレビ電話で相手の顔を見ながら会話をしているような映像と、トークアプリでのトークを中心に構成した作品。縦型映像と顔のみを撮影することで、視聴者は吉岡と吉沢と、それぞれ疑似恋人になったような不思議な感覚が体験できる“超没入型ラブストーリー”となる。<br />\n<br />\n　「Insta・nt manager」は、三吉が本人役として出演し、SNSアカウントと連動した企画。劇中に登場するインスタグラムのアカウントが実際に開設され、現実の世界と連動。インスタの画像や映像を併せて三吉の1日を覗いた気分になり、ドラマの世界が実際の出来事のように錯覚するような作品。実在のSNSを通して、登場人物と同じ気分を味わえる新感覚ドラマに仕上がっている。<br />\n<br />\n　「恋の503」は、Yahoo! JAPANが運営する「Yahoo!検索」とコラボレーションした作品で「Yahoo!検索」のシステムが、実は人力であったという架空の世界を描き、実際にはありえないことでも想像したら楽しくなるような世界を描く。また、「Yahoo!検索」で「なかのひと」と検索すると、「Yahoo!検索」のなかのひとが検索結果を考え奮闘する様子が描かれた「Yahoo!検索」限定動画を無料で視聴可能。主演の志崎かすみ役は、桜井が務める。<br />\n<br />\n　前編3作は配信中で後編3作は9日の正午より配信開始。', 1, '2016-12-08 15:45:18', '2017-02-06 15:45:18', NULL, NULL, NULL, 0, 'http://www.oricon.co.jp/news/2078375/photo/5/?anc=112', '【写真】大胆にスカートをまくり上げ…大人の色気を発揮した桜井日奈子', 'http://www.oricon.co.jp/news/2075178/photo/1/?anc=112', '【写真】透け感のある白ワンピで…美少女っぷりを際立たせた桜井日奈子', 'http://www.oricon.co.jp/news/2058910/photo/1/?anc=112', '【写真】“岡山の奇跡”桜井日奈子、CMでメイド服姿', '', '', '', '', 0, 0, 1, 1, '2016-12-08 17:25:07', '2016-12-08 17:25:07'),
    (112, '4_1_r_20161208_1481185507474158454', 4, 1, 1, 1, 1, 'http://www.oricon.co.jp/news/2082703/', '7afab8d68f6d6117b7d5e2a6f42b163c_1481185507', '片瀬那奈「交際順調？」質問に言葉濁す　「優等生発言ですいません」と謝罪も', '', '<img src=\"http://news.oricon.co.jp/feed/images/size640wh/20161208_152055_size640wh_4963.jpg\" title=\"交際質問に言葉を濁した片瀬那奈 （C）ORICON NewS inc.\" alt=\"交際質問に言葉を濁した片瀬那奈 （C）ORICON NewS inc.\" ><br />\n　女優の片瀬那奈（35）が8日、都内で行われた『ディズニーアート展《いのちを吹き込む魔法》』記者発表会に出席。昨年12月に一部週刊誌でIT企業経営者との交際が報じられていた片瀬は、報道陣から「交際は順調？」と近況を問われると「日々、必死に生きてます」と笑顔を浮かべつつ、言葉を濁した。<br />\n<br />\n　この日の会見にかわいらしいドレス姿で登場し「姫っぽくしてみました」と上機嫌で話すと「もうそろそろ着れなくなる。（身長が）デカいのでお姫様抱っことか憧れだけは異常にあります。プライベートでも今すぐにでも着てみたい」と自虐交じりにプリンセス願望を告白。<br />\n<br />\n　「プライベートでドレスを着る予定は…」と報道陣からゴールインの予定を追及されると「いや～、ドレスはどうでしょうかね。ご想像にお任せします」と含み笑み。最後まで交際について明言することはなかったが、取材後、会場を後にする記者に対して「優等生発言ですいません」と茶目っ気たっぷりに笑いながら謝罪していた。<br />\n<br />\n　昨年の交際報道直後、片瀬の所属事務所はORICON STYLEの取材に対し「大人なのでプライベートは本人に任せております」とコメントしていた。<br />\n<br />\n　来年4月8日から9月24日まで東京・日本科学未来館で開催される同展覧会は、ミッキーマウスの誕生から、最新作『モアナと伝説の海』まで約90年分のディズニー作品のオリジナル原画、スケッチ、コンセプトアートなど約450点を展示。ディズニーの初期作品だけではなく、現代作品をも網羅した展覧会は、日本初の試みとなる。<br />\n<br />\n　芸能界きっての“ディズニーマニア”として知られ「年間パスポートを持っていて月に1回はディズニーランドに行ってます」と豪語した片瀬。大勢の報道陣を前に「『不思議の国のアリス』が一番好き。不思議な世界観やアリスのかわいらしさ、独特なカラーの色彩や絵のタッチが発揮されてる作品。週に1回は観てますね」とディズニー愛を熱弁していた。', 1, '2016-12-08 15:23:02', '2017-02-06 15:23:02', NULL, NULL, NULL, 0, 'http://beauty.oricon.co.jp/trend-culture/trend/news/photo/68539/2/?anc=013', '【写真】片瀬那奈、プールサイドで「グラマラスボディ」披露', 'http://beauty.oricon.co.jp/trend-culture/trend/news/2063134/?anc=013', '片瀬那奈に新恋人報道　所属事務所「本人に任せております」 （15年12月01日）', 'http://www.oricon.co.jp/news/video/2047865/full/?anc=013', '＜過去には＞溝端淳平、片瀬那奈との破局質問に無言 （15年01月29日）', '', '', '', '', 0, 0, 1, 1, '2016-12-08 17:25:07', '2016-12-08 17:25:07'),
    (113, '4_1_r_20161208_1481185507481183407', 4, 1, 1, 1, 1, 'https://mdpr.jp/wedding/1643800', '62c8c6fcb8222215e7cb9e7c74c49416_1481185507', '下田美咲、“元ホストでセフレ”夫との結婚の経緯を赤裸々告白「性行為が良すぎてびっくり」', '', '【下田美咲／モデルプレス＝12月7日】11月1日に結婚したタレントの下田美咲（27）が、7日発売の雑誌「姉ageha」1月号にて結婚について赤裸々に語った。<br/><br/><img src=\"https://mdpr.jp/photo/images/2014/05/21/200_f5039a80d3d7f0b112b34ecfb8a32f31f00e456b7dac60aa.jpg\" alt=\"結婚を赤裸々に告白した下田美咲\" /><br/><div class=\"caption\">（写真 : 結婚を赤裸々に告白した下田美咲）</div><br/>ウェディングドレス姿で誌面を飾った下田は自身の連載「下田美咲のもういっちょ！女子力道場」にて「下田美咲、結婚しました～旦那様は元ホストでセフレです～」と題し、年下男性との結婚を報告した。\n<br/>\n<br/><strong>◆結婚の経緯をぶっちゃけ告白</strong>\n<br/>\n<br/>「覚えているのは、酔った私を支えてくれた時の握力がほど良かったことと、早めに手を出してくれそうな支え方だなーと思ったこと」と、出会って3ヶ月でゴールインとなった下田は第一印象を回顧。その後、彼からの連絡を機に肉体関係を持ったそう。\n<br/>\n<br/>「性行為が良すぎてびっくり！おまけに安眠できるようになって体調まで良くなったし（笑）」「今回愛が育ったのはその部分も大きいはず」と相性の良さもきっかけとなったようだが、その時点では付き合うつもりはなく、会い続けるうち「『付き合ってもいいんじゃないか…？』って気持ちがだんだん大きくなってきた」と心境の変化を明かした。\n<br/>\n<br/>このほか誌面では、セフレから本命になるための昇格法も伝授。プロポーズを受けた心境や夫に対する愛も語っている。（modelpress編集部）\n<br/>\n<br/>情報提供：「姉ageha」（主婦の友社）\n<br/>\n<br/><strong>■下田美咲（しもだみさき）プロフィール</strong>\n<br/>\n<br/>生年月日：1989年8月15日\n<br/>出身地：東京都\n<br/>血液型：B型\n<br/>身長：163cm\n<br/>\n<br/>「出来ないことは一切やらず、出来ることを全力でやる」をモットーに、2011年頃から、全て自分でディレクションしたパフォーマンス動画をYouTubeに投稿し人気に。独創的な活動を展開し、2013年には自分都合で生きていくための会社「ミサキ式」を設立し、会長兼社長に就任。“コールの女王”の異名を持ち、現在はテレビや雑誌、ラジオやインターネットにてユニークな恋愛論を披露するなど幅広く活動する。', 1, '2016-12-08 14:40:25', '2017-02-06 14:40:25', NULL, NULL, NULL, 0, 'https://mdpr.jp/wedding/1643800', '他の写真・記事詳細をもっと見る', 'https://mdpr.jp/interview/1365422', '「ロンハー」で話題の美女・下田美咲、“エロさ”への強烈な想い＆驚愕の恋愛観を告白　モデルプレスインタビュー', 'https://mdpr.jp/news/1296649', '「ロンハー」で話題のニート美女、新たな活動を発表', 'https://mdpr.jp/news/1271025', '「ロンハー」初出演の“無所属ニート”美女に話題沸騰　独特キャラで存在感示す', '', '', 0, 0, 1, 1, '2016-12-08 17:25:07', '2016-12-08 17:25:07'),
    (114, '4_1_r_20161208_1481185507678743523', 4, 1, 1, 1, 1, 'http://www.oricon.co.jp/news/2082734/', 'be7464356693c7e391398edf382bb439_1481185507', '安住アナ、星野源＆石田ゆり子と恋ダンス　『ぴったんこカン・カン』とコラボ', '', '<img src=\"http://news.oricon.co.jp/feed/images/size640wh/20161208_144335_size640wh_2920.jpg\" title=\"星野源＆石田ゆり子と“恋ダンス”を披露した安住紳一郎アナウンサー（右） （C）TBS\" alt=\"星野源＆石田ゆり子と“恋ダンス”を披露した安住紳一郎アナウンサー（右） （C）TBS\" ><br />\n　TBSアナウンサーの安住紳一郎が出演する9日放送の同局バラエティー番組『ぴったんこカン・カン』（毎週金曜　後7：56）で、現在放送中の人気連続ドラマ『逃げるは恥だが役に立つ』（毎週火曜　後10：00）とコラボレーション企画が実施され、安住アナがドラマ出演中の女優・石田ゆり子と星野源とともに主題歌「恋」に合わせたキュートな振り付けが話題の『恋ダンス』に挑戦していることがわかった。<br />\n<br />\n　海野つなみ氏による同名コミックを原作に、「職ナシ」「彼氏ナシ」「居場所ナシ」の主人公・森山みくり（新垣結衣）が、恋愛経験のない独身サラリーマン・津崎平匡（星野）と契約結婚しながら同居生活を送ることとなるラブコメディー。同枠ドラマの歴代最高視聴率を更新し続ける好調ぶりをみせている。<br />\n<br />\n　今回、『ぴったんこカン・カン』にはアラフィフの独身キャリアウーマンで、みくりの伯母であり親友でもある土屋百合役の石田が安住とともに街の最新スポットや人気のお店へ。その流れで『逃げ恥』を撮影している緑山スタジオを訪れ、撮影スタジオの様子やドラマのセットなどを石田の案内で紹介、撮影中のキャストも続々登場する。<br />\n<br />\n　そこで、石田は「安住さんと恋ダンスが踊りたい」と提案。必死に抵抗する安住アナだったが、石田やスタッフに押され、急きょ、星野と石田とともに『恋ダンス』にチャレンジすることに…。練習風景はもちろん、本番の『石田ゆり子・星野源・安住紳一郎の“恋ダンス”ぴったんこバージョンの完成披露版』は番組のエンディングで披露する。', 1, '2016-12-08 14:56:17', '2017-02-06 14:56:17', NULL, NULL, NULL, 0, 'http://www.oricon.co.jp/news/2081490/photo/4/?anc=096', '【写真】ドラマ内で清楚なセーラー服姿を披露したガッキー', 'http://www.oricon.co.jp/news/70401/photo/1/?anc=096', '【写真】こちらは7年前…制服姿を披露した新垣結衣', 'http://www.oricon.co.jp/news/2067714/photo/1/?anc=096', '【写真】美しすぎる…白のひざ上ワンピのガッキー', '', '', '', '', 0, 0, 1, 1, '2016-12-08 17:25:08', '2016-12-08 17:25:08'),
    (115, '4_1_r_20161208_1481185507827311305', 4, 1, 1, 1, 1, 'https://mdpr.jp/interview/1365422', '2d568a3a5fd59e9442c553d67f247691_1481185507', '「ロンハー」で話題の美女・下田美咲、“エロさ”への強烈な想い＆驚愕の恋愛観を告白　モデルプレスインタビュー', '', 'カラオケパフォーマンスや飲み会コールの動画で一躍話題となり、今やテレビなどからオファーが絶えない下田美咲。その激しいパフォーマンスから“盛り上げすぎる女”という異名も持つ彼女だが、並々ならぬ恋愛観を持つ一面もある。モデルプレスは今回、そんな彼女の素顔に迫ってみた。<br/><br/><img src=\"https://mdpr.jp/photo/images/2014/05/21/200_f5039a80d3d7f0b112b34ecfb8a32f31f00e456b7dac60aa.jpg\" alt=\"自身の価値観を赤裸々に語ってくれた下田美咲\" /><br/><div class=\"caption\">（写真 : 自身の価値観を赤裸々に語ってくれた下田美咲）</div><br/><strong>◆下田美咲参上</strong>\n<br/>\n<br/>13歳からモデル・アイドルとして幅広く活躍していた下田は、エアーバンド・ゴールデンボンバーの「女々しくて」のPVに感銘を受け、2011年5月より加入を志し「下田美咲の動画プロジェクト」を開始。“飲み会コール”“全力カラオケ”など、下田自ら企画から撮影、出演、編集まで全ての工程を手がけた動画は、約1年で動画投稿サイトで再生回数100万回を突破し、ネット上で話題となった。\n<br/>\n<br/>昨年は、フジテレビ系「アウト×デラックス」やテレビ朝日系「ロンドンハーツ」などで露出を増やし、その堂々たる存在感に大反響。11月には、株式会社ミサキ式”という個人事務所を立ち上げ、会長兼社長に就任し、6年に及んだニート生活に終止符を打った。\n<br/>\n<br/>そんな下田には、たびたびTwitterに悩み相談が寄せられるという。激動の人生で得られた経験値をもとに、下田は次々と名案を施す。相談者たちは「心臓にズバズバくる」「恋愛や性に対する答えがもはや達人レベル」とその名案に感銘を受けた様子。そして今回、それら悩みを119個集め、「生きているだけで死にたくなるような世の中で 生きていてもいいような気がしてくる119の名案」（6月3日）として刊行する。下田はなぜ名案が出せるのか、そしてどんな恋愛、性体験をしてきたのか、彼女の常人離れした考え方を明らかにした。\n<br/>\n<br/><strong>◆書籍「生きているだけで死にたくなるような世の中で 生きていてもいいような気がしてくる119の名案」</strong>\n<br/>\n<br/><strong>― 本書は数々の悩みに答えたものになっていますが、過去どんな悩みが寄せられましたか？</strong>\n<br/>\n<br/>下田：かなりヘビーなものからライトなものまで色々あったんですけど、一貫してこれっていうものはないですね。恋愛や家族の問題、夢や自分のコンプレックス、友情もあれば学校についてもあります。\n<br/>\n<br/><strong>― ヘビーなものではどんなご相談を受けましたか？</strong>\n<br/>\n<br/>下田：「家庭が崩壊している」や「学校でいじめられている」、究極なものでは「死にたい」っていうのがありました。ライトなものは、「布団から出たくない」「寝坊してしまう」といった、悩みなのかもあやしい相談もありましたね。こういったあまりにもどうでも良すぎて、「誰も解決しようとしなかった」悩みに解決法を用意したのは、今まで世になかった試みなのかなと思います。\n<br/>\n<br/><strong>― なぜ悩みに答え始めたのでしょうか？</strong>\n<br/>\n<br/>下田：最初は、相談されてなくても解決策をひとりでツイートしていたんです。そしたら相談リプライが飛んでくるようになって、それに答えていくと、どんどん相談が増えていくようになって、いつのまにかリプライが相談事で埋まるようになったんです。それだと私も困るので、最近は時間限定で「お悩み相談室」を開くようにしています。\n<br/>\n<br/><strong>― 名案のひとつひとつがまるで悟りを開いたかのような、ハッとするような回答ですが、これはご自身のどういったところから生まれてくるのですか？</strong>\n<br/>\n<br/>下田：私自身が悩みやすいんです。自分も過去に同じような悩みを経験して、そして全部ひとりで解決してきたことなので、みんなの名案になれるのだと思います。「死にたい」という気持ちだって、私が一番強いはずなので、はっきりと解決策も浮かびました。基本的に生きているだけでも死にたくなってくるので、この本を読んでもらえれば、生きていてもいいかなって感じてもらえると思います。\n<br/>\n<br/><strong>◆下田美咲の恋愛とエロさと性行為</strong>\n<br/>\n<br/><strong>― 本書の中で、「ありのままの私が愛されていない」「彼氏が3ヶ月もエッチしようとしない」というものにも、ズバリと名案を提示していますが、恋愛の解答には目を見張るものがありました。</strong>\n<br/>\n<br/>下田：私自身、10代の頃から誰よりも早く愛と契り（セックス）を経験して、トライアンドエラーを繰り返し、男女関係や性心理を研究してきました。それでたくさんの答えを出し、徐々に磨き上げてきたので、男女間の問題には答えられていると思います。\n<br/>\n<br/><strong>― 恋愛に対してはどのような考えを持っているのですか？</strong>\n<br/>\n<br/>下田：恋愛って運とか縁っていうような、神様問題にしがちなところがあると思います。好かれたから行く、いいなぁと思ったから付き合う、そんな甘い設定でみんな恋愛していると思うんですけど、このスタンスだと本当にドンピシャで好みの人と付き合うのは難しんです。なので私は、仕事と同じように、恋愛も目標を立てて達成するようにしています。その目標が「両思いになる」ということであれば、私は常にミッションコンプリートしてきました。\n<br/>\n<br/><strong>― つまり確実に両思いになってきたということですか！その百発百中の秘訣を教えてください。</strong>\n<br/>\n<br/>下田：全部相手の好みに寄せます。恋愛するなら自分なんか全部捨てるべきだと思います。「どこを変えればいい？」「どうすれば好きになってもらえますか？」って質問してもいい。その姿勢が大事だと思います。意外とこれで好かれるんです。\n<br/>\n<br/><strong>― 「自分がない」ってことに抵抗ある方も多いと思うのですが…。</strong>\n<br/>\n<br/>下田：それは違います。確固たる意志をもってあなたの好みに寄せるということなんです。「相手に好きになってもらいたい」という自分があるからこそ、好み通りになれるんです。\n<br/>\n<br/><strong>― なるほど。下田さんは例えばどんな好みに寄せたりしてきたんですか？</strong>\n<br/>\n<br/>下田：性行為において全部イエスマンになるということに取り組んできました。相手からどんな希望が出てきても「オッケーやろう」って。性行為だけじゃなくても、相手が言ったことに対して全部イエスって言うことが重要です。\n<br/>\n<br/><strong>― 性行為が下田さんの恋愛観の中で、重要なファクターになっているのですね。</strong>\n<br/>\n<br/>下田：性行為は大事だと思います。性行為に及べない人とは付き合えないですね。でもその際に必要なのは、愛ではなくエロさなんです。友人や家族に対して、愛があってもエロさは抱かない。その点は、いかにエロさを打ち出せるのかだと思っています。\n<br/>\n<br/><strong>― そのエロさを出し続けるために大切なことはなんだと思いますか？</strong>\n<br/>\n<br/>下田：エロさは髪の毛に宿ります。もし私が坊主だったら性行為の機会が絶対に減っていると思います。女性だったらロング、男性だったら襟足やもみあげ、前髪がちゃんとあること、それがエロい気持ちになりやすい髪型ですね。女性の方で性行為がしたいなら問答無用でロング。髪型って私は恋愛がしたいという決意表明でもあると思うので、髪型がぶっ飛んでると恋愛する姿勢がないと思われます。パッツン系も危ない。安定した彼氏いるって目で見られてしまいます。\n<br/>\n<br/><strong>― 下田さんの今日の髪型が、まさにエロさの出る髪型なんですか？</strong>\n<br/>\n<br/>下田：エロい気持ちは阻害されないと思います。毛先が揺れるとエロい気持ちをかき立てやすいんです。\n<br/>\n<br/><strong>― そうなんですね。同じ男女がずっと付き合っていく中で、エロい気持ちは持ち続けられるものなのでしょうか。</strong>\n<br/>\n<br/>下田：難しいですね。そもそも男女交際自体が、予定調和の性行為になってくるし、付き合うって定期的な性行為の保証にすぎないじゃないですか。その時点でもうエロくないんですよね。絶対できる相手に性欲は湧き続けないでしょう。ある日エロい目で見られなくなる恐怖と常に隣り合わせになります。エロい気持ちを優先するなら、付き合うという形を取らないこともひとつの答えですね。\n<br/>\n<br/><strong>― そうなると結婚観も気になるところですが…</strong>\n<br/>\n<br/>下田：末永いエロさにも限界があるので、結婚しても短期スパンで離婚をしたいですね。そもそも、男女が一生添い遂げようってことが間違っている気がするんです。\n<br/>\n<br/><strong>◆動画投稿は、「自分に生きていたいと思わせる」ひとつの手段</strong>\n<br/>\n<br/><strong>― 動画で話題の下田さんですが、投稿をはじめたきっかけを教えて下さい。</strong>\n<br/>\n<br/>下田：ゴールデンボンバーさんのPVを見た時に、「加入したい！」って思ったんです。それで加入するためにはどうしたらいいんだろうと考えた結果、私にとってゴールデンボンバーさんの魅力が動画だったので、動画スキルをあげれば即戦力になれると思い、修行の一環として投稿を始めました。\n<br/>\n<br/><strong>― 加入するために事務所の社長さんにお会いしたとお聞きしたのですが…。</strong>\n<br/>\n<br/>下田：事務所の社長さんと会食へ行って交渉もしたんですけど、ストレートには入れないと分かったんです。なので自分の活動をしっかりやって、同じぐらいの知名度になってからもう一度志願しようって作戦に切り替えました。今でも加入したいです。\n<br/>\n<br/><strong>― では下田さんの活動の最終目標はゴールデンボンバーさんに加入ということなのですか？</strong>\n<br/>\n<br/>下田：最終的な目標は「生まれてきてよかった。産んでくれてありがとう」って親に言うことです。その気持になれそうなのが、ゴールデンボンバーさんに加入することなんです。\n<br/>\n<br/><strong>― そこまでゴールデンボンバーさんに引きつけられるのは、なぜですか？</strong>\n<br/>\n<br/>下田：エンタメ系の夢って才能やスキル、小さい頃からの英才教育がないと間に合わないものだと思っていたんです。でもゴールデンボンバーさんは違いました。技術や特別な才能、身体能力ではなく、気合とアイディアで夢をかなえる方法を見せてくれたんです。こっちに「無理だ」「もう間に合わない」って劣等感を感じさせないで夢をかなえる方法を教えくれた唯一の先輩なんです。\n<br/>\n<br/><strong>― 相当なる愛が伝わってきました。下田さんは“飲み会コール”の動画が話題となり、テレビ出演時にも披露されていましたが、コールをする理由はなんですか？</strong>\n<br/>\n<br/>下田：単純に好きなんです。テンションがあがるからいいですよね。意味のない所が良い。でもまさかコールでテレビに出るとは思っていませんでした。それに「飲み会コール賞」まで頂けて…。通常では努力だと認めてもらえない分野なのですが、表彰してもらえたことで努力だと認められて、親が喜んでくれたのが良かったです。\n<br/>\n<br/><strong>― そういう意味では下田さんも、いわゆる才能とはまた違ったところで、スポットライトを浴びる存在なのではないですか？</strong>\n<br/>\n<br/>下田：才能がないと成功できないとなるとほとんどの人が無理じゃないですか。才能がなくても成功して楽しくなる世の中じゃないと、いつまでも死にたいって気持ちのままですよね。\n<br/>\n<br/><strong>― でも、モデルなどの活動経験もあることから、ルックスは周りから認められるモノを持っていると思うんです。それでも死生観にこだわり続ける理由が気になるのですが…。</strong>\n<br/>\n<br/>下田：そもそも人生の設定が嫌なんです。生まれてきて、どんどん劣化の一途をたどっていく、どんどん出来ることが減っていく、そのたびに契りの選択肢も減っていく。よっぽど良い人生じゃないと、プラスマイナスはマイナスなんです。どんな美人に生まれても、マイナスのほうが大きいんです。生きていく中で、親や友達もいつか死ぬって決められていること自体、相当マイナスじゃないですか。なのでそれをプラスにしていくとなると、仕事は楽しくやって、恋愛もうまくいって、お金持ちにもなって、全部出来て最低限。それが私の考えなんです。\n<br/>\n<br/><strong>― しっかりと自分の哲学を持っていることがわかりました。</strong>\n<br/>\n<br/>下田：親が私のことをすごく大事にしてくれているので、私がいなくなると親の人生が台無しになってしまうと思うんです。そこまでして死ぬ理由はあるかと問われると、それはない。自分を生きていたいと思わせるために編み出してきたのが、これまでの私の活動なんです。でも実は今、生まれてきてよかったって思える瞬間まで、近いところに来てるんじゃないかと感じているんです。\n<br/>\n<br/><strong>― では最後に、下田さんの考える“夢を叶える秘訣”を教えて下さい。</strong>\n<br/>\n<br/>下田：生まれることと死ぬこと以外は大体どうにか出来ると思います。なにかを達成したいと思った時、「次どうすればいいのか」って瞬間が必ずやってきます。その時に必要なのが名案です。行き詰まったら私に聞いて欲しいですね。対策が打てないことはないと思います。\n<br/>\n<br/><strong>― ありがとうございました。</strong>\n<br/>\n<br/>下田美咲。抜群の美貌に隠された、うちなる想いを語ってくれた。インタビューでも、Twitterに悩み相談が殺到するのも頷けるような、同年代に比肩しない達観した考え方を持っていることがわかった。活動について「生きていたいと自分に思わせるため」と語ったように、彼女の考え方の出発点は常に「死生観」。恋愛も、セックスも、仕事も、どこを切り取っても、見え隠れするのが、死に対する強烈なまでもの思考の繰り返し。コールやカラオケなどの奇抜なパフォーマンスに目を奪われやすいが、彼女の魅力は、その独特な思考部分にある。淡々と語る口調から、そう思わざるを得なかった。（モデルプレス）\n<br/>\n<br/>■下田美咲（しもだ・みさき）プロフィール\n<br/>生年月日：1989年8月15日\n<br/>出身地：東京都\n<br/>血液型：B型\n<br/>身長：163cm\n<br/>\n<br/>「出来ないことは一切やらず、出来ることを全力でやる」をモットーに、2011年頃から、全て自分でディレクションしたパフォーマンス動画をYouTubeに投稿。口コミのみ100万回再生を突破（2014年5月現在は累計650万回突破）。自宅の車を宣伝カーに改造しゲリラパフォーマンスを行うなど、独創的な活動を続ける。2013年に自分都合で生きていくための会社「ミサキ式」を設立し、会長兼社長に就任。現在、飲みサポート飲料のイメージキャラクターや「MTV ZUSHI FES 14」公認宣伝大使を務めるなど、盛り上げクイーンとしても精力的に活動中。', 1, '2016-12-08 14:42:07', '2017-02-06 14:42:07', NULL, NULL, NULL, 0, 'https://mdpr.jp/interview/1365422', '他の写真・記事詳細をもっと見る', 'https://mdpr.jp/news/1296649', '「ロンハー」で話題のニート美女、新たな活動を発表', 'https://mdpr.jp/news/1271025', '「ロンハー」初出演の“無所属ニート”美女に話題沸騰　独特キャラで存在感示す', '', '', '', '', 0, 0, 1, 1, '2016-12-08 17:25:08', '2016-12-08 17:25:08'),
    (116, '4_1_r_20161208_1481185507854577245', 4, 1, 1, 1, 1, 'http://www.oricon.co.jp/news/2082733/', 'f002df1edde2269c6c57410affbc758f_1481185507', '“引退発言”星田英利に元相方・宮川大輔が電話　フジモン明かす', '', '<img src=\"http://news.oricon.co.jp/feed/images/size640wh/20161208_142611_size640wh_9852.jpg\" title=\"元ほっしゃん。星田英利の引退騒動の裏を明かしたFUJIWARAの藤本敏史\" alt=\"元ほっしゃん。星田英利の引退騒動の裏を明かしたFUJIWARAの藤本敏史\" ><br />\n　お笑いコンビ・FUJIWARAの藤本敏史が7日放送のAbemaTV『原宿アベニュー』（月～金　後6：30）に出演。6日に星田英利（旧芸名：ほっしゃん。）が“引退発言”で騒動となり、かつてお笑いコンビ・チュパチャップスで星田の相方だった宮川大輔から相談を受けていたことを明かした。<br />\n<br />\n　星田は6日、来年3月での引退を示唆する内容をインスタグラムに投稿し、同日中に撤回。藤本は、25年の長い付き合いである星田について「昔から思い詰めやすいところがあった」と“引退発言”の背景を思いやった。<br />\n<br />\n　また、発言の一報が出た直後、宮川から「ほっしゃん。何とかしてやりたいんやけど、フジモンどうしよう」といった内容のメッセージがあったことを告白。その後、宮川は電話で星田さん本人と話し、星田は「涙を流していたらしい」と“元コンビ”の裏エピソードも明かした。<br />\n<br />\n　宮川の言葉がきっかけで引退を撤回したかもしれないとの推測も挙がるなか、スタジオメンバーは、「良い話。よかった、よかった」と、星田と宮川の絆に感激した様子だった。', 1, '2016-12-08 14:47:02', '2017-02-06 14:47:02', NULL, NULL, NULL, 0, 'http://www.oricon.co.jp/news/2082657/?anc=164', '松本人志、元ほっしゃん。星田の引退発言イジる　年末『ガキ使』の“引き際”は…？ （16年12月07日）', 'http://www.oricon.co.jp/news/2082642/?anc=164', '元ほっしゃん。星田英利が引退発言を撤回「ひたむきに頑張ります」 （16年12月06日）', 'http://www.oricon.co.jp/news/2034718/?anc=164', 'ほっしゃん。浮気していた過去を告白 （14年03月05日）', '', '', '', '', 0, 0, 1, 1, '2016-12-08 17:25:08', '2016-12-08 17:25:08'),
    (117, '4_1_r_20161208_1481185508529783522', 4, 1, 1, 1, 1, 'https://mdpr.jp/news/1296649', 'cfb92f240aef0252c9b99f4df4ed94fc_1481185508', '「ロンハー」で話題のニート美女、新たな活動を発表', '', '自称“無所属ニート”として様々なバラエティ番組に出演し、話題を呼んでいる美女・下田美咲が8日、都内にて緊急記者会見を開き、今後の活動を発表した。<br/><br/><img src=\"https://mdpr.jp/photo/images/2013/11/08/200_429d568bf1da9c8da5e897ca1df2241e0545939784529904.jpg\" alt=\"記者会見を開き新たな活動を発表した下田美咲\" /><br/><div class=\"caption\">（写真 : 記者会見を開き新たな活動を発表した下田美咲）</div><br/>13歳からモデル・アイドルとして幅広く活躍していた下田は、エアーバンド・ゴールデンボンバーの「女々しくて」のPVに感銘を受け、2011年5月より「下田美咲の動画プロジェクト」を開始。下田自ら企画から撮影、出演、編集まで全ての工程を手がけた動画は、動画投稿サイトで再生回数100万回を突破し、ネット上で話題となった。飲み会の“コール”を得意とする下田は「コール大好き下田美咲のコール講座」をはじめ、さまざまな動画をシリーズ展開。今年に入ってからは、バラエティ番組「ロンドンハーツ」（テレビ朝日系）、「今夜くらべてみました」（日本テレビ系）、情報番組「サンデージャポン」などに出演し、注目を浴びてきたが、未だに、どこの芸能事務所にも所属せず、ニート6年目の無所属タレントとして活動してきた。\n<br/>\n<br/>同会見で下田は、「私、ニートを卒業します」と宣言。しかし、「これからも芸能事務所には入りません」とキッパリ語り、「どこかの事務所に所属して惰性で人と仕事がするのが嫌で、下田という人間がわからないまま営業をかけられるのが嫌で、これまで所属してきませんでした」と理由を説明。そして、「これだけの状況が揃いましたので、会社を立ち上げる決意をしました」と“株式会社ミサキ式”という個人事務所を立ち上げ、会長兼社長に就任することを発表した。\n<br/>\n<br/>下田が立ち上げた事務所には、2名のスタッフが就いているが、そのうちの1人は、顧問として東京都立川市でホストとして働いている実の弟・下田修一が就任。下田修一氏も、同会見に登場し、初の姉弟ショットを披露。自身のコール芸は、弟をホストとして働かせ、情報収集し身に付けたという。\n<br/>\n<br/>さらに、同会見では事務所の立ち上げのほか、ポニーキャニオンより12月4日にレンタル限定CD＋DVD「お手を拝借！もういっちょ！」と12月18日にDVD＋CD「下田美咲参上」をリリース、2014年3月28日に都内某所にてワンマンライブを開催、2月26日によしもとアールアンドシーより芸人コラボDVDをリリース、2月14日に夜間飛行より4月には竹書房より「下田美咲本（仮）」がリリースすることを発表。下田は、これらに関わるメンバーを紹介するとともに、「下田美咲の第1章はこの半年間でした。第2章はこれからの半年間だと思います。そして、第2章をしっかりやらなきゃ第3章は存在しないと思います」と意気込み、「大きな目標は、ネバーランド（遊園地）を作りたい」と高らかに宣言した。\n<br/>\n<br/>また、明日9日に実施する“ラストカーニバル”では、自宅の車を改造して制作した宣伝カーを壊すことも発表。「一か八かで壊します」と言い切り、「ポニーキャニオンに宣伝トラックを出してもらいたいんです。そのためにCDを発売します」と訴えていた。（モデルプレス）\n<br/>\n<br/>■下田美咲　プロフィール\n<br/>愛称：みーちゃん、みーやん、みちゃ、みーこ\n<br/>生年月日：1989年8月15日\n<br/>出身地：東京都\n<br/>血液型：B型\n<br/>身長：163cm', 1, '2016-12-08 14:43:38', '2017-02-06 14:43:38', NULL, NULL, NULL, 0, 'https://mdpr.jp/news/1296649', '他の写真・記事詳細をもっと見る', 'https://mdpr.jp/news/1271025', '「ロンハー」初出演の“無所属ニート”美女に話題沸騰　独特キャラで存在感示す', '', '', '', '', '', '', 0, 0, 1, 1, '2016-12-08 17:25:09', '2016-12-08 17:25:09'),
    (118, '4_1_r_20161208_1481185508624892058', 4, 1, 1, 1, 1, 'http://www.oricon.co.jp/news/2082674/', '1f7cbdb0580e515b0a2f6212735b3661_1481185508', '白石麻衣、2年ぶり写真集で新境地に挑戦　男女両方満足の“おしゃれセクシー”', '', '<img src=\"http://news.oricon.co.jp/feed/images/size640wh/20161207_192951_size640wh_7479.jpg\" title=\"2nd写真集でランジェリー姿に挑戦した乃木坂46・白石麻衣（撮影：中村和孝）\" alt=\"2nd写真集でランジェリー姿に挑戦した乃木坂46・白石麻衣（撮影：中村和孝）\" ><br />\n　人気アイドルグループ・乃木坂46の白石麻衣（24）が、来年2月7日に2nd写真集（タイトル未定・講談社）を発売することが8日、明らかになった。初めてのランジェリーショットで透き通るような白い肌を大胆に露出するなど、グループ史上過去最高のセクシーショットに挑んでいる。<br />\n<br />\n　グループ随一の美形メンバーとして多くの男性ファンから支持され、女性ファッション誌『Ray』『LARME』のモデルとして女性ファンも多い白石。グループにとって初のソロ写真集として大ヒットした『清純な大人』以来2年ぶりの待望の第2弾は、男女両方のファンが満足できる“おしゃれでセクシー”な内容に仕上がっている。<br />\n<br />\n　撮影の舞台は、本人が「ずっと行きたいと思っていた」というアメリカ・サンディエゴとロサンゼルス。誰もが魅了される圧倒的な美しさはもちろん、おいしそうにご飯を食べる様子など、リラックスした表情も見どころとなっている。撮影を担当したのは、大ヒットした小嶋陽菜の写真集『どうする？』や渡辺麻友の写真集『知らないうちに』、そして数多くの女性ファッション誌のカバーを手がけてきた中村和孝氏。白石の魅力を極限まで引き出している。<br />\n<br />\n　白石は「初めてのランジェリーでの撮影や、これまでやったことのないようなショットにも挑戦しています。とってもきれいに撮ってもらえた自信作なので、男性女性問わず、いろんな方に“新しい白石麻衣”を見てもらえたらいいなと思います！」と仕上がりに自信を込めている。<br />\n<br />\n　編集スタッフも「白石さんは楽しみながらとても積極的に撮影に取り組んでくれました。これまでの乃木坂46の写真集のイメージを覆す、意欲作になっています！先行カットで公開した写真は、ごく一部なのでまだまだいい写真がたくさんあるので楽しみにしていてください！」と胸を張った。<br />\n<br />\n　今年は映画『闇金ウシジマくん Part3』に出演するなど、女優としても活躍。この写真集が、白石が来年さらに飛躍する大きなきっかけとなる。', 1, '2016-12-08 11:30:03', '2017-02-06 11:30:03', NULL, NULL, NULL, 0, 'http://www.oricon.co.jp/news/2082607/photo/1/?anc=142', '【写真】乃木坂46「クリスマスライブ」初日の模様', 'http://joshiplus.jp/trend/newsphoto/2046083/5/?anc=142', '【写真】1st写真集では赤いビキニで抜群のスタイルを披露', 'http://joshiplus.jp/fashion/newsphoto/2078709/1/?cat_id=OBT_1610202116&anc=142', '【写真】乃木坂・白石麻衣、三毛ネコメイクで“まいにゃん”に変身', '', '', '', '', 0, 0, 1, 1, '2016-12-08 17:25:09', '2016-12-08 17:25:09'),
    (119, '4_1_r_20161208_1481185508778687803', 4, 1, 1, 1, 1, 'http://www.oricon.co.jp/news/2082697/', '64442952353a673fb70598d1570f9bd1_1481185508', 'オスカー美女10人が恒例晴れ着姿披露　武井＆剛力らが来年の抱負語る', '', '<img src=\"http://news.oricon.co.jp/feed/images/size640wh/20161208_125035_size640wh_1639.jpg\" title=\"晴れ着姿を披露した（左から）藤田ニコル、小芝風花、吉本実憂、本田望結、剛力彩芽、武井咲、河北麻友子、高橋ひかる、岡田結実、是永瞳 （C）ORICON NewS inc.\" alt=\"晴れ着姿を披露した（左から）藤田ニコル、小芝風花、吉本実憂、本田望結、剛力彩芽、武井咲、河北麻友子、高橋ひかる、岡田結実、是永瞳 （C）ORICON NewS inc.\" ><br />\n　女優の武井咲（22）、剛力彩芽（24）河北麻友子（25）らが8日、所属事務所のオスカープロモーションが都内で開催する毎年恒例の「晴れ着撮影会」に出席。今年は10人による艶やかな晴れ着姿で、報道陣を魅了した。<br />\n<br />\n　深緑に花柄の着物で登場した武井は、毎年恒例のイベントに「ここに立てて感謝です」とにっこり。2016年は「たくさんの作品に恵まれた一年だった。幅広くお芝居ができてとても恵まれています」といい、「来年は今よりもっともっと頑張らないと。体調管理をしっかりとしたい」と抱負を語った。<br />\n<br />\n　今年24歳となった剛力は「いつも着物は明るい色だったけど、今回は落ち着いた色。ちょっとずつ大人の階段をのぼっているみたいでうれしい」と笑顔。今年は初舞台にも挑戦したが「すごく落ち込んでいままでにないくらいネガティブになった。でも終わってみて舞台が好きになった。今年は初めてをたくさん経験して人として役者として学んだ一年だった」と振り返った。<br />\n<br />\n　モデルにバラエティーに大活躍だった藤田ニコル（18）は、自身がデザインしたというポップな着物を披露。「ここ2年間はすごく忙しい日々だった。来年も同じことをしていたい。体を張るロケが多いので、それが続けばいい。自分の明るさを届けられればいい」と宣言。ますだおかだの岡田圭右（48）の娘・岡田結実（16）は「剣道や殺陣をやりたい。来年は出川（哲朗）さんと一緒に氷水に入りたい」とバラエティーでのさらなる活躍を誓った。<br />\n<br />\n　そのほか、本田望結（12）、吉本実憂（19）、小芝風花（19）、高橋ひかる（15）、是永瞳（21）も出席し、来年の目標や抱負を明かした。', 1, '2016-12-08 12:48:03', '2017-02-06 12:48:03', NULL, NULL, NULL, 0, 'http://joshiplus.jp/love/newsphoto/2021101/2/?anc=072', '【写真】武井咲、自身プロデュースのドレスで美背中披露', 'http://www.oricon.co.jp/news/2082385/photo/3/?anc=072', '【写真】河北麻友子、バービー風ドレスで背中見せ！', 'http://www.oricon.co.jp/news/2037242/photo/1/?anc=072', '【写真】奇跡のスタイルを披露した河北麻友子', '', '', '', '', 0, 0, 1, 1, '2016-12-08 17:25:09', '2016-12-08 17:25:09'),
    (120, '4_1_r_20161208_1481185508887283140', 4, 1, 1, 1, 1, 'http://www.oricon.co.jp/news/2082699/', 'c04fdb5d757761c3d44959e69a84cf91_1481185508', '陣内孝則、同世代の渡辺謙に対抗心　ドラァグクイーン役で「新しい扉が…」', '', '<img src=\"http://news.oricon.co.jp/feed/images/size640wh/20161208_130816_size640wh_1340.jpg\" title=\"“オネエ口調”で渡辺謙に対抗心を燃やした陣内孝則 （C）ORICON NewS inc.\" alt=\"“オネエ口調”で渡辺謙に対抗心を燃やした陣内孝則 （C）ORICON NewS inc.\" ><br />\n　俳優の陣内孝則（58）が8日、都内で行われたミュージカル『プリシラ』初日公演前の囲み取材に出席。同作でドラァグクイーン（女装したパフォーマー）を演じる陣内は、役衣装で登場すると“オネエ口調”で「私はストレートだから女の子が好きなんだけど、だんだんその気になってきたわ。新しい扉が開いてきた」と声を弾ませた。<br />\n<br />\n　続けて「芸能界もそういう方が多いでしょう？　今は踏みとどまっているけど、わかんなくなってきちゃった」とにっこり。役作りにも、かなり力を入れているようで「毎日Tバックよ。きっと渡辺謙だったらやらないと思うけど、私は振り切っているから」と、世界で活躍する同世代俳優の名を挙げて対抗心を燃やし、「みんな、踊りがうまいわよ。私の時代は顔と人間性だけで36年持ってきたけど、今は腕がないとダメだから」と見どころをアピールした。<br />\n<br />\n　同じく、ドラァグクイーンに挑戦している主演の山崎育三郎（30）は「女性らしく動くっていうことに関して言えば、メイクを外していても皆さんとの関係性がちゃんと出来上がっていますね」と手応えをにじませながら「笑いあり涙ありの振り幅の広いミュージカルで、2016年を締めくくる最高のエンターテインメントに仕上がっています」と呼びかけていた。<br />\n<br />\n　同作は、1994年に公開された3人のドラァグクイーンのドタバタ珍道中を描いたオーストラリアの映画が原作で、宮本亜門氏の演出でミュージカル版が日本初上陸する。囲み取材にはそのほか、超新星のユナク、Leadの古屋敬多、宮本氏も出席。きょう8日から29日まで、東京・日生劇場で上演される。', 1, '2016-12-08 12:49:01', '2017-02-06 12:49:01', NULL, NULL, NULL, 0, 'http://www.oricon.co.jp/news/2078034/photo/2/?anc=148', '【全体ショット】歩くのも一苦労…ド派手衣装で登場した山崎育三郎ら', 'http://www.oricon.co.jp/news/2070630/photo/1/?anc=148', '【写真】山崎育三郎、ドラァグクィーン姿を初公開「キレイ」', 'http://www.oricon.co.jp/news/2007741/?anc=148', '＜過去にも＞陣内孝則、16年ぶり主演映画で10kg減量明かす （12年02月29日）', '', '', '', '', 0, 0, 1, 1, '2016-12-08 17:25:09', '2016-12-08 17:25:09'),
    (121, '4_1_r_20161208_1481185509093970065', 4, 1, 1, 1, 1, 'http://www.oricon.co.jp/news/2082729/', '63c3d8a787a3db1ff9754c0d188cdc20_1481185509', 'キハチカフェのXmasシーズン限定りんごメニュー', '', '<img src=\"http://news.oricon.co.jp/feed/images/size640wh/20161208_125451_size640wh_5876.jpg\" title=\"「キハチ カフェ」に、旬のりんごを使ったXmas限定メニューが登場\" alt=\"「キハチ カフェ」に、旬のりんごを使ったXmas限定メニューが登場\" ><br />\n　「キハチ カフェ」に、旬のりんごを使った限定メニューが登場。なかでも、15日から展開する同カフェ自慢のパンケーキをクリスマスバージョンにした『リンゴキャラメルパンケーキ マスカルポーネのアイスを添えて』は、見た目も華やか。25日までの11日間限定の味わいとなっている。<br />\n<br />\n　同商品は、自慢のリコッタチーズのパンケーキに、温かいりんごのキャラメル煮とカスタードクリームを重ね、トップにマスカルポーネアイスを組み合わせている。キャラメルでほろ苦く煮詰めたりんごが、少し大人の味わい。周りに添えられたヘーゼルナッツのクランブルやローズマリー、キャラメールソースで味の変化も楽しめる。<br />\n<br />\n　また、もうひとつのりんごメニュー『ホットアップルサイダー』も、寒い冬におすすめの一杯。すりおろしリンゴジュースにスパイスを加えてことこと煮込んだ、心も体も温めてくれるホットドリンクとなっている。<br />\n<br />\n【商品詳細】<br />\n『リンゴキャラメルパンケーキ マスカルポーネのアイスを添えて』<br />\n税込価格：単品 1512円／ドリンクセット 1944円<br />\n販売期間：2016年12月15日～12月25日<br />\n『ホットアップルサイダー』<br />\n税込価格：864円 ※小倉井筒屋店756円<br />\n販売期間：～2016年12月31日', 1, '2016-12-08 12:58:34', '2017-02-06 12:58:34', NULL, NULL, NULL, 0, 'http://joshiplus.jp/gourmet/news/2080562/?anc=044', 'ちょっと小さめの2人用　キハチのXmasケーキ （16年10月27日）', 'http://joshiplus.jp/special/107/?cat_id=ISM&anc=044', '【特集】SNS映えバツグンのXmasケーキをピックアップ☆', 'http://joshiplus.jp/gourmet/news/2079201/?anc=044', '幸せな時間の真ん中にケーキを　キハチのカラフルXmas （16年09月30日）', '', '', '', '', 0, 0, 1, 1, '2016-12-08 17:25:09', '2016-12-08 17:25:09'),
    (122, '4_1_r_20161208_1481185509228844254', 4, 1, 1, 1, 1, 'http://www.oricon.co.jp/news/2082728/', '8f15797a3c3014ecd49cfbd73902ba79_1481185509', '田中涼子、第1子男児出産を報告　「想像以上の感動と幸せな気持ち」', '', '<img src=\"http://news.oricon.co.jp/feed/images/size640wh/20161208_120511_size640wh_6038.jpg\" title=\"第1子出産を報告した田中涼子 （C）ORICON NewS inc.\" alt=\"第1子出産を報告した田中涼子 （C）ORICON NewS inc.\" ><br />\n　タレントの田中涼子（32）が8日、自身のブログを更新。6日に第1子となる男児を出産したことを報告した。<br />\n<br />\n　ブログでは「12月6日に無事に3206グラムの男の子の赤ちゃんを出産できました」と報告。「大変だったぁー。もう私の体も赤ちゃんの命もどうなるかと思ったー」と初産の苦労をつづった。<br />\n<br />\n　続けて「想像を絶する痛みと想像以上の感動と幸せな気持ち。24時間かかった陣痛→分娩で放心状態と人生最大の感動でしばし分娩台の上でグッタリでした」と振り返り、「世の中のどんな形であれ出産経験したことある先輩お母さんたち、本当に改めて尊敬しました。女性って凄いです」と感慨深げに語っている。', 1, '2016-12-08 12:08:01', '2017-02-06 12:08:01', NULL, NULL, NULL, 0, 'http://www.oricon.co.jp/news/76913/photo/5/?anc=164', '【写真】ロリータフェイスの田中涼子がボンデージ風衣装で肉食美女に変身', 'http://beauty.oricon.co.jp/trend-culture/love/news/photo/2080274/2/?anc=164', '【写真】ゆず・北川と結婚　アヤパン、愛娘2人との3ショットを公開', 'http://www.oricon.co.jp/news/2080647/photo/1/?anc=164', '【写真】パパの顔に…ロンブー淳が愛娘を抱く微笑ましいショット', '', '', '', '', 0, 0, 1, 1, '2016-12-08 17:25:09', '2016-12-08 17:25:09'),
    (123, '4_1_r_20161208_1481185509303158314', 4, 1, 1, 1, 1, 'http://www.oricon.co.jp/news/2082684/', '571e15547b04c8f3bf0a09172b5c96a8_1481185509', '南原清隆、貴重なコンビ仕事で内村光良に感謝「ありがと、ツッコミ！」', '', '<img src=\"http://news.oricon.co.jp/feed/images/size640wh/20161207_170313_size640wh_9668.jpg\" title=\"12日放送の日本テレビ系特番『歌唱王』（後7：00）で司会を務めるウッチャンナンチャン （C）日本テレビ\" alt=\"12日放送の日本テレビ系特番『歌唱王』（後7：00）で司会を務めるウッチャンナンチャン （C）日本テレビ\" ><br />\n　お笑いコンビ・ウッチャンナンチャンが司会を務める日本テレビ系特番『歌唱王』（後7：00）が12日に放送。このほど2人が日本一の歌声を決める決勝大会を前に、進行の桝太一アナからみどころを聞かれるスペシャル動画が公開された。<br />\n<br />\n　10月、南原清隆MCの同局昼の情報番組『ヒルナンデス！』が、今年初のテレビでのコンビ共演となったほど2人がそろう機会も珍しくなったウンナンだが、動画では安定した内村光良のツッコミに南原が「ありがと、ツッコミ！」と肩をガッチリ掴んで感謝する場面も。当日も、コンビの息ピッタリなやりとりが期待される。<br />\n<br />\n　同番組は全国から歌自慢の参加者を募集し、日本一心に響く歌声の持ち主を決める“全日本歌唱力選手権”。4回目を迎える今年は、2万5831件もの応募があり、厳しい審査を勝ち抜いた20人が『歌唱王』の称号と、優勝賞金200万円（応援団にも100万円）をかけて生放送で激突する。<br />\n<br />\n　ゲストには森三中・大島美幸、クリス・ハート、佐藤栞里、篠原信一、千原ジュニア、審査員には相川七瀬、梅沢登美男、鹿賀丈史、小室哲哉、瀬川瑛子、平尾昌晃、マシコタツロウ、松崎しげる、miwa、湯川れい子らが顔をそろえる。視聴者もスマートフォン・パソコン・データ放送・LINE アプリ・ニコニコ生放送で審査に参加することができる。<br />\n<br /><br />\n<iframe width=\"640\" height=\"360\" src=\"https://www.youtube.com/embed/JKEM2IGVg2s\" frameborder=\"0\" allowfullscreen></iframe>', 1, '2016-12-08 12:00:00', '2017-02-06 12:00:00', NULL, NULL, NULL, 0, 'http://www.oricon.co.jp/news/2081559/?anc=096', '桑田佳祐 新曲CMでウッチャン号泣!? （16年11月16日）', 'http://www.oricon.co.jp/news/2081137/?anc=096', '内村光良、“盟友”出川哲朗のねぎらいに涙なし「泣いてねーよ！」 （16年11月08日）', 'http://www.oricon.co.jp/news/2080280/?anc=096', '内村光良、相方からMC奪い『ヒルウンデス！』スタート　水卜アナはコンビ共演に感涙 （16年10月21日）', '', '', '', '', 0, 0, 1, 1, '2016-12-08 17:25:09', '2016-12-08 17:25:09'),
    (124, '4_1_r_20161208_1481185509385799815', 4, 1, 1, 1, 1, 'http://www.oricon.co.jp/news/2082717/', 'cc60de1800daf2374b7a0a9ab17af6f9_1481185509', '16年夏、山田孝之はカンヌを目指していた…ドキュメンタリードラマ放送へ', '', '<img src=\"http://news.oricon.co.jp/feed/images/size640wh/20161207_220420_size640wh_4022.jpg\" title=\"山田孝之カンヌを目指す!?　『山田孝之のカンヌ映画祭』テレビ東京・テレビ大阪ほかで1月6日スタート（C）「山田孝之のカンヌ映画祭」製作委員会\" alt=\"山田孝之カンヌを目指す!?　『山田孝之のカンヌ映画祭』テレビ東京・テレビ大阪ほかで1月6日スタート（C）「山田孝之のカンヌ映画祭」製作委員会\" ><br />\n　2016年夏、俳優・山田孝之（33）は、世界三大映画祭の一つ、仏・カンヌ国際映画祭に向けた映画づくりに取り掛かっていた。その過程を記録したドキュメンタリードラマ『山田孝之のカンヌ映画祭』が、来年1月6日よりテレビ東京・テレビ大阪（毎週金曜　深0：52～1：23）に放送される。<br />\n<br />\n　映画監督の山下敦弘氏を呼び出した山田は、「世界最高峰のカンヌ映画祭で賞をとりたい」と言い出す。後日、とある場所で集まった山田と山下は、さっそく映画の内容について話し始めていた。この唐突な展開に、山下監督の友人でドキュメンタリー監督の松江哲明氏も巻き込み、ドキュメンタリードラマとして作品化。<br />\n<br />\n　山田は「今回カンヌを目指して、大切なことをたくさん学びました。振り返ってみると、決してベストとは言い切れない決断があったと思います。しかし、行動したことにより気づくことができ、次に役立てることができます。これからも“山田孝之の”やり方で頑張っていこうと思います。しっかりと注意してご覧ください」と神妙に語った。<br />\n<br />\n　山下監督は「山田くんから連絡があり、ひと夏を一緒に過ごしました。山田くんと一緒にいると自分の中の何かが壊れてしまうのでこれからは誘われても山田くんの話に乗らないようにします。山田孝之は変な魔法をもっているので皆さんも気をつけてください」と含みのあるコメントを寄せている。<br />\n<br />\n　あえて触れないのだが、ドキュメンタリードラマの経験のある3人が集まって、何をするのか。松江監督は「この企画が決まった時『一体、何人の先輩監督たちに怒られることになるんだろう』と思いました。実際、すでに怒られています。かなり本気で（それも撮りました）」と報告。続けて「山田くんに巻き込まれ、翻弄された日本映画を代表する俳優、女優、監督、スタッフ、そしてカンヌの皆さんには『ご迷惑をおかけしました』としか言いようがない」と謝った。「けど仕方がないのです。山田孝之が先頭に立っているのだから。嗚呼、おそるべし、山田孝之。無事、最終回まで放送されますように」と祈るほど。“山田孝之”がつくる映画とは一体どのような内容なのか。', 1, '2016-12-08 06:00:01', '2017-02-06 06:00:01', NULL, NULL, NULL, 0, 'http://www.oricon.co.jp/news/2050718/?anc=014', '『山田孝之の東京都北区赤羽』とは何だったのか…山下＆松江監督インタビュー （15年03月27日）', 'http://www.oricon.co.jp/news/2066814/?anc=014', '松岡茉優、本人役でドラマ主演　山田孝之の『北区赤羽』に続くフェイクドキュメンタリー （16年02月14日）', 'http://www.oricon.co.jp/news/2082254/?anc=014', '名脇役6人がシェアハウスで暮らしたら～テレ東の深夜ドラマで夢の共演 （16年11月30日）', '', '', '', '', 0, 0, 1, 1, '2016-12-08 17:25:09', '2016-12-08 17:25:09'),
    (125, '4_1_r_20161208_1481185509457554352', 4, 1, 1, 1, 1, 'http://www.oricon.co.jp/news/2082721/', '7069bccc471d1e0eced6bfa4d884642e_1481185509', '不倫疑惑報道のテレ朝・加藤泰平アナ＆田中萌アナ、そろってレギュラー番組欠席', '', '<img src=\"http://news.oricon.co.jp/feed/images/size640wh/20161208_091619_size640wh_2162.jpg\" title=\"田中萌アナウンサー （C）ORICON NewS inc.\" alt=\"田中萌アナウンサー （C）ORICON NewS inc.\" ><br />\n　8日発売の『週刊文春』で不倫疑惑を報じられたテレビ朝日の加藤泰平アナと田中萌アナが同日、ともにレギュラーを務めている『グッド！モーニング』を欠席した。番組からは特に説明はなかった。<br />\n<br />\n　田中アナは、メインMCの松尾由美子アナ、フリーアナの新井恵理那とともにオープニングであいさつをするのが恒例となっているが、この日は田中アナに代わって島本真衣アナがオープニングに登場した。<br />\n<br />\n　加藤アナが担当する「明快！まとめるパネル」コーナーは、吉野真治アナが代役を努めた。<br />\n<br />\n　なお、同番組のホームページの「出演者」の欄には、2人の写真と名前が表示されている。', 1, '2016-12-08 09:44:02', '2017-02-06 09:44:02', NULL, NULL, NULL, 0, 'http://www.oricon.co.jp/news/2082587/photo/6/?anc=142', '【写真】織田信成と共演した加藤泰平アナウンサー', 'http://www.oricon.co.jp/prof/1009385/?anc=142', '■田中萌アナウンサーのプロフィール', 'http://www.oricon.co.jp/news/2059206/?anc=142', 'テレ朝の新人・田中萌アナウンサー、“朝一番”の顔に抜てき （15年09月15日）', '', '', '', '', 0, 0, 1, 1, '2016-12-08 17:25:09', '2016-12-08 17:25:09'),
    (126, '4_1_r_20161208_1481185509568019772', 4, 1, 1, 1, 1, 'http://www.oricon.co.jp/news/2082723/', '508169881ea7d0560c16294e3e7408d4_1481185509', '冬の安全運転に重宝、カールツァイスのドライバー専用メガネ', '', '<img src=\"http://news.oricon.co.jp/feed/images/size640wh/20161208_103341_size640wh_6140.jpg\" title=\"環境による瞳孔のサイズ変化なども研究して作られたドライバー用メガネ「ZEISS DriveSafe」\" alt=\"環境による瞳孔のサイズ変化なども研究して作られたドライバー用メガネ「ZEISS DriveSafe」\" ><br />\n　光学機器メーカーのカールツァイスから、クルマの運転時の視界をサポートするメガネ「ZEISS DriveSafe」が発売された。夜間の運転でも焦点が合わせやすく、雨天の対向車のヘッドライトによる目のくらみなどを抑え、快適なドライブを助けてくれる。日没が早く、雨や雪などドライバーの視界が悪くなるこれからの季節に重宝しそうだ。<br />\n<br />\n　「ZEISS DriveSafe」のレンズは単焦点と遠近両用から選べる、いずれも暗がりや夜間の運転時でも距離を正確に判断できるように、瞳孔サイズの変化を考慮して設計されている。特殊コーティングを施し、運転中の目のくらみ、フロントガラスについた水粒の反射によるぎらつき・不快感をやわらげてくれる。<br />\n<br />\n　また、遠近両用レンズでは、レンズ内の遠用部分のバランスを再設計。道路、ダッシュボード、バックミラーやサイドミラーの間で目を移動した場合に焦点を合わせやすく設計されており、読み取りのストレスも軽減してくれる。', 1, '2016-12-08 10:53:12', '2017-02-06 10:53:12', NULL, NULL, NULL, 0, 'http://mens.oricon.co.jp/news/2082468/?anc=162', '池田秀一、36年間にわたるシャアの人気の秘密語る （16年12月03日）', 'http://mens.oricon.co.jp/news/2082430/?anc=162', 'メルセデス・ベンツでラーメン!?　「メルセデス・ベンツ　コネクション」のクリスマスコンテンツ （16年12月02日）', 'http://mens.oricon.co.jp/news/2082363/?anc=162', 'メルセデス・ベンツ、新型「Eクラス　ステーションワゴン」 （16年12月01日）', '', '', '', '', 0, 0, 1, 1, '2016-12-08 17:25:10', '2016-12-08 17:25:10'),
    (127, '4_1_r_20161208_1481185509679521420', 4, 1, 1, 1, 1, 'http://www.oricon.co.jp/news/2082671/', '7d72bc5c464fadd9f4c3e8be520fa2f8_1481185509', '年間CM起用社数、吉田羊が初の女王に　男性は2連覇の相葉雅紀＆初王座の錦織圭', '', '<img src=\"http://news.oricon.co.jp/feed/images/size640wh/20161207_184558_size640wh_3578.jpg\" title=\"CM起用社数で初めて1位を獲得した（左から）吉田羊、錦織圭 （C）ORICON NewS inc.\" alt=\"CM起用社数で初めて1位を獲得した（左から）吉田羊、錦織圭 （C）ORICON NewS inc.\" ><br />\n　テレビ放送の調査・測定を行うニホンモニターは8日、『2016タレントCM起用社数ランキング』を発表した。男性部門は、嵐の相葉雅紀と男子プロテニスの錦織圭選手が12社で1位を獲得した。相葉は2年連続、錦織選手は初の首位。女性部門は、女優の吉田羊が13社で初めてCMクイーンの座に輝いた。<br />\n<br />\n　男性部門の2位は11社で、嵐の櫻井翔、二宮和也、俳優の西島秀俊といった上位常連タレントに並び、俳優の遠藤憲一もランクイン。3位は10社でTOKIOの長瀬智也、嵐の松本潤、歌手・タレントのDAIGO。4位は9社で嵐の大野智、スポーツキャスターの松岡修造氏、トレンディエンジェルの斎藤司となった。<br />\n<br />\n　女性部門では、女優の広瀬すず、モデルのローラ、タレントのマツコ・デラックスが12社で2位。3位は11社で女優の有村架純、松岡茉優。以下、女優の桐谷美玲、波瑠、平祐奈が10社で4位。5位は9社で昨年1位の上戸彩のほか、綾瀬はるか、杏、木村文乃、山本美月という結果になった。<br />\n<br />\n■2016年タレントCM起用社数ランキング（同順位は50音順）<br />\n◇男性部門<br />\n1位　12社　相葉雅紀（嵐）、錦織圭<br />\n2位　11社　遠藤憲一、櫻井翔（嵐）、西島秀俊、二宮和也（嵐）<br />\n3位　10社　DAIGO、長瀬智也（TOKIO）、松本潤（嵐）<br />\n4位　9社　大野智（嵐）、斎藤司（トレンディエンジェル）、松岡修造<br />\n5位　8社　小栗旬、城島茂（TOKIO）、松重豊<br />\n<br />\n◇女性部門<br />\n1位　13社　吉田羊<br />\n2位　12社　広瀬すず、マツコ・デラックス、ローラ<br />\n3位　11社　有村架純、松岡茉優<br />\n4位　10社　桐谷美玲、平祐奈、波瑠<br />\n5位　9社　綾瀬はるか、杏、上戸彩、木村文乃、山本美月<br />\n<br />\n■過去5年の年間CM起用社数1位<br />\n◇男性部門<br />\n2015年：相葉雅紀（嵐）（12社）<br />\n2014年：櫻井翔（嵐）／松岡修造（11社）<br />\n2013年：石川遼（12社）<br />\n2012年：石川遼（15社）<br />\n2011年：石川遼（17社）<br />\n<br />\n◇女性部門<br />\n2015年：上戸彩（13社）<br />\n2014年：ローラ（14社）<br />\n2013年：武井咲（17社）<br />\n2012年：板野友美／篠田麻里子（20社　共に当時はAKB48）<br />\n2011年：大島優子（19社　当時はAKB48）<br />\n<br />\n【調査概要】 <br />\n調査項目：テレビCMに出演している男女タレントの起用社数ランキング <br />\n調査期間：2016年1月1日～11月30日 <br />\n対象局：日本テレビ、テレビ朝日、TBS、テレビ東京、フジテレビ（東京地区民放5局地上波オンエア分） <br />\n備考：番組宣伝や本人出演のドラマコラボCM、本人出演のCD／DVD／ゲーム・楽曲配信・映画CM、楽曲PV内出演、ナレーションのみでの出演などは除く <br />\n（すべてニホンモニター調べ／同社サイト：http://www.n-monitor.co.jp/pressreiease/2016/1208.html）', 1, '2016-12-08 10:30:00', '2017-02-06 10:30:00', NULL, NULL, NULL, 0, 'http://www.oricon.co.jp/news/2059623/photo/7/?anc=142', '【写真】真っ赤なスカートで…スレンダー体型の吉田羊', 'http://www.oricon.co.jp/news/2077356/photo/2/?anc=142', '【写真】インスタ公開…吉田羊のキュートなオフショット', 'http://www.oricon.co.jp/news/2065168/photo/6/?anc=142', '【写真】有村架純&菜々緒、“美人姉妹”が初共演で歓喜 浦ちゃん「両手に花」', '', '', '', '', 0, 0, 1, 1, '2016-12-08 17:25:10', '2016-12-08 17:25:10'),
    (128, '4_1_r_20161208_1481185509953801535', 4, 1, 1, 1, 1, 'http://www.oricon.co.jp/news/2082722/', '9d4ef5be038f2478880fbc2291c84da9_1481185509', 'BABYMETAL、今度はメタリカ公演に出演決定', '', '<img src=\"http://news.oricon.co.jp/feed/images/size640wh/20161208_094557_size640wh_3386.jpg\" title=\"BABYMETALがメタリカの韓国公演にゲスト出演決定\" alt=\"BABYMETALがメタリカの韓国公演にゲスト出演決定\" ><br />\n　3人組メタルダンスユニット・BABYMETALが、米ロックバンド・メタリカのワールドツアー『WorldWired Tour 2017』にゲスト出演することが明らかになった。来年1月11日に韓国・ソウルの高尺スカイドームで行われる公演にスペシャルゲストとして登場する。<br />\n<br />\n　これまでにロックフェスで競演することはあったものの、メタリカの単独公演にゲスト出演するのは今回が初。尊敬するアーティストとしてメタリカの名前をあげてきたBABYMETALが、ついに憧れの“メタルマスター”のステージに立つ。<br />\n<br />\n　BABYMETALは7月に米オハイオ州で開催されたアワードで「メタルゴッド」の愛称で知られるジューダス・プリーストのボーカル、ロブ・ハルフォードとコラボレーションステージを展開。現在はレッド・ホット・チリ・ペッパーズのUKツアーに帯同し、年明けにはガンズ・アンド・ローゼズのジャパンツアーにゲスト出演することも決まっており、大物との競演が相次いでいる。', 1, '2016-12-08 10:00:00', '2017-02-06 10:00:00', NULL, NULL, NULL, 0, 'http://music.oricon.co.jp/php/artist/ArtistTop.php?artist=A822345&ref_cd=MS0098&anc=015', '【試聴】BABYMETALの主な配信楽曲', 'http://www.oricon.co.jp/news/2082693/?anc=015', 'BABYMETAL、レッチリ帯同UKツアー開幕 （16年12月07日）', 'http://www.oricon.co.jp/news/2082303/?anc=015', 'ガンズ・アンド・ローゼズ前座にベビメタ＆マンウィズ（16年12月01日）', '', '', '', '', 0, 0, 1, 1, '2016-12-08 17:25:10', '2016-12-08 17:25:10'),
    (129, '4_1_r_20161208_1481185510059968197', 4, 1, 1, 1, 1, 'http://www.oricon.co.jp/news/2082720/', 'fcaa53c59c184b48bcde37c05e518426_1481185510', '石原さとみ主演『校閲ガール』最終回は12.3％', '', '<img src=\"http://news.oricon.co.jp/feed/images/size640wh/20161207_221954_size640wh_7631.jpg\" title=\"石原さとみ主演『地味にスゴイ！校閲ガール・河野悦子』（日本テレビ系、毎週水曜　後10：00）最終回視聴率は12.3％ （C）日本テレビ\" alt=\"石原さとみ主演『地味にスゴイ！校閲ガール・河野悦子』（日本テレビ系、毎週水曜　後10：00）最終回視聴率は12.3％ （C）日本テレビ\" ><br />\n　女優・石原さとみが主演する日本テレビ系連続ドラマ『地味にスゴイ！校閲ガール・河野悦子』（毎週水曜　後10：00）最終話が7日放送され、平均視聴率が12.3％だったことが8日、明らかになった。全10話通しての平均視聴率は2桁超えの安定した記録を維持し、有終の美を飾った（ビデオリサーチ調べ、関東地区）。<br />\n<br />\n　同ドラマは、宮木あや子氏『校閲ガール』シリーズ（KADOKAWA・角川文庫刊）をもとに、ファッション雑誌の編集者を目指す河野悦子が、文章や原稿の誤りを正す校閲部という地味な職場で奮闘し、新しい風を巻き起こしていく姿をコメディータッチで描く。毎回、悦子が披露するド派手ファッションやスカーフを使用したヘアアレンジも話題を集めた。<br />\n<br />\n　最終回では『Lassy』編集長の亀井（芳本美代子）から、春の巻頭特集の企画書を書いてプレゼンするように言われた悦子が、森尾（本田翼）から、その企画が認められれば編集部に異動できるチャンスと聞き、大張り切り。しかし悦子がこれまで担当してきた作家で幸人（菅田将暉）の父親でもある本郷（鹿賀丈史）の盗作を告発する文書が届き…。', 1, '2016-12-08 09:54:36', '2017-02-06 09:54:36', NULL, NULL, NULL, 0, 'http://beauty.oricon.co.jp/trend-culture/trend/news/photo/76613/1/?anc=096', '【写真】石原さとみがくびれで悩殺！ 柔らか“美ボディ”披露', 'http://www.oricon.co.jp/news/2067908/photo/19/?anc=096', '【写真】頬を赤く染め…キュートなおフェロ顔の石原さとみ', 'http://www.oricon.co.jp/news/2080920/full/?anc=096', '【動画】菅田将暉“壁ドン＆あごクイ”練習もシーン無し （16年11月03日）', '', '', '', '', 0, 0, 1, 1, '2016-12-08 17:25:10', '2016-12-08 17:25:10'),
    (130, '4_1_r_20161208_1481185510198218183', 4, 1, 1, 1, 1, 'http://www.oricon.co.jp/news/2082615/', '79abed74d5667de3a101e6ef878f20bd_1481185510', '車のタイプや排気量が同じでも「自動車保険料」に差が出る!?　その理由は…', '', '<img src=\"http://news.oricon.co.jp/feed/images/size640wh/20161207_143120_size640wh_5093.jpg\" title=\"車のタイプや排気量などは、自動車保険料にどう影響するのか？\" alt=\"車のタイプや排気量などは、自動車保険料にどう影響するのか？\" ><br />\n　自動車保険では、主に運転する人、つまり記名被保険者の情報が保険料を決める大きな要素となる。等級や年齢、免許証の色などだ。だが、それだけではなく、車の情報も重視される。<br />\n<br />\n　例えば、3ナンバーの高級外車と軽自動車では、軽自動車の方が保険料は安くなる。「もともとの価格が違うので当然では？」と思うかもしれない。確かに車価を含む車のタイプは、車の損害を補償する「車両保険」の金額に影響するが、事故相手の損害を補償する「対人賠償」や他人の物の損害を補償する「対物賠償」といった保険にも影響するのだ。<br />\n<br />\n　なぜなら、車によってリスクが異なるため。実は過去の事故データから、それぞれの車の保険金支払い実績が導き出されていて、それが保険料にも反映されている。3ナンバーの高級外車と軽自動車を比較した場合は、前者の方が事故時の損害が大きいので、保険料は高い傾向にあるのだ。<br />\n<br />\n　さらにいえば、同じタイプかつ同じ排気量でも、保険料に差が出ることもある。例えば、トヨタの「カローラ　フィールダー」。型式の違いだけで1年間の保険料を比較すると、NZE161GはNZE164Gより高くなる。車種、タイプ、排気量とほぼ同一のはずなのに、なぜこのような違いが生じるのだろうか。<br />\n<br />\n■事故リスクは型式によって異なる<br />\n<br />\n　その理由は、高級外車と軽自動車の場合と同様、車のリスクの差にある。過去の保険金支払い実績は、型式別に管理されているのだ。これを「車両料率クラス」という。<br />\n<br />\n　車両料率クラスは、「対人賠償保険」「対物賠償保険」「傷害保険（搭乗者傷害、人身傷害）」「車両保険」で設定されており、1（低リスク）～9（高リスク）に分類されている。<br />\n<br />\n　例えば、前述の「カローラ フィールダー」の場合、車両料率クラスは次のようになる。<br />\n<br />\n【型式NZE161G】<br />\n・対人賠償保険／4<br />\n・対物賠償保険／5<br />\n・傷害保険／4<br />\n・車両保険／3<br />\n<br />\n【型式NZE164G】<br />\n・対人賠償保険／3<br />\n・対物賠償保険／4<br />\n・傷害保険／3<br />\n・車両保険／3<br />\n<br />\n　対人、対物、傷害でNZE164Gの方が車両料率クラスは低い。これは保険金支払い実績から低リスクと判断されているためで、保険料もその分、安くなる。もし同一の車種やタイプ、排気量で迷ったら、車両料率クラスを参考に購入するといいだろう。<br />\n<br />\n（文／西村有樹）', 1, '2016-12-08 09:40:00', '2017-02-06 09:40:00', NULL, NULL, NULL, 0, 'http://life.oricon.co.jp/rank_insurance/age/50s.html?anc=099', '年代別でみる！　ドライバーの満足度が高い“自動車保険”＜ベスト10＞', 'http://life.oricon.co.jp/rank_insurance/news/2081702/?anc=099', '知って得する【自動車保険】　人身傷害補償の「保険料」3つの節約術', 'http://life.oricon.co.jp/rank_insurance/?cat_id=cs_insurance&anc=099', '【最新版】　事故時に役立つ「自動車保険」満足度ランキング', '', '', '', '', 0, 0, 1, 1, '2016-12-08 17:25:10', '2016-12-08 17:25:10'),
    (131, '4_1_r_20161208_1481185510294913281', 4, 1, 1, 1, 1, 'http://www.oricon.co.jp/news/2082685/', 'cc84979bf73710044d7f888163e553b6_1481185510', 'ミラ・ジョヴォヴィッチ、愛娘と『バイオ』最終作で親子初共演', '', '<img src=\"http://news.oricon.co.jp/feed/images/size640wh/20161207_171749_size640wh_3566.jpg\" title=\"『バイオハザード：ザ・ファイナル』に出演するミラ･ジョヴォヴィッチの愛娘エヴァ・アンダーソン\" alt=\"『バイオハザード：ザ・ファイナル』に出演するミラ･ジョヴォヴィッチの愛娘エヴァ・アンダーソン\" ><br />\n　女優のミラ・ジョヴォヴィッチ（40）が、映画『バイオハザード：ザ・ファイナル』（12月23日公開）で、愛娘エヴァ・アンダーソン（9）と初共演していることが7日、わかった。夫のポール・W・S・アンダーソン監督（51）がメガホンをとり、エヴァは重要なキーパーソンとなるレッドクイーン役でスクリーンデビューを果たす。<br />\n<br />\n　レッドクイーンは、ミラ演じるアリスの宿敵・アンブレラ社が開発した最先端かつ高性能の人工知能。シリーズ最初の作品『バイオハザード』では、地下研究所「ハイブ」全体を統御するメインコンピューターとして登場し、Tウィルスによる世界支配をもくろむアンブレラ社の中枢的な存在として、本作でも物語の重要な役どころとなる。<br />\n<br />\n　エヴァの出演について、ミラは「彼女が仕事をしているのを見るのは素晴らしいし、すごく情熱があるから映画の中のとてもワクワクする部分になると思うわ」と喜んでいる。<br />\n<br />\n　エヴァが登場する本編映像が公開され、シリーズで見せてきた残忍な姿とは違い、アリスに「私を止めてほしい」と語りかけてくるレッドクイーンが描かれている。果たしてレッドクイーンの真の目的とは？　本作でついに明かされる「アンブレラ社の陰謀」「Tウィルスの謎」、「アリスの誕生」の謎をひもとく存在となるのか。<br />\n<br />\n■『バイオハザード：ザ・ファイナル』本編映像<br />\nhttps://youtu.be/ryovJq3BOs8', 1, '2016-12-07 20:00:01', '2017-02-05 20:00:01', NULL, NULL, NULL, 0, 'http://www.oricon.co.jp/news/2081983/photo/2/?anc=115', '【場面写真】ローラ＆”恋人”の2ショット', 'http://www.oricon.co.jp/news/2081816/photo/2/?anc=115', '【独占公開】『バイオハザード：ザ・ファイナル』場面写真', 'http://www.oricon.co.jp/news/2081116/?anc=115', 'ラルク、新曲が『バイオハザード』最終作の日本版主題歌　タイトルはダジャレ入り!? （16年11月08日）', '', '', '', '', 0, 0, 1, 1, '2016-12-08 17:25:10', '2016-12-08 17:25:10'),
    (132, '4_1_r_20161208_1481185510415879188', 4, 1, 1, 1, 1, 'http://www.oricon.co.jp/news/2082698/', 'ad1c7c3214ec02f58b06bb9e22bb51c7_1481185510', 'CHEMISTRYが再始動宣言　来年2月末に5年ぶりライブ', '', '<img src=\"http://news.oricon.co.jp/feed/images/size640wh/20161207_202231_size640wh_2111.jpg\" title=\"5年ぶりに再始動するCHEMISTRY（左から堂珍嘉邦、川畑要）\" alt=\"5年ぶりに再始動するCHEMISTRY（左から堂珍嘉邦、川畑要）\" ><br />\n　2012年4月から活動休止していた男性デュオ・CHEMISTRY（ケミストリー）が、約5年ぶりに活動を再開させることが明らかになった。公式サイトで公開された映像で堂珍嘉邦と川畑要が4年8ヶ月ぶりに2人で肩を並べ、来年2月28日、3月1日に東京国際フォーラム ホールAで行うワンマンライブから再始動することを宣言した。<br />\n<br />\n　テレビ東京系『ASAYAN』の「超男子。オーディション」で約2万人の候補者の中から選ばれたCHEMISTRYは、2001年3月にシングル「PIECES OF A DREAM」でデビュー。2人の歌声はユニット名どおり“化学反応”起こし、いきなりミリオンセラーに。その後もアルバム『The Way We Are』（01年7月発売）、『Second to None』（03年1月発売）がダブルミリオンのヒットを記録した。<br />\n<br />\n　2012年4月、デビュー10周年イヤーを締めくくるワンマンツアーを最後に、「CHEMISTRYという柱をもっと大きくしていくこと」を見据えてそれぞれソロ活動に専念することを発表し、活動を休止。お互いにソロシンガーとしてのキャリアを積んできた。<br />\n<br />\n　2人は「ソロ活動を経て強くなったCHEMISTRYを、待ってくれているファンの皆さんに見せたいという気持ちを確認し合った」といい、二人だけで打ち合わせを重ねて再始動を決意。「まずは、ファンの皆さんの目の前でパフォーマンスを」との思いから、ワンマンライブを皮切りに活動を再開させる。<br />\n<br />\n　公式サイトで公開された映像で2人は「皆さんお久しぶりです、堂珍です、川畑です」とあいさつし、「このフレーズ、懐かしくないですか？」（堂珍）、「懐かしいです。久々だね」（川畑）と掛け合いも。堂珍は「デビュー15周年を迎えたころから何かやったほうがいいんじゃないかという話から、1年以上かけて構想を練ってきた」と説明した。<br />\n<br />\n　再始動ライブのチケットは、堂珍、川畑それぞれのファンクラブで最速先行受付を開始。一般発売は来年1月28日午前10時スタート。<br />\n<br />\n■『CHEMISTRY LIVE 2017 ‐TWO‐』<br />\n日程：2017年2月28日（火）、3月1日（水）<br />\n会場：東京国際フォーラム ホールA', 1, '2016-12-08 09:00:00', '2017-02-06 09:00:00', NULL, NULL, NULL, 0, 'http://www.oricon.co.jp/news/2068083/?anc=015', 'CHEMISTRY、4年ぶりコメント発信　デビュー15周年記念日に （16年03月07日）', 'http://www.oricon.co.jp/news/2057284/?anc=015', 'マツコロイド、今度は歌唱に挑戦　CHEMISTRY川畑とデュエット （15年08月08日）', 'http://www.oricon.co.jp/news/2036721/?anc=015', 'CHEMISTRY堂珍に離婚危機報道　妻サイドは「事実ではない」 （14年04月23日）', '', '', '', '', 0, 0, 1, 1, '2016-12-08 17:25:10', '2016-12-08 17:25:10'),
    (133, '4_1_r_20161208_1481185510508394547', 4, 1, 1, 1, 1, 'http://www.oricon.co.jp/news/2082713/', '80731775d711bf95156d7a5c9ca4b1df_1481185510', '狙い通り？　朝ドラ“栄輔さん”再登場を望む声続々', '', '<img src=\"http://news.oricon.co.jp/feed/images/size640wh/20161207_220920_size640wh_4974.jpg\" title=\"『べっぴんさん』岩佐栄輔役の松下優也（写真・尾鷲陽介）\" alt=\"『べっぴんさん』岩佐栄輔役の松下優也（写真・尾鷲陽介）\" ><br />\n　12月3日の放送では自己最高の平均視聴率21.9％を記録するなど、好調が続いているNHK連続テレビ小説『べっぴんさん』。今回の作品でも新たな“ロス”が巻き起こっているという。昨年下期放送の『あさが来た』の五代友厚（ディーン・フジオカ）、今年上期の『とと姉ちゃん』の星野武蔵（坂口健太郎）らに続く“イケメン枠”として投入され、11月26日の放送でヒロイン・坂東すみれ（芳根京子）のもとを去った、松下優也演じる岩佐栄輔。すみれをはじめとする周囲の人々との交流や心情の変化などが長く丁寧に描かれていたために、ファンから悲痛な叫びが巻き起こっている。<br />\n<br />\n■1ヶ月以上にわたり丁寧に描かれたすみれと栄輔の交流<br />\n<br />\n　すみれの姉・ゆり（蓮佛美沙子）の夫・野上潔（高良健吾）の友人として10月19日の放送で初登場した栄輔。放送2週目にして戦地に赴くことになった夫・紀夫と入れ替わるかたちで登場し、帰ってこない夫を待ちながら仲間とともに新しい道を模索するすみれ、そして娘・さくらを支え続けた。すみれへの好意はバレバレだったが、彼女が出征中の旦那を待ち続けていることも理解し、幸せになってほしいと見守る栄輔。葛藤しながらもすみれの家に泊まることになり、さくらが「お父さん」と呼んでしまい――心が揺れる中で紀夫が帰還し、また入れ替わるようにすみれのもとを去っていった。すみれの幼少期から結婚、夫の出征、出産までが2週間ほどでスピーディに描かれていたのに対し、栄輔登場から去るまで1ヶ月以上の期間を費やしたことから、すみれの紀夫への思いを汲み取りながらもつい栄輔に感情移入してしまう視聴者も多かったようだ。<br />\n<br />\n　栄輔を演じる松下は、2008年に歌手デビューし、ソロアーティスト、そしてグループ「X4」として歌手活動を続ける一方で、俳優としても舞台を中心に活躍。朝ドラ出演も、そんなさなかの今年1月から2月にかけて上演されたミュージカル『花より男子』（松下はメインキャラクターのひとり・道明寺司役）を関係者が鑑賞したのがきっかけだったという。そして熱狂的なファンを持つ“知る人ぞ知る”存在だったのが、朝ドラで一気に幅広い層に知られるところとなった。デビューから8年、真摯に活動を続ける中でつかんだチャンスとも言えるが、松下自身はORICON STYLEのインタビューに対し「チャンスが欲しいと思っているわけじゃないんです」、さらに「“イケメン枠”ということを言っていただいたんですけど、プレッシャーで…（笑）」と謙虚に語っている。<br />\n<br />\n「役者はそういう（チャンスだという）欲を持っちゃいけない気がして。常にその役として自分が生きていなければならないし、うわべだけの芝居は見抜かれる。そんななかで、誰かが見ていてくれるかもしれない、なんていう考えは“邪念”になってしまうと思うんです。確かにチャンスだとは思います。でも、意識するとプレッシャーで思うようなお芝居ができなくなってしまうので、なるべく抑えるようにしています。今はとにかく、この作品の中で何を求められているのかということに集中したいです」（松下）<br />\n<br />\n■Twitterでは“あま絵”をほうふつさせる投稿が続々<br />\n<br />\n　栄輔に対する視聴者の温かい目線は、そうした役ととことん向き合う松下の役者に対しての姿勢あってのものといえるかもしれない。Twitterなどでは、『あまちゃん』（2013年放送）の時に流行した“あま絵”のように、“今日の栄輔”といったふうにすみれを守る栄輔の生き生きした表情を描いたイラストなどが投稿されていた。それだけに、栄輔が人ごみに紛れて去っていく姿を見たファンはショックが大きかったようで、いわゆる“栄輔ロス”という言葉が飛び交うようになっている。松下は先のインタビューで「（栄輔さん、と）役名で呼ばれたらうれしいです。役者をやっていて一番楽しいのって、自分がその役そのものだと思われることなので」と語っていたが、この状況は役者冥利に尽きるだろう。<br />\n<br />\n　1ヶ月後に夢のなかに再登場した『朝が来た』の“五代様”や、再びヒロインとの恋の予感を感じさせた『とと姉ちゃん』の星野のように、再登場を待ち望むファンは多い。来年1月14日にNHK大阪ホールで行われる『連続テレビ小説「べっぴんさん」ファン感謝祭～THE男会～』に紀夫役の永山絢斗とともに松下も出演することがアナウンスされており、なおさら期待が高まっているようだ。すみれを演じる芳根も自身のブログで「とてつもなく寂しいです。。」と心境を明かしていたが、男らしく好きな人のもとをさった栄輔の背中はまだまだ余韻を残しそうだ。', 1, '2016-12-08 08:40:00', '2017-02-06 08:40:00', NULL, NULL, NULL, 0, 'http://www.oricon.co.jp/photo/1732/120166/?anc=028', '【写真】ちょっと初々しい？　朝ドラ前の芳根京子', 'http://www.oricon.co.jp/news/2061156/photo/4/?anc=028', '【写真】『花より男子』道明寺司に扮した松下優也', 'http://www.oricon.co.jp/photo/1750/120679/?anc=028', '【朝ドラヒロイン】堀北真希、白シースルー衣装で肌透け', '', '', '', '', 0, 0, 1, 1, '2016-12-08 17:25:11', '2016-12-08 17:25:11'),
    (134, '4_1_r_20161208_1481185510732907424', 4, 1, 1, 1, 1, 'http://www.oricon.co.jp/news/2082700/', 'b7aa363585769ca18d4efc095187a0e8_1481185510', '“ポスト・菜々緒”　妖艶美女の田中道子が『ドクターX』でブレイクの兆し', '', '<img src=\"http://news.oricon.co.jp/feed/images/size640wh/20161207_203359_size640wh_8363.jpg\" title=\"『ドクターX～外科医・大門未知子～』に秘書兼愛人役で出演している田中道子 （C）ORICON NewS inc.\" alt=\"『ドクターX～外科医・大門未知子～』に秘書兼愛人役で出演している田中道子 （C）ORICON NewS inc.\" ><br />\n　今期放送のドラマ視聴率で1位を独走する『ドクターX～外科医・大門未知子～』（テレビ朝日系）の第4シーズン。相変わらず米倉涼子の凄まじいメスさばきと病院内のドロドロが描かれ、特に病院長役・西田敏行と新キャストの副院長役・泉ピン子の“怪演”ぶりが話題になっているが、なかでもひときわ目を引く美女がいる。病院長の秘書兼愛人・白水里果役に抜擢された田中道子だ。田中は『ミス・ワールド2013日本代表』にもなったモデルであり、今年3月23日の“女優宣言”後、本作で女優デビュー。その日本人離れした美貌と、身長172センチのスタイルの良さで演じる魔性の女ぶりは、“第二の菜々緒”との声も上がっているようだ。<br />\n<br />\n◆菜々緒がアジアンテイストのクールビューティーなら、田中は濃厚な“ラテン系美女”<br />\n<br />\n　田中の役どころは、先述のように“ザ・狸オヤジ”の西田の秘書兼愛人。劇中の設定年齢でも西田65歳に田中26歳と、実に39歳差ながら完全に西田を手玉に取り、西田を膝枕で“ゴロニャン”させたり、肩を揉んで“ご奉仕”するなど、確かに“ザ・愛人”の様子だ。西田の腰巾着である外科部長（吉田鋼太郎）と内科部長（生瀬勝久）に対しては、「フッ！」と汚い物を見るような態度でバカにしたかと思えば、ニヤッと笑って見せたり、外科・内科両部長の言葉を意訳して西田に伝える通訳係になったりもするが、外科部長とは何度かアイコンタクトをしており、（外科部長ともデキてるんじゃないの…？）と思わせるしたたかさもある。さらに、西田の見えないところでダルそうにあくびをしたり、舌打ちをしたり、西田の食べ残したエビにむしゃぶりついたり……とコミカルな演技も見せつつ、表面上は病院長に従う従順な女性秘書ながら、回を追うごとに“悪女”としての独特の存在感を放ちはじめているのである。<br />\n<br />\n　「田中さんのセリフはそれほど多くないですし、演技もまだまだな部分があります。ただ、田中さんの美貌とスタイルから受けるインパクトは大きいですね。菜々緒さんがアジアンテイストのクールビューティーなら、田中さんは濃厚なラテン系美女といったところでしょうか。菜々緒さんもドラマ出演を重ねることで着実に“悪女”（役）として成長してきましたが、田中さんのルックスも言ってみれば“ドS顔”ですし、イジメられたい、手玉に取られたいという中高年男性にとってはたまらないでしょう。親父ウケすることはもう約束されたようなものですね」（ドラマ制作会社スタッフ）<br />\n<br />\n◆悪女キャラとのギャップで、女優として“大化け”する可能性も<br />\n<br />\n　田中は2011年、『ミス・ユニバース・ジャパン2011』で3位入賞。2013年には『ミス・ワールド』で日本一の美女に輝き、モデルとしてCMやファッション誌などで活躍してきたが、同じ事務所の先輩・米倉涼子に憧れて女優を目指すことになる。今回の役についても、撮影前に「悪女役というものをやってみたかったし、演じがいがあるので楽しみです」と語り、撮影に入ってからも「現場を経験するのが、一番勉強になりますね。次はこうしてみよう、といった新しいアイディアも浮かんできますし、演じる楽しさも実感しています。まだまだ確たる自信もなく、手探りでもがいているところですが、このドラマが終わった頃に、何か道すじが見えてくるのではないかと、期待もしています」と話すなど、謙虚に学ぶ姿勢がうかがえる。<br />\n<br />\n　さらには、西田の撮影中のアドリブに関して、「そのおかげで、セリフも増えるし、テレビに映る時間も増える。自分にとってオイシイ、チャンスだと思っています。視聴者の方にも面白がってもらえるように頑張りたいです」と語り、田中は謙虚さだけではなく、芯の強さや女優魂をも持ち合わせていることがわかるのだ。<br />\n<br />\n　今期の『ドクターX』第4シーズンでデビューを果たした“女優”田中道子。今回の悪女役がハマれば、“ポスト・菜々緒”的な悪女や愛人が似合う女優になることができるかもしれない。さらには菜々緒もそうだったように、“悪くない美女”役を演じ、悪女キャラとのギャップも効いて、女優として“大化け”する可能性だって十分に秘めているのだ。来年以降、彼女がどのようなインパクトを残してくれるのか？ また1人、今後の動向が非常に楽しみな女優が誕生した。', 1, '2016-12-08 08:40:00', '2017-02-06 08:40:00', NULL, NULL, NULL, 0, 'http://www.oricon.co.jp/news/2022526/photo/1/?anc=022', '【写真】米倉涼子、“35歳”でミニスカ制服姿', 'http://www.oricon.co.jp/photo/1634/116297/?anc=022', '【写真】ブルーのドレスに身を包んだ女王様姿の菜々緒', 'http://www.oricon.co.jp/photo/1634/133086/?anc=022', '【写真】見る者を圧倒する“菜々緒ポーズ”下着モデルに起用された菜々緒', '', '', '', '', 0, 0, 1, 1, '2016-12-08 17:25:11', '2016-12-08 17:25:11'),
    (135, '4_1_r_20161208_1481185511049111094', 4, 1, 1, 1, 1, 'http://www.oricon.co.jp/news/2082688/', '781ac7a9ba21221e288ff2e8416c41f9_1481185511', '市川海老蔵、木村拓哉と初対決　長髪姿をほめられ「うれしかった」', '', '<img src=\"http://news.oricon.co.jp/feed/images/size640wh/20161207_175419_size640wh_7741.jpg\" title=\"映画『無限の住人』で長髪姿を披露している市川海老蔵 （C）沙村広明／講談社（C）2017映画「無限の住人」製作委員会\" alt=\"映画『無限の住人』で長髪姿を披露している市川海老蔵 （C）沙村広明／講談社（C）2017映画「無限の住人」製作委員会\" ><br />\n　SMAPの木村拓哉主演で人気漫画を実写化する映画『無限の住人』（2017年4月29日公開）で、最強の刺客・閑馬永空（しずまえいくう）を演じる市川海老蔵の劇中カットが初公開された。“丸刈り”の海老蔵とは違い、原作同様にウェーブのかかった長髪姿を披露している。<br />\n<br />\n　原作は、累計発行部数750万部突破を誇る人気漫画。不死身の剣士・万次と、復讐のために彼を用心棒として雇った少女・凜が、壮絶な戦いに身を投じる姿が描かれる。三池崇史監督がメガホンをとり、杉咲花、福士蒼汰、市原隼人、戸田恵梨香らが共演する。<br />\n<br />\n　海老蔵が演じる永空は、福士蒼汰が演じる天津影久（あのつかげひさ）率いる剣客集団“逸刀流”に属し、不死身の万次を死に追い込むほどの力を持っている。ミステリアスな雰囲気で、実はある秘密を抱えているという役どころだ。<br />\n<br />\n　初共演ながら、劇中で木村と壮絶な戦いを繰り広げた海老蔵は「木村さんとの1対1の真剣勝負のお芝居は、緊迫した空気が張り詰め、久しぶりの映画の現場でとても刺激的でした。完成がどうなっているか僕自身本当に楽しみです」と期待。出演が発表された際の自身のブログでは「劇中で私、髪の毛長いんですが、撮影しばらくしたら、あのロン毛の髪の毛の木村さんから、海老蔵ロン毛もいけんじゃん！と褒めてもらった、うれしかった（笑）」とエピソードも明かしていた。', 1, '2016-12-08 08:00:00', '2017-02-06 08:00:00', NULL, NULL, NULL, 0, 'http://www.oricon.co.jp/news/2081612/photo/1/?anc=115', '【写真】栗山千明、金髪の美女剣士姿を披露　銀髪・市原隼人は「最恐の役」', 'http://www.oricon.co.jp/news/2081466/photo/1/?anc=115', '【写真】『無限の住人』福士蒼汰＆戸田恵梨香のビジュアル解禁', 'http://www.oricon.co.jp/news/2077183/photo/1/?anc=115', '【原作カット】木村拓哉が演じる万次', '', '', '', '', 0, 0, 1, 1, '2016-12-08 17:25:11', '2016-12-08 17:25:11'),
    (136, '4_1_r_20161208_1481185511212482592', 4, 1, 1, 1, 1, 'http://www.oricon.co.jp/news/2082716/', '29b7f7c5835520dc91828404fb71dfcd_1481185511', '小堺一機、憧れ“ショーケン”と対面に感激「こんな幸せなことはない」', '', '<img src=\"http://news.oricon.co.jp/feed/images/size640wh/20161207_220229_size640wh_6683.jpg\" title=\"“ショーケン”こと俳優の萩原健一がタレントの小堺一機が司会を務めるフジテレビ系トークバラエティ『かたらふ～ぼくたちのスタア～』（毎週土曜 後5：00）で10日と17日の放送回に2週連続で出演\" alt=\"“ショーケン”こと俳優の萩原健一がタレントの小堺一機が司会を務めるフジテレビ系トークバラエティ『かたらふ～ぼくたちのスタア～』（毎週土曜 後5：00）で10日と17日の放送回に2週連続で出演\" ><br />\n　“ショーケン”こと俳優の萩原健一（66）が、タレントの小堺一機（60）が司会を務めるフジテレビ系トークバラエティー『かたらふ～ぼくたちのスタア～』（毎週土曜　後5：00）で10日と17日の放送回に2週連続で出演。小堺とゲストが秘蔵映像とともに“憧れのスター”について語り合う同番組で、サンボマスター・山口隆（40）と小堺が憧れるショーケン本人が登場する。<br />\n<br />\n　今年4月にスタートした番組では初回と第2回の放送で、小堺と山口がショーケンのかっこよさ、伝説の数々を熱く語り合った。実は萩原本人がその時オンエアを見て、2人をプライベートの食事会に誘ったのが今回の夢の企画が実現したきっかけに。番組始まって以来のビッグゲストの登場にさすがの小堺も興奮を隠しきれず、山口も憧れのスターを前に感極まった様子だった。<br />\n<br />\n　1週目の放送では「ご本人を前に語れる、こんな幸せなことはない」と興奮気味に語る小堺と山口のそれぞれが萩原とのエピソードを語り、萩原もその時の思い出などを回顧。2週目の放送では、今後について語るうち、話がどんどん盛り上がり、サンボマスターへのゲスト参加も萩原本人が約束。小堺ともある約束を交わす。', 1, '2016-12-08 07:00:00', '2017-02-06 07:00:00', NULL, NULL, NULL, 0, 'http://www.oricon.co.jp/news/2072146/?anc=096', '萩原健一、29年ぶりダウンタウンと共演　大みそか『笑ってはいけない』出演予定あった （16年05月24日）', 'http://www.oricon.co.jp/news/2040724/?anc=096', '萩原健一＆リカ夫妻、結婚後イベント初2ショット　「愛している」と大のろけ （14年08月07日）', 'http://beauty.oricon.co.jp/trend-culture/trend/news/87107/?anc=096', '冨田リカ、ショーケンとの新婚生活は「意外と穏やか」（11年04月27日）', '', '', '', '', 0, 0, 1, 1, '2016-12-08 17:25:11', '2016-12-08 17:25:11'),
    (137, '4_1_r_20161208_1481185511360689520', 4, 1, 1, 1, 1, 'http://www.oricon.co.jp/news/2082715/', 'f0083a403028a52ca51c0b0cd270b7c8_1481185511', '松岡昌宏×ココリコ・田中、「そっくり」と話題のふたりが最終回で共演', '', '<img src=\"http://news.oricon.co.jp/feed/images/size640wh/20161207_215833_size640wh_1955.jpg\" title=\"12月9日放送、テレビ朝日系ドラマ『家政夫のミタゾノ』最終回にゲスト出演する田中直樹（ココリコ）\" alt=\"12月9日放送、テレビ朝日系ドラマ『家政夫のミタゾノ』最終回にゲスト出演する田中直樹（ココリコ）\" ><br />\n　お笑いコンビ・ココリコの田中直樹が、9日放送のテレビ朝日系『家政夫のミタゾノ』最終話（後11：15※一部地域で放送時間が異なる）にゲスト出演することがわかった。<br />\n<br />\n　TOKIOの松岡昌宏が主演し、家政夫としてのスキルは一流なのに、なぜか女装している三田園薫が、派遣先の部屋の汚れだけでなく、家庭・家族に巣食う“根深い汚れ”までもスッキリと落としていくドラマ。<br />\n<br />\n　実は、放送開始当初から、女装した松岡の映像を見た視聴者から「ココリコ・田中かと思った」「ココリコ・田中とそっくり」といった反応があり、ネットなどでも話題になっていた。それを受け、「そっくり」と話題のふたりが最終回でまさかの共演が実現する。最終回を前に再びネットがざわつきそうだ。<br />\n<br />\n　田中も周囲から「コントなどで女装するときの格好に似ているんじゃない？（笑）」と言われていたそうで、「そのたびに申し訳ないと思いながらも、意識して観ていました」と告白。「（ゲスト出演者に）呼んでいただけるなんて思っていなかったので、すごく驚いたし、すごくうれしかったです！」と撮影を振り返った。<br />\n<br />\n　なぜ、ミタゾノは家政夫になったのか。なぜ、女装しているのか。なぜ、家庭を壊すのか…ミタゾノ誕生の壮絶過去が明かされる!?', 1, '2016-12-08 07:00:00', '2017-02-06 07:00:00', NULL, NULL, NULL, 0, 'http://www.oricon.co.jp/news/2080515/?anc=014', 'ココリコ田中、3年ぶり連ドラ主演 一流の「超能力師」演じる （16年11月03日）', 'http://www.oricon.co.jp/news/2081297/?anc=014', '大掃除の季節に役立つ　家政夫・ミタゾノの家事テクニック （16年11月11日）', 'http://www.oricon.co.jp/news/2078875/?anc=014', '清水富美加、TOKIO松岡と連ドラ初共演「好奇心全開で挑みたい」 （16年09月24日）', '', '', '', '', 0, 0, 1, 1, '2016-12-08 17:25:11', '2016-12-08 17:25:11'),
    (138, '4_1_r_20161208_1481185511499579232', 4, 1, 1, 1, 1, 'http://www.oricon.co.jp/news/2082681/', '02c71a7900407177809e75715a15ea1b_1481185511', '桜井日奈子、奇跡のキュートスマイル　『ヤンジャン』表紙に初登場', '', '<img src=\"http://news.oricon.co.jp/feed/images/size640wh/20161207_160320_size640wh_9080.jpg\" title=\"キュートすぎるスマイルを見せた桜井日奈子　（C）藤本和典／集英社\" alt=\"キュートすぎるスマイルを見せた桜井日奈子　（C）藤本和典／集英社\" ><br />\n　女優の桜井日奈子（19）が、8日発売の『週刊ヤングジャンプ』2号の表紙と巻頭グラビアに登場。大きく飛躍した今年の勢いを象徴するように、まぶしく輝く満面のスマイルとキュートなポーズで誌面を飾った。<br />\n<br />\n　“岡山の奇跡”として注目を集めた桜井は、『いい部屋ネット』や『白猫プロジェクト』などのCM、さらにドラマ『そして誰もいなくなった』や『ラストコップ』に出演するなど、今年は大活躍。さらに、人気の若手女優らを起用し、毎年冬に注目を集める『JR SKISKI』の今年の“ゲレンデ美女”にも抜てきされ、大きな話題を集めた<br />\n<br />\n　『ヤンジャン』初登場となる今回のグラビアでは、「日曜の昼下がり、午後3時」をテーマに家でまったりしたり、自転車で近所にお使いに行くなど、等身大の女の子に扮し、大きく口を開けた明るい笑顔やちょっといたずらっぽい笑顔、お菓子を頬ぼる瞬間など、さまざまな表情を披露。見ている人が彼女のことを身近に感じられる内容となっている。<br />\n<br />\n　巻末グラビアには、『ミラクルジャンプ』10月号で行われた読者投票企画「ミラドル！」で優勝した、アイドルグループ「アイドルカレッジ」の石塚汐花（いしづかしおか・19）が登場。温泉旅館を舞台に、恥ずかしげな表情を浮かべながら大胆なビキニ姿や浴衣姿に挑戦した。<br />\n<br />\n　巻頭カラー漫画は、最新コミック10巻が19日に発売される松原利光氏乃『リクドウ』。', 1, '2016-12-08 06:30:00', '2017-02-06 06:30:00', NULL, NULL, NULL, 0, 'http://www.oricon.co.jp/news/2078566/photo/1/?anc=142', '【写真】かわいすぎる美人ポリス！1日警察署長に挑戦', 'http://www.oricon.co.jp/news/2082326/?anc=142', '『JR SKISKI』今年の“ゲレンデ美女”は桜井日奈子に決定 （16年12月01日）', 'http://www.oricon.co.jp/news/2081456/photo/3/?anc=142', '【写真】レースクイーン頂点に三城千咲　水着でスレンダーBODYを披露', '', '', '', '', 0, 0, 1, 1, '2016-12-08 17:25:11', '2016-12-08 17:25:11'),
    (139, '4_1_r_20161208_1481185511630905140', 4, 1, 1, 1, 1, 'http://www.oricon.co.jp/news/2082692/', '6e094b0c335deef223d4cd2195c387a8_1481185511', '馬場ふみか、初写真集が部門首位　「モグラ」牽引役が完璧ボディを惜しげもなく解禁', '', '<img src=\"http://news.oricon.co.jp/feed/images/size640wh/20161207_185435_size640wh_8634.jpg\" title=\"12/12付オリコン週間“本”ランキングの「写真集部門」で首位を獲得した馬場ふみか写真集『色っぽょ』 （C）熊谷貫／週刊プレイボーイ\" alt=\"12/12付オリコン週間“本”ランキングの「写真集部門」で首位を獲得した馬場ふみか写真集『色っぽょ』 （C）熊谷貫／週刊プレイボーイ\" ><br />\n　女性ファッション誌『non-no』専属モデルで女優の馬場ふみか（21）の1st写真集『色っぽょ』（2日発売／集英社）が週間9320部を売り上げ、12/12付オリコン週間“本”ランキングの「写真集部門」で首位を獲得した。<br />\n<br />\n　馬場はモデル活動と並行して、水着グラビアにも挑戦する「モグラ」という新たな潮流の牽引役として活躍中。記念すべき初写真集の表紙では、衣装を脱ぎ去り髪だけでその豊満で均整のとれたバストを隠すという“髪ブラ”に挑戦。誌面でもダイナマイトな83センチのバスト、さらに見事なくびれを兼ね備えた“パーフェクトボディー”を出し惜しみなく見せつけている。<br />\n<br />\n　総合部門にあたる「BOOK部門」では、　人気シリーズ「ハリー・ポッター」の最新作『ハリー・ポッターと呪いの子　第一部・第二部　特別リハーサル版』（J.K.ローリング、ジョン・ティファニー、ジャック・ソーン著　松岡佑子訳　静山社　11月11日発売）が、週間6.2万部を売り上げ、11/21付から4週連続の1位となり、累積売上は64.9万部となった。<br />\n<br />\n　そのほかコミック部門では、『ハイキュー!!』の最新24巻（古館晴一／2日発売）が、週間売上28.2万部で1位を獲得した。', 1, '2016-12-08 06:00:00', '2017-02-06 06:00:00', NULL, NULL, NULL, 0, 'http://www.oricon.co.jp/news/2079493/photo/4/?anc=142', '【写真】横乳チラリ…美背中も大胆に披露する馬場ふみか', 'http://www.oricon.co.jp/news/2078057/photo/4/?anc=142', '【写真】白のビキニでセクシーBODY全開の馬場ふみか', 'http://www.oricon.co.jp/news/2078057/photo/2/?anc=142', '【写真】大胆な手ブラショットを披露した片山萌美', '', '', '', '', 0, 0, 1, 1, '2016-12-08 17:25:12', '2016-12-08 17:25:12'),
    (140, '4_1_r_20161208_1481185511711170820', 4, 1, 1, 1, 1, 'http://www.oricon.co.jp/news/2082719/', 'bd528d396914910829f9b148d3016a56_1481185511', '織田信成、『ドラえもん』に本人役で登場＆ダンスも披露', '', '<img src=\"http://news.oricon.co.jp/feed/images/size640wh/20161207_220831_size640wh_5898.jpg\" title=\"12月9日放送、テレビ朝日系『ドラえもん』織田信成とドラえもんが「びっくりラッキーマンボ！」で夢のコラボ（C）藤子プロ・小学館・テレビ朝日・シンエイ・ADK\" alt=\"12月9日放送、テレビ朝日系『ドラえもん』織田信成とドラえもんが「びっくりラッキーマンボ！」で夢のコラボ（C）藤子プロ・小学館・テレビ朝日・シンエイ・ADK\" ><br />\n　プロフィギュアスケーターの織田信成が、9日放送のテレビ朝日系アニメ『ドラえもん』（毎週金曜　後7：00～7：30）の「南極ペンギンを救え！」に、本人役で登場することがわかった。この日、同局系ではフィギュアスケートの『グランプリファイナル　マルセイユ大会』を午後8時から放送。織田も解説を担当するとあって、まさに“スケート一色”に染まった夜となる。<br />\n<br />\n　織田の出演回は、壮大な景色の中でスケートを楽しもうと、南極を訪れたドラえもんたちがペンギンを助ける冒険物語。氷上で転んでばかりののび太を見かねたドラミが、日本国内のスケートリンクで演技中の織田にこっそり“まねラジコン”の送信アンテナを装着、織田の演技のとおりにのび太を滑れるようにしてあげる、という場面。演技中のシーンのためせりふはないものの、スタッフが織田そっくりにキャラクターを描き上げている。<br />\n<br />\n　さらにこの日は番組内のミニゲームコーナー「びっくりラッキーマンボ！」にも登場。ドラえもんと共にラテンのリズムに乗ってダンスに挑戦し、クイズを出題。収録では、フィギュアスケーターらしくキレッキレなステップを披露して、ドラえもんとスタッフを圧倒していたという。<br />\n<br />\n　子どもの頃から大のドラえもんファンで、毎週家族と放送を楽しんでいるという織田は、今回のドラえもんとの共演に大感激。「テレビを観ながら家族で参加していたラッキーマンボに自分が出題する側で出演させていただけるなんてすごくうれしいです！　たくさんの方に楽しんでいただければ‼」と喜んでいる。', 1, '2016-12-08 06:00:00', '2017-02-06 06:00:00', NULL, NULL, NULL, 0, 'http://www.oricon.co.jp/news/2082587/?anc=014', '織田信成、本人役でアニメ『ユーリ!!! on ICE』にゲスト出演 （16年12月06日）', 'http://www.oricon.co.jp/news/2080435/?anc=014', '織田信成＆浅田舞、『映画ドラえもん』で声優初挑戦　氷上で初共演も （16年10月28日）', 'http://www.oricon.co.jp/news/2060744/photo/3/?anc=014', '【写真】息子2人とUSJへ…イクメン織田信成のプライベートショット', '', '', '', '', 0, 0, 1, 1, '2016-12-08 17:25:12', '2016-12-08 17:25:12'),
    (141, '4_1_r_20161208_1481185511860330455', 4, 1, 1, 1, 1, 'http://www.oricon.co.jp/news/2082690/', '80ecfc53572ca15f69fc778f95dd671d_1481185511', 'May\'n、台湾人気映画の日本版主題歌を担当　『私の少女時代 Our Times』', '', '<img src=\"http://news.oricon.co.jp/feed/images/size640wh/20161207_180046_size640wh_5393.jpg\" title=\"『私の少女時代 Our Times』の日本版主題歌を担当するMay’n\" alt=\"『私の少女時代 Our Times』の日本版主題歌を担当するMay’n\" ><br />\n　歌手のMay\'nが、台湾で2015年度興行収入第1位を記録したラブストーリー映画『私の少女時代 Our Times』の日本版主題歌「小さな幸せ」を歌唱することが、わかった。<br />\n<br />\n　映画は、1990年代の台湾が舞台。平凡な女子高生・林真心（リン・チェンシン）は、女生徒の憧れの的・欧陽（オウヤン）に片思いしていたが、不幸の手紙を受け取ったことを機に学校一の不良・大宇（タイユィ）と急接近。タイプの異なる2人の間で揺れる真心の恋を描く。<br />\n<br />\n　本国台湾で興収約13億円、中国では公開から4日間で興収16億円を突破した話題作で、アンディ・ラウ、ジェリー・イェンら人気スターがゲスト出演している。May\'nの歌う日本版主題歌は、台湾の大人気歌手のHebeが歌ったオリジナル主題歌「小幸運」の日本語カバー。「小幸運」は台湾および中国語の楽曲としては初となる再生回数1億回を突破した大ヒットソングとなる。<br />\n<br />\n　May\'nは「海外のファンの方からも反響がとても大きく、改めて作品の人気を感じました。近くにいてくれるときには気付かなかった小さな幸せ。胸がキュンとする切ないとてもすてきな楽曲です！」と紹介。また、先日、日本で行われた舞台あいさつでMay\'nの生歌唱を絶賛していた主演の宋芸樺(ビビアン・ソン）は「頑張って!　この歌があなたのためにいっぱいの小さい幸運を持ってくることができるのを願っています」とメッセージを寄せている。<br />\n<br />\n　日本版主題歌は12月10日から全国劇場にて公開され、同日にシネマート心斎橋にて舞台あいさつも予定されている。', 1, '2016-12-08 06:00:00', '2017-02-06 06:00:00', NULL, NULL, NULL, 0, 'http://www.oricon.co.jp/news/2074970/?anc=112', 'May\'n、シングル「Belief」MV＆ジャケ写公開　アニメ『タブー・タトゥー』OP曲 （16年07月12日）', 'http://www.oricon.co.jp/news/2074935/?anc=112', 'May’n『ストリートファイターV』とタッグ　新曲がアジア版イメージソングに起用 （16年07月12日）', 'http://www.oricon.co.jp/news/2073709/?anc=112', 'May\'n、地元名古屋でツアー完走　国内＆海外めぐる新ツアーも発表 （16年06月20日）', '', '', '', '', 0, 0, 1, 1, '2016-12-08 17:25:12', '2016-12-08 17:25:12'),
    (142, '4_1_r_20161208_1481185511931944787', 4, 1, 1, 1, 1, 'http://www.oricon.co.jp/news/2082714/', '2360b55a93e53a4bf9cba09e28a30755_1481185511', '大谷亮平、元カノは倉科カナで妻は水野美紀　『奪い愛、冬』キャスト追加発表', '', '<img src=\"http://news.oricon.co.jp/feed/images/size640wh/20161207_215150_size640wh_2656.jpg\" title=\"1月20日スタート、テレビ朝日系ドラマ『奪い愛、冬』に大谷亮平と水野美紀の出演が決定（C）テレビ朝日\" alt=\"1月20日スタート、テレビ朝日系ドラマ『奪い愛、冬』に大谷亮平と水野美紀の出演が決定（C）テレビ朝日\" ><br />\n　秋ドラマ『逃げるは恥だが役に立つ』（TBS系）でハイスペックイケメンを演じ、ネクストブレイクの筆頭株とされる俳優の大谷亮平（36）が、来年1月20日スタートのテレビ朝日系『奪い愛、冬』（毎週金曜　後11：15※一部地域で放送時間が異なる）に出演することが7日、わかった。主人公・池内光（倉科カナ）とその婚約者・奥川康太（三浦翔平）を中心に、さまざまな男女が入り乱れ、愛を奪い合うストーリーが展開する“金曜の夜のドロキュン劇場”に参戦する。<br />\n<br />\n　「現在出演させていただいている楽しく明るい恋愛ドラマ（『逃げ役』）とは対局にある作品。そのドロキュンの世界にどっぷりはまっていくのが、今から楽しみです。自分のエネルギーを思いっきり解放して、時に楽しく、時に苦しみながら、頑張って演じます」とコメント。<br />\n<br />\n　大谷が演じるのは光の元彼・森山（旧姓・尾田）信。かつては光の上司であり、結婚を意識した恋人でもあった。だが突然、ある事情から一方的に別れを告げ、光の前から姿を消す。現在は、福岡在住で、幼なじみの森山蘭（水野美紀）と結婚し、別のデザイン会社に勤務している。それが3ヶ月限定で東京勤務になり、光と再会する。<br />\n<br />\n　この再会がすべての発端に。「信のように女性に依存される役を演じるのは初めて。信の表の顔、そして決して外には見せない“とてつもない闇”をしっかりと表現し、それがセクシーさにつながればいいな」、と新ドラマでも世の女性をとりこにしようと意気込んでいる。<br />\n<br />\n　水野が演じる信の妻・蘭もただ者ではない。もはや狂気としか思えないほどの愛情を夫へ注ぎ、事故で動かなくなった右足が痛むたび、信にさすらせるなど、夫を支配下に置くような謎の行動も見せる。まるで狙いを定めた獲物に音もなく忍びよるヘビのように、闇でうごめく“強烈な情念の女”をねっとりと演じ、視聴者を震え上がらせる。<br />\n　<br />\n　「今回のような完全なヒール役は自分史上初なので、やりがいを感じてワクワクしました。この機会に“恋愛依存体質の女の深い業”をとことん突き詰めたいです。私が全員を闇に取り込んでバッドエンド…という可能性もあるかもしれない―視聴者の皆さんがそう思うくらいの闇に、爽やかな人たちを全員引きずり込んでやりたいと思います」と気合十分だ。<br />\n<br />\n　ほかに、息子の康太を溺愛し、光に陰湿な嫌がらせを繰り返す奥川美佐役に榊原郁恵。光の幸せを切望する優しき母・池内麻紀役にキムラ緑子。光のことが好きだった過去を持つ上司・武田玄役に三宅弘城。表面上は光と仲良くしながらも、姑息な手を使って康太を奪おうとする豊野秀子役に秋元才加。大学時代に康太と付き合っていた幼なじみ・尾上礼香役にダレノガレ明美を配役。それぞれ個性を炸裂させながら、「奪い愛」バトルを繰り広げる。<br />\n<br />\n　さらに、『仮面ライダーゴースト』で主演を務めた西銘駿も、信の後輩・加藤清で出演。子どもたちのヒーローが一転、大人の濃厚ドラマでどんな顔を見せてくれるのか。', 1, '2016-12-08 05:00:01', '2017-02-06 05:00:01', NULL, NULL, NULL, 0, 'http://www.oricon.co.jp/news/2081584/?anc=014', '倉科カナ＆三浦翔平、ドロキュン恋愛ドラマで共演　　鈴木おさむ氏が描く“奪い愛” （16年11月17日）', 'http://www.oricon.co.jp/news/2077486/?anc=014', '大谷亮平、日本の連ドラ初レギュラー　星野源の“恋敵”に （16年08月28日）', 'http://www.oricon.co.jp/news/2071156/?anc=014', '“第2のディーン”大谷亮平、目標は「大河ドラマを狙いたい」 （16年05月03日）', '', '', '', '', 0, 0, 1, 1, '2016-12-08 17:25:12', '2016-12-08 17:25:12');
INSERT INTO `article_content` (`id`, `code`, `cp_id`, `cp_genre_id`, `cp_sub_genre_id`, `genre_id`, `sub_genre_id`, `uid`, `hash_timestamp`, `title`, `short_title`, `body`, `article_type`, `post_start_date`, `post_end_date`, `first_composition_date_main`, `first_composition_date_other`, `category_composition_date`, `category_integration_time`, `relation_title_1`, `relation_url_1`, `relation_title_2`, `relation_url_2`, `relation_title_3`, `relation_url_3`, `relation_title_4`, `relation_url_4`, `relation_title_5`, `relation_url_5`, `deleted_flag`, `import_deleted_flag`, `created_user_id`, `updated_user_id`, `created_date`, `updated_date`)
VALUES
    (143, '4_1_r_20161208_1481185512143717342', 4, 1, 1, 1, 1, 'http://www.oricon.co.jp/news/2082687/', 'a76b00588ab0e118e480cdb67f2a11b9_1481185512', '関ジャニ安田、舞台『俺節』で演歌歌手役　ヒロインにシャーロット・ケイト・フォックス', '', '<img src=\"http://news.oricon.co.jp/feed/images/size640wh/20161207_173235_size640wh_8215.jpg\" title=\"関ジャニ∞・安田章大が主演する舞台『俺節』は、2017年5月28日(日)～6月18日(日)東京・TBS赤坂ACTシアターで開幕\" alt=\"関ジャニ∞・安田章大が主演する舞台『俺節』は、2017年5月28日(日)～6月18日(日)東京・TBS赤坂ACTシアターで開幕\" ><br />\n　人気グループ・関ジャニ∞の安田章大が来年5月28日より東京・TBS赤坂アクトシアターで開幕する舞台『俺節』に主演することがわかった。土田世紀氏の同名漫画を原作に演歌歌手を目指す不器用な主人公が、恋や仲間に傷つきぶつかりながら成長していく姿を、登場人物たちが劇中で唄う演歌の名曲やギターの生演奏を交えながら熱く描いていくエンターテインメント作品。共演には日本語での舞台初出演となるシャーロット・ケイト・フォックス、俳優・福士誠治らが起用された。<br />\n<br />\n　2年ぶりの舞台主演となる安田が演じるのは演歌歌手を目指して青森から上京したコージこと海鹿緋治。気弱であがり症だが、抜群の歌唱力を持つという役どころ。そんなコージがお調子者のギター弾き・オキナワこと南風原野郎（福士誠治）、彼が根城とするドヤ街・みれん横丁で出会った不法滞在中のストリッパー・テレサ（シャーロット・ケイト・フォックス）と出会う。テレサへの想いが募るほどコージの演歌は加速。やがて飲み屋街で流しの演歌歌手として修行を始めたコージとオキナワはコンビで演歌界デビューを目指す。<br />\n<br />\n　このほかにも、みれん横丁のボス的存在“流しの大野”に六角精児、元アイドル歌手でストリッパーのリーダー寺泊行代／まりあんに高田聖子、コージの歌の先生・エドゥアルダに桑原裕子、テレサが働くストリップ小屋の支配人・戌亥辰巳／金村に中村まこと、演歌界の大物歌手・北野波平は西岡徳馬らがそれぞれ演じる。<br />\n<br />\n　関ジャニ∞としてはデビュー曲が演歌「浪花いろは節」だったことから「もともと演歌には縁があります」という安田。グループでは主にギターとコーラスを担当しているが、プロデューサーと脚本・演出の福原充則氏もが「鳥肌モノ！」と絶賛するその歌唱力を舞台上で披露するが「その演歌に気持ちを乗せて、舞台で目一杯唄えることも楽しみです。毎ステージ惜しみなく全力を出し切って、感情が爆発しているような舞台になればと思います」とやる気をにじませている。<br />\n<br />\n■安田章大<br />\n今回のお話をいただいて初めて原作を読みました。不器用だけど一途で、何度つまずいても必死に喰らいついていくコージの姿が素敵でカッコよくて、土田さんの熱量と思いを感じました。あがいても上手くいかないことって、誰にでもあると思うんです。そこで必死にもがく姿、諦めずに貫き通す心の清らかさや強さは、人の心を打つ美しさを持っていると思う。そんな世界観を、まだまだもがいている僕がコージを演じることで伝えられたら、ものすごく嬉しい。関ジャニ∞のデビュー曲が「浪花いろは節」で、もともと演歌には縁があります。その演歌に気持ちを乗せて、舞台で目一杯唄えることも楽しみです。毎ステージ惜しみなく全力を出し切って、感情が爆発しているような舞台になればと思います。<br />\n<br />\n■シャーロット・ケイト・フォックス<br />\nお話をいただいた時は、私が日本語の舞台に!?と驚きました。大きなチャレンジなので怖さもありますが、ストリッパー役というのもなかなか経験できないと思うので、楽しみたいです。試しにポールダンスのレッスンを始めたら想像以上に大変で、最初はアザだらけになりました。役の背景まで掘り下げながら、孤独なサバイバーとして生きてきたテレサの人間性や愛を見出したいです。子どもの頃から舞台が大好きだった私は、劇場の匂いに包まれると家に帰ってきたような気持ちになります。エネルギッシュな素晴らしい舞台になることを、とても楽しみにしています。<br />\n<br />\n■福士誠治<br />\n演歌という大きな主軸を持った、芝居と音楽が融合したこの舞台で、コージの相棒・オキナワを演じます。コージと切っても切れない関係を作って、お客さんに何か届けられたら幸せです。この 12 月に初めて舞台の演出を経験して、より一層、演劇の世界が好きになったと感じているので、まずは稽古場でのセッションを楽しみたいですね。ギターの生演奏もあるので、四六時中ギターに触れて猛特訓して備えたい。それでももし足りない時は、音楽に対する熱い気持ちで勝負できたらと思います。芝居もギターも原点に戻る気持ちで、覚悟を決めて頑張ります。', 1, '2016-12-08 05:00:00', '2017-02-06 05:00:00', NULL, NULL, NULL, 0, 'http://www.oricon.co.jp/news/2082336/?anc=096', '『ジャニーズカウントダウン』今年もフジテレビで放送　TOKIO、関ジャニ、Hey! Say!ら出演者発表 （16年12月01日）', 'http://www.oricon.co.jp/news/2081664/?anc=096', '蔵之介＆横山裕W主演『破門』予告公開　関ジャニ主題歌が初お披露目 （16年11月19日）', 'http://www.oricon.co.jp/news/2081222/?anc=096', '関ジャニ横山、手塚治虫“幻の傑作”舞台化で主演　野心家のダークヒーロー「憧れる」 （16年11月10日）', '', '', '', '', 0, 0, 1, 1, '2016-12-08 17:25:12', '2016-12-08 17:25:12'),
    (144, '4_1_r_20161208_1481185512413720097', 4, 1, 1, 1, 1, 'http://www.oricon.co.jp/news/2082686/', '8eb1c7109391219684ebdd235847d6b9_1481185512', '『ハリー・ポッター』最新作が4週連続首位　海外著者作品で初', '', '<img src=\"http://news.oricon.co.jp/feed/images/size640wh/20161116_164308_size640wh_5148.jpg\" title=\"『ハリー・ポッターと呪いの子　第一部・第二部　特別リハーサル版』（静山社）（著）Ｊ．Ｋ．ローリング，ジョン・ティファニー，ジャック・ソーン／（訳）松岡佑子\" alt=\"『ハリー・ポッターと呪いの子　第一部・第二部　特別リハーサル版』（静山社）（著）Ｊ．Ｋ．ローリング，ジョン・ティファニー，ジャック・ソーン／（訳）松岡佑子\" ><br />\n　人気シリーズ「ハリー・ポッター」の最新作『ハリー・ポッターと呪いの子　第一部・第二部　特別リハーサル版』（J.K.ローリング、ジョン・ティファニー、ジャック・ソーン著　松岡佑子訳　静山社　11月11日発売）が、週間6.2万部を売り上げ、12/12付オリコン週間“本”ランキング総合部門にあたる「BOOK部門」で首位を獲得。11/21付から4週連続の1位となり、累積売上は64.9万部となった。<br />\n<br />\n　2008年の当ランキング発表開始以来、海外著者作品による連続首位獲得週数記録としては、『おやすみ、ロジャー魔法のぐっすり絵本』（著：カール＝ヨハン・エリーン、監修：三橋美穂／2015年11月発売）が2016/2/15、22、29付で記録した3週連続（通算5週）が最長だったが、4週連続同部門1位獲得は初めて。前作『～と死の秘宝』（2008年7月発売）が2008/8/4、11付で記録した2週連続1位もすでに上回り、次週の5週連続1位を視野に入れている。<br />\n<br />\n　8年ぶりのシリーズ最新作となる同書は、ローリング氏が書き下ろした新しいストーリーをもとに、今年7月にロンドンで上演された舞台劇の書籍化。今作の“特別リハーサル版”とはプレミア上演前のリハーサル上演期間の脚本を翻訳したもの。最終版の脚本は今後“愛蔵版”として出版される（詳細は後日発表予定）。舞台は前作『～と死の秘宝』（2008年7月発売）での戦いから19年が経ち、父親になったハリーが2人目の子どもをホグワーツ魔法学校へと送り出したその後の物語が描かれている。', 1, '2016-12-08 04:00:00', '2017-02-06 04:00:00', NULL, NULL, NULL, 0, 'http://www.oricon.co.jp/news/2079188/full/?anc=112#vk', '【動画】『ファンタスティック・ビーストと魔法使いの旅』予告編', 'http://www.oricon.co.jp/news/2080950/?anc=112', '宮野真守、ハリポタ新章『ファンタビ』声優に決定「気合を入れて臨みました」 （16年11月04日）', 'http://www.oricon.co.jp/news/2080637/photo/1/?anc=112', 'エディ・レッドメインら『ファンタビ』キャラ写真', '', '', '', '', 0, 0, 1, 1, '2016-12-08 17:25:12', '2016-12-08 17:25:12'),
    (145, '4_1_r_20161208_1481185512691345519', 4, 1, 1, 1, 1, 'http://www.oricon.co.jp/news/2082712/', 'cfbd1577f860b0f62b5b7cd0291f6288_1481185512', 'AKB48、ハイレゾ配信解禁　ベスト盤を高音質化', '', '<img src=\"http://news.oricon.co.jp/feed/images/size640wh/20161207_212442_size640wh_6637.jpg\" title=\"ハイレゾ音源化されるAKB48のベスト盤『0と1の間【Complete Singles】』\" alt=\"ハイレゾ音源化されるAKB48のベスト盤『0と1の間【Complete Singles】』\" ><br />\n　人気アイドルグループ・AKB48が誕生11周年を迎えた8日、ハイレゾ音源配信を解禁した。代表曲「ヘビーローテーション」「恋するフォーチュンクッキー」などのヒット曲が高音質で配信される。<br />\n<br />\n　今回、ハイレゾ音源化（FLAC 96.0kHz／24bit）されたのは、昨年11月に発売されたベストアルバム『0と1の間』。<br />\n<br />\n　レーベルの垣根を越えてシングル表題曲を網羅した「Complete Singles」（全46曲、5800円）、累積100万枚以上を売り上げたヒットシングル集「Million Singles」（全29曲、3800円）、オリコン週間ランキング1位を獲得したシングル集「No.1 Singles」（全31曲、3800円）、劇場盤「Theater Edition」（全25曲、3800円）の4形態が一挙配信となった。', 1, '2016-12-08 00:00:00', '2017-02-06 00:00:00', NULL, NULL, NULL, 0, 'http://www.oricon.co.jp/prof/385015/products/1149911/1/?anc=015', 'AKB48ベスト盤『0と1の間【No.1 Singles】』収録曲一覧', 'http://www.oricon.co.jp/news/2081946/?anc=015', 'AKBが“紅白版”総選挙!?　視聴者投票で48人を選抜（16年11月24日）', 'http://www.oricon.co.jp/news/2081820/?anc=015', 'AKB48、来年1・25新アルバム　来年3月にイベント開催も （16年11月22日）', '', '', '', '', 0, 0, 1, 1, '2016-12-08 17:25:13', '2016-12-08 17:25:13'),
    (146, '4_1_r_20161208_1481185512900540332', 4, 1, 1, 1, 1, 'http://www.oricon.co.jp/news/2082661/', '4be7bcf622fa22885fad300f6480baec_1481185512', 'CMで話題のサチモス、イベント＆レア映像を生配信', '', '<img src=\"http://news.oricon.co.jp/feed/images/size640wh/20161207_113910_size640wh_7239.jpg\" title=\"CMソング「STAY TUNE」には“TOKYO”という歌詞も織り込まれている\" alt=\"CMソング「STAY TUNE」には“TOKYO”という歌詞も織り込まれている\" ><br />\n　Honda『VEZEL』（ヴェゼル）のCMソング「STAY TUNE」を歌う、神奈川育ちの6人組グループ・Suchmos（サチモス）。都会的で洗練された音楽が話題の彼らが、12月10日（土）に神奈川県内でイベント『Suchmos DRIVE-IN TEATER』を開催する。<br />\n<br />\n　こちらは、1日限定のドライブインシアター。イベントではSuchmosのライブ映像を上映するが、さらに今回、メンバーが出演する映像やドライブインシアターで上映するフィルムを楽しむことができる、特別番組の生配信が決定した。<br />\n<br />\n　特別番組は、Suchmosが2台のVEZELに分乗するところからスタート。メンバー自らハンドルを握り、ドライブインシアターの会場まで運転するというものだ。なかなか見ることのできないレアな場面だけに、本人たちがどんなドライブミュージックを選曲するのか、車中のメンバートークなども興味深い。<br />\n<br />\n　同様に、会場で上映するSuchmos初のワンマンツアー『TOUR MINT CONDITION』の映像も生配信されるということで、彼らの音楽やパフォーマンスもたっぷり楽しめることになる。直接ドライブインシアターに足を運ぶことができなくても、イベントを疑似体験することができそうだ。<br />\n<br />\n　生配信は、スペシャアプリおよびLINE LIVEで、12月10日（月）15：30頃～19：00頃の配信を予定している。', 1, '2016-12-07 18:30:01', '2017-02-05 18:30:01', NULL, NULL, NULL, 0, 'http://www.oricon.co.jp/news/2075297/photo/3/?anc=166', '【写真】Suchmosと共演、渡辺直美が“ド迫力”コスチュームで魅せる', 'http://www.oricon.co.jp/news/2081165/full/?anc=166', '【動画】深田恭子が彼氏を“のりかえる”!?　魔性の女ぶりとキュートな裏側', 'http://www.oricon.co.jp/news/2082204/photo/1/?anc=166', '【写真】菅田将暉がタキシード姿でCMに！ 歌声も披露', '', '', '', '', 0, 0, 1, 1, '2016-12-08 17:25:13', '2016-12-08 17:25:13'),
    (147, '4_1_r_20161208_1481185512982686005', 4, 1, 1, 1, 1, 'http://www.oricon.co.jp/news/2082711/', '1a0d2e32308e58ad02d747c2d07fc63c_1481185512', '博多華丸、ブラバン少女150人と生コラボ　指揮に初挑戦', '', '<img src=\"http://news.oricon.co.jp/feed/images/size640wh/20161207_210700_size640wh_1877.jpg\" title=\"ブラスバンドの指揮に初挑戦した博多華丸\" alt=\"ブラスバンドの指揮に初挑戦した博多華丸\" ><br />\n　お笑いコンビ・博多華丸・大吉の博多華丸が6日、福岡・精華女子高等学校で行われた博多座3月公演『熱血！ブラバン少女。』の記者会見に出席。ブラスバンドを題材にした同作にちなみ、指揮初挑戦の華丸に合わせて、同高吹奏楽部員150人が生演奏を披露した。<br />\n<br />\n　全国屈指の強豪である同校吹奏楽部のCDをモチーフにし、博多座オリジナルの脚本で舞台化した同作。華丸演じる熱血コーチのもと、全国大会を目指す女子高校吹奏楽部の奮闘する姿が描かれる。<br />\n<br />\n　同校の演奏を動画で確認し、総勢150人の現役“ブラバン少女”たちの指揮に臨んだ華丸。ところどころミスがみえる一幕もあったが、何とか乗り切ると「きっと僕がいなくても演奏ができる皆さんなので僕に合わせてくれたんだと思います。生徒の皆さんは途中から僕の方をあまり見なくなっていた（笑）」と、自虐交じりに初挑戦の感想を語った。<br />\n<br />\n　昨年3月の初主演舞台『めんたいぴりり～博多座版～』以来、2度目の舞台演技となる華丸は「今回は2回目ということでその進化を問われる。プロ野球でも、だいたい2年目で肘を壊すとかそういうジンクスもあるので、どうしても力は入ります」と気合十分に語り「青春時代はナガブチ（長渕剛）一本で、吹奏楽には無縁だった僕ですが、皆さんに喜んでいただける舞台にしたいという気持ちは強くあります！」とやる気をにじませた。<br />\n<br />\n　熱血教師という役どころに「ちょうど高2の娘がいるので、そこで練習しようかなと思ってるんですが、そんなに口もきいてくれないので自主トレもうまくいってない」とプライベートの苦労も垣間見せつつ「博多弁で先生というと、どうしても金八先生が頭に浮かびますが、いい意味でああはならないようにしようと（笑）。尊敬する素晴らしい先輩のイメージも参考にしつつ、僕なりの女子高の先生を演じられれば」と意気込んだ。<br />\n<br />\n　同舞台は来年3月4日から26日まで博多座で上演される。記者会見にはそのほか、特別レポーターとしてアイドルグループ・HKT48の上野遥も出席した。', 1, '2016-12-07 21:18:02', '2017-02-05 21:18:02', NULL, NULL, NULL, 0, 'http://www.oricon.co.jp/news/2065183/?anc=013', '博多華丸・大吉25周年　渡辺直美、衣装で“悩殺”　BIG3も祝福 （16年01月12日）', 'http://www.oricon.co.jp/news/2064187/?anc=013', '博多華丸・大吉、バラ売りしない“コンビ”の転機　「気合いを入れた」相方への気遣い （15年12月22日）', 'http://www.oricon.co.jp/news/2080540/full/?anc=013', '【動画】杏、指揮者役を絶賛される「熱意と気迫で見事に演じていた」 （16年10月26日）', '', '', '', '', 0, 0, 1, 1, '2016-12-08 17:25:13', '2016-12-08 17:25:13'),
    (148, '4_1_r_20161208_1481185513136108413', 4, 1, 1, 1, 1, 'http://www.oricon.co.jp/news/2082294/', '3d2efc0bac86bba0fc0507a5bdc37e8b_1481185513', '【2016新語・流行語】大賞は「神ってる」　今年も芸能ネタ逃す', '', '<img src=\"http://news.oricon.co.jp/feed/images/size640wh/20161201_182712_size640wh_5059.jpg\" title=\"「神ってる」で2016流行語大賞を受賞したでプロ野球・広島カープの鈴木誠也選手 （C）ORICON NewS inc.\" alt=\"「神ってる」で2016流行語大賞を受賞したでプロ野球・広島カープの鈴木誠也選手 （C）ORICON NewS inc.\" ><br />\n　その年話題となった新語・流行語を決定する年末恒例の『2016 ユーキャン新語・流行語大賞』（現代用語の基礎知識選）が12月1日に発表され、「神ってる」が年間大賞に選ばれた。<br />\n<br />\n　各分野からバランス良く言葉が生まれ、“豊作”と言われた今年の流行語候補だったが、唯一の年間大賞を獲得したのは、「神がかっている」さまを表現した省略語だった。プロ野球・広島カープの緒方孝市監督が6月18日、2試合連続サヨナラホームランを放った鈴木誠也選手の神がかった活躍を「神ってる」と表現した。<br />\n<br />\n　それ以来、この言葉は球団全体の好調ぶりを象徴するようになり、チームは1991年以来25年ぶりのリーグ優勝を達成した。広島の勢いとともに「神ってる」も使いやすさから世間へ広く浸透。チームは惜しくも日本シリーズで破れ日本一を逃したが、「神ってる」が流行語の頂点を獲得する運びとなった。<br />\n<br />\n　そのほか、トップ10には「ゲス不倫」「聖地巡礼」「トランプ現象」「PPAP」「保育園落ちた日本死ね」「（僕の）アモーレ」「ポケモンGO」「マイナス金利」「盛り土」が選出された。また、『選考委員特別賞』として「復興城主」が受賞した。<br />\n<br />\n　2014年は「ダメよ～ダメダメ」、13年は「じぇじぇじぇ！」「倍返し」「今でしょ！」、12年は「ワイルドだろぉ」など、数年前までは芸能ネタが大賞を受賞していたが、昨年に続き今年も大賞を逃した。<br />\n<br />\n　同賞は1年の間に軽妙に世相を表現している“言葉”、広く大衆の目や口、耳をにぎわせた“言葉”の中から選出。11月17日に大賞候補30語が発表されていた。<br />\n<br />\n【2016新語・流行語】<br />\n「大賞受賞語」：受賞者<br />\n◆「神ってる」：緒方孝市監督、鈴木誠也選手（広島東洋カープ）<br />\n<br />\n「トップテン受賞語」：受賞者<br />\n■「ゲス不倫」：週刊文春編集部<br />\n■「聖地巡礼」：冨田英揮氏（ディップ代表取締役社長 兼 CEO）<br />\n■「トランプ現象」：なし<br />\n■「PPAP」：ピコ太郎（シンガーソングライター）<br />\n■「保育園落ちた日本死ね」：山内志桜里氏（衆議院議員）<br />\n■「（僕の）アモーレ」：長友佑都選手（伊・インテル）、平愛梨（女優）<br />\n■「ポケモンGO」：株式会社ナイアンティック、株式会社ポケモン<br />\n■「マイナス金利」：日本銀行<br />\n■「盛り土」：受賞者辞退<br />\n<br />\n「選考委員特別賞」：受賞者<br />\n◇「復興城主」：熊本市<br />\n<br />\n【過去の新語・流行語大賞】<br />\n2015年：「トリプルスリー」「爆買」<br />\n2014年：「ダメよ～ダメダメ」「集団的自衛権」<br />\n2013年：「今でしょ！」「お・も・て・な・し」「じぇじぇじぇ」「倍返し」<br />\n2012年：「ワイルドだろぉ」<br />\n2011年：「なでしこジャパン」<br />\n2010年：「ゲゲゲの」', 1, '2016-12-01 17:00:10', '2017-01-30 17:00:10', NULL, NULL, NULL, 0, 'http://www.oricon.co.jp/news/2082294/full/?anc=142#rk', '【一覧表】2016年の流行語　大賞＆トップテン', 'http://www.oricon.co.jp/news/2082357/?anc=142', '平愛梨が感涙　“アモーレ”は「大切な言葉」　流行語トップ10に！ （16年12月01日）', 'http://www.oricon.co.jp/news/2078919/photo/4/?anc=142', '【写真集カット】ベッドに寝そべり…オトナの色気全開の平愛梨', '', '', '', '', 0, 0, 1, 1, '2016-12-08 17:25:13', '2016-12-08 17:25:13'),
    (149, '4_1_r_20161208_1481185513266231079', 4, 1, 1, 1, 1, 'http://www.oricon.co.jp/news/2082355/', '953e42c2bd56a6fe0d2ce9a1c483d186_1481185513', '「ゲス不倫」が流行語トップ10　ベッキー＆ゲス川谷らは表彰式欠席…', '', '<img src=\"http://news.oricon.co.jp/feed/images/size640wh/20161201_173310_size640wh_4135.jpg\" title=\"『新語・流行語大賞』表彰式に出席した『週刊文春』記者の文春（ふみはる）くん （C）ORICON NewS inc.\" alt=\"『新語・流行語大賞』表彰式に出席した『週刊文春』記者の文春（ふみはる）くん （C）ORICON NewS inc.\" ><br />\n　その年話題となった新語・流行語を決定する年末恒例の『2016 ユーキャン新語・流行語大賞』（現代用語の基礎知識選）が12月1日に発表され、今年の芸能界を席巻した「ゲス不倫」がトップ10に選出された。この日都内で行われた表彰式には、スクープを連発した『週刊文春』編集部の記者が代表して出席した。<br />\n<br />\n　覆面姿のまま登壇し、笑いを誘った文春記者は、記念品を受け取り「僕自身は張り込み記者。あまり人前に出てしまうと、今後の張り込みに支障をきたす…」とジョークを交えて説明。続けて「本来ならば取材対象者でありますベッキーさん、宮崎（謙介）元議員にぜひとも来ていただき、もらう賞だとは思うのですが、代表して頂きます。ありがとうございます」と話した。<br />\n<br />\n　「ゲス不倫」はトップ10が確実視されており、発表前からベッキー、ゲスの極み乙女。の川谷絵音ら不倫騒動で名前の挙がった著名人の出席も注目されていたが、会場に姿を見せることはなかった。<br />\n<br />\n　そのほか、プロ野球・広島カープの緒方孝市監督の「神ってる」が年間大賞。トップ10には「聖地巡礼」「トランプ現象」「PPAP」「保育園落ちた日本死ね」「（僕の）アモーレ」「ポケモンGO」「マイナス金利」「盛り土」が選出された。また、『選考委員特別賞』として「復興城主」が受賞した。<br />\n<br />\n　同賞は1年の間に軽妙に世相を表現している“言葉”、広く大衆の目や口、耳をにぎわせた“言葉”の中から選出。11月17日に大賞候補30語が発表されていた。', 1, '2016-12-01 17:35:05', '2017-01-30 17:35:05', NULL, NULL, NULL, 0, 'http://www.oricon.co.jp/news/2082294/full/?anc=013#rk', '【一覧表】2016年の流行語　大賞＆トップテン', 'http://www.oricon.co.jp/photo/1720/134662/?anc=013', '【写真】ベッキー、“裸一貫”の覚悟で美背中披露', 'http://www.oricon.co.jp/news/2079699/?anc=013', 'ベッキー、不倫騒動ネタにした東野幸治を追及「私のLINEを朗読した」 （16年10月09日）', '', '', '', '', 0, 0, 1, 1, '2016-12-08 17:25:13', '2016-12-08 17:25:13'),
    (150, '4_1_r_20161208_1481185513372988511', 4, 1, 1, 1, 1, 'http://www.oricon.co.jp/news/2082357/', 'adaf48c79cd99c75bfbb1afab182952b_1481185513', '平愛梨が感涙　“アモーレ”は「大切な言葉」　流行語トップ10に！', '', '<img src=\"http://news.oricon.co.jp/feed/images/size640wh/20161201_174308_size640wh_8926.jpg\" title=\"“大切な言葉”「アモーレ」が流行語トップ10に選ばれた平愛梨 （C）ORICON NewS inc.\" alt=\"“大切な言葉”「アモーレ」が流行語トップ10に選ばれた平愛梨 （C）ORICON NewS inc.\" ><br />\n　その年話題となった新語・流行語を決定する年末恒例の『2016 ユーキャン新語・流行語大賞』（現代用語の基礎知識選）が1日に発表され、サッカー日本代表DFの長友佑都（31）が発した「（僕の）アモーレ」がトップ10に選出。都内で行われた表彰式には、恋人で女優の平愛梨（31）が代理出席し、涙ながらに喜びを爆発させた。<br />\n<br />\n　「アモーレ」は交際宣言の際に長友が発言し、話題を呼んだ。平は「こんな素晴らしい賞をいただくことができてうれしく思っています」と笑顔を浮かべ「長友さんに『アモーレ』と言っていただいて、周りの方から『アモーレ！』とあたたかく声をかけていただきました」と、改めてその反響の大きさを明かした。<br />\n<br />\n　喜びを語るなかで、思わず感涙した平。「遠距離である私はこの言葉にすごく支えられました。私にとってはとても大切な言葉で、2人で一緒に頂けた賞を大切にします」とほほ笑み、イタリアで奮闘している長友に向けて「こんな私ですが末永くよろしくお願いします」とカメラ越しにラブコール。会場からはあたたかい拍手が沸き起こっていた。<br />\n<br />\n　そのほか、プロ野球・広島カープの緒方孝市監督の「神ってる」が年間大賞。トップ10には「ゲス不倫」「聖地巡礼」「トランプ現象」「PPAP」「保育園落ちた日本死ね」「ポケモンGO」「マイナス金利」「盛り土」が選出された。また、『選考委員特別賞』として「復興城主」が受賞した。<br />\n<br />\n　同賞は1年の間に軽妙に世相を表現している“言葉”、広く大衆の目や口、耳をにぎわせた“言葉”の中から選出。11月17日に大賞候補30語が発表されていた。', 1, '2016-12-01 17:47:08', '2017-01-30 17:47:08', NULL, NULL, NULL, 0, 'http://www.oricon.co.jp/news/2082294/full/?anc=013#rk', '【一覧表】2016年の流行語　大賞＆トップテン', 'http://www.oricon.co.jp/news/2078919/photo/4/?anc=013', '【写真集カット】ベッドに寝そべり…オトナの色気全開の平愛梨', 'http://www.oricon.co.jp/news/2072797/photo/1/?anc=013', '【写真】妹・平祐奈も祝福！ 平愛梨との2ショット公開', '', '', '', '', 0, 0, 1, 1, '2016-12-08 17:25:13', '2016-12-08 17:25:13'),
    (151, '4_1_r_20161208_1481185513495666813', 4, 1, 1, 1, 1, 'http://www.oricon.co.jp/news/2082358/', '2a57d8ad9ead80302dad9a4edb6e3f74_1481185513', '「神ってる」が流行語大賞　鈴木誠也選手「信じられないの一言」', '', '<img src=\"http://news.oricon.co.jp/feed/images/size640wh/20161201_174325_size640wh_1736.jpg\" title=\"「神ってる」で2016流行語大賞を受賞したでプロ野球・広島カープの鈴木誠也選手 （C）ORICON NewS inc.\" alt=\"「神ってる」で2016流行語大賞を受賞したでプロ野球・広島カープの鈴木誠也選手 （C）ORICON NewS inc.\" ><br />\n　その年話題となった新語・流行語を決定する年末恒例の『2016 ユーキャン新語・流行語大賞』（現代用語の基礎知識選）が12月1日に発表され、年間大賞はプロ野球・広島カープの緒方孝市監督が発した「神ってる」が受賞。その言葉で評された鈴木誠也選手が同日、都内ホテルで行われた表彰式に出席した。<br />\n<br />\n　まずは、トップ10へ選出された言葉の代表としてスーツ姿で登場した鈴木選手は、多くのフラッシュを浴びると「僕自身が『神ってる』と言った言葉ではないけど、うれしく思っています。広島カープが優勝していい年だなと思っているので、こういう年に『神ってる』という言葉を受賞したことを一生忘れないと思います」と喜びを語った。<br />\n<br />\n　スピーチ後、年間大賞への選出が発表されると「信じられないの一言です」と目を丸くた鈴木選手。「（打席より）こっち（表彰式）のほうが緊張します」と終始恐縮しきりだった。<br />\n<br />\n　そのほか、トップ10には「ゲス不倫」「聖地巡礼」「トランプ現象」「PPAP」「保育園落ちた日本死ね」「（僕の）アモーレ」「ポケモンGO」「マイナス金利」「盛り土」が選出された。また、『選考委員特別賞』として「復興城主」が受賞した。<br />\n<br />\n　同賞は1年の間に軽妙に世相を表現している“言葉”、広く大衆の目や口、耳をにぎわせた“言葉”の中から選出。11月17日に大賞候補30語が発表されていた。', 1, '2016-12-01 18:00:03', '2017-01-30 18:00:03', NULL, NULL, NULL, 0, 'http://www.oricon.co.jp/news/2082294/full/?anc=013#rk', '【一覧表】2016年の流行語　大賞＆トップテン', 'http://www.oricon.co.jp/news/2082357/?anc=013', '平愛梨が感涙　“アモーレ”は「大切な言葉」　流行語トップ10に！ （16年12月01日）', 'http://www.oricon.co.jp/news/2078919/photo/4/?anc=013', '【写真集カット】ベッドに寝そべり…オトナの色気全開の平愛梨', '', '', '', '', 0, 0, 1, 1, '2016-12-08 17:25:13', '2016-12-08 17:25:13'),
    (152, '4_1_r_20161208_1481185513587130462', 4, 1, 1, 1, 1, 'http://www.oricon.co.jp/news/2082361/', '7c31b2f2cf0211ef2baa8a56d6045301_1481185513', 'ピコ太郎、流行語トップ10入りでボケ倒し　電話出演でPPAP生披露もグダグダ', '', '<img src=\"http://news.oricon.co.jp/feed/images/size640wh/20161201_182043_size640wh_5767.jpg\" title=\"「PPAP」が流行語トップ10入りしたピコ太郎 （C）ORICON NewS inc.\" alt=\"「PPAP」が流行語トップ10入りしたピコ太郎 （C）ORICON NewS inc.\" ><br />\n　その年話題となった新語・流行語を決定する年末恒例の『2016 ユーキャン新語・流行語大賞』（現代用語の基礎知識選）が1日に発表され、世界的なブレイクを果たした千葉県出身シンガー・ソングライター「ピコ太郎」による「PPAP」がトップ10に選出された。スケジュールの都合で、この日都内で行われた表彰式を欠席したピコ太郎だったが、電話での出演で受賞の喜びを語った。<br />\n<br />\n　司会の生島ヒロシから「トップ10に選ばれましたよ」と祝福されると「えっ？『神ってる』ですか？　違いますね。本当にありが玉置成実と言わざるを得ません」と早速ボケ倒し。生島から「今（電話口から）ライブで『PPAP』をやってくれませんか？」と急きょリクエストされると「上手くできればいいですが…」とアカペラでイントロ部分のメロディーを口ずさみ始めた。<br />\n<br />\n　会場から笑い声が漏れる中、歌を披露しようと試みるピコ太郎だったが、タイミング悪く会場からBGMが流れ始めてしまい、リズムがズレてしまうというグダグダな展開に。「音ズレていると思うんですけど…」と言いながらも何とか歌い終え「今、踊っていますー。これ、事故ってるんじゃないですか？　『事故ってる』は流行語にノミネートされてないですか？」と大賞に絡めながら切実に訴えて、会場の笑いを誘っていた。<br />\n<br />\n　そのほか、プロ野球・広島カープの緒方孝市監督の「神ってる」が年間大賞。トップ10には「聖地巡礼」「トランプ現象」「ゲス不倫」「保育園落ちた日本死ね」「（僕の）アモーレ」「ポケモンGO」「マイナス金利」「盛り土」が選出された。また、『選考委員特別賞』として「復興城主」が受賞した。<br />\n<br />\n　同賞は1年の間に軽妙に世相を表現している“言葉”、広く大衆の目や口、耳をにぎわせた“言葉”の中から選出。11月17日に大賞候補30語が発表されていた。', 1, '2016-12-01 18:22:05', '2017-01-30 18:22:05', NULL, NULL, NULL, 0, 'http://www.oricon.co.jp/news/2082294/full/?anc=148#rk', '【一覧表】2016年の流行語　大賞＆トップテン', 'http://www.oricon.co.jp/news/2081221/?anc=148', 'ピコ太郎のブレイク、海外と日本で温度差？「サウナ、水風呂な感じ」 （16年11月10日）', 'http://www.oricon.co.jp/news/2081656/?anc=148', '可愛すぎる！ピコ太郎がセサミストリートとコラボ（動画あり）', '', '', '', '', 0, 0, 1, 1, '2016-12-08 17:25:14', '2016-12-08 17:25:14'),
    (153, '4_1_r_20161208_1481185514044284325', 4, 1, 1, 1, 1, 'http://www.oricon.co.jp/news/2082695/', 'f7e45d10424c10e054bf69731cd5f519_1481185514', 'エクセルシオールから冬のご褒美スイーツ続々', '', '<img src=\"http://news.oricon.co.jp/feed/images/size640wh/20161207_193522_size640wh_9578.jpg\" title=\"エクセルシオールから冬のご褒美スイーツ続々！\" alt=\"エクセルシオールから冬のご褒美スイーツ続々！\" ><br />\n　「エクセルシオール カフェ」が、旬のりんごを使った『国産りんごのアップルパイ』と『フォンダンショコラ』、新作モーニング『フレンチトースト』を8日より販売。寒い冬にほっこり温まる“ご褒美スイーツ”になっている。<br />\n<br />\n　『国産りんごのアップルパイ』は、ざく切りにした皮付きの国産りんごとりんごペーストを合わせ、香ばしく焼き上げた素朴な味わいが楽しめる。ベルギー産チョコレートを使った『フォンダンショコラ』は、温めたチョコレート生地を切り分けると、中からとろりとチョコソースが溶けだす“あったかスイーツ”。<br />\n<br />\n　モーニングには、中央までしっとりとした食感の厚切り食パンを使った『フレンチトースト』が登場。お好みでメープルシロップをかければ、優しい甘さが広がるスイートな朝食に。<br />\n<br />\n【商品詳細】※すべて税込<br />\n『国産りんごのアップルパイ』（480円）<br />\n『フォンダンショコラ』（ホイップ付／480円）<br />\n『フレンチトースト』（ドリンク付／480円～）<br />\n販売開始：2016年12月8日～', 1, '2016-12-07 19:40:37', '2017-02-05 19:40:37', NULL, NULL, NULL, 0, 'http://joshiplus.jp/special/113/?cat_id=OBT&anc=016', '【特集】あったかい部屋で食べたい！おすすめ“冬アイス”6選', 'http://joshiplus.jp/gourmet/news/2082624/?cat_id=OBT&anc=016', '飲むチョコのよう！？　マックカフェの“チョコづくし”ドリンク （16年12月07日）', 'http://joshiplus.jp/gourmet/news/2082597/?cat_id=OBT&anc=016', 'キャンドルみたいでキュート！　スタバのホリデースイーツ （16年12月06日）', '', '', '', '', 0, 0, 1, 1, '2016-12-08 17:25:14', '2016-12-08 17:25:14'),
    (154, '4_1_r_20161208_1481185514142535066', 4, 1, 1, 1, 1, 'http://www.oricon.co.jp/news/2082660/', '36fc9df0992b843ad6e6b6710c6b62e5_1481185514', '北島三郎『ゆうもあ大賞』を笑顔で受賞「愛してもらえる歌をうたい続ける」', '', '<img src=\"http://news.oricon.co.jp/feed/images/size640wh/20161207_192027_size640wh_6524.jpg\" title=\"『平成28年度 ゆうもあ大賞』授賞式に登壇した北島三郎 （C）ORICON NewS inc.\" alt=\"『平成28年度 ゆうもあ大賞』授賞式に登壇した北島三郎 （C）ORICON NewS inc.\" ><br />\n　演歌歌手の北島三郎（80）が、その年に“ユーモア”で貢献し、世の中に明るい話題を提供した人に贈られる『平成28年度 ゆうもあ大賞』を受賞し7日、都内で行われた表彰式に出席した。<br />\n<br />\n　記念品などを受け取った北島は「ありがとうございます。おかげさまでこの道を歩かせてもらって55年。まだその途中でございます」と感慨深げに喜ぶと「今年で80歳になりました。80だからもう歳だからとは思ってない。まだまだ好きな道、愛してもらえる歌をうたい続けていきたい。健康に気をつけながら一生懸命頑張りながら今後共よろしくお願いします」とあいさつした。<br />\n<br />\n　一方で自身が保有する競走馬・キタサンブラックが先日、G1・ジャパンカップを優勝。「この賞は私の馬でもらったのかな？　馬が有名になりまして、私よりも馬のほうが人気が出まして、この頃は『サブちゃん』よりも『ブラック』と呼ばれる」とうれしそうに明かして、笑いを誘っていた。<br />\n<br />\n　同賞にはそのほか、リオ五輪陸上男子400メートルリレーの銀メダリストの桐生祥秀選手、女子200メートルバタフライ銅メダルの星奈津美選手、今季限りで現役引退した三浦大輔氏が受賞し、表彰式に出席した。', 1, '2016-12-07 19:30:01', '2017-02-05 19:30:01', NULL, NULL, NULL, 0, 'http://www.oricon.co.jp/news/2079466/photo/3/?anc=013', '【写真】お孫さん（左）と共に登場した北島三郎', 'http://www.oricon.co.jp/news/2067604/photo/1/?anc=013', '【写真】北島三郎と娘の水町レイコ　親子2ショット', 'http://www.oricon.co.jp/news/2079528/?anc=013', '北島三郎、“同期”吉永小百合の祝福に感涙 （16年10月05日）', '', '', '', '', 0, 0, 1, 1, '2016-12-08 17:25:14', '2016-12-08 17:25:14'),
    (155, '4_1_r_20161208_1481185514225752725', 4, 1, 1, 1, 1, 'http://www.oricon.co.jp/news/2082693/', '5def9df4a8dd28f8833f5a4e59b65213_1481185514', 'BABYMETAL、レッチリ帯同UKツアー開幕', '', '<img src=\"http://news.oricon.co.jp/feed/images/size640wh/20161207_191257_size640wh_3746.jpg\" title=\"レッド・ホット・チリ・ペッパーズのUKツアーに帯同中のBABYMETAL（写真は初日公演）\" alt=\"レッド・ホット・チリ・ペッパーズのUKツアーに帯同中のBABYMETAL（写真は初日公演）\" ><br />\n　3人組メタルダンスユニット・BABYMETALが現地時間5・6日、イギリス・ロンドンのTHE O2（O2アリーナ）でスタートした米ロックバンド「レッド・ホット・チリ・ペッパーズ」のUKツアーにスペシャルゲストとして出演した。<br />\n<br />\n　BABYMETALは4月2日にイギリスの名門「SSEアリーナ・ウェンブリー」（ウェンブリー・アリーナ）で日本人アーティスト初のワンマンライブを行い、1万2000人を熱狂させた実績もあり、会場にはBABYMETALのTシャツを身に着けた現地ファンの姿も。<br />\n<br />\n　ライブでは英語でのコール＆レスポンスで観客をあおりながら、「KARATE」「ギミチョコ!!」「メギツネ」などライブ人気曲を披露。大歓声を浴びながら、おなじみの「See you！」のあいさつで締めくくった。<br />\n<br />\n　レッチリのUKツアーは、今月18日に再びTHE O2で行われる追加公演まで残り6公演。BABYMETALは全公演に帯同する。年明けには、米大御所バンド「ガンズ・アンド・ローゼズ」のジャパンツアーにゲスト出演することも決まっている。', 1, '2016-12-07 19:14:02', '2017-02-05 19:14:02', NULL, NULL, NULL, 0, 'http://music.oricon.co.jp/php/artist/ArtistTop.php?artist=A822345&ref_cd=MS0098&anc=015', '【試聴】BABYMETALの主な配信楽曲', 'http://www.oricon.co.jp/news/2082303/?anc=015', 'ガンズ・アンド・ローゼズ前座にベビメタ＆マンウィズ（16年12月01日）', 'http://www.oricon.co.jp/news/2069569/?anc=015', 'BABYMETAL、日本人初のウェンブリー公演　1万2000人熱狂 （16年04月03日）', '', '', '', '', 0, 0, 1, 1, '2016-12-08 17:25:14', '2016-12-08 17:25:14'),
    (156, '4_1_r_20161208_1481185514410605398', 4, 1, 1, 1, 1, 'http://www.oricon.co.jp/news/2082691/', 'c095ceba52a31eee327468fcb9bd6aaf_1481185514', 'YUKI、新曲が『3月のライオン』OPに 「今の自分の決意を歌ってみました」', '', '<img src=\"http://news.oricon.co.jp/feed/images/size640wh/20161207_183800_size640wh_8013.jpg\" title=\"YUKIの新曲がアニメ『3月のライオン』のオープニングテーマに\" alt=\"YUKIの新曲がアニメ『3月のライオン』のオープニングテーマに\" ><br />\n　歌手・YUKIの新曲「さよならバイスタンダー」（来年2月1日発売）が、NHK総合で放送中のアニメ『3月のライオン』（毎週土曜　後11：00）のオープニングテーマに決まり、来年1月から放送されることが明らかになった。<br />\n<br />\n　同曲は、来年2月6日にソロデビュー15周年を迎えるYUKIが、自身とアニメ主人公の若手棋士・桐山零を重ね合わせた歌詞に、バンドサウンドとストリングスを融合させたスピード感あふれるロックナンバー。<br />\n<br />\n　YUKIは「私は勝負の世界とは無縁の歌手ですが、いつも自分と闘って歌っています。これが本当に私のしたいことなのか、自問自答を繰り返しながら、歌う喜びに身を委ねてやっています。主人公の零くんの、将棋を指す静かな快楽を無視できずどんどんその世界にのめり込んでいく気持ちとは、交わることがないようでいて共感できるところはたくさんあるのです」と説明。「今の自分の決意を歌ってみました」と同曲に込めた思いを明かした。<br />\n<br />\n■YUKIコメント<br />\n『3月のライオン』のオープニングテーマを歌うことになって、とってもうれしいです。ありがとうございます。<br />\n私は勝負の世界とは無縁の歌手ですが、いつも自分と闘って歌っています。これが本当に私のしたいことなのか、自問自答を繰り返しながら、歌う喜びに身を委ねてやっています。<br />\n主人公の零くんの、将棋を指す静かな快楽を無視できずどんどんその世界にのめり込んでいく気持ちとは、交わることがないようでいて共感できるところはたくさんあるのです。<br />\n“他力”が働くと、良くも悪くも人は思わぬ方向へいくものです。私はずっとそうしてきたように思います。<br />\nでもここで、自分の中の傍観者とは距離を置きたくなりました。もう時は来たのです。「さよならバイスタンダー」は今の自分の決意を歌ってみました。<br />\n好きな女の子を守るために自分の足で歩き、強くなっていく零くんを見ているのは楽しくもあり、苦しくもなります。<br />\n私もずっと漫画を読みながら応援しています。<br />\n羽海野先生、ゆで卵を上手に作る方程式、ずっと解けなかったのですが、見事な実験結果の末の8分間、穴開け方法、早速試したところ、大成功でした…。<br />\n先生！ありがとうございます！！<br />\n美味しくて、かっこよくて泣けて笑える、先生の作品が大好きです。', 1, '2016-12-07 18:45:22', '2017-02-05 18:45:22', NULL, NULL, NULL, 0, 'http://www.oricon.co.jp/news/2077853/?anc=015', 'アニメ『3月のライオン』NHK総合で10・8スタート　 （16年09月03日）', 'http://www.oricon.co.jp/news/2075488/?anc=015', 'アニメ『3月のライオン』桐山零＆川本3姉妹のキャスト決定 （16年07月22日）', 'http://www.oricon.co.jp/news/2078158/?anc=015', '『3月のライオン』追加キャスト発表　井上麻里奈、細谷佳正ら （16年09月09日）', '', '', '', '', 0, 0, 1, 1, '2016-12-08 17:25:14', '2016-12-08 17:25:14'),
    (157, '4_1_r_20161208_1481185514480514878', 4, 1, 1, 1, 1, 'http://www.oricon.co.jp/news/2082418/', '6f7ab5aa44f64ce17dfc4fb2ae93c9cf_1481185514', '2016ブレイク芸人ランキング　1位はメイプル超合金', '', '<img src=\"http://news.oricon.co.jp/feed/images/size640wh/20161202_155639_size640wh_9127.jpg\" title=\"2016年ブレイク芸人ランキング、1位はメイプル超合金\" alt=\"2016年ブレイク芸人ランキング、1位はメイプル超合金\" ><br />\n　いよいよ暮れも差し迫った感のある2016年。ORICON STYLEでは、今年のエンタメシーンの総決算として、お茶の間に新しい笑いを提示してくれた芸人が多数登場する『2016年ブレイク芸人ランキング』を発表。見事1位に選ばれたのは、金髪に全身真っ赤の衣装がトレードマークのカズレーザーと、紫の衣装で体重130キロの巨体・安藤なつによる2人組【メイプル超合金】。文字通り“赤い彗星”のごとくお笑いシーンを席巻。決して“一発屋”では終わらない確かな実力を誇示してくれた。<br />\n<br />\n■見た目の派手さとは異なるどの番組にもハマる汎用性の高さ<br />\n<br />\n　1位に選ばれた【メイプル超合金】のブレイクは、お笑いシーンの勢力図に変化をもたらすほどのインパクトだった。2012年8月に結成した彼らは、昨年末に開催された『M-1グランプリ2015』で初の決勝進出。一気に知名度を上げた彼らは今年に入り、あらゆるバラエティ番組に出演。彼らは、何か有名な一発ギャグやネタがあるわけではないのに、一体どこがウケたのか？<br />\n<br />\n「ネタよりも、キャラが目立っている。キャラ自体が面白いので、そこがほかの芸人とは違うように感じた。トーク力も高く場を回す力もある」（新潟／10代・女性）。実際に、ネタ番組よりトーク番組への出演やピンでの活動が多いのは、キャラウケしている証明だ。カズレーザーについては、「頭もいいし万能」（埼玉／30代・女性）、「多くのクイズ番組に出演している」（千葉／20代・女性）と、同志社大学卒の高学歴芸人として『Qさま!!』など数々のクイズ番組への出演も高評価。また、ドラマ『吉祥寺だけが住みたい街ですか？』（テレビ東京系）に出演するなど、女優としても活躍する安藤なつについても「ピンでもキャラを発揮し始めてる稀有なケース」（宮城／40代・男性）、「コンビでも1人でも沢山の番組に出ている」（静岡／10代・女性）と、まったく違った個性を活かしたピン活動が、ブレイクに拍車をかけたといえる。見た目の破壊力が凄まじい2人だが、決してインパクトだけではなく実は奥が深い……そのギャップが魅力となっているようだ。<br />\n<br />\n■下半期に彗星の如く表れたピコ太郎が“世界”を席巻！<br />\n<br />\n　2位は、下半期に入って一気にブレイクした【ピコ太郎】がランクイン。ジャスティン・ビーバーがTwitterで「PPAP」を紹介したことをきっかけに、YouTubeの再生回数が加速。YouTubeの週間再生回数ランキング『ミュージック全世界トップ100』（集計期間9/30～10/6付）で日本人初の世界一。さらに、ビルボードチャートトップ100にランクインしたもっとも短い曲としてギネス記録に認定、外国人記者クラブで会見を行うなど話題にこと欠かない。<br />\n<br />\n　「YouTubeで1位を取ったのと、ビルボードにランクインしたのは凄い！と思います」（青森／40代・男性）。特にハロウィーンにかけての勢いはめざましく、「ブレイクの爆発度で言ったら他とは段違い」（東京／40代・男性）、「InstagramとかTwitterとかで、真似している人たちの動画を見ることがすごく多い」（福岡／20代・女性）と、誰もがネットやSNSによる口コミ効果の大きさを感じたようだ。俗に言えば一発ギャグとリズムネタの合わせ技ではあるが、「単純な英語とスローテンポダンスで見る人を中毒に陥れた」（東京／30代・女性）と、ネタの個性にも評価。シュールながらシンプルなテイストと、プロデューサー＝古坂大魔王によるキャッチーなサウンドが相まって、中毒者が続出したことがブレイクの要因になった。<br />\n<br />\n■“ハゲ＝モテない”の定義を覆す活躍<br />\n<br />\n　3位には【トレンディエンジェル】がランクイン。「M-1グランプリを取ってからの仕事量がかなり増えていると思う」（愛知／30代・女性）、「M-1優勝から、さらに勢いが増した」（東京／40代・女性）と、昨年のM-1グランプリ王者になって以降のコンスタントなテレビ出演が認知を広めた。また「色んなジャンルのテレビ番組で見たから」（千葉／10代・女性）、「バラエティ番組で頻繁に見る機会があり、CMでも活躍していた」（滋賀／30代・男性）と、ジャンルを問わない幅広い活躍を挙げる声。<br />\n<br />\n　今年は、文化放送『トレンディエンジェルのPePePeラジオ』のレギュラー放送のほか、CMやドラマへの出演、『ウチくる!?』『おしゃれイズム』（共に日本テレビ系）など多彩な番組に出演し、フリートークの面白さとポテンシャルの高さで引っ張りだこに。また、斎藤のギャグ「斎藤さんだぞ！」や「ペッペッペ?！」は、「子供たちにも人気」（山形／30代・女性）、「斎藤さんが出演されていない時も他の芸能人が真似している」（大阪／30代・女性）と、子どもから大人まで幅広く浸透していることも人気の要因だ。<br />\n<br />\n<br />\n■ブレイク芸人の必須事項はやはり“リズムネタ”なのか？<br />\n<br />\n　そのほか、5位にはRADIOFISHとして発表した「PERFECT HUMAN」がリズムネタの枠を越えて人気になり、再ブレイクを果たした【オリエンタルラジオ】。ユーザーの意見も「「PERFECTHUMAN」は面白かったし、芸人という枠を越えて活躍している」（北海道／10代・男性）、「楽曲「PERFECT HUMAN」が曲とダンスが格好よくてヒットしたから」（大阪／50代・男性）。最初のブレイクネタである「武勇伝」に始まり、再び「PERFECT HUMAN」というリズムネタに回帰。何度も這い上がるバイタリティに感服した人も多いだろう。<br />\n<br />\n　また、苦節20数年でついにお茶の間に浸透した【永野】が9位に。奇しくもピコ太郎やオリエンタルラジオと同様リズムネタ「ラッセンが好き」でブレイク。「あれは強烈で忘れられないから」（福島／20代・女性）など、意外に女性ユーザーから人気。ももクロやAKBなどのアイドルとの共演も後押しし、永野をきっかけにラッセンの再ブームも起きたことも印象的だった。。<br />\n<br />\n■横澤、平野、ゆりやん…女性ピン芸人も豊作の1年<br />\n<br />\n　また、今年は女性芸人の躍進も顕著だった。4位（女性芸人部門では1位）の【横澤夏子】は、細かすぎるものまねが話題となりバラエティ番組に進出。「存在感がすごいし間違いなく笑いをとるから驚き」（愛知／10代・女性）、「トーク力からもバラエティ番組でよく見かけた」（千葉／20代・女性）と、ネタ同様独自の視点から繰り出すコメントや返しの上手さで、バラエティ番組には欠かせない存在になった。<br />\n<br />\n　6位には、バブルネタでブレイクした【平野ノラ】。「バブル時代が懐かしく面白く、一番ネタとして見れる芸人」（兵庫／40代・女性）と、かつてバブル時代を謳歌した世代に刺さっただけでなく、「1度見て印象に残る。衣装やバブル時代を思わせるような髪型、化粧もインパクトがある」（福岡／10代・女性）など、バブルを知らない世代にも新鮮に受け入れられたようだ。7位の【おかずクラブ】は、「2人のバランスが、良く、色々な番組にコーナーを持っている」（福岡／50代・女性）など、『ヒルナンデス！』（日本テレビ系）レギュラー出演もあってか、主婦層からの支持が増加。他、Instagramのフォロワー数や海外ライブの成功など、芸人の枠を越えた活動に支持が集まった8位の【渡辺直美】。「すい星のごとく出てきた」（広島／40代・男性）と注目の【ゆりやんレトリィバァ】が10位にランクインした。<br />\n<br />\n　今年ブレイクしたからと言って、一発屋で終わらないとは限らないのが芸人の世界。「芸人は2度売れないといけない」。テレビ業界や芸人の間でよく言われる説だ。今年ブレイクを果たしたニューフェイスは、芸人としての真価が問われる、来年以降の活躍に注目だ。<br />\n<br />\n【2016年 ブレイク芸人ランキングTOP10】<br />\n1位　メイプル超合金<br />\n2位　ピコ太郎（古坂大魔王）<br />\n3位　トレンディエンジェル<br />\n4位　横澤夏子<br />\n5位　オリエンタルラジオ<br />\n6位　平野ノラ<br />\n7位　おかずクラブ<br />\n8位　渡辺直美<br />\n9位　永野<br />\n10位 ゆりやんレトリィバァ<br />\n<br />\n【調査概要】<br />\n調査時期：2016年10月12日（水）～10月17日（火）<br />\n調査対象：計1000名（自社アンケート・パネル【オリコン・モニターリサーチ】会員10代、20代、30代、40代、50代の男女）<br />\n調査地域：全国<br />\n調査方法：インターネット調査<br />\n調査機関：オリコン・モニターリサーチ', 1, '2016-12-06 08:40:01', '2017-02-04 08:40:01', NULL, NULL, NULL, 0, 'http://www.oricon.co.jp/photo/2000/136065/?anc=019', '【写真】世界を席巻した「PPAP」のあの人がランクイン…ブレイク芸人フォトギャラリー', 'http://www.oricon.co.jp/special/49132/?anc=019#link4', '【ランキング表】2016 上半期ブレイク芸人ランキング', 'http://www.oricon.co.jp/special/48557/?anc=019#link4', '【ランキング表】2015年 ブレイク芸人ランキング', '', '', '', '', 0, 0, 1, 1, '2016-12-08 17:25:14', '2016-12-08 17:25:14'),
    (158, '4_1_r_20161208_1481185514978334954', 4, 1, 1, 1, 1, 'http://www.oricon.co.jp/news/2082678/', '43dbc439bb1a43cd79852ccb23143d3d_1481185514', '浜田、声帯炎で『ガキ使』特番会見を欠席　松本は疑いの目「愛人のところで…」', '', '<img src=\"http://news.oricon.co.jp/feed/images/size640wh/20161207_171239_size640wh_2367.jpg\" title=\"大みそか年越しスペシャル『絶対に笑ってはいけない科学博士24時！』の記者会見に出席した（左から）月亭方正、松本人志、遠藤章造、田中直樹\" alt=\"大みそか年越しスペシャル『絶対に笑ってはいけない科学博士24時！』の記者会見に出席した（左から）月亭方正、松本人志、遠藤章造、田中直樹\" ><br />\n　ダウンタウンの浜田雅功（53）が声帯炎のため、7日に都内で行われた日本テレビ系『ダウンタウンのガキの使いやあらへんで!!』の大みそか年越しスペシャル『絶対に笑ってはいけない科学博士24時！』（後6：30～前0：30）記者会見を欠席した。会見冒頭、進行役を務めた同局の岩本乃蒼アナウンサーが「浜田さんは体調不良から声帯を炎症しており、声帯炎と診断され、本日はお休みということで…」と説明した。<br />\n<br />\n　岩本アナの言葉を受けて、相方の松本人志（53）が「もう、本当に申し訳ございません」と代わりに謝罪しながらも「何か、今聞いたら浜田さんは愛人のところで静養している」とチクリ。ココリコの遠藤章造（45）や月亭方正（48）が「いやいや、そんなことはないですよ」と否定しようとするも、すぐさま「いや…マジやって！」と意味ありげに切り返すやりとりをひとしきり続けて、会場の笑いを誘った。<br />\n<br />\n　そこからは、堰（せき）を切ったように本人不在の“浜田イジり”で大盛り上がり。田中直樹（45）は収録中の出来事を挙げ「浜田さんのことがとても心配なんですけど、思い返せばその時から予兆があった。浜田さんが、数ミリの段差でつまずいて『何やっているんですか？』みたいなのがあったんですけど、2時間後また同じところでつまずいたんですよ」と回想。松本も同調し「彼は、デビュー当時から同じことでつまずいている。これからもつまずくと思います」といたずらっぽく笑っていた。<br />\n<br />\n　所属事務所によると、きょう浜田からの申し出があり欠席が決定。同日の『ダウンタウンDX』（毎週木曜　後10:00）収録も見合わせとなったが、期限を決めた休養という形ではなく、症状が回復次第、仕事を再開していく予定だという。<br />\n<br />\n　今回で11年目に突入する、大みそか恒例の“笑ってはいけないシリーズ”。今回「新人科学研究員」に扮した5人は『ガースー黒光り科学研究所』を舞台に、科学研究所という設定で起こり得る数々の訓練やイベントはもちろん、移動中のバスの中で仕掛けられたトラップや、“驚いてはいけない”ブロックなど、バラエティーに富んだ笑いを届けていく。', 1, '2016-12-07 18:00:00', '2017-02-05 18:00:00', NULL, NULL, NULL, 0, 'http://beauty.oricon.co.jp/trend-culture/trend/news/photo/2016025/1/?anc=148', '【写真】ココリコ田中の美人妻・小日向しえとは…', 'http://www.oricon.co.jp/news/2038637/?anc=148', '浜田雅功、不倫報道で謝罪「羽を伸ばし過ぎた」 （14年06月14日）', 'http://www.oricon.co.jp/news/2038638/?anc=148', '小川菜摘、浜ちゃん不倫報道にも「我が家は大丈夫」 （14年06月14日）', '', '', '', '', 0, 0, 1, 1, '2016-12-08 17:25:15', '2016-12-08 17:25:15'),
    (159, '4_1_r_20161208_1481185515071577523', 4, 1, 1, 1, 1, 'http://www.oricon.co.jp/news/2082657/', '327a413b4a8bdba6bc934a35b40f0c34_1481185515', '松本人志、元ほっしゃん。星田の引退発言イジる　年末『ガキ使』の“引き際”は…？', '', '<img src=\"http://news.oricon.co.jp/feed/images/size640wh/20161207_171932_size640wh_3957.jpg\" title=\"元ほっしゃん。星田の引退発言イジった松本人志 （C）ORICON NewS inc.\" alt=\"元ほっしゃん。星田の引退発言イジった松本人志 （C）ORICON NewS inc.\" ><br />\n　お笑いコンビ・ダウンタウンの松本人志（53）が7日、都内で行われた日本テレビ系『ダウンタウンのガキの使いやあらへんで!!』の大みそか年越しスペシャル『絶対に笑ってはいけない科学博士24時！』（後6：30～前0：30）記者会見に出席。昨年の同特番の会見でシリーズ卒業をほのめかしていたが、この日は一転して「本当は去年で辞めるって言っていたんですが、皆さんの熱いご要望ということでやってみようと。まぁ、ほっしゃん。みたいになってしまったんですが」と後輩芸人の話題に絡めて、シリーズ継続を宣言した。<br />\n<br />\n　同席したココリコや月亭方正（48）が「その日のうちに（引退を）撤回して『何で言うたんや』っていう感じですけど」と合いの手を入れると、松本も「僕も全くわかっていないので、このあと会社の人間に聞いてみたいと思います」とコメント。騒動をイジりながらも「名前を変え始めた頃から、ちょっとおかしかったんですけどね…」と優しく気遣った。<br />\n<br />\n　今回で11年目に突入する、大みそか恒例の“笑ってはいけないシリーズ”。今年は「新人科学研究員」に扮して様々なトラップに挑んでいるが、松本は「もう53歳になりましたけど、昔で言うと元帥ですからね。それが、おしりを数百発叩かれて、我ながらよく頑張っているなと思います」と不条理を訴えながら「我々の高齢化を突いてきて『ハイテク機械を使って、何かやれ』みたいなことをしてくる。あれは、本当に初老虐待やと思います」とスタッフが仕掛けたトラップへの不満も口にした。<br />\n<br />\n　同シリーズにたびたび出演しているマツコ・デラックスが、裏番組『第67回NHK紅白歌合戦』に出演することが発表されたことを受け「ガキ使シフトでは？」との声も飛んだが、松本は「どっちかというと、我々も紅白に出たい側なんですよ。おしりを叩かれることもないし、まさかタイキックとかもないだろうし」と本音をポツリ。「ただ、ヒット曲がないから、来年は何とかヒット曲を出すことで、この番組から卒業していくという」と理想的な引き際を明かしていた。<br />\n<br />\n　浜田雅功（53）は声帯炎のため、この日の会見を欠席。所属事務所によると、同日に収録される冠番組『ダウンタウンDX』（毎週木曜 後10:00）への参加も見合わせとなったが、期限を決めた休養という形ではなく、症状が回復次第、仕事を再開していく予定だという。', 1, '2016-12-07 18:00:00', '2017-02-05 18:00:00', NULL, NULL, NULL, 0, 'http://www.oricon.co.jp/news/2082642/?anc=148', '元ほっしゃん。星田英利が引退発言を撤回「ひたむきに頑張ります」 （16年12月06日）', 'http://beauty.oricon.co.jp/trend-culture/trend/news/photo/2016025/1/?anc=148', '【写真】ココリコ田中の美人妻・小日向しえとは…', 'http://www.oricon.co.jp/news/2038637/?anc=148', '浜田雅功、不倫報道で謝罪「羽を伸ばし過ぎた」 （14年06月14日）', '', '', '', '', 0, 0, 1, 1, '2016-12-08 17:25:15', '2016-12-08 17:25:15'),
    (160, '4_1_r_20161208_1481185515546613409', 4, 1, 1, 1, 1, 'http://www.oricon.co.jp/news/2082682/', '39af0b37c96e92a43df41deda74361ae_1481185515', 'ベッキー、騒動後初テレビCM　ナレーションで起用', '', '<img src=\"http://news.oricon.co.jp/feed/images/size640wh/20160930_182704_size640wh_4741.jpg\" title=\"ベッキー （C）ORICON NewS inc.\" alt=\"ベッキー （C）ORICON NewS inc.\" ><br />\n　タレントのベッキー（32）が、コミュニケーションアプリ「LINE」の新TVCMにナレーターとして起用されたことが7日、わかった。ベッキーは今年1月の『週刊文春』でゲスの極み乙女。の川谷絵音（28）との不倫騒動後、出版社・宝島社の企業広告への登場はあるが、テレビCMは初となる。<br />\n<br />\n　「LINE」サービス開始当初の2011年に同社のCMに出演していたベッキー。今回CMでは、本人が登場することはないが“声”で出演する。同社もサービスを開始して今年で6年目となり、原点に帰る意味も込めてベッキーを起用しているという。<br />\n<br />\n　ベッキーは騒動後の休業を経て、5月13日のTBS系『中居正広の金曜日のスマイルたちへ』で104日ぶりにTV出演。7月放送開始のBSスカパーの音楽番組『FULL CHORUS～音楽は、フルコーラス～』で本格復帰し、9月29日に出版社・宝島社で“背中ヌード”を披露。<br />\n<br />\n　10月に入ってからはラジオレギュラー番組開始や9日放送の『ワイドナショー』（フジテレビ系）に出演。その後も人気ロックユニット・VAMPSによる毎年恒例のハロウィンライブイベント『HALLOWEEN PARTY 2016』に出演するなど、徐々に活動を本格化させている。<br />\n<br />\n　新CMは年末年始に放送される予定。', 1, '2016-12-07 16:34:05', '2017-02-05 16:34:05', NULL, NULL, NULL, 0, 'http://beauty.oricon.co.jp/trend-culture/trend/news/photo/2045387/2/?anc=112', '【写真】ベッキー、ドット柄ワンピでスレンダーボディを披露', 'http://beauty.oricon.co.jp/trend-culture/trend/news/photo/71222/1/?anc=112', '【写真】人生初のボブヘアを披露したベッキー', 'http://www.oricon.co.jp/news/2073165/photo/1/?anc=112', '【写真】復帰時…会見を行ったベッキー', '', '', '', '', 0, 0, 1, 1, '2016-12-08 17:25:16', '2016-12-08 17:25:16'),
    (161, '4_1_r_20161208_1481185515732753774', 4, 1, 1, 1, 1, 'http://www.oricon.co.jp/news/2082658/', '81826cd1d5f0769f39caf2eca75f483e_1481185515', 'DeNA、管理体制の不備陳謝「サービスの成長を追い求める過程で…」', '', '<img src=\"http://news.oricon.co.jp/feed/images/size640wh/20161207_171020_size640wh_5934.jpg\" title=\"都内で記者会見を行ったディー・エヌ・エーの（左から）小林賢治氏、守安功氏、南場智子氏 （C）ORICON NewS inc.\" alt=\"都内で記者会見を行ったディー・エヌ・エーの（左から）小林賢治氏、守安功氏、南場智子氏 （C）ORICON NewS inc.\" ><br />\n　ディー・エヌ・エー（DeNA）が運営するヘルスケア情報キュレーションサイト「WELQ（ウェルク）」の無断引用や事実誤認の指摘に端を発した問題で、同社は7日、都内で記者会見を行った。一連の経緯、記事作成のプロセスや非公開化、第三者調査委員会設置などについて説明し、騒動について謝罪した。<br />\n<br />\n　会見には、同社の代表取締役社長兼CEO・守安功氏、取締役会長・南場智子氏、執行役員 経営企画本部長・小林賢治氏が出席。会見冒頭、守安社長が「すべての関係者に多大なるご迷惑、ご心配をおかけしましたことを深くお詫び申し上げます。また皆さんに対して直接、私から説明することが遅くなってしまいましたことを重ねてお詫び申し上げます」と陳謝し、出席者全員が頭を下げた。<br />\n<br />\n　南場会長も「ご批判が企業風土や組織のあり方に及んでいるという事態になりまして、取締役会長として、そして創業者として、責任のある立場にあります」と神妙な面持ちで話すと「そこでみなさんに直接お詫びを申し上げたく参りました。本当に申し訳ございませんでした」と陳謝した。<br />\n<br />\n　「WELQ」では、これまで専門家の監修なしに医療や薬品に関する情報を多数掲載。医学的根拠の乏しい記事が多いとして、医療関係者らから指摘が相次いでいた。 守安氏は「大きな問題と反省している」と過ちを認め、7日までにキュレーションメディアプラットフォーム事業（DeNAパレット）で展開する「WELQ」、「MERY（メリー）」を含む全10媒体を非公開化し、同社ホームページ上に相談窓口を開設した。<br />\n<br />\n　守安社長は「サービスの成長を追い求める過程で、ユーザーのみなさまに正しい信頼できる情報をお届けすることを担保できない体制となっていた。他の方が作成された記事などを不適切に利用することを助長しかねない運営体制となっていたこと、このような運営体制にならない管理体制を構築できなかったことについて深く反省しております」と述べた。<br />\n<br />\n　今後は、第三者調査委員会の調査に「全面的に協力する」とし「今回の問題の全容を報告するとともに社内でもしっかりと問題のある部分とその原因を洗い出し、徹底的な改善策を実行してまいります。1日でも早く皆さま信頼を回復できるよう私自身が全社員の先頭に立ち、全力で責任を果たしてまいります」と話した。', 1, '2016-12-07 17:14:01', '2017-02-05 17:14:01', NULL, NULL, NULL, 0, 'http://www.oricon.co.jp/news/2082244/?anc=013', 'ディー・エヌ・エー「WELQ」の全記事非公開に　医療情報の信憑性問題受け （16年11月29日）', 'http://www.oricon.co.jp/article/64052/?anc=013', '「NAVERまとめ」が新方針　“キュレーションサイト問題”受け「まとめ作成者の信頼ランク」採用へ（16年12月05日）', 'http://www.oricon.co.jp/article/4298/?anc=013', '元ユーザーの個人情報さらして炎上した「ゴルスタ」終了、法的にどんな問題があった？（16年09月07日）', '', '', '', '', 0, 0, 1, 1, '2016-12-08 17:25:16', '2016-12-08 17:25:16');
